  Surah(
      id_=3,
      titleEnglish_="Aal-e-Imran ",
      titleArabic_="آل عمران",
      verses_=listOf(
      Verse(
              id_=1,
              arabic_="الٓمَّٓ  ۙ﴿۱﴾",
              urdu_="الم۔",
          ),

      Verse(
              id_=2,
              arabic_="اللّٰہُ  لَاۤ اِلٰہَ  اِلَّا ہُوَ ۙ الۡحَیُّ الۡقَیُّوۡمُ ؕ﴿۲﴾ ",
              urdu_="اللہ جو معبود برحق ہے اس کے سوا کوئی عبادت کے لائق نہیں زندہ ہے سب کو تھامنے والا۔",
          ),

      Verse(
              id_=3,
              arabic_="نَزَّلَ عَلَیۡکَ الۡکِتٰبَ بِالۡحَقِّ مُصَدِّقًا لِّمَا بَیۡنَ یَدَیۡہِ وَ اَنۡزَلَ التَّوۡرٰٮۃَ وَ الۡاِنۡجِیۡلَ ۙ﴿۳﴾",
              urdu_="اس نے اے نبی تم پر سچی کتاب نازل کی جو پہلی آسمانی کتابوں کی تصدیق کرتی ہے اور اسی نے تورات اور انجیل نازل کی تھی۔",
          ),

      Verse(
              id_=4,
              arabic_="مِنۡ قَبۡلُ ہُدًی لِّلنَّاسِ وَ اَنۡزَلَ الۡفُرۡقَانَ ۬ؕ اِنَّ الَّذِیۡنَ کَفَرُوۡا بِاٰیٰتِ اللّٰہِ لَہُمۡ عَذَابٌ شَدِیۡدٌ ؕ وَ اللّٰہُ عَزِیۡزٌ ذُو انۡتِقَامٍ ﴿۴﴾",
              urdu_="یہ دو کتابیں اس نے پہلے نازل کی تھیں لوگوں کی ہدایت کے لئے اور پھر قرآن جو حق اور باطل کو الگ الگ کر دینے والا ہے نازل کیا۔ جو لوگ اللہ کی آیتوں کا انکار کرتے ہیں انکو سخت عذاب ہو گا۔ اور اللہ زبردست ہے بدلہ لینے والا ہے۔",
          ),

      Verse(
              id_=5,
              arabic_="اِنَّ اللّٰہَ  لَا  یَخۡفٰی عَلَیۡہِ شَیۡءٌ  فِی الۡاَرۡضِ وَ لَا فِی السَّمَآءِ ؕ﴿۵﴾",
              urdu_="اللہ ایسا خبیر و بصیر ہے کہ کوئی چیز اس سے پوشیدہ نہیں نہ زمین میں اور نہ آسمان میں۔",
          ),

      Verse(
              id_=6,
              arabic_="ہُوَ  الَّذِیۡ یُصَوِّرُکُمۡ  فِی الۡاَرۡحَامِ  کَیۡفَ یَشَآءُ ؕ لَاۤ اِلٰہَ  اِلَّا ہُوَ الۡعَزِیۡزُ الۡحَکِیۡمُ ﴿۶﴾",
              urdu_="وہی تو ہے جو ماں کے پیٹ میں جیسی چاہتا ہے تمہاری صورتیں بناتا ہے۔ اس غالب حکمت والے کے سوا کوئی عبادت کے لائق نہیں۔",
          ),

      Verse(
              id_=7,
              arabic_="ہُوَ الَّذِیۡۤ اَنۡزَلَ عَلَیۡکَ الۡکِتٰبَ مِنۡہُ اٰیٰتٌ مُّحۡکَمٰتٌ ہُنَّ اُمُّ  الۡکِتٰبِ وَ اُخَرُ مُتَشٰبِہٰتٌ ؕ فَاَمَّا الَّذِیۡنَ فِیۡ قُلُوۡبِہِمۡ زَیۡغٌ فَیَتَّبِعُوۡنَ مَا تَشَابَہَ مِنۡہُ ابۡتِغَآءَ الۡفِتۡنَۃِ وَ ابۡتِغَآءَ تَاۡوِیۡلِہٖ ۚ؃ وَ مَا یَعۡلَمُ  تَاۡوِیۡلَہٗۤ  اِلَّا اللّٰہُ  ۘؔ وَ الرّٰسِخُوۡنَ فِی الۡعِلۡمِ یَقُوۡلُوۡنَ اٰمَنَّا بِہٖ ۙ کُلٌّ  مِّنۡ عِنۡدِ رَبِّنَا ۚ وَ مَا یَذَّکَّرُ  اِلَّاۤ اُولُوا الۡاَلۡبَابِ ﴿۷﴾ ",
              urdu_="اے پیغمبر وہی تو ہے جس نے تم پر کتاب نازل کی جس کی بعض آیتیں محکم ہیں اور وہی اصل کتاب ہیں اور بعض دوسری متشابہ ہیں تو جن لوگوں کے دلوں میں کجی ہے و متشابہات کا اتباع کرتے ہیں تاکہ فتنہ برپا کریں اور مراد اصلی کا پتہ لگائیں حالانکہ مراد اصلی اللہ کے سوا کوئی نہیں جانتا اور جو لوگ علم میں گہری نظر رکھتے ہیں وہ یہ کہتے ہیں کہ ہم ان پر ایمان لائے۔ یہ سب آیات ہمارے پروردگار کی طرف سے ہیں۔ اور نصیحت تو عقلمند ہی قبول کرتے ہیں۔",
          ),

      Verse(
              id_=8,
              arabic_="رَبَّنَا لَا تُزِغۡ قُلُوۡبَنَا بَعۡدَ  اِذۡ ہَدَیۡتَنَا وَ ہَبۡ لَنَا مِنۡ لَّدُنۡکَ رَحۡمَۃً ۚ اِنَّکَ اَنۡتَ الۡوَہَّابُ ﴿۸﴾",
              urdu_="اے پروردگار جب تو نے ہمیں ہدایت بخشی ہے تو اس کے بعد ہمارے دلوں میں کجی نہ پیدا کر دیجیو اور ہمیں اپنے ہاں سے نعمت عطا فرما تو تو بڑا عطا فرمانے والا ہے۔",
          ),

      Verse(
              id_=9,
              arabic_="رَبَّنَاۤ اِنَّکَ جَامِعُ النَّاسِ لِیَوۡمٍ لَّا رَیۡبَ فِیۡہِ ؕ اِنَّ اللّٰہَ  لَا یُخۡلِفُ الۡمِیۡعَادَ ٪﴿۹﴾ ",
              urdu_="اے پروردگار تو اس روز جس کے آنے میں کچھ بھی شک نہیں سب لوگوں کو اپنے حضور میں جمع کر لے گا بیشک اللہ خلاف وعدہ نہیں کرتا۔",
          ),

      Verse(
              id_=10,
              arabic_="اِنَّ الَّذِیۡنَ کَفَرُوۡا لَنۡ تُغۡنِیَ عَنۡہُمۡ اَمۡوَالُہُمۡ  وَ لَاۤ  اَوۡلَادُہُمۡ  مِّنَ اللّٰہِ شَیۡئًا ؕ وَ اُولٰٓئِکَ ہُمۡ  وَقُوۡدُ  النَّارِ ﴿ۙ۱۰﴾ ",
              urdu_="جو لوگ کافر ہوئے اس دن نہ تو ان کا مال ہی اللہ کےعذاب سے ان کو بچا سکے گا اور نہ ان کی اولاد ہی کچھ کام آئے گی اور یہ لوگ آتش جہنم کا ایندھن ہونگے۔",
          ),

      Verse(
              id_=11,
              arabic_="کَدَاۡبِ اٰلِ فِرۡعَوۡنَ ۙ وَ الَّذِیۡنَ مِنۡ قَبۡلِہِمۡ ؕ  کَذَّبُوۡا بِاٰیٰتِنَا ۚ فَاَخَذَہُمُ اللّٰہُ بِذُنُوۡبِہِمۡ ؕ وَ اللّٰہُ شَدِیۡدُ الۡعِقَابِ ﴿۱۱﴾ ",
              urdu_="ان کا حال بھی فرعونیوں اور ان سے پہلے لوگوں کا سا ہوگا جنہوں نے ہماری آیتوں کی تکذیب کی تھی تو اللہ نے ان کو ان کے گناہوں کے سبب عذاب میں پکڑ لیا تھا اور اللہ سخت عذاب کرنے والا ہے۔",
          ),

      Verse(
              id_=12,
              arabic_="قُلۡ لِّلَّذِیۡنَ کَفَرُوۡا سَتُغۡلَبُوۡنَ وَ تُحۡشَرُوۡنَ اِلٰی جَہَنَّمَ ؕ وَ بِئۡسَ الۡمِہَادُ ﴿۱۲﴾ ",
              urdu_="اے پیغمبر کافروں سے کہدو کہ تم دنیا میں بھی عنقریب مغلوب ہو جاؤ گے اور آخرت میں جہنم کی طرف اکٹھا کئے جاؤ گے اور وہ بری جگہ ہے۔",
          ),

      Verse(
              id_=13,
              arabic_="قَدۡ کَانَ لَکُمۡ اٰیَۃٌ فِیۡ فِئَتَیۡنِ الۡتَقَتَا ؕ فِئَۃٌ تُقَاتِلُ فِیۡ سَبِیۡلِ اللّٰہِ وَ اُخۡرٰی کَافِرَۃٌ یَّرَوۡنَہُمۡ مِّثۡلَیۡہِمۡ رَاۡیَ الۡعَیۡنِ ؕ وَ اللّٰہُ یُؤَیِّدُ بِنَصۡرِہٖ مَنۡ یَّشَآءُ ؕ اِنَّ فِیۡ ذٰلِکَ لَعِبۡرَۃً   لِّاُولِی الۡاَبۡصَارِ ﴿۱۳﴾ ",
              urdu_="تمہارے لئے دو گروہوں میں جو جنگ بدر کے دن آپس میں بھڑ گئے قدرت الٰہی کی عظیم الشان نشانی تھی ایک گروہ مسلمانوں کا تھا وہ اللہ کی راہ میں لڑ رہا تھا۔ اور دوسرا گروہ کافروں کا تھا وہ ان کو اپنی آنکھوں سے اپنے سے دوگنا مشاہدہ کر رہا تھا۔ اور اللہ اپنی نصرت سے جس کو چاہتا ہے مدد دیتا ہے جو اہل بصارت ہیں ان کے لئے اس واقعے میں بڑی عبرت ہے۔",
          ),

      Verse(
              id_=14,
              arabic_="زُیِّنَ لِلنَّاسِ حُبُّ الشَّہَوٰتِ مِنَ النِّسَآءِ وَ الۡبَنِیۡنَ وَ الۡقَنَاطِیۡرِ الۡمُقَنۡطَرَۃِ مِنَ الذَّہَبِ وَ الۡفِضَّۃِ وَ الۡخَیۡلِ الۡمُسَوَّمَۃِ وَ الۡاَنۡعَامِ وَ الۡحَرۡثِ ؕ ذٰلِکَ مَتَاعُ  الۡحَیٰوۃِ الدُّنۡیَا ۚ وَ اللّٰہُ عِنۡدَہٗ حُسۡنُ الۡمَاٰبِ ﴿۱۴﴾ ",
              urdu_="لوگوں کو ان کی خواہشوں کی چیزیں یعنی عورتیں اور بیٹے اور سونے اور چاندی کے بڑے بڑے ڈھیر اور نشان لگے ہوئے گھوڑے اور مویشی اور کھیتی بڑی زینت دار معلوم ہوتی ہیں مگر یہ سب دنیا ہی کی زندگی کے سامان ہیں۔ اور اللہ کے پاس بہت اچھا ٹھکانہ ہے۔",
          ),

      Verse(
              id_=15,
              arabic_="قُلۡ اَؤُنَبِّئُکُمۡ بِخَیۡرٍ مِّنۡ ذٰلِکُمۡ ؕ لِلَّذِیۡنَ اتَّقَوۡا عِنۡدَ رَبِّہِمۡ جَنّٰتٌ  تَجۡرِیۡ مِنۡ تَحۡتِہَا الۡاَنۡہٰرُ خٰلِدِیۡنَ فِیۡہَا وَ اَزۡوَاجٌ مُّطَہَّرَۃٌ وَّ رِضۡوَانٌ مِّنَ اللّٰہِ ؕ وَ اللّٰہُ بَصِیۡرٌۢ  بِالۡعِبَادِ ﴿ۚ۱۵﴾ ",
              urdu_="اے پیغمبر ان سے کہو کہ بھلا میں تم کو ایسی چیز بتاؤں جو ان چیزوں سے کہیں اچھی ہو سنو جو لوگ پرہیزگار ہیں ان کے لئے اللہ کے ہاں باغات بہشت ہیں جن کے نیچے نہریں بہ رہی ہیں ان میں وہ ہمیشہ رہیں گے۔ اور پاکیزہ بیویاں ہیں اور سب سے بڑھ کر اللہ کی خوشنودی۔ اور اللہ اپنے سب بندوں کو دیکھ رہا ہے۔",
          ),

      Verse(
              id_=16,
              arabic_="اَلَّذِیۡنَ یَقُوۡلُوۡنَ رَبَّنَاۤ اِنَّنَاۤ اٰمَنَّا فَاغۡفِرۡ لَنَا ذُنُوۡبَنَا وَ قِنَا عَذَابَ النَّارِ ﴿ۚ۱۶﴾ ",
              urdu_="جو اللہ سے التجا کرتے ہیں کہ اے پروردگار ہم ایمان لے آئے سو ہم کو ہمارے گناہ معاف فرما اور ہم کو دوزخ کے عذاب سے محفوظ رکھ۔",
          ),

      Verse(
              id_=17,
              arabic_="اَلصّٰبِرِیۡنَ وَ الصّٰدِقِیۡنَ وَ الۡقٰنِتِیۡنَ وَ الۡمُنۡفِقِیۡنَ وَ الۡمُسۡتَغۡفِرِیۡنَ بِالۡاَسۡحَارِ ﴿۱۷﴾ ",
              urdu_="یہ وہ لوگ ہیں جو مشکلات میں صبر کرتے اور سچ بولتے اور عبادت میں لگے رہتے اور نیکی میں خرچ کرتے اور اوقات سحر میں گناہوں کی معافی مانگا کرتے ہیں۔",
          ),

      Verse(
              id_=18,
              arabic_="شَہِدَ اللّٰہُ  اَنَّہٗ  لَاۤ  اِلٰہَ  اِلَّا ہُوَ ۙ وَ الۡمَلٰٓئِکَۃُ وَ اُولُوا الۡعِلۡمِ قَآئِمًۢا بِالۡقِسۡطِ ؕ لَاۤ  اِلٰہَ  اِلَّا ہُوَ الۡعَزِیۡزُ الۡحَکِیۡمُ ﴿ؕ۱۸﴾ ",
              urdu_="اللہ تو اس بات کی گواہی دیتا ہے کہ اس کے سوا کوئی معبود نہیں اس حال میں کہ  وہ انصاف کا حاکم ہے اور فرشتے اور علم والے بھی گواہی دیتے ہیں کہ اس غالب حکمت والے کے سوا کوئی لائق عبادت نہیں۔",
          ),

      Verse(
              id_=19,
              arabic_="اِنَّ الدِّیۡنَ عِنۡدَ اللّٰہِ الۡاِسۡلَامُ ۟ وَ مَا اخۡتَلَفَ الَّذِیۡنَ اُوۡتُوا الۡکِتٰبَ اِلَّا مِنۡۢ بَعۡدِ مَا جَآءَہُمُ الۡعِلۡمُ بَغۡیًۢا بَیۡنَہُمۡ ؕ وَ مَنۡ یَّکۡفُرۡ بِاٰیٰتِ اللّٰہِ فَاِنَّ اللّٰہَ سَرِیۡعُ  الۡحِسَابِ ﴿۱۹﴾ ",
              urdu_="دین تو اللہ کے نزدیک اسلام ہی ہے۔ اور اہل کتاب نے جو اس دین سے اختلاف کیا تو علم حاصل ہونے کے بعد آپس کی ضد سے کیا۔ اور جو شخص اللہ کی آیتوں کو نہ مانے تو اللہ جلد حساب لینے والا ہے۔",
          ),

      Verse(
              id_=20,
              arabic_="فَاِنۡ حَآجُّوۡکَ فَقُلۡ اَسۡلَمۡتُ وَجۡہِیَ لِلّٰہِ وَ مَنِ اتَّبَعَنِ ؕ وَ قُلۡ لِّلَّذِیۡنَ اُوۡتُوا الۡکِتٰبَ وَ الۡاُمِّیّٖنَ ءَاَسۡلَمۡتُمۡ ؕ فَاِنۡ اَسۡلَمُوۡا فَقَدِ اہۡتَدَوۡا ۚ وَ اِنۡ تَوَلَّوۡا فَاِنَّمَا عَلَیۡکَ الۡبَلٰغُ ؕ وَ اللّٰہُ بَصِیۡرٌۢ بِالۡعِبَادِ ﴿٪۲۰﴾ ",
              urdu_="اے پیغمبر پس اگر یہ لوگ تم سے جھگڑنے لگیں تو کہنا کہ میں اور میرے پیرو تو اللہ کے فرمانبردار ہو چکے۔ اور اہل کتاب اور ان پڑھ لوگوں سے کہو کہ کیا تم بھی اللہ کے فرمانبردار بنتے اور اسلام لاتے ہو؟ پس اگر یہ لوگ اسلام لے آئیں تو بے شک ہدایت پا لیں اور اگر تمہارا کہا نہ مانیں تو تمہارا کام صرف اللہ کا پیغام پہنچا دینا ہے۔ اور اللہ اپنے بندوں کو دیکھ رہا ہے۔",
          ),

      Verse(
              id_=21,
              arabic_="اِنَّ الَّذِیۡنَ یَکۡفُرُوۡنَ بِاٰیٰتِ اللّٰہِ وَ یَقۡتُلُوۡنَ النَّبِیّٖنَ بِغَیۡرِ حَقٍّ ۙ  وَّ یَقۡتُلُوۡنَ الَّذِیۡنَ یَاۡمُرُوۡنَ بِالۡقِسۡطِ مِنَ النَّاسِ ۙ فَبَشِّرۡہُمۡ  بِعَذَابٍ اَلِیۡمٍ ﴿۲۱﴾  ",
              urdu_="جو لوگ اللہ کی آیتوں کو نہیں مانتے اور انبیاء کو ناحق قتل کرتے رہے ہیں اور جو انصاف کرنے کا حکم دیتے ہیں انہیں بھی مار ڈالتے ہیں ان کو دکھ دینے والے عذاب کی خوشخبری سنا دو۔",
          ),

      Verse(
              id_=22,
              arabic_="اُولٰٓئِکَ الَّذِیۡنَ حَبِطَتۡ اَعۡمَالُہُمۡ فِی الدُّنۡیَا وَ الۡاٰخِرَۃِ ۫ وَ مَا لَہُمۡ مِّنۡ نّٰصِرِیۡنَ ﴿۲۲﴾ ",
              urdu_="یہ ایسے لوگ ہیں جن کے اعمال دنیا اور آخرت دونوں میں برباد ہیں اور ان کا کوئی مددگار نہیں ہو گا۔",
          ),

      Verse(
              id_=23,
              arabic_="اَلَمۡ تَرَ اِلَی الَّذِیۡنَ اُوۡتُوۡا نَصِیۡبًا مِّنَ الۡکِتٰبِ یُدۡعَوۡنَ  اِلٰی کِتٰبِ اللّٰہِ لِیَحۡکُمَ بَیۡنَہُمۡ ثُمَّ یَتَوَلّٰی فَرِیۡقٌ مِّنۡہُمۡ وَ ہُمۡ مُّعۡرِضُوۡنَ ﴿۲۳﴾",
              urdu_="بھلا تم نے ان لوگوں کو نہیں دیکھا جنکو کتاب یعنی تورات سے حصہ دیا گیا اور وہ اس کتاب اللہ یعنی قرآن کی طرف بلا ئے جاتے ہیں تا کہ یہ ان کے تنازعات کا ان میں فیصلہ کر دے تو ایک فریق ان میں سے پھر جاتا ہے اور منہ پھیر لیتا ہے۔",
          ),

      Verse(
              id_=24,
              arabic_="ذٰلِکَ بِاَنَّہُمۡ قَالُوۡا لَنۡ تَمَسَّنَا النَّارُ اِلَّاۤ اَیَّامًا مَّعۡدُوۡدٰتٍ ۪ وَ غَرَّہُمۡ فِیۡ دِیۡنِہِمۡ مَّا  کَانُوۡا یَفۡتَرُوۡنَ ﴿۲۴﴾",
              urdu_="یہ اس لئے کہ یہ اس بات کے قائل ہیں کہ دوزخ کی آگ ہمیں چند روز کے سوا چھو ہی نہیں سکے گی۔ اور جو کچھ یہ دین کے بارے میں بہتان باندھتے رہے ہیں اس نے ان کو دھوکے میں ڈال رکھا ہے۔",
          ),

      Verse(
              id_=25,
              arabic_="فَکَیۡفَ اِذَا جَمَعۡنٰہُمۡ لِیَوۡمٍ لَّا رَیۡبَ فِیۡہِ ۟ وَ وُفِّیَتۡ کُلُّ نَفۡسٍ مَّا کَسَبَتۡ وَ ہُمۡ لَا یُظۡلَمُوۡنَ ﴿۲۵﴾ ",
              urdu_="تو اس وقت کیا حال ہو گا جب ہم ان کو جمع کریں گے یعنی اس روز جس کے آنے میں کچھ بھی شک نہیں اور ہر نفس اپنے اعمال کا پورا پورا بدلہ پائے گا اور ان پر ظلم نہیں کیا جائے گا۔",
          ),

      Verse(
              id_=26,
              arabic_="قُلِ اللّٰہُمَّ مٰلِکَ الۡمُلۡکِ تُؤۡتِی الۡمُلۡکَ مَنۡ تَشَآءُ وَ تَنۡزِعُ الۡمُلۡکَ مِمَّنۡ تَشَآءُ ۫ وَ تُعِزُّ مَنۡ تَشَآءُ وَ تُذِلُّ مَنۡ تَشَآءُ ؕ بِیَدِکَ الۡخَیۡرُ ؕ اِنَّکَ عَلٰی کُلِّ شَیۡءٍ قَدِیۡرٌ ﴿۲۶﴾ ",
              urdu_="کہو کہ اے اللہ اے بادشاہی کے مالک تو جسکو چاہے بادشاہی بخشے اور جس سے چاہے بادشاہی چھین لے اور جس کو چاہے عزت دے اور جسے چاہے ذلیل کرے ہر طرح کی بھلائی تیرے ہی ہاتھ ہے اور بےشک تو ہر چیز پر قادر ہے۔",
          ),

      Verse(
              id_=27,
              arabic_="تُوۡلِجُ الَّیۡلَ فِی النَّہَارِ وَ تُوۡلِجُ النَّہَارَ فِی الَّیۡلِ ۫ وَ تُخۡرِجُ الۡحَیَّ مِنَ الۡمَیِّتِ وَ تُخۡرِجُ الۡمَیِّتَ مِنَ الۡحَیِّ ۫ وَ تَرۡزُقُ مَنۡ تَشَآءُ بِغَیۡرِ  حِسَابٍ ﴿۲۷﴾ ",
              urdu_="تو ہی رات کو دن میں داخل کرتا اور تو ہی دن کو رات میں داخل کرتا ہے تو ہی بےجان سے جاندار پیدا کرتا ہے اور تو ہی جاندار سے بےجان پیدا کرتا ہے اور تو ہی جسکو چاہتا ہے بے حساب رزق بخشتا ہے۔",
          ),

      Verse(
              id_=28,
              arabic_="لَا یَتَّخِذِ الۡمُؤۡمِنُوۡنَ الۡکٰفِرِیۡنَ اَوۡلِیَآءَ مِنۡ دُوۡنِ الۡمُؤۡمِنِیۡنَ ۚ وَ مَنۡ یَّفۡعَلۡ ذٰلِکَ فَلَیۡسَ مِنَ اللّٰہِ  فِیۡ شَیۡءٍ اِلَّاۤ  اَنۡ تَتَّقُوۡا مِنۡہُمۡ تُقٰٮۃً ؕ وَ یُحَذِّرُکُمُ اللّٰہُ نَفۡسَہٗ ؕ وَ اِلَی اللّٰہِ الۡمَصِیۡرُ ﴿۲۸﴾ ",
              urdu_="مومنوں کو چاہیے کے مومنوں کے سوا کافروں کو دوست نہ بنائیں۔ اور جو ایسا کرے گا اس سے اللہ کا کچھ عہد نہیں۔ ہاں اگر اس طریق سے تم ان کے شر سے بچاؤ کی صورت پیدا کرو تو مضائقہ نہیں اور اللہ تم کو اپنے غضب سے ڈراتا ہے۔ اور اللہ ہی کی طرف تم کو لوٹ کر جانا ہے۔",
          ),

      Verse(
              id_=29,
              arabic_="قُلۡ اِنۡ تُخۡفُوۡا مَا فِیۡ صُدُوۡرِکُمۡ اَوۡ تُبۡدُوۡہُ یَعۡلَمۡہُ اللّٰہُ ؕ وَ یَعۡلَمُ مَا فِی السَّمٰوٰتِ وَ مَا فِی الۡاَرۡضِ ؕ وَ اللّٰہُ عَلٰی کُلِّ شَیۡءٍ قَدِیۡرٌ ﴿۲۹﴾ ",
              urdu_="اے پیغمبر لوگوں سے کہدو کہ کوئی بات تم اپنے دلوں میں مخفی رکھو یا اسے ظاہر کرو اللہ اس کو جانتا ہے اور جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے اس کو سب کی خبر ہے اور وہ ہر چیز پر قادر ہے۔",
          ),

      Verse(
              id_=30,
              arabic_="یَوۡمَ تَجِدُ کُلُّ نَفۡسٍ مَّا عَمِلَتۡ مِنۡ خَیۡرٍ مُّحۡضَرًا ۚۖۛ وَّ مَا عَمِلَتۡ مِنۡ سُوۡٓءٍ ۚۛ تَوَدُّ لَوۡ اَنَّ بَیۡنَہَا وَ بَیۡنَہٗۤ اَمَدًۢا بَعِیۡدًا ؕ وَ یُحَذِّرُکُمُ اللّٰہُ نَفۡسَہٗ ؕ وَ اللّٰہُ رَءُوۡفٌۢ بِالۡعِبَادِ ﴿٪۳۰﴾ ",
              urdu_="جس دن ہر شخص اپنی کی ہوئی نیکی کو موجود پا لے گا اور اپنی برائی کو بھی دیکھ لے گا تو آرزو کرے گا کہ اے کاش اس میں اور اس برائی میں دور کی مسافت ہو جاتی۔ اور اللہ تمکو اپنے غضب سے ڈراتا ہے۔ اور اللہ اپنے بندوں پر نہایت مہربان ہے۔",
          ),

      Verse(
              id_=31,
              arabic_="قُلۡ  اِنۡ کُنۡتُمۡ تُحِبُّوۡنَ اللّٰہَ فَاتَّبِعُوۡنِیۡ یُحۡبِبۡکُمُ اللّٰہُ وَ یَغۡفِرۡ لَکُمۡ ذُنُوۡبَکُمۡ ؕ وَ اللّٰہُ غَفُوۡرٌ  رَّحِیۡمٌ ﴿۳۱﴾ ",
              urdu_="اے پیغمبر ﷺ لوگوں سے کہدو کہ اگر تم اللہ کو دوست رکھتے ہو تو میری پیروی کرو اللہ بھی تمہیں دوست رکھے گا اور تمہارے گناہ معاف کر دے گا اور اللہ بخشنے والا ہے مہربان ہے۔",
          ),

      Verse(
              id_=32,
              arabic_="قُلۡ اَطِیۡعُوا اللّٰہَ وَ الرَّسُوۡلَ ۚ فَاِنۡ تَوَلَّوۡا فَاِنَّ اللّٰہَ  لَا یُحِبُّ الۡکٰفِرِیۡنَ ﴿۳۲﴾ ",
              urdu_=" کہدو کہ اللہ اور اس کے رسول کا حکم مانو۔ پس اگر وہ نہ مانیں تو اللہ بھی کافروں کو دوست نہیں رکھتا۔",
          ),

      Verse(
              id_=33,
              arabic_="اِنَّ اللّٰہَ اصۡطَفٰۤی اٰدَمَ وَ نُوۡحًا وَّ اٰلَ اِبۡرٰہِیۡمَ  وَ اٰلَ عِمۡرٰنَ عَلَی الۡعٰلَمِیۡنَ ﴿ۙ۳۳﴾ ",
              urdu_="اللہ نے آدم اور نوح اور خاندان ابراہیم اور خاندان عمران کو تمام جہان کے لوگوں میں سے منتخب فرمایا تھا۔",
          ),

      Verse(
              id_=34,
              arabic_="ذُرِّیَّۃًۢ بَعۡضُہَا مِنۡۢ بَعۡضٍ ؕ وَ اللّٰہُ سَمِیۡعٌ  عَلِیۡمٌ ﴿ۚ۳۴﴾",
              urdu_="ان میں سے بعض بعض کی اولاد تھے۔ اور اللہ سننے والا ہے جاننے والا ہے۔",
          ),

      Verse(
              id_=35,
              arabic_="اِذۡ  قَالَتِ امۡرَاَتُ عِمۡرٰنَ رَبِّ اِنِّیۡ نَذَرۡتُ لَکَ مَا فِیۡ بَطۡنِیۡ مُحَرَّرًا فَتَقَبَّلۡ مِنِّیۡ ۚ اِنَّکَ اَنۡتَ السَّمِیۡعُ الۡعَلِیۡمُ ﴿۳۵﴾ ",
              urdu_="وہ وقت یاد کرنے کے لائق ہے جب عمران کی بیوی نے کہا کہ اے پروردگار جو بچہ میرے پیٹ میں ہے میں اس کو تیری نذر کرتی ہوں اسے دنیا کے کاموں سے آزاد رکھوں گی تو اسے میری طرف سے قبول فرما تو تو سننے والا ہے جاننے والا ہے۔",
          ),

      Verse(
              id_=36,
              arabic_="فَلَمَّا وَضَعَتۡہَا قَالَتۡ رَبِّ اِنِّیۡ وَضَعۡتُہَاۤ  اُنۡثٰی ؕ وَ اللّٰہُ اَعۡلَمُ بِمَا وَضَعَتۡ ؕ وَ لَیۡسَ الذَّکَرُ  کَالۡاُنۡثٰی ۚ وَ اِنِّیۡ سَمَّیۡتُہَا مَرۡیَمَ وَ اِنِّیۡۤ  اُعِیۡذُہَا بِکَ وَ ذُرِّیَّتَہَا مِنَ الشَّیۡطٰنِ  الرَّجِیۡمِ ﴿۳۶﴾ ",
              urdu_="پھر جب ان کے ہاں بچہ پیدا ہوا تو کہنے لگیں کہ پروردگار! میرے تو لڑکی ہوئی ہے جبکہ اللہ خوب جانتا تھا جو انکے ہاں ولادت ہوئی تھی اور نہ ہوتا لڑکا مانند اس لڑکی کے اور کہنے لگیں میں  نے اس کا نام مریم رکھا ہے اور میں اس کو اور اس کی اولاد کو شیطان مردود سے تیری پناہ میں دیتی ہوں۔",
          ),

      Verse(
              id_=37,
              arabic_="فَتَقَبَّلَہَا رَبُّہَا بِقَبُوۡلٍ حَسَنٍ وَّ اَنۡۢبَتَہَا نَبَاتًا حَسَنًا ۙ وَّ کَفَّلَہَا زَکَرِیَّا ۚؕ کُلَّمَا دَخَلَ عَلَیۡہَا زَکَرِیَّا الۡمِحۡرَابَ ۙ وَجَدَ عِنۡدَہَا رِزۡقًا ۚ قَالَ یٰمَرۡیَمُ اَنّٰی لَکِ ہٰذَا ؕ قَالَتۡ ہُوَ مِنۡ عِنۡدِ اللّٰہِ ؕ اِنَّ اللّٰہَ یَرۡزُقُ مَنۡ یَّشَآءُ بِغَیۡرِ حِسَابٍ ﴿۳۷﴾ ",
              urdu_="تو اسکے پروردگار نے اس کو پسندیدگی کے ساتھ قبول فرمایا اور اسے اچھی طرح پرورش کیا اور زکریا کو اس کا کفیل بنایا۔ زکریا جب کبھی عبادت گاہ میں اس کے پاس جاتے تو اسکے پاس کھانا پاتے یہ کیفیت دیکھ کر ایک دن مریم سے پوچھنے لگے کہ مریم یہ کھانا تمہارے پاس کہاں سے آتا ہے۔ وہ بولیں کہ اللہ کے ہاں سے آتا ہے بیشک اللہ جسے چاہتا ہے بے حساب رزق دیتا ہے۔",
          ),

      Verse(
              id_=38,
              arabic_="ہُنَالِکَ دَعَا زَکَرِیَّا رَبَّہٗ ۚ قَالَ رَبِّ ہَبۡ لِیۡ مِنۡ لَّدُنۡکَ ذُرِّیَّۃً طَیِّبَۃً ۚ اِنَّکَ سَمِیۡعُ  الدُّعَآءِ ﴿۳۸﴾ ",
              urdu_="اس وقت زکریا نے اپنے پروردگار کو پکارا عرض کی کہ پروردگار مجھے اپنی طرف سے اولاد صالح عطا فرما۔ تو بےشک دعا سننے والا ہے۔",
          ),

      Verse(
              id_=39,
              arabic_="فَنَادَتۡہُ  الۡمَلٰٓئِکَۃُ وَ ہُوَ قَآئِمٌ یُّصَلِّیۡ فِی الۡمِحۡرَابِ ۙ اَنَّ اللّٰہَ یُبَشِّرُکَ بِیَحۡیٰی مُصَدِّقًۢا بِکَلِمَۃٍ مِّنَ اللّٰہِ وَ سَیِّدًا وَّ حَصُوۡرًا وَّ نَبِیًّا مِّنَ الصّٰلِحِیۡنَ ﴿۳۹﴾ ",
              urdu_="چنانچہ ابھی عبادت گاہ میں کھڑے نماز ہی پڑھ رہے تھے کہ فرشتوں نے آواز دی کہ زکریا اللہ تمہیں یحیٰی کی بشارت دیتا ہے جو کلمۃ اللہ یعنی عیسٰی کی تصدیق کریں گے اور سردار ہونگے اور عورتوں سے رغبت نہ رکھنے والے اور ایک پیغمبر یعنی نیکو کاروں میں ہونگے۔",
          ),

      Verse(
              id_=40,
              arabic_="قَالَ رَبِّ اَنّٰی یَکُوۡنُ لِیۡ غُلٰمٌ  وَّ قَدۡ بَلَغَنِیَ الۡکِبَرُ وَ امۡرَاَتِیۡ عَاقِرٌ ؕ قَالَ کَذٰلِکَ اللّٰہُ  یَفۡعَلُ مَا یَشَآءُ ﴿۴۰﴾ ",
              urdu_="زکریا نے کہا اے پروردگار میرے ہاں لڑکا کیونکر پیدا ہو گا کہ میں تو بڈھا ہو گیا ہوں اور میری بیوی بانجھ ہے فرمایا اسی طرح اللہ جو چاہتا ہے کرتا ہے۔",
          ),

      Verse(
              id_=41,
              arabic_="قَالَ رَبِّ اجۡعَلۡ لِّیۡۤ  اٰیَۃً ؕ قَالَ اٰیَتُکَ  اَلَّا تُکَلِّمَ النَّاسَ ثَلٰثَۃَ اَیَّامٍ  اِلَّا رَمۡزًا ؕ وَ اذۡکُرۡ رَّبَّکَ کَثِیۡرًا وَّ سَبِّحۡ بِالۡعَشِیِّ وَ الۡاِبۡکَارِ ﴿٪۴۱﴾ ",
              urdu_="زکریا نے کہا کہ پروردگار میرے لئے کوئی نشانی مقرر فرما دے فرمایا نشانی یہ ہے کہ تم لوگوں سے تین دن اشارے کے سوا بات نہ کر سکو گے تو ان دنوں میں اپنے پروردگار کی کثرت سے یاد اور صبح و شام اس کی تسبیح کرنا۔",
          ),

      Verse(
              id_=42,
              arabic_="وَ اِذۡ قَالَتِ الۡمَلٰٓئِکَۃُ یٰمَرۡیَمُ اِنَّ اللّٰہَ اصۡطَفٰکِ وَ طَہَّرَکِ وَ اصۡطَفٰکِ عَلٰی نِسَآءِ  الۡعٰلَمِیۡنَ ﴿۴۲﴾ ",
              urdu_="اور جب فرشتوں نے مریم سے کہا کہ مریم! اللہ نے تم کو برگزیدہ کیا ہے اور تمہیں پاک بنایا ہے اور دنیا جہان کی عورتوں میں سے تم کو منتخب کیا ہے۔",
          ),

      Verse(
              id_=43,
              arabic_="یٰمَرۡیَمُ اقۡنُتِیۡ لِرَبِّکِ وَ اسۡجُدِیۡ وَ ارۡکَعِیۡ مَعَ  الرّٰکِعِیۡنَ ﴿۴۳﴾ ",
              urdu_="اے مریم اپنے پروردگار کی فرمانبرداری کرنا اور سجدہ کرنا اور رکوع کرنے والوں کے ساتھ رکوع کرنا۔",
          ),

      Verse(
              id_=44,
              arabic_="ذٰلِکَ مِنۡ اَنۡۢبَآءِ الۡغَیۡبِ نُوۡحِیۡہِ اِلَیۡکَ ؕ وَ مَا کُنۡتَ لَدَیۡہِمۡ  اِذۡ  یُلۡقُوۡنَ اَقۡلَامَہُمۡ اَیُّہُمۡ یَکۡفُلُ مَرۡیَمَ ۪ وَ مَا کُنۡتَ لَدَیۡہِمۡ  اِذۡ  یَخۡتَصِمُوۡنَ ﴿۴۴﴾ ",
              urdu_="اے نبی یہ باتیں اخبار غیب میں سے ہیں جو ہم تمہارے پاس بھیجتے ہیں۔ اور جب وہ لوگ اپنے قلم بطور قرعہ ڈال رہے تھے کہ مریم کا کفیل کون بنے تو تم ان کے پاس نہیں تھے۔ اور نہ اس وقت ہی ان کے پاس تھے جب وہ آپس میں جھگڑ رہے تھے۔",
          ),

      Verse(
              id_=45,
              arabic_="اِذۡ قَالَتِ الۡمَلٰٓئِکَۃُ یٰمَرۡیَمُ اِنَّ اللّٰہَ یُبَشِّرُکِ بِکَلِمَۃٍ مِّنۡہُ ٭ۖ اسۡمُہُ الۡمَسِیۡحُ عِیۡسَی ابۡنُ مَرۡیَمَ وَجِیۡہًا فِی الدُّنۡیَا وَ الۡاٰخِرَۃِ  وَ مِنَ الۡمُقَرَّبِیۡنَ ﴿ۙ۴۵﴾ ",
              urdu_="وہ وقت بھی یاد کرنے کے لائق ہے جب فرشتوں نے مریم سے کہا کہ مریم اللہ تم کو اپنی طرف سے ایک کلمہ کی بشارت دیتا ہے جس کا نام مسیح اور مشہور عیسٰی ابن مریم ہو گا اور جو دنیا اور آخرت میں آبرومند اور مقربین میں سے ہو گا۔",
          ),

      Verse(
              id_=46,
              arabic_="وَ یُکَلِّمُ النَّاسَ فِی الۡمَہۡدِ وَ کَہۡلًا  وَّ مِنَ  الصّٰلِحِیۡنَ ﴿۴۶﴾ ",
              urdu_="اور ماں کی گود میں اور بڑی عمر کا ہو کر دونوں حالتوں میں لوگوں سے یکساں گفتگو کرے گا اور نیکوکاروں میں ہو گا۔",
          ),

      Verse(
              id_=47,
              arabic_="قَالَتۡ رَبِّ اَنّٰی یَکُوۡنُ لِیۡ وَلَدٌ وَّ لَمۡ یَمۡسَسۡنِیۡ بَشَرٌ ؕ قَالَ کَذٰلِکِ اللّٰہُ یَخۡلُقُ مَا  یَشَآءُ ؕ اِذَا قَضٰۤی اَمۡرًا فَاِنَّمَا یَقُوۡلُ لَہٗ  کُنۡ فَیَکُوۡنُ ﴿۴۷﴾ ",
              urdu_="مریم نے کہا کہ پروردگار میرے ہاں بچہ کیونکر ہوگا کہ کسی انسان نے مجھے ہاتھ تک تو لگایا نہیں فرمایا کہ اللہ اسی طرح جو چاہتا ہے پیدا کرتا ہے۔ جب وہ کوئی کام کرنا چاہتا ہے تو ارشاد فرما دیتا ہے کہ ہو جا تو وہ ہو جاتا ہے۔",
          ),

      Verse(
              id_=48,
              arabic_="وَ یُعَلِّمُہُ الۡکِتٰبَ وَ الۡحِکۡمَۃَ وَ التَّوۡرٰٮۃَ  وَ الۡاِنۡجِیۡلَ ﴿ۚ۴۸﴾ ",
              urdu_="اور وہ اسے کتاب و حکمت اور تورات اور انجیل کا علم عطا کرے گا۔",
          ),

      Verse(
              id_=49,
              arabic_="وَ رَسُوۡلًا اِلٰی بَنِیۡۤ  اِسۡرَآءِیۡلَ ۬ۙ اَنِّیۡ قَدۡ جِئۡتُکُمۡ بِاٰیَۃٍ مِّنۡ رَّبِّکُمۡ ۙ اَنِّیۡۤ  اَخۡلُقُ لَکُمۡ مِّنَ الطِّیۡنِ کَہَیۡـَٔۃِ الطَّیۡرِ فَاَنۡفُخُ فِیۡہِ فَیَکُوۡنُ طَیۡرًۢا بِاِذۡنِ اللّٰہِ ۚ وَ اُبۡرِیٴُ الۡاَکۡمَہَ وَ الۡاَبۡرَصَ وَ اُحۡیِ الۡمَوۡتٰی بِاِذۡنِ اللّٰہِ ۚ  وَ اُنَبِّئُکُمۡ بِمَا تَاۡکُلُوۡنَ وَ مَا تَدَّخِرُوۡنَ ۙ فِیۡ بُیُوۡتِکُمۡ ؕ اِنَّ فِیۡ ذٰلِکَ لَاٰیَۃً لَّکُمۡ اِنۡ کُنۡتُمۡ مُّؤۡمِنِیۡنَ ﴿ۚ۴۹﴾ ",
              urdu_="اور عیسٰی بنی اسرائیل کی طرف پیغمبر ہو کر جائیں گے اور کہیں گے کہ میں تمہارے پاس تمہارے پروردگار کی طرف سے نشانی لے کر آیا ہوں۔ وہ یہ کہ تمہارے سامنے مٹی سے پرندے کی صورت بناتا ہوں پھر اس میں پھونک مارتا ہوں تو وہ اللہ کے حکم سے سچ مچ پرندہ ہو جاتا ہے۔ اور پیدائشی اندھے اور ابرص کو تندرست کر دیتا ہوں۔ اور اللہ کے حکم سے مردوں کو زندہ کر دیتا ہوں۔ اور جو کچھ تم کھا کر آتے ہو اور جو اپنے گھروں میں جمع کر رکھتے ہو سب تم کو بتا دیتا ہوں اگر تم صاحب ایمان ہو تو ان باتوں میں تمہارے لئے نشانی ہے۔",
          ),

      Verse(
              id_=50,
              arabic_="وَ مُصَدِّقًا لِّمَا بَیۡنَ یَدَیَّ مِنَ التَّوۡرٰٮۃِ وَ لِاُحِلَّ لَکُمۡ بَعۡضَ الَّذِیۡ حُرِّمَ عَلَیۡکُمۡ وَ جِئۡتُکُمۡ بِاٰیَۃٍ مِّنۡ  رَّبِّکُمۡ ۟ فَاتَّقُوا اللّٰہَ وَ اَطِیۡعُوۡنِ ﴿۵۰﴾ ",
              urdu_="اور مجھ سے پہلے جو تورات نازل ہوئی تھی اس کی تصدیق بھی کرتا ہوں اور میں اس لئے بھی آیا ہوں کہ بعض چیزیں جو تم پر حرام تھیں ان کو تمہارے لئے حلال کردوں اور میں تو تمہارے پروردگار کی طرف سے نشانی لے کر آیا ہوں تو اللہ سے ڈرو اور میرا کہا مانو۔",
          ),

      Verse(
              id_=51,
              arabic_="اِنَّ اللّٰہَ رَبِّیۡ وَ رَبُّکُمۡ فَاعۡبُدُوۡہُ ؕ ہٰذَا صِرَاطٌ  مُّسۡتَقِیۡمٌ ﴿۵۱﴾ ",
              urdu_="کچھ شک نہیں کہ اللہ ہی میرا اور تمہارا پروردگار ہے تو اسی کی عبادت کرو یہی سیدھا رستہ ہے۔",
          ),

      Verse(
              id_=52,
              arabic_="فَلَمَّاۤ  اَحَسَّ عِیۡسٰی مِنۡہُمُ الۡکُفۡرَ قَالَ مَنۡ اَنۡصَارِیۡۤ اِلَی اللّٰہِ ؕ قَالَ الۡحَوَارِیُّوۡنَ نَحۡنُ اَنۡصَارُ اللّٰہِ ۚ اٰمَنَّا بِاللّٰہِ ۚ وَ اشۡہَدۡ بِاَنَّا مُسۡلِمُوۡنَ ﴿۵۲﴾ ",
              urdu_="پھر جب عیسٰی نے انکی طرف سے انکار اور نافرمانی ہی دیکھی تو کہنے لگے کہ کوئی ہے جو اللہ کا طرفدار اور میرا مددگار ہو۔ حواری بولے کہ ہم اللہ کے طرفدار اور آپ کے مددگار ہیں ہم اللہ پر ایمان لائے اور آپ گواہ رہیں کہ ہم فرمانبردار ہیں۔",
          ),

      Verse(
              id_=53,
              arabic_="رَبَّنَاۤ  اٰمَنَّا بِمَاۤ اَنۡزَلۡتَ وَ اتَّبَعۡنَا الرَّسُوۡلَ فَاکۡتُبۡنَا مَعَ الشّٰہِدِیۡنَ ﴿۵۳﴾ ",
              urdu_="اے پروردگار جو کتاب تو نے نازل فرمائی ہے ہم اس پر ایمان لے آئے اور تیرے پیغمبر کے متبع ہو چکے تو ہم کو ماننے والوں میں لکھ رکھ۔",
          ),

      Verse(
              id_=54,
              arabic_="وَ مَکَرُوۡا وَ مَکَرَ اللّٰہُ ؕ وَ اللّٰہُ خَیۡرُ الۡمٰکِرِیۡنَ ﴿٪۵۴﴾ ",
              urdu_="اور وہ یعنی یہود قتل عیسٰی کے بارے میں ایک چال چلے اور اللہ نے بھی عیسٰی کو بچانے کے لئے تدبیر کی اور اللہ بہترین تدبیر کرنے والا ہے۔",
          ),

      Verse(
              id_=55,
              arabic_="اِذۡ قَالَ اللّٰہُ یٰعِیۡسٰۤی اِنِّیۡ مُتَوَفِّیۡکَ وَ رَافِعُکَ اِلَیَّ وَ مُطَہِّرُکَ مِنَ الَّذِیۡنَ کَفَرُوۡا  وَ جَاعِلُ الَّذِیۡنَ اتَّبَعُوۡکَ فَوۡقَ الَّذِیۡنَ کَفَرُوۡۤا اِلٰی یَوۡمِ الۡقِیٰمَۃِ ۚ ثُمَّ  اِلَیَّ مَرۡجِعُکُمۡ فَاَحۡکُمُ بَیۡنَکُمۡ فِیۡمَا کُنۡتُمۡ  فِیۡہِ  تَخۡتَلِفُوۡنَ ﴿۵۵﴾ ",
              urdu_="اس وقت اللہ نے فرمایا کہ عیسٰی! میں تمہاری دنیا میں رہنے کی مدت پوری کر کے تم کو اپنی طرف اٹھا لوں گا اور تمہیں کافروں کی صحبت سے پاک کر دوں گا۔ اور جو لوگ تمہاری پیروی کریں گے ان کو کافروں پر قیامت تک فائق و غالب رکھوں گا پھر تم سب میرے پاس لوٹ کر آؤ گے تو جن باتوں میں تم اختلاف کرتے رہے اس دن تم میں ان کا فیصلہ کر دوں گا۔",
          ),

      Verse(
              id_=56,
              arabic_="فَاَمَّا الَّذِیۡنَ کَفَرُوۡا فَاُعَذِّبُہُمۡ عَذَابًا شَدِیۡدًا فِی الدُّنۡیَا وَ الۡاٰخِرَۃِ ۫ وَ مَا لَہُمۡ مِّنۡ نّٰصِرِیۡنَ ﴿۵۶﴾ ",
              urdu_="اب جو کافر ہوئے ان کو دنیا اور آخرت دونوں میں سخت عذاب دوں گا اور ان کا کوئی مددگار نہ ہو گا۔",
          ),

      Verse(
              id_=57,
              arabic_="وَ اَمَّا الَّذِیۡنَ اٰمَنُوۡا وَ عَمِلُوا الصّٰلِحٰتِ فَیُوَفِّیۡہِمۡ اُجُوۡرَہُمۡ ؕ وَ اللّٰہُ لَا یُحِبُّ الظّٰلِمِیۡنَ ﴿۵۷﴾ ",
              urdu_="اور جو لوگ ایمان لائے اور عمل نیک کرتے رہے ان کو اللہ پورا پورا صلہ دے گا اور اللہ ظالموں کو دوست نہیں رکھتا۔",
          ),

      Verse(
              id_=58,
              arabic_="ذٰلِکَ نَتۡلُوۡہُ عَلَیۡکَ مِنَ الۡاٰیٰتِ وَ الذِّکۡرِ الۡحَکِیۡمِ ﴿۵۸﴾ ",
              urdu_="اے نبی یہ ہم تم کو اللہ کی آیتیں اور حکمت بھری نصیحتیں پڑھ پڑھ کر سناتے ہیں۔",
          ),

      Verse(
              id_=59,
              arabic_="اِنَّ مَثَلَ عِیۡسٰی عِنۡدَ اللّٰہِ کَمَثَلِ اٰدَمَ ؕ خَلَقَہٗ مِنۡ تُرَابٍ ثُمَّ قَالَ لَہٗ  کُنۡ فَیَکُوۡنُ ﴿۵۹﴾ ",
              urdu_="عیسٰی کا حال اللہ کے نزدیک آدم کا سا ہے کہ اس نے پہلے مٹی سے انکو بنایا پھر فرمایا باشعور انسان ہو جا تو وہ ویسے ہی گئے۔",
          ),

      Verse(
              id_=60,
              arabic_="اَلۡحَقُّ مِنۡ رَّبِّکَ فَلَا تَکُنۡ مِّنَ الۡمُمۡتَرِیۡنَ ﴿۶۰﴾ ",
              urdu_="یہ بات تمہارے پروردگار کی طرف سے حق ہے تو تم ہر گز شک کرنے والوں میں نہ ہونا۔",
          ),

      Verse(
              id_=61,
              arabic_="فَمَنۡ حَآجَّکَ فِیۡہِ مِنۡۢ بَعۡدِ مَا جَآءَکَ مِنَ الۡعِلۡمِ فَقُلۡ تَعَالَوۡا نَدۡعُ اَبۡنَآءَنَا وَ اَبۡنَآءَکُمۡ وَ نِسَآءَنَا وَ نِسَآءَکُمۡ وَ اَنۡفُسَنَا وَ اَنۡفُسَکُمۡ ۟ ثُمَّ نَبۡتَہِلۡ فَنَجۡعَلۡ لَّعۡنَتَ اللّٰہِ عَلَی الۡکٰذِبِیۡنَ ﴿۶۱﴾ ",
              urdu_="پھر اگر یہ لوگ عیسٰی کے بارے میں تم سے جھگڑا کریں اسکے بعد کہ تم کو حقیقت معلوم ہو ہی چلی ہے تو ان سے کہنا کہ آؤ ہم اپنے بیٹوں اور بیٹیوں کو بلائیں تم اپنے بیٹوں اور بیٹیوں کو بلاؤ اور ہم خود بھی آئیں اور تم خود بھی آؤ پھر دونوں فریق اللہ سے دعا و التجا کریں اس طرح جھوٹوں پر اللہ کی لعنت بھیجیں۔",
          ),

      Verse(
              id_=62,
              arabic_="اِنَّ ہٰذَا لَہُوَ الۡقَصَصُ الۡحَقُّ ۚ وَ مَا مِنۡ  اِلٰہٍ  اِلَّا اللّٰہُ ؕ وَ اِنَّ اللّٰہَ لَہُوَ الۡعَزِیۡزُ الۡحَکِیۡمُ ﴿۶۲﴾ ",
              urdu_="یہ تمام بیانات صحیح ہیں۔ اور اللہ کے سوا کو ئی معبود نہیں۔ اور بے شک اللہ غالب ہے صاحب حکمت ہے۔",
          ),

      Verse(
              id_=63,
              arabic_="فَاِنۡ تَوَلَّوۡا فَاِنَّ اللّٰہَ عَلِیۡمٌۢ  بِالۡمُفۡسِدِیۡنَ ﴿٪۶۳﴾ ",
              urdu_="پس اگر یہ لوگ پھر جائیں تو اللہ مفسدوں کو خوب جانتا ہے۔",
          ),

      Verse(
              id_=64,
              arabic_="قُلۡ یٰۤاَہۡلَ الۡکِتٰبِ تَعَالَوۡا اِلٰی کَلِمَۃٍ سَوَآءٍۢ  بَیۡنَنَا وَ بَیۡنَکُمۡ اَلَّا نَعۡبُدَ اِلَّا اللّٰہَ وَ لَا نُشۡرِکَ بِہٖ شَیۡئًا وَّ لَا یَتَّخِذَ بَعۡضُنَا بَعۡضًا اَرۡبَابًا مِّنۡ دُوۡنِ اللّٰہِ ؕ فَاِنۡ تَوَلَّوۡا فَقُوۡلُوا اشۡہَدُوۡا بِاَنَّا مُسۡلِمُوۡنَ ﴿۶۴﴾ ",
              urdu_="کہدو کہ اے اہل کتاب جو بات ہمارے اور تمہارے دونوں کے درمیان یکساں تسلیم کی گئ ہے اس کی طرف آؤ وہ یہ کہ اللہ کے سوا ہم کسی کی عبادت نہ کریں۔ اور اس کے ساتھ کسی چیز کو شریک نہ بنائیں اور ہم میں کوئی کسی کو اللہ کے سوا اپنا کارساز نہ سمجھے۔ پھر اگر یہ لوگ اس بات کو نہ مانیں تو ان سے کہدو کہ تم گواہ رہو کہ ہم اللہ کے فرمانبردار ہیں۔",
          ),

      Verse(
              id_=65,
              arabic_="یٰۤاَہۡلَ الۡکِتٰبِ لِمَ تُحَآجُّوۡنَ فِیۡۤ اِبۡرٰہِیۡمَ وَ مَاۤ اُنۡزِلَتِ التَّوۡرٰٮۃُ وَ الۡاِنۡجِیۡلُ  اِلَّا مِنۡۢ بَعۡدِہٖ ؕ اَفَلَا  تَعۡقِلُوۡنَ ﴿۶۵﴾ ",
              urdu_="اے اہل کتاب تم ابراہیم کے بارے میں کیوں  جھگڑتے ہو حالانکہ تورات اور انجیل ان کے بعد اتری ہیں اور وہ پہلے ہو چکے تو کیا تم عقل نہیں رکھتے۔",
          ),

      Verse(
              id_=66,
              arabic_="ہٰۤاَنۡتُمۡ  ہٰۤؤُلَآءِ  حَاجَجۡتُمۡ فِیۡمَا لَکُمۡ بِہٖ عِلۡمٌ فَلِمَ تُحَآجُّوۡنَ فِیۡمَا لَیۡسَ لَکُمۡ بِہٖ عِلۡمٌ ؕ وَ اللّٰہُ یَعۡلَمُ وَ اَنۡتُمۡ لَا تَعۡلَمُوۡنَ ﴿۶۶﴾ ",
              urdu_="دیکھو ایسی بات میں تو تم نے جھگڑا کیا ہی تھا جس کا تمہیں کچھ علم تھا بھی۔ مگر ایسی بات میں کیوں جھگڑتے ہو جس کا تم کو کچھ بھی علم نہیں۔ اور اللہ جانتا ہے اور تم نہیں جانتے۔",
          ),

      Verse(
              id_=67,
              arabic_="مَا کَانَ  اِبۡرٰہِیۡمُ یَہُوۡدِیًّا وَّ لَا نَصۡرَانِیًّا وَّ لٰکِنۡ کَانَ حَنِیۡفًا مُّسۡلِمًا ؕ وَ مَا کَانَ مِنَ الۡمُشۡرِکِیۡنَ ﴿۶۷﴾",
              urdu_="ابراہیم نہ تو یہودی تھے اور نہ عیسائی بلکہ سب سے بے تعلق ہو کر ایک اللہ کے ہو رہے تھے اور اسی کے فرمانبردار تھے اور مشرکوں میں نہ تھے۔",
          ),

      Verse(
              id_=68,
              arabic_="اِنَّ اَوۡلَی النَّاسِ بِاِبۡرٰہِیۡمَ لَلَّذِیۡنَ اتَّبَعُوۡہُ وَ ہٰذَا النَّبِیُّ وَ الَّذِیۡنَ  اٰمَنُوۡا ؕ وَ اللّٰہُ وَلِیُّ  الۡمُؤۡمِنِیۡنَ ﴿۶۸﴾ ",
              urdu_="ابراہیم سے زیادہ قرب رکھنے والے تو وہ لوگ ہیں جو ان کی پیروی کرتے رہے اور یہ پیغمبر آخرالزماں ﷺ اور وہ لوگ جو ایمان لائے ہیں۔ اور اللہ مومنوں کا کارساز ہے۔",
          ),

      Verse(
              id_=69,
              arabic_="وَدَّتۡ طَّآئِفَۃٌ مِّنۡ اَہۡلِ الۡکِتٰبِ لَوۡ یُضِلُّوۡنَکُمۡ ؕ وَ مَا یُضِلُّوۡنَ  اِلَّاۤ اَنۡفُسَہُمۡ  وَ مَا یَشۡعُرُوۡنَ ﴿۶۹﴾ ",
              urdu_="اے اہل اسلام بعضے اہل کتاب اس بات کی خواہش رکھتے ہیں کہ کسی طرح تم کو گمراہ کر دیں۔ حالانکہ یہ تم کو کیا گمراہ کریں گے اپنے آپ کو ہی گمراہ کر رہے ہیں اور نہیں جانتے۔",
          ),

      Verse(
              id_=70,
              arabic_="یٰۤاَہۡلَ الۡکِتٰبِ لِمَ تَکۡفُرُوۡنَ بِاٰیٰتِ اللّٰہِ  وَ اَنۡتُمۡ  تَشۡہَدُوۡنَ ﴿۷۰﴾ ",
              urdu_="اے اہل کتاب تم اللہ کی آیتوں سے کیوں انکار کرتے ہو اور تم تورات کو مانتے تو ہو۔",
          ),

      Verse(
              id_=71,
              arabic_="یٰۤاَہۡلَ الۡکِتٰبِ لِمَ تَلۡبِسُوۡنَ الۡحَقَّ بِالۡبَاطِلِ وَ تَکۡتُمُوۡنَ الۡحَقَّ وَ اَنۡتُمۡ تَعۡلَمُوۡنَ ﴿٪۷۱﴾ ",
              urdu_="اے اہل کتاب تم سچ کو جھوٹ کے ساتھ خلط ملط کیوں کرتے ہو اور حق کو کیوں چھپاتے ہو جبکہ تم جانتے بھی ہو۔",
          ),

      Verse(
              id_=72,
              arabic_="وَ قَالَتۡ طَّآئِفَۃٌ مِّنۡ اَہۡلِ الۡکِتٰبِ اٰمِنُوۡا بِالَّذِیۡۤ اُنۡزِلَ عَلَی الَّذِیۡنَ اٰمَنُوۡا وَجۡہَ النَّہَارِ وَ اکۡفُرُوۡۤا اٰخِرَہٗ  لَعَلَّہُمۡ  یَرۡجِعُوۡنَ ﴿ۚۖ۷۲﴾ ",
              urdu_="اور اہل کتاب ایک گروہ اپنے آدمیوں سے کہتا ہے کہ جو کتاب مومنوں پر نازل ہوئی ہے اس پر دن کے شروع میں تو ایمان لے آیا کرو اور اس کے آخر میں انکار کر دیا کرو تا کہ وہ اسلام سے برگشتہ ہو جائیں۔",
          ),

      Verse(
              id_=73,
              arabic_="وَ لَا تُؤۡمِنُوۡۤا اِلَّا لِمَنۡ تَبِعَ دِیۡنَکُمۡ ؕ قُلۡ  اِنَّ الۡہُدٰی ہُدَی اللّٰہِ ۙ اَنۡ یُّؤۡتٰۤی اَحَدٌ مِّثۡلَ مَاۤ اُوۡتِیۡتُمۡ اَوۡ یُحَآجُّوۡکُمۡ عِنۡدَ رَبِّکُمۡ ؕ قُلۡ  اِنَّ الۡفَضۡلَ بِیَدِ اللّٰہِ ۚ یُؤۡتِیۡہِ مَنۡ یَّشَآءُ ؕ وَ اللّٰہُ وَاسِعٌ عَلِیۡمٌ ﴿ۚۙ۷۳﴾ ",
              urdu_="اور اپنے دین کے پیرو کے سوا کسی اور کی بات کا یقین نہ کر لینا اے پیغمبر کہدو کہ ہدایت تو اللہ ہی کی ہدایت ہے وہ یہ بھی کہتے ہیں یہ بھی نہ ماننا کہ جو چیز تم کو ملی ہے ویسی کسی اور کو بھی مل سکتی ہے ورنہ وہ تم پر تمہارے رب کے روبرو حجت قائم کر دیں گے اے پیغمبر کہدو کہ بزرگی اللہ ہی کے ہاتھ میں ہے وہ جسے چاہتا ہے دیتا ہے اور اللہ کشائش والا ہے علم والا ہے۔",
          ),

      Verse(
              id_=74,
              arabic_="یَّخۡتَصُّ بِرَحۡمَتِہٖ مَنۡ  یَّشَآءُ ؕ وَ اللّٰہُ ذُو الۡفَضۡلِ الۡعَظِیۡمِ ﴿۷۴﴾ ",
              urdu_="وہ اپنی رحمت سے جس کو چاہتا ہے خاص کر لیتا ہے اور اللہ بڑے فضل کا مالک ہے۔",
          ),

      Verse(
              id_=75,
              arabic_="وَ مِنۡ اَہۡلِ الۡکِتٰبِ مَنۡ  اِنۡ تَاۡمَنۡہُ بِقِنۡطَارٍ یُّؤَدِّہٖۤ  اِلَیۡکَ ۚ وَ مِنۡہُمۡ مَّنۡ  اِنۡ تَاۡمَنۡہُ بِدِیۡنَارٍ لَّا یُؤَدِّہٖۤ  اِلَیۡکَ اِلَّا مَادُمۡتَ عَلَیۡہِ قَآئِمًا ؕ ذٰلِکَ بِاَنَّہُمۡ قَالُوۡا لَیۡسَ عَلَیۡنَا فِی الۡاُمِّیّٖنَ سَبِیۡلٌ ۚ وَ یَقُوۡلُوۡنَ عَلَی اللّٰہِ الۡکَذِبَ وَ ہُمۡ  یَعۡلَمُوۡنَ ﴿۷۵﴾ ",
              urdu_="اور اہل کتاب میں سے کوئی تو ایسا ہے کہ اگر تم اس کے پاس دیناروں کا ڈھیر امانت رکھ دو تو تم کو فوراً واپس دیدے اور کوئی اس طرح کا ہے کہ اگر اس کے پاس ایک دینار بھی امانت رکھو تو جب تک اس کے سر پر ہر وقت کھڑے نہ رہو تمہیں دے ہی نہیں یہ اس لئے کہ وہ کہتے ہیں کہ اُمّیوں کے بارے میں ہم سے مواخذہ نہیں ہو گا۔ یہ اللہ پر محض جھوٹ بولتے ہیں اور اس بات کو جانتے بھی ہیں۔",
          ),

      Verse(
              id_=76,
              arabic_="بَلٰی مَنۡ اَوۡفٰی بِعَہۡدِہٖ وَ اتَّقٰی فَاِنَّ اللّٰہَ یُحِبُّ الۡمُتَّقِیۡنَ ﴿۷۶﴾ ",
              urdu_="ہاں کیوں نہیں جو شخص اپنے اقرار کو پورا کرے اور پرہیزگار بنا رہے تو اللہ بھی پرہیزگاروں کو دوست رکھتا ہے۔",
          ),

      Verse(
              id_=77,
              arabic_="اِنَّ الَّذِیۡنَ یَشۡتَرُوۡنَ بِعَہۡدِ اللّٰہِ وَ اَیۡمَانِہِمۡ ثَمَنًا قَلِیۡلًا  اُولٰٓئِکَ لَا خَلَاقَ لَہُمۡ فِی الۡاٰخِرَۃِ وَ لَا یُکَلِّمُہُمُ اللّٰہُ وَ لَا یَنۡظُرُ اِلَیۡہِمۡ یَوۡمَ الۡقِیٰمَۃِ وَ لَا یُزَکِّیۡہِمۡ ۪ وَ لَہُمۡ عَذَابٌ اَلِیۡمٌ ﴿۷۷﴾ ",
              urdu_="جو لوگ اللہ کے اقراروں اور اپنی قسموں کو بیچ ڈالتے ہیں اور ان کے عوض تھوڑی سی قیمت حاصل کرتے ہیں ان کا آخرت میں کچھ حصہ نہیں۔ ان سے اللہ نہ تو کلام کرے گا اور نہ قیامت کے روز ان کی طرف دیکھے گا اور نہ ان کو پاک کرے گا اور ان کو دکھ دینے والا عذاب ہو گا۔",
          ),

      Verse(
              id_=78,
              arabic_="وَ اِنَّ مِنۡہُمۡ لَفَرِیۡقًا یَّلۡوٗنَ اَلۡسِنَتَہُمۡ بِالۡکِتٰبِ لِتَحۡسَبُوۡہُ مِنَ الۡکِتٰبِ وَ مَا ہُوَ مِنَ الۡکِتٰبِ ۚ وَ یَقُوۡلُوۡنَ ہُوَ مِنۡ عِنۡدِ اللّٰہِ  وَ مَا ہُوَ  مِنۡ عِنۡدِ اللّٰہِ ۚ وَ یَقُوۡلُوۡنَ عَلَی اللّٰہِ الۡکَذِبَ وَ ہُمۡ  یَعۡلَمُوۡنَ ﴿۷۸﴾ ",
              urdu_="اور ان اہل کتاب میں بعض ایسے ہیں کہ کتاب یعنی تورات کو زبان مروڑ مروڑ کر پڑھتے ہیں تا کہ تم سمجھو کہ جو کچھ وہ پڑھتے ہیں کتاب میں سے ہے حالانکہ وہ کتاب میں سے نہیں ہوتا اور کہتے ہیں کہ وہ اللہ کی طرف سے نازل ہوا ہے حالانکہ وہ اللہ کی طرف سے نہیں ہوتا اور اللہ پر جھوٹ بولتے ہیں اور یہ بات جانتے بھی ہیں۔",
          ),

      Verse(
              id_=79,
              arabic_="مَا کَانَ لِبَشَرٍ اَنۡ یُّؤۡتِیَہُ اللّٰہُ الۡکِتٰبَ وَ الۡحُکۡمَ وَ النُّبُوَّۃَ ثُمَّ یَقُوۡلَ لِلنَّاسِ کُوۡنُوۡا عِبَادًا لِّیۡ مِنۡ دُوۡنِ اللّٰہِ وَ لٰکِنۡ کُوۡنُوۡا رَبّٰنِیّٖنَ بِمَا کُنۡتُمۡ تُعَلِّمُوۡنَ الۡکِتٰبَ وَ بِمَا کُنۡتُمۡ  تَدۡرُسُوۡنَ ﴿ۙ۷۹﴾",
              urdu_="کسی آدمی کو شایاں نہیں کہ اللہ اسے کتاب اور حکومت اور نبوت عطا فرمائے پھر وہ لوگوں سے کہے کہ اللہ کو چھوڑ کر میرے بندے ہو جاؤ۔ بلکہ اس کو یہ کہنا چاہئے کہ اے اہل کتاب تم ربانی ہو جاؤ کیونکہ تم کتاب کی تعلیم بھی دیتے ہو اور کیونکہ پڑھتے پڑھاتے رہتے ہو۔",
          ),

      Verse(
              id_=80,
              arabic_="وَ لَا یَاۡمُرَکُمۡ اَنۡ تَتَّخِذُوا الۡمَلٰٓئِکَۃَ وَ النَّبِیّٖنَ اَرۡبَابًا ؕ اَیَاۡمُرُکُمۡ بِالۡکُفۡرِ بَعۡدَ اِذۡ اَنۡتُمۡ مُّسۡلِمُوۡنَ ﴿٪۸۰﴾ ",
              urdu_="اور اس کو یہ بھی نہیں کہنا چاہیے کہ تم فرشتوں اور پیغمبروں کو معبود بنا لو۔ بھلا جب تم مسلمان ہو چکے تو کیا وہ تمہیں کافر ہونے کو کہے گا۔",
          ),

      Verse(
              id_=81,
              arabic_="وَ اِذۡ اَخَذَ اللّٰہُ مِیۡثَاقَ النَّبِیّٖنَ لَمَاۤ اٰتَیۡتُکُمۡ مِّنۡ کِتٰبٍ وَّ حِکۡمَۃٍ ثُمَّ جَآءَکُمۡ رَسُوۡلٌ مُّصَدِّقٌ لِّمَا مَعَکُمۡ لَتُؤۡمِنُنَّ بِہٖ وَ لَتَنۡصُرُنَّہٗ ؕ قَالَ ءَاَقۡرَرۡتُمۡ وَ اَخَذۡتُمۡ عَلٰی ذٰلِکُمۡ اِصۡرِیۡ ؕ قَالُوۡۤا اَقۡرَرۡنَا ؕ قَالَ فَاشۡہَدُوۡا وَ اَنَا مَعَکُمۡ مِّنَ  الشّٰہِدِیۡنَ ﴿۸۱﴾ ",
              urdu_="اور جب اللہ نے پیغمبروں سے عہد لیا کہ جب میں تم کو کتاب اور دانائی عطا کروں پھر تمہارے پاس کوئی پیغمبر آئے جو تمہاری کتاب کی تصدیق کرے تو تمہیں ضرور اس پر ایمان لانا ہو گا اور ضرور اس کی مدد کرنی ہوگی۔  اور عہد لینے کے بعد پوچھا کہ بھلا تم نے اقرار کیا اور اس اقرار پر میرا ذمہ لیا یعنی مجھے ضامن ٹھہرایا انہوں نے کہا ہاں ہم نے اقرار کیا۔ فرمایا کہ تم اس عہدوپیمان کے گواہ رہو اور میں بھی تمہارے ساتھ گواہ ہوں۔",
          ),

      Verse(
              id_=82,
              arabic_="فَمَنۡ تَوَلّٰی بَعۡدَ ذٰلِکَ فَاُولٰٓئِکَ ہُمُ الۡفٰسِقُوۡنَ ﴿۸۲﴾ ",
              urdu_="اب جو لوگ اس کے بعد پھر جائیں تو وہی بدکردار ہیں۔",
          ),

      Verse(
              id_=83,
              arabic_="اَفَغَیۡرَ دِیۡنِ اللّٰہِ یَبۡغُوۡنَ وَ لَہٗۤ  اَسۡلَمَ  مَنۡ فِی السَّمٰوٰتِ وَ الۡاَرۡضِ طَوۡعًا وَّ کَرۡہًا وَّ اِلَیۡہِ یُرۡجَعُوۡنَ ﴿۸۳﴾",
              urdu_="پھر کیا یہ کافر اللہ کے دین کے سوا کسی اور دین کے طالب ہیں حالانکہ سب اہل آسمان و زمین خوشی یا ناخوشی سے اللہ کے فرمانبردار ہیں اور اسی کی طرف لوٹ کر جانے والے ہیں۔",
          ),

      Verse(
              id_=84,
              arabic_="قُلۡ اٰمَنَّا بِاللّٰہِ وَ مَاۤ  اُنۡزِلَ عَلَیۡنَا وَ مَاۤ اُنۡزِلَ عَلٰۤی اِبۡرٰہِیۡمَ وَ اِسۡمٰعِیۡلَ وَ اِسۡحٰقَ وَ یَعۡقُوۡبَ وَ الۡاَسۡبَاطِ وَ مَاۤ اُوۡتِیَ مُوۡسٰی وَ عِیۡسٰی وَ النَّبِیُّوۡنَ مِنۡ رَّبِّہِمۡ ۪ لَا نُفَرِّقُ بَیۡنَ اَحَدٍ مِّنۡہُمۡ ۫ وَ نَحۡنُ لَہٗ مُسۡلِمُوۡنَ ﴿۸۴﴾ ",
              urdu_="کہو کہ ہم اللہ پر ایمان لائے اور جو کتاب ہم پر نازل ہوئی اور جو صحیفے ابراہیم اور اسمٰعیل اور اسحٰق اور یعقوب اور ان کی اولاد پر اترے اور جو کتابیں موسٰی اور عیسٰی اور دوسرے انبیاء کو پروردگار کی طرف سے ملیں سب پر ایمان لائے۔ ہم ان پیغمبروں میں سے کسی میں کچھ فرق نہیں کرتے اور ہم اسی اللہ کے فرماں بردار ہیں۔",
          ),

      Verse(
              id_=85,
              arabic_="وَ مَنۡ یَّبۡتَغِ غَیۡرَ الۡاِسۡلَامِ دِیۡنًا فَلَنۡ یُّقۡبَلَ مِنۡہُ ۚ وَ ہُوَ فِی الۡاٰخِرَۃِ مِنَ الۡخٰسِرِیۡنَ ﴿۸۵﴾ ",
              urdu_="اور جو شخص اسلام کے سوا کسی اور دین کا طالب ہو گا وہ اس سے ہر گز قبول نہیں کیا جائے گا اور ایسا شخص آخرت میں نقصان اٹھانے والوں میں ہو گا۔",
          ),

      Verse(
              id_=86,
              arabic_="کَیۡفَ یَہۡدِی اللّٰہُ  قَوۡمًا کَفَرُوۡا بَعۡدَ اِیۡمَانِہِمۡ وَ شَہِدُوۡۤا اَنَّ الرَّسُوۡلَ حَقٌّ وَّ جَآءَہُمُ الۡبَیِّنٰتُ ؕ وَ اللّٰہُ  لَا یَہۡدِی الۡقَوۡمَ الظّٰلِمِیۡنَ ﴿۸۶﴾ ",
              urdu_="اللہ ایسے لوگوں کو کیونکر ہدایت دے جو ایمان لانے کے بعد کافر ہو گئے اور پہلے اس بات کی گواہی دے چکے کہ یہ پیغمبر برحق ہے اور ان کے پاس دلائل بھی آ گئے اور اللہ بے انصافوں کو ہدایت نہیں دیتا۔",
          ),

      Verse(
              id_=87,
              arabic_="اُولٰٓئِکَ جَزَآؤُہُمۡ  اَنَّ عَلَیۡہِمۡ لَعۡنَۃَ اللّٰہِ وَ الۡمَلٰٓئِکَۃِ وَ النَّاسِ اَجۡمَعِیۡنَ ﴿ۙ۸۷﴾ ",
              urdu_="ان لوگوں کی سزا یہ ہے کہ ان پر اللہ کی اور فرشتوں کی اور انسانوں کی سب کی لعنت ہے۔",
          ),

      Verse(
              id_=88,
              arabic_="خٰلِدِیۡنَ فِیۡہَا ۚ لَا یُخَفَّفُ عَنۡہُمُ الۡعَذَابُ وَ لَا  ہُمۡ  یُنۡظَرُوۡنَ ﴿ۙ۸۸﴾ ",
              urdu_="ہمیشہ اس لعنت میں گرفتار رہیں گے ان سے نہ تو عذاب ہلکا کیا جائے گا اور نہ انہیں مہلت دی جائے گی۔",
          ),

      Verse(
              id_=89,
              arabic_="اِلَّا الَّذِیۡنَ تَابُوۡا مِنۡۢ بَعۡدِ ذٰلِکَ وَ اَصۡلَحُوۡا ۟ فَاِنَّ اللّٰہَ  غَفُوۡرٌ  رَّحِیۡمٌ ﴿۸۹﴾ ",
              urdu_="ہاں جنہوں نے اس کے بعد توبہ کی اور اپنی حالت درست کر لی تو اللہ بخشنے والا مہربان ہے۔",
          ),

      Verse(
              id_=90,
              arabic_="اِنَّ الَّذِیۡنَ کَفَرُوۡا بَعۡدَ اِیۡمَانِہِمۡ ثُمَّ ازۡدَادُوۡا کُفۡرًا  لَّنۡ تُقۡبَلَ تَوۡبَتُہُمۡ ۚ وَ اُولٰٓئِکَ ہُمُ  الضَّآلُّوۡنَ ﴿۹۰﴾ ",
              urdu_="جو لوگ ایمان لانے کے بعد کافر ہو گئے پھر کفر میں بڑھتے گئے۔ ایسوں کی توبہ ہر گز قبول نہیں ہو گی اور یہ لوگ گمراہ ہیں۔",
          ),

      Verse(
              id_=91,
              arabic_="اِنَّ الَّذِیۡنَ کَفَرُوۡا وَ مَاتُوۡا وَ ہُمۡ کُفَّارٌ فَلَنۡ یُّقۡبَلَ مِنۡ اَحَدِہِمۡ مِّلۡءُ  الۡاَرۡضِ ذَہَبًا وَّلَوِ افۡتَدٰی بِہٖ ؕ اُولٰٓئِکَ لَہُمۡ عَذَابٌ اَلِیۡمٌ  وَّ مَا لَہُمۡ  مِّنۡ  نّٰصِرِیۡنَ ﴿٪۹۱﴾ ",
              urdu_="جو لوگ کافر ہوئے اور کفر ہی کی حالت میں مر گئے وہ اگر نجات حاصل کرنی چاہیں اور بدلے میں زمین بھر سونا دیں تو ہر گز قبول نہیں کیا جائے گا۔ ان لوگوں کو دکھ دینے والا عذاب ہوگا۔ اور ان کا کوئی مددگار نہیں ہو گا۔",
          ),

      Verse(
              id_=92,
              arabic_="لَنۡ تَنَالُوا الۡبِرَّ حَتّٰی تُنۡفِقُوۡا مِمَّا تُحِبُّوۡنَ ۬ؕ وَ مَا تُنۡفِقُوۡا مِنۡ شَیۡءٍ فَاِنَّ اللّٰہَ بِہٖ  عَلِیۡمٌ ﴿۹۲﴾ ",
              urdu_="مومنو! جب تک تم ان چیزوں میں سے جو تمہیں عزیز ہیں اللہ کی راہ میں خرچ نہ کرو گے کبھی نیکی حاصل نہ کر سکو گے اور جو چیز تم خرچ کرو گے اللہ اس کو جانتا ہے۔",
          ),

      Verse(
              id_=93,
              arabic_="کُلُّ الطَّعَامِ کَانَ حِلًّا  لِّبَنِیۡۤ  اِسۡرَآءِیۡلَ اِلَّا مَا حَرَّمَ  اِسۡرَآءِیۡلُ  عَلٰی  نَفۡسِہٖ مِنۡ قَبۡلِ اَنۡ تُنَزَّلَ التَّوۡرٰٮۃُ ؕ قُلۡ فَاۡتُوۡا بِالتَّوۡرٰٮۃِ فَاتۡلُوۡہَاۤ  اِنۡ  کُنۡتُمۡ صٰدِقِیۡنَ ﴿۹۳﴾ ",
              urdu_="بنی اسرائیل کے لئے تورات کے نازل ہونے سے پہلے کھانے کی تمام چیزیں حلال تھیں بجز ان کے جو یعقوب نے خود اپنے اوپر حرام کر لی تھیں کہدو کہ اگر سچے ہو تو تورات لاؤ اور اسے پڑھو یعنی دلیل پیش کرو۔",
          ),

      Verse(
              id_=94,
              arabic_="فَمَنِ افۡتَرٰی عَلَی اللّٰہِ الۡکَذِبَ مِنۡۢ بَعۡدِ ذٰلِکَ  فَاُولٰٓئِکَ ہُمُ  الظّٰلِمُوۡنَ ﴿؃۹۴﴾",
              urdu_="اب جو اس کے بعد بھی اللہ پر جھوٹے افتراء کریں تو ایسے لوگ ہی بے انصاف ہیں۔",
          ),

      Verse(
              id_=95,
              arabic_="قُلۡ صَدَقَ اللّٰہُ  ۟ فَاتَّبِعُوۡا مِلَّۃَ  اِبۡرٰہِیۡمَ حَنِیۡفًا ؕ وَ مَا کَانَ مِنَ الۡمُشۡرِکِیۡنَ ﴿۹۵﴾ ",
              urdu_="کہدو کہ اللہ نے سچ فرما دیا پس دین ابراہیم کی پیروی کرو جو سب سے بے تعلق ہو کر ایک اللہ کے ہو رہے تھے اور مشرکوں میں سے نہ تھے۔",
          ),

      Verse(
              id_=96,
              arabic_="اِنَّ اَوَّلَ بَیۡتٍ وُّضِعَ لِلنَّاسِ لَلَّذِیۡ بِبَکَّۃَ مُبٰرَکًا  وَّ ہُدًی  لِّلۡعٰلَمِیۡنَ ﴿ۚ۹۶﴾ ",
              urdu_="سب سے پہلا گھر جو لوگوں کے عبادت کرنے کے لئے مقرر کیا گیا تھا وہی ہے جو مکے میں ہے۔ بابرکت اور جہان والوں کے لئے موجب ہدایت۔",
          ),

      Verse(
              id_=97,
              arabic_="فِیۡہِ اٰیٰتٌۢ بَیِّنٰتٌ مَّقَامُ  اِبۡرٰہِیۡمَ ۬ۚ وَ مَنۡ دَخَلَہٗ کَانَ اٰمِنًا ؕ وَ لِلّٰہِ عَلَی النَّاسِ حِجُّ الۡبَیۡتِ مَنِ اسۡتَطَاعَ اِلَیۡہِ سَبِیۡلًا ؕ وَ مَنۡ  کَفَرَ فَاِنَّ اللّٰہَ غَنِیٌّ عَنِ  الۡعٰلَمِیۡنَ ﴿۹۷﴾ ",
              urdu_="اس میں کھلی ہوئی نشانیاں ہیں جن میں سے ایک ابراہیم کے کھڑے ہونے کی جگہ ہے۔ جو شخص اس مبارک گھر میں داخل ہوا اس نے امن پا لیا۔ اور لوگوں پر اللہ کا حق یعنی فرض ہے کہ جو اس گھر تک جانے کا مقدور رکھے وہ اس کا حج کرے۔ اور جو اس حکم کی تعمیل نہ کرے گا تو اللہ بھی اہل عالم سے بے نیاز ہے۔",
          ),

      Verse(
              id_=98,
              arabic_="قُلۡ یٰۤاَہۡلَ الۡکِتٰبِ لِمَ تَکۡفُرُوۡنَ بِاٰیٰتِ اللّٰہِ ٭ۖ وَ اللّٰہُ شَہِیۡدٌ عَلٰی مَا تَعۡمَلُوۡنَ ﴿۹۸﴾ ",
              urdu_="کہو کہ اے اہل کتاب! تم اللہ کی آیتوں سے کیوں کفر کرتے ہو جبکہ اللہ تمہارے سب اعمال سے باخبر ہے۔",
          ),

      Verse(
              id_=99,
              arabic_="قُلۡ یٰۤاَہۡلَ  الۡکِتٰبِ لِمَ تَصُدُّوۡنَ عَنۡ سَبِیۡلِ اللّٰہِ مَنۡ اٰمَنَ تَبۡغُوۡنَہَا عِوَجًا وَّ اَنۡتُمۡ شُہَدَآءُ ؕ وَ مَا اللّٰہُ بِغَافِلٍ عَمَّا تَعۡمَلُوۡنَ ﴿۹۹﴾ ",
              urdu_="کہو کہ اے اہل کتاب تم مومنوں کو اللہ کے رستے سے کیوں روکتے ہو اور باوجودیکہ تم اس سے واقف ہو اس میں کجی نکالتے ہو اور اللہ تمہارے کرتوتوں سے بے خبر نہیں۔",
          ),

      Verse(
              id_=100,
              arabic_="یٰۤاَیُّہَا الَّذِیۡنَ اٰمَنُوۡۤا اِنۡ تُطِیۡعُوۡا فَرِیۡقًا مِّنَ الَّذِیۡنَ اُوۡتُوا الۡکِتٰبَ یَرُدُّوۡکُمۡ  بَعۡدَ اِیۡمَانِکُمۡ  کٰفِرِیۡنَ ﴿۱۰۰﴾ ",
              urdu_="مومنو! اگر تم اہل کتاب کے کسی فریق کا کہا مان لو گے تو وہ تمہیں ایمان لانے کے بعد کافر بنا دیں گے۔",
          ),

      Verse(
              id_=101,
              arabic_="وَ کَیۡفَ تَکۡفُرُوۡنَ وَ اَنۡتُمۡ تُتۡلٰی عَلَیۡکُمۡ اٰیٰتُ اللّٰہِ وَ فِیۡکُمۡ رَسُوۡلُہٗ ؕ وَ مَنۡ یَّعۡتَصِمۡ بِاللّٰہِ فَقَدۡ ہُدِیَ  اِلٰی صِرَاطٍ مُّسۡتَقِیۡمٍ  ﴿۱۰۱﴾٪",
              urdu_="اور تم کیوں کر کفر کرو گے جبکہ تم کو اللہ کی آیتیں پڑھ پڑھ کر سنائی جاتی ہیں اور تم میں اس کے پیغمبر موجود ہیں اور جس نے اللہ کی رسی کو مضبوط پکڑ لیا وہ سیدھے رستے لگ گیا۔",
          ),

      Verse(
              id_=102,
              arabic_="یٰۤاَیُّہَا الَّذِیۡنَ اٰمَنُوا اتَّقُوا اللّٰہَ حَقَّ تُقٰتِہٖ وَ لَا تَمُوۡتُنَّ  اِلَّا وَ اَنۡتُمۡ  مُّسۡلِمُوۡنَ ﴿۱۰۲﴾ ",
              urdu_="مومنو! اللہ سے ڈرو جیسا کہ اس سے ڈرنے کا حق ہے اور مرنا تو مسلمان ہی مرنا۔",
          ),

      Verse(
              id_=103,
              arabic_="وَ اعۡتَصِمُوۡا بِحَبۡلِ اللّٰہِ جَمِیۡعًا وَّ لَا تَفَرَّقُوۡا ۪ وَ اذۡکُرُوۡا نِعۡمَتَ اللّٰہِ عَلَیۡکُمۡ  اِذۡ  کُنۡتُمۡ اَعۡدَآءً فَاَلَّفَ بَیۡنَ قُلُوۡبِکُمۡ فَاَصۡبَحۡتُمۡ بِنِعۡمَتِہٖۤ اِخۡوَانًا ۚ وَ کُنۡتُمۡ عَلٰی شَفَا حُفۡرَۃٍ مِّنَ النَّارِ فَاَنۡقَذَکُمۡ مِّنۡہَا ؕ کَذٰلِکَ یُبَیِّنُ اللّٰہُ لَکُمۡ اٰیٰتِہٖ  لَعَلَّکُمۡ  تَہۡتَدُوۡنَ ﴿۱۰۳﴾ ",
              urdu_="اور سب مل کر اللہ کی ہدایت کی رسی کو مضبوط پکڑے رہنا اور فرقے فرقے نہ ہو جانا۔ اور اپنے اوپر اللہ کی اس مہربانی کو یاد رکھنا جب تم ایک دوسرے کے دشمن تھے تو اس نے تمہارے دلوں میں الفت ڈال دی اور تم اس کی مہربانی سے بھائی بھائی ہو گئے اور تم آگ کے گڑھے کے کنارے تک پہنچ چکے تھے تو اس نے تم کو اس سے بچا لیا۔ اس طرح اللہ تم کو اپنی آیتیں کھول کھول کر سناتا ہے تاکہ تم راہ راست پر رہو۔",
          ),

      Verse(
              id_=104,
              arabic_="وَلۡتَکُنۡ مِّنۡکُمۡ اُمَّۃٌ یَّدۡعُوۡنَ اِلَی الۡخَیۡرِ وَ یَاۡمُرُوۡنَ بِالۡمَعۡرُوۡفِ وَ یَنۡہَوۡنَ عَنِ الۡمُنۡکَرِ ؕ وَ اُولٰٓئِکَ ہُمُ  الۡمُفۡلِحُوۡنَ ﴿۱۰۴﴾",
              urdu_="اور تم میں ایک جماعت ایسی ہونی چاہیے جو لوگوں کو نیکی کی طرف بلائے اور اچھے کام کرنے کا حکم دے اور برے کاموں سے منع کرے یہی لوگ ہیں جو کامیاب ہیں۔",
          ),

      Verse(
              id_=105,
              arabic_="وَ لَا تَکُوۡنُوۡا کَالَّذِیۡنَ تَفَرَّقُوۡا وَ اخۡتَلَفُوۡا مِنۡۢ بَعۡدِ مَا جَآءَہُمُ الۡبَیِّنٰتُ ؕ وَ اُولٰٓئِکَ لَہُمۡ عَذَابٌ عَظِیۡمٌ ﴿۱۰۵﴾ۙ ",
              urdu_="اور ان لوگوں کی طرح نہ ہونا جو متفرق ہو گئے اور صاف احکام آنے کے بعد ایک دوسرے سے اختلاف کرنے لگے یہ وہ لوگ ہیں جنکو قیامت کے دن بڑا عذاب ہو گا۔",
          ),

      Verse(
              id_=106,
              arabic_="یَّوۡمَ تَبۡیَضُّ وُجُوۡہٌ  وَّ تَسۡوَدُّ وُجُوۡہٌ ۚ فَاَمَّا الَّذِیۡنَ اسۡوَدَّتۡ وُجُوۡہُہُمۡ ۟ اَکَفَرۡتُمۡ بَعۡدَ اِیۡمَانِکُمۡ فَذُوۡقُوا الۡعَذَابَ بِمَا کُنۡتُمۡ تَکۡفُرُوۡنَ ﴿۱۰۶﴾ ",
              urdu_="جس دن بہت سے منہ سفید ہوں گے اور بہت سے سیاہ۔ تو جن لوگوں کے منہ سیاہ ہوں گے ان سے اللہ فرمائے گا کیا تم ایمان لا کر کافر ہو گئے تھے؟ سو اب اس کفر کے بدلے عذاب کے مزے چکھو۔",
          ),

      Verse(
              id_=107,
              arabic_="وَ اَمَّا الَّذِیۡنَ ابۡیَضَّتۡ وُجُوۡہُہُمۡ فَفِیۡ رَحۡمَۃِ اللّٰہِ ؕ ہُمۡ  فِیۡہَا خٰلِدُوۡنَ ﴿۱۰۷﴾ ",
              urdu_="اور جن لوگوں کے منہ سفید ہوں گے وہ اللہ کی رحمت کے باغوں میں ہوں گے اور ان میں ہمیشہ رہیں گے۔",
          ),

      Verse(
              id_=108,
              arabic_="تِلۡکَ اٰیٰتُ اللّٰہِ نَتۡلُوۡہَا عَلَیۡکَ بِالۡحَقِّ ؕ وَ مَا اللّٰہُ یُرِیۡدُ  ظُلۡمًا لِّلۡعٰلَمِیۡنَ ﴿۱۰۸﴾",
              urdu_="یہ اللہ کی آیتیں ہیں جو ہم تم کو ٹھیک ٹھیک پڑھ کر سناتے ہیں۔ اور اللہ اہل عالم پر ظلم نہیں کرنا چاہتا۔",
          ),

      Verse(
              id_=109,
              arabic_="وَ لِلّٰہِ مَا فِی السَّمٰوٰتِ وَ مَا فِی الۡاَرۡضِ ؕ وَ  اِلَی اللّٰہِ  تُرۡجَعُ  الۡاُمُوۡرُ  ﴿۱۰۹﴾٪",
              urdu_="اور جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے سب اللہ ہی کا ہے اور سب کاموں کا انجام اللہ ہی کی طرف ہے۔",
          ),

      Verse(
              id_=110,
              arabic_="کُنۡتُمۡ خَیۡرَ اُمَّۃٍ اُخۡرِجَتۡ لِلنَّاسِ تَاۡمُرُوۡنَ بِالۡمَعۡرُوۡفِ وَ تَنۡہَوۡنَ عَنِ الۡمُنۡکَرِ وَ تُؤۡمِنُوۡنَ بِاللّٰہِ ؕ وَ لَوۡ اٰمَنَ اَہۡلُ  الۡکِتٰبِ لَکَانَ خَیۡرًا لَّہُمۡ ؕ مِنۡہُمُ الۡمُؤۡمِنُوۡنَ وَ اَکۡثَرُہُمُ الۡفٰسِقُوۡنَ ﴿۱۱۰﴾ ",
              urdu_="مومنو! تم بہترین امت ہو جسے لوگوں کی ہدایت کیلئے میدان میں لایا گیا ہے کہ تم نیک کام کرنے کو کہتے ہو اور برے کاموں سے منع کرتے ہو اور اللہ پر ایمان رکھتے ہو اور اگر اہل کتاب بھی ایمان لے آتے تو ان کے لئے بہت اچھا ہوتا۔ ان میں ایمان لانے والے بھی ہیں لیکن تھوڑے اور اکثر نافرمان ہیں۔",
          ),

      Verse(
              id_=111,
              arabic_="لَنۡ یَّضُرُّوۡکُمۡ  اِلَّاۤ اَذًی ؕ وَ اِنۡ  یُّقَاتِلُوۡکُمۡ   یُوَلُّوۡکُمُ الۡاَدۡبَارَ ۟ ثُمَّ لَا  یُنۡصَرُوۡنَ ﴿۱۱۱﴾ ",
              urdu_="یہ تمہیں خفیف سی تکلیف کے سوا کچھ نقصان نہیں پہنچا سکیں گے اور اگر تم سے لڑیں گے تو پیٹھ پھیر کر بھاگ جائیں گے۔ پھر ان کو مدد بھی کہیں سے نہیں ملے گی۔",
          ),

      Verse(
              id_=112,
              arabic_="ضُرِبَتۡ عَلَیۡہِمُ الذِّلَّۃُ  اَیۡنَ مَا ثُقِفُوۡۤا اِلَّا بِحَبۡلٍ مِّنَ اللّٰہِ وَ حَبۡلٍ مِّنَ النَّاسِ وَ بَآءُوۡ بِغَضَبٍ مِّنَ اللّٰہِ وَ ضُرِبَتۡ عَلَیۡہِمُ الۡمَسۡکَنَۃُ ؕ ذٰلِکَ بِاَنَّہُمۡ کَانُوۡا یَکۡفُرُوۡنَ بِاٰیٰتِ اللّٰہِ وَ یَقۡتُلُوۡنَ الۡاَنۡۢبِیَآءَ بِغَیۡرِ حَقٍّ ؕ ذٰلِکَ بِمَا عَصَوۡا وَّ کَانُوۡا یَعۡتَدُوۡنَ ﴿۱۱۲﴾٭ ",
              urdu_="یہ جہاں نظر آئیں گے تم دیکھو گے کہ ذلت ان سے چمٹ رہی ہے۔ بجز اس کے کہ یہ اللہ کی پناہ میں یا مسلمان لوگوں کی پناہ میں آجائیں اور یہ لوگ اللہ کے غضب میں گرفتار ہیں اور ناداری ان سے لپٹ رہی ہے۔ یہ اس لئے کہ اللہ کی آیتوں سے انکار کرتے تھے اور اس کے پیغمبروں کو ناحق قتل کر دیتے تھے۔ یہ اس لئے کہ یہ نافرمانی کئے جاتے اور حد سے بڑھے جاتے تھے۔",
          ),

      Verse(
              id_=113,
              arabic_="لَیۡسُوۡا سَوَآءً ؕ مِنۡ اَہۡلِ الۡکِتٰبِ اُمَّۃٌ قَآئِمَۃٌ یَّتۡلُوۡنَ اٰیٰتِ اللّٰہِ اٰنَآءَ الَّیۡلِ  وَ ہُمۡ  یَسۡجُدُوۡنَ ﴿۱۱۳﴾ ",
              urdu_="یہ بھی سب ایک جیسے نہیں ہیں۔ ان اہل کتاب میں کچھ اچھے لوگ بھی ہیں۔ جو رات کے وقت اللہ کی آیتیں پڑھتے اور اس کے آگے سجدے کرتے ہیں۔",
          ),

      Verse(
              id_=114,
              arabic_="یُؤۡمِنُوۡنَ بِاللّٰہِ وَ الۡیَوۡمِ الۡاٰخِرِ وَ یَاۡمُرُوۡنَ بِالۡمَعۡرُوۡفِ وَ یَنۡہَوۡنَ عَنِ الۡمُنۡکَرِ وَ یُسَارِعُوۡنَ فِی الۡخَیۡرٰتِ ؕ وَ اُولٰٓئِکَ مِنَ  الصّٰلِحِیۡنَ ﴿۱۱۴﴾ ",
              urdu_="وہ اللہ پر اور روز آخر پر ایمان رکھتے اور اچھے کام کرنے کو کہتے اور بری باتوں سے منع کرتے اور نیکیوں پر لپکتے ہیں اور یہی لوگ نیکوکار ہیں۔",
          ),

      Verse(
              id_=115,
              arabic_="وَ مَا یَفۡعَلُوۡا مِنۡ خَیۡرٍ فَلَنۡ یُّکۡفَرُوۡہُ ؕ وَ اللّٰہُ  عَلِیۡمٌۢ  بِالۡمُتَّقِیۡنَ ﴿۱۱۵﴾ ",
              urdu_="اور یہ جس طرح کی نیکی کریں گے اس کی ناقدری نہیں کی جائے گی اور اللہ پرہیزگاروں کو خوب جانتا ہے۔",
          ),

      Verse(
              id_=116,
              arabic_="اِنَّ الَّذِیۡنَ کَفَرُوۡا لَنۡ تُغۡنِیَ عَنۡہُمۡ اَمۡوَالُہُمۡ وَ لَاۤ اَوۡلَادُہُمۡ مِّنَ اللّٰہِ شَیۡـًٔا ؕ وَ اُولٰٓئِکَ اَصۡحٰبُ النَّارِ ۚ ہُمۡ فِیۡہَا خٰلِدُوۡنَ  ﴿۱۱۶﴾",
              urdu_="جو لوگ کافر ہیں ان کے مال اور اولاد اللہ کے مقابلہ میں انکے ہرگز کام نہ آئیں گے۔ اور یہ لوگ اہل دوزخ ہیں کہ ہمیشہ اسی میں رہیں گے۔",
          ),

      Verse(
              id_=117,
              arabic_="مَثَلُ مَا یُنۡفِقُوۡنَ فِیۡ ہٰذِہِ الۡحَیٰوۃِ الدُّنۡیَا کَمَثَلِ رِیۡحٍ فِیۡہَا صِرٌّ اَصَابَتۡ حَرۡثَ قَوۡمٍ ظَلَمُوۡۤا اَنۡفُسَہُمۡ فَاَہۡلَکَتۡہُ ؕ وَ مَا ظَلَمَہُمُ اللّٰہُ وَ لٰکِنۡ  اَنۡفُسَہُمۡ  یَظۡلِمُوۡنَ ﴿۱۱۷﴾",
              urdu_="یہ لوگ جو مال دنیا کی زندگی میں خرچ کرتے ہیں اس کی مثال آندھی کی سی ہے جس میں سخت سردی ہو اور وہ ایسے لوگوں کی کھیتی پر جو اپنے آپ پر ظلم کرتے تھے چلے اور اسے تباہ کر دے اور اللہ نے ان پر کچھ ظلم نہیں کیا بلکہ یہ خود اپنے اوپر ظلم کر رہے ہیں۔",
          ),

      Verse(
              id_=118,
              arabic_="یٰۤاَیُّہَا الَّذِیۡنَ اٰمَنُوۡا لَا تَتَّخِذُوۡا بِطَانَۃً مِّنۡ دُوۡنِکُمۡ لَا یَاۡلُوۡنَکُمۡ خَبَالًا ؕ وَدُّوۡا مَا عَنِتُّمۡ ۚ قَدۡ بَدَتِ الۡبَغۡضَآءُ مِنۡ اَفۡوَاہِہِمۡ ۚۖ وَ مَا تُخۡفِیۡ صُدُوۡرُہُمۡ اَکۡبَرُ ؕ قَدۡ بَیَّنَّا لَکُمُ الۡاٰیٰتِ  اِنۡ  کُنۡتُمۡ تَعۡقِلُوۡنَ ﴿۱۱۸﴾ ",
              urdu_="مومنو! کسی غیر مذہب کے آدمی کو اپنا رازدار نہ بنانا یہ لوگ تمہاری خرابی میں کسی طرح کی کوتاہی نہیں کرتے اور چاہتے ہیں کہ جس طرح ہو تمہیں تکلیف پہنچے۔ ان کی زبانوں سے تو دشمنی ظاہر ہو ہی چکی ہے۔ اور جو کینے ان کے سینوں میں مخفی ہیں وہ کہیں زیادہ ہیں۔ اگر تم عقل رکھتے ہو تو ہم نے تم کو اپنی آیتیں کھول کھول کر سنا دی ہیں۔",
          ),

      Verse(
              id_=119,
              arabic_="ہٰۤاَنۡتُمۡ اُولَآءِ تُحِبُّوۡنَہُمۡ وَ لَا یُحِبُّوۡنَکُمۡ وَ تُؤۡمِنُوۡنَ بِالۡکِتٰبِ کُلِّہٖ ۚ وَ اِذَا لَقُوۡکُمۡ قَالُوۡۤا اٰمَنَّا ۚ٭ۖ  وَ اِذَا خَلَوۡا عَضُّوۡا عَلَیۡکُمُ  الۡاَنَامِلَ مِنَ الۡغَیۡظِ ؕ قُلۡ مُوۡتُوۡا بِغَیۡظِکُمۡ ؕ اِنَّ اللّٰہَ عَلِیۡمٌۢ بِذَاتِ الصُّدُوۡرِ ﴿۱۱۹﴾ ",
              urdu_="دیکھو تم ایسے صاف دل لوگ ہو کہ ان لوگوں سے دوستی رکھتے ہو حالانکہ یہ تم سے دوستی نہیں رکھتے اور تم سب کتابوں پر ایمان رکھتے ہو اور یہ تمہاری کتاب کو نہیں مانتے اور جب تم سے ملتے ہیں تو کہتے ہیں۔ ہم ایمان لے آئے ہیں اور جب تنہا ہوتے ہیں تو تم پر غصے کے سبب انگلیاں کاٹ کاٹ کھاتے ہیں ان سے کہدو کہ اپنے غصے میں مر جاؤ۔ اللہ تمہارے دلوں کی باتوں سے خوب واقف ہے۔",
          ),

      Verse(
              id_=120,
              arabic_="اِنۡ تَمۡسَسۡکُمۡ حَسَنَۃٌ تَسُؤۡہُمۡ ۫ وَ اِنۡ تُصِبۡکُمۡ سَیِّئَۃٌ یَّفۡرَحُوۡا بِہَا ؕ وَ اِنۡ تَصۡبِرُوۡا وَ تَتَّقُوۡا لَا یَضُرُّکُمۡ کَیۡدُہُمۡ شَیۡـًٔا ؕ اِنَّ اللّٰہَ بِمَا یَعۡمَلُوۡنَ مُحِیۡطٌ ﴿۱۲۰﴾٪ ",
              urdu_="اگر تمہیں آسودگی حاصل ہو تو ان کو بری لگتی ہے اور اگر تمہیں رنج پہنچے تو خوش ہوتے ہیں اور اگر تم تکلیفوں کی برداشت اور نافرمانی سے کنارہ کشی کرتے رہو گے تو ان کا فریب تمہیں کچھ بھی نقصان نہ پہنچا سکے گا۔ یہ جو کچھ کرتے ہیں اللہ اس کا احاطہ کئے ہوئے ہے۔",
          ),

      Verse(
              id_=121,
              arabic_="وَ اِذۡ غَدَوۡتَ مِنۡ اَہۡلِکَ تُبَوِّیٴُ الۡمُؤۡمِنِیۡنَ مَقَاعِدَ لِلۡقِتَالِ ؕ وَ اللّٰہُ سَمِیۡعٌ عَلِیۡمٌ ﴿۱۲۱﴾ۙ",
              urdu_="اور اس وقت کو یاد کرو جب تم صبح کو اپنے گھر سے روانہ ہو کر ایمان والوں کو لڑائی کے لئے مورچوں پر موقع بہ موقع متعین کرنے لگے اور اللہ سب کچھ سنتا ہے جانتا ہے۔",
          ),

      Verse(
              id_=122,
              arabic_="اِذۡ ہَمَّتۡ طَّآئِفَتٰنِ مِنۡکُمۡ اَنۡ تَفۡشَلَا ۙ وَ اللّٰہُ وَلِیُّہُمَا ؕ وَ عَلَی اللّٰہِ فَلۡیَتَوَکَّلِ الۡمُؤۡمِنُوۡنَ ﴿۱۲۲﴾",
              urdu_="اس وقت تم میں سے دو جماعتوں نے جی چھوڑ دینا چاہا۔ مگر اللہ ان کا مددگار تھا۔ اور مومنوں کو تو اللہ ہی پر بھروسا کرنا چاہیے۔",
          ),

      Verse(
              id_=123,
              arabic_="وَ لَقَدۡ نَصَرَکُمُ اللّٰہُ بِبَدۡرٍ وَّ اَنۡتُمۡ اَذِلَّۃٌ ۚ فَاتَّقُوا اللّٰہَ  لَعَلَّکُمۡ  تَشۡکُرُوۡنَ ﴿۱۲۳﴾ ",
              urdu_="اور اللہ نے جنگ بدر میں بھی تمہاری مدد کی تھی جبکہ اس وقت بھی تم بے سروسامان تھے۔ پس اللہ کی نافرمانی سے بچو تاکہ شکزگذار بن جاؤ۔",
          ),

      Verse(
              id_=124,
              arabic_="اِذۡ تَقُوۡلُ لِلۡمُؤۡمِنِیۡنَ اَلَنۡ یَّکۡفِیَکُمۡ اَنۡ یُّمِدَّکُمۡ رَبُّکُمۡ بِثَلٰثَۃِ اٰلٰفٍ مِّنَ الۡمَلٰٓئِکَۃِ  مُنۡزَلِیۡنَ ﴿۱۲۴﴾ؕ ",
              urdu_="جب تم مومنوں سے یہ کہہ کر ان کے دل بڑھا رہے تھے کہ کیا تم کو یہ کافی نہیں کہ پروردگار تین ہزار فرشتے نازل کر کے تمہیں مدد دے۔",
          ),

      Verse(
              id_=125,
              arabic_="بَلٰۤی ۙ اِنۡ  تَصۡبِرُوۡا وَ تَتَّقُوۡا وَ یَاۡتُوۡکُمۡ مِّنۡ فَوۡرِہِمۡ ہٰذَا یُمۡدِدۡکُمۡ رَبُّکُمۡ بِخَمۡسَۃِ اٰلٰفٍ مِّنَ الۡمَلٰٓئِکَۃِ مُسَوِّمِیۡنَ ﴿۱۲۵﴾ ",
              urdu_="ہاں کیوں نہیں اگر تم دل کو مضبوط رکھو۔ اور نافرمانی سے بچتے رہو اور کافر تم پر جوش کے ساتھ دفعتًہ حملہ کر دیں تو تمہارا پروردگار پانچ ہزار فرشتے جن پر نشان ہوں گے تمہاری مدد کو بھیجے گا۔",
          ),

      Verse(
              id_=126,
              arabic_="وَ مَا جَعَلَہُ اللّٰہُ  اِلَّا بُشۡرٰی لَکُمۡ وَ لِتَطۡمَئِنَّ قُلُوۡبُکُمۡ بِہٖ ؕ وَ مَا النَّصۡرُ اِلَّا  مِنۡ  عِنۡدِ اللّٰہِ الۡعَزِیۡزِ  الۡحَکِیۡمِ ﴿۱۲۶﴾ۙ ",
              urdu_="اور اس مدد کو تو اللہ نے تمہارے لئے ذریعہ بشارت بنایا یعنی اس لئے کہ تمہارے دلوں کو اس سے اطمینان حاصل ہو ورنہ مدد تو اللہ ہی کی طرف سے ہوتی ہے۔ جو غالب ہے حکمت والا ہے۔",
          ),

      Verse(
              id_=127,
              arabic_="لِیَقۡطَعَ طَرَفًا مِّنَ الَّذِیۡنَ کَفَرُوۡۤا اَوۡ یَکۡبِتَہُمۡ فَیَنۡقَلِبُوۡا خَآئِبِیۡنَ ﴿۱۲۷﴾ ",
              urdu_="یہ اللہ نے اس لئے کیا کہ کافروں کی ایک جماعت کو ہلاک کر دے یا انہیں ذلیل و مغلوب کر دے کہ ناکام واپس جائیں۔",
          ),

      Verse(
              id_=128,
              arabic_="لَیۡسَ لَکَ مِنَ الۡاَمۡرِ شَیۡءٌ اَوۡ یَتُوۡبَ عَلَیۡہِمۡ  اَوۡ  یُعَذِّبَہُمۡ فَاِنَّہُمۡ ظٰلِمُوۡنَ ﴿۱۲۸﴾ ",
              urdu_="اے پیغمبر اس کام میں تمہارا کچھ اختیار نہیں اب دو صورتیں ہیں یا اللہ ان کے حال پر مہربانی کرے یا انہیں عذاب دے کہ یہ ظالم لوگ ہیں۔",
          ),

      Verse(
              id_=129,
              arabic_="وَ لِلّٰہِ مَا فِی السَّمٰوٰتِ وَ مَا فِی الۡاَرۡضِ ؕ یَغۡفِرُ لِمَنۡ یَّشَآءُ وَ یُعَذِّبُ مَنۡ یَّشَآءُ ؕ وَ اللّٰہُ غَفُوۡرٌ  رَّحِیۡمٌ ﴿۱۲۹﴾٪ ",
              urdu_="اور جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے سب اللہ ہی کا ہے۔ وہ جسے چاہے بخش دے اور جسے چاہے عذاب کرے۔ اور اللہ بخشنے والا مہربان ہے۔",
          ),

      Verse(
              id_=130,
              arabic_="یٰۤاَیُّہَا الَّذِیۡنَ اٰمَنُوۡا لَا تَاۡکُلُوا الرِّبٰۤوا اَضۡعَافًا مُّضٰعَفَۃً  ۪ وَ اتَّقُوا اللّٰہَ لَعَلَّکُمۡ  تُفۡلِحُوۡنَ ﴿۱۳۰﴾ۚ ",
              urdu_="اے ایمان والو! دگنا چوگنا سود نہ کھاؤ اور اللہ سے ڈرو تا کہ کامیاب ہو جاؤ۔",
          ),

      Verse(
              id_=131,
              arabic_="وَ اتَّقُوا النَّارَ الَّتِیۡۤ  اُعِدَّتۡ لِلۡکٰفِرِیۡنَ ﴿۱۳۱﴾ۚ ",
              urdu_="اور دوزخ کی آگ سے بچو جو کافروں کے لئے تیار کی گئ ہے۔",
          ),

      Verse(
              id_=132,
              arabic_="وَ اَطِیۡعُوا اللّٰہَ وَ الرَّسُوۡلَ لَعَلَّکُمۡ تُرۡحَمُوۡنَ ﴿۱۳۲﴾ۚ ",
              urdu_="اور اللہ اور اس کے رسول کی اطاعت کرو تا کہ تم پر رحمت کی جائے۔",
          ),

      Verse(
              id_=133,
              arabic_="وَ سَارِعُوۡۤا اِلٰی مَغۡفِرَۃٍ  مِّنۡ رَّبِّکُمۡ وَ جَنَّۃٍ عَرۡضُہَا السَّمٰوٰتُ وَ الۡاَرۡضُ ۙ اُعِدَّتۡ  لِلۡمُتَّقِیۡنَ ﴿۱۳۳﴾ۙ ",
              urdu_="اور اپنے پروردگار کی بخشش اور اس بہشت کی طرف لپکو جس کا عرض آسمان اور زمین کے برابر ہے اور جو پرہیزگاروں کے لئے تیار کی گئ ہے۔",
          ),

      Verse(
              id_=134,
              arabic_="الَّذِیۡنَ یُنۡفِقُوۡنَ فِی السَّرَّآءِ وَ الضَّرَّآءِ وَ الۡکٰظِمِیۡنَ الۡغَیۡظَ وَ الۡعَافِیۡنَ عَنِ النَّاسِ ؕ وَ اللّٰہُ یُحِبُّ الۡمُحۡسِنِیۡنَ ﴿۱۳۴﴾ۚ ",
              urdu_="جو آسودگی اور تنگی میں اپنا مال اللہ کی راہ میں خرچ کرتے ہیں اور غصے کو روکتے اور لوگوں کے قصور معاف کرتے ہیں۔ اور اللہ نیکوکاروں کو دوست رکھتا ہے۔",
          ),

      Verse(
              id_=135,
              arabic_="وَ الَّذِیۡنَ  اِذَا فَعَلُوۡا فَاحِشَۃً اَوۡ ظَلَمُوۡۤا اَنۡفُسَہُمۡ ذَکَرُوا  اللّٰہَ  فَاسۡتَغۡفَرُوۡا لِذُنُوۡبِہِمۡ ۪ وَ مَنۡ یَّغۡفِرُ الذُّنُوۡبَ اِلَّا اللّٰہُ ۪۟ وَ لَمۡ یُصِرُّوۡا عَلٰی مَا فَعَلُوۡا وَ ہُمۡ یَعۡلَمُوۡنَ ﴿۱۳۵﴾ ",
              urdu_="اور وہ کہ جب کوئی کھلا گناہ یا اپنے حق میں کوئی اور برائی کر بیٹھتے ہیں تو اللہ کو یاد کرتے اور اپنے گناہوں کی بخشش مانگتے ہیں اور اللہ کے سوا گناہ بخش بھی کون سکتا ہے؟ اور جان بوجھ کر اپنے افعال پر اڑے نہیں رہتے۔",
          ),

      Verse(
              id_=136,
              arabic_="اُولٰٓئِکَ جَزَآؤُہُمۡ مَّغۡفِرَۃٌ مِّنۡ رَّبِّہِمۡ وَ جَنّٰتٌ تَجۡرِیۡ مِنۡ تَحۡتِہَا الۡاَنۡہٰرُ خٰلِدِیۡنَ فِیۡہَا ؕ وَ نِعۡمَ اَجۡرُ  الۡعٰمِلِیۡنَ ﴿۱۳۶﴾ؕ ",
              urdu_="ایسے ہی لوگوں کا صلہ انکے پروردگار کی طرف سے بخشش اور باغ ہیں۔ جن کے نیچے نہریں بہ رہی ہیں اور وہ ان میں ہمیشہ بستے رہیں گے۔ اور اچھے کام کرنے والوں کا بدلہ بہت اچھا ہے۔",
          ),

      Verse(
              id_=137,
              arabic_="قَدۡ خَلَتۡ مِنۡ قَبۡلِکُمۡ سُنَنٌ ۙ فَسِیۡرُوۡا فِی الۡاَرۡضِ فَانۡظُرُوۡا کَیۡفَ کَانَ عَاقِبَۃُ الۡمُکَذِّبِیۡنَ ﴿۱۳۷﴾ ",
              urdu_="تم لوگوں سے پہلے بھی بہت سے واقعات گذر چکے ہیں۔ تو تم زمین میں سیر کر کے دیکھ لو کہ جھٹلانے والوں کا کیسا انجام ہوا۔",
          ),

      Verse(
              id_=138,
              arabic_="ہٰذَا بَیَانٌ لِّلنَّاسِ وَ ہُدًی وَّ مَوۡعِظَۃٌ لِّلۡمُتَّقِیۡنَ ﴿۱۳۸﴾ ",
              urdu_="یہ قرآن لوگوں کے لئے صاف پیغام اور اہل تقوٰی کے لئے ہدایت اور نصیحت ہے۔",
          ),

      Verse(
              id_=139,
              arabic_="وَ لَا تَہِنُوۡا وَ لَا تَحۡزَنُوۡا وَ اَنۡتُمُ الۡاَعۡلَوۡنَ  اِنۡ  کُنۡتُمۡ مُّؤۡمِنِیۡنَ ﴿۱۳۹﴾ ",
              urdu_="اور بد دل نہ ہونا اور نہ غم کرنا اگر تم سچے مومن ہو تو تم ہی غالب رہو گے۔",
          ),

      Verse(
              id_=140,
              arabic_="اِنۡ یَّمۡسَسۡکُمۡ قَرۡحٌ فَقَدۡ مَسَّ الۡقَوۡمَ قَرۡحٌ مِّثۡلُہٗ ؕ وَ تِلۡکَ الۡاَیَّامُ نُدَاوِلُہَا بَیۡنَ النَّاسِ ۚ وَ لِیَعۡلَمَ اللّٰہُ الَّذِیۡنَ اٰمَنُوۡا وَ یَتَّخِذَ مِنۡکُمۡ  شُہَدَآءَ ؕ وَ اللّٰہُ  لَا یُحِبُّ الظّٰلِمِیۡنَ ﴿۱۴۰﴾ۙ ",
              urdu_="اگر تمہیں زخم شکست لگا ہے تو ان لوگوں کو بھی ایسا زخم لگ چکا ہے۔ اور یہ ایام زمانہ ہیں کہ ہم ان کو لوگوں میں بدلتے رہتے ہیں۔ اور اس سے یہ بھی مقصود تھا کہ اللہ ایمان والوں کو جدا کر دے اور تم میں سے بعض کو شہید بنائے اور اللہ بے انصافوں کو پسند نہیں کرتا۔",
          ),

      Verse(
              id_=141,
              arabic_="وَ لِیُمَحِّصَ اللّٰہُ الَّذِیۡنَ اٰمَنُوۡا وَ یَمۡحَقَ الۡکٰفِرِیۡنَ ﴿۱۴۱﴾ ",
              urdu_="اور یہ بھی مقصود تھا کہ اللہ ایمان والوں کو پاک صاف بنا دے اور کافروں کو نابود کردے۔",
          ),

      Verse(
              id_=142,
              arabic_="اَمۡ حَسِبۡتُمۡ اَنۡ تَدۡخُلُوا الۡجَنَّۃَ وَ لَمَّا یَعۡلَمِ اللّٰہُ الَّذِیۡنَ جٰہَدُوۡا مِنۡکُمۡ وَ یَعۡلَمَ الصّٰبِرِیۡنَ ﴿۱۴۲﴾ ",
              urdu_="کیا تم یہ سمجھتے ہو کہ بے آزمائش بہشت میں جا داخل ہو گے حالانکہ ابھی اللہ نے تم میں سے جہاد کرنے والوں کو تو اچھی طرح معلوم کیا ہی نہیں اور یہ بھی مقصود ہے کہ وہ ثابت قدم رہنے والوں کو معلوم کرے۔",
          ),

      Verse(
              id_=143,
              arabic_="وَ لَقَدۡ کُنۡتُمۡ تَمَنَّوۡنَ الۡمَوۡتَ مِنۡ قَبۡلِ اَنۡ تَلۡقَوۡہُ ۪ فَقَدۡ رَاَیۡتُمُوۡہُ وَ اَنۡتُمۡ تَنۡظُرُوۡنَ ﴿۱۴۳﴾٪",
              urdu_="اور تم موت شہادت کے آنے سے پہلے اس کی تمنا کیا کرتے تھے سو تم نے اس کو آنکھوں سے دیکھ لیا۔",
          ),

      Verse(
              id_=144,
              arabic_="وَ مَا مُحَمَّدٌ اِلَّا رَسُوۡلٌ ۚ قَدۡ خَلَتۡ مِنۡ قَبۡلِہِ الرُّسُلُ ؕ اَفَا۠ئِنۡ مَّاتَ اَوۡ قُتِلَ انۡقَلَبۡتُمۡ عَلٰۤی اَعۡقَابِکُمۡ ؕ وَ مَنۡ یَّنۡقَلِبۡ عَلٰی عَقِبَیۡہِ فَلَنۡ یَّضُرَّ اللّٰہَ شَیۡئًا ؕ وَ سَیَجۡزِی اللّٰہُ  الشّٰکِرِیۡنَ ﴿۱۴۴﴾ ",
              urdu_="اور محمد ﷺ ایک پیغمبر ہی تو ہیں۔ ان سے پہلے بھی بہت سے پیغمبر ہو گذرے ہیں۔ بھلا اگر یہ مر جائیں یا مارے جائیں تو تم الٹے پاؤں پھر جاؤ گے؟ اور جو الٹے پاؤں پھر جائے گا تو اللہ کا کچھ نقصان نہیں کر سکے گا اور اللہ شکرگذاروں کو بڑا ثواب دے گا۔",
          ),

      Verse(
              id_=145,
              arabic_="وَ مَا کَانَ لِنَفۡسٍ اَنۡ تَمُوۡتَ اِلَّا بِاِذۡنِ اللّٰہِ کِتٰبًا مُّؤَجَّلًا ؕ وَ مَنۡ یُّرِدۡ ثَوَابَ الدُّنۡیَا نُؤۡتِہٖ مِنۡہَا ۚ وَ مَنۡ یُّرِدۡ  ثَوَابَ الۡاٰخِرَۃِ نُؤۡتِہٖ مِنۡہَا ؕ وَ سَنَجۡزِی الشّٰکِرِیۡنَ ﴿۱۴۵﴾ ",
              urdu_="اور کسی شخص میں طاقت نہیں کہ اللہ کے حکم کے بغیر مر جائے اس نے موت کا وقت مقرر کر کے لکھ رکھا ہے اور جو شخص دنیا میں اپنے اعمال کا بدلہ چاہے اس کو ہم یہیں بدلہ دے دیں گے اور جو آخرت میں طالب ثواب ہو اس کو وہاں اجر عطا کریں گے اور ہم شکرگذاروں کو عنقریب بہت اچھا صلہ دیں گے۔",
          ),

      Verse(
              id_=146,
              arabic_="وَ کَاَیِّنۡ مِّنۡ نَّبِیٍّ قٰتَلَ ۙ مَعَہٗ رِبِّیُّوۡنَ کَثِیۡرٌ ۚ فَمَا وَہَنُوۡا لِمَاۤ اَصَابَہُمۡ فِیۡ سَبِیۡلِ اللّٰہِ وَ مَا ضَعُفُوۡا وَ مَا اسۡتَکَانُوۡا ؕ وَ اللّٰہُ  یُحِبُّ الصّٰبِرِیۡنَ ﴿۱۴۶﴾ ",
              urdu_="اور بہت سے نبی ہوئے ہیں جن کے ساتھ ہو کر اکثر اہل اللہ کے دشمنوں سے لڑے ہیں۔ تو جو مصیبتیں ان پر اللہ کی راہ میں واقع ہوئیں ان کے سبب انہوں نے نہ تو ہمت ہاری اور نہ بزدلی کی نہ کافروں سے دبے اور اللہ استقلال رکھنے والوں کو دوست رکھتا ہے۔",
          ),

      Verse(
              id_=147,
              arabic_="وَ مَا کَانَ قَوۡلَہُمۡ اِلَّاۤ اَنۡ قَالُوۡا رَبَّنَا اغۡفِرۡ لَنَا ذُنُوۡبَنَا وَ اِسۡرَافَنَا فِیۡۤ  اَمۡرِنَا وَ ثَبِّتۡ اَقۡدَامَنَا وَ انۡصُرۡنَا عَلَی الۡقَوۡمِ الۡکٰفِرِیۡنَ ﴿۱۴۷﴾ ",
              urdu_="اور اس حالت میں ان کے منہ سے کوئی بات نکلتی تو یہی کہ اے پروردگار ہمارے گناہ اور زیادتیاں جو ہم اپنے کاموں میں کرتے رہے ہیں معاف فرما۔ اور ہم کو ثابت قدم رکھ اور کافروں پر فتح عنایت کر۔",
          ),

      Verse(
              id_=148,
              arabic_="فَاٰتٰٮہُمُ اللّٰہُ ثَوَابَ الدُّنۡیَا وَ حُسۡنَ ثَوَابِ الۡاٰخِرَۃِ ؕ وَ اللّٰہُ یُحِبُّ الۡمُحۡسِنِیۡنَ ﴿۱۴۸﴾٪ ",
              urdu_="تو اللہ نے ان کو دنیا میں بھی بدلہ دیا اور آخرت میں بھی بہت اچھا بدلہ دے گا اور اللہ نیکوکاروں کو دوست رکھتا ہے۔",
          ),

      Verse(
              id_=149,
              arabic_="یٰۤاَیُّہَا الَّذِیۡنَ اٰمَنُوۡۤا اِنۡ تُطِیۡعُوا الَّذِیۡنَ کَفَرُوۡا یَرُدُّوۡکُمۡ عَلٰۤی اَعۡقَابِکُمۡ فَتَنۡقَلِبُوۡا خٰسِرِیۡنَ ﴿۱۴۹﴾ ",
              urdu_="مومنو! اگر تم کافروں کا کہا مان لو گے تو وہ تم کو الٹے پاؤں پھیر کر مرتد کر دیں گے پھر تم بڑے خسارے میں پڑ جاؤ گے۔",
          ),

      Verse(
              id_=150,
              arabic_="بَلِ اللّٰہُ مَوۡلٰٮکُمۡ ۚ وَ ہُوَ خَیۡرُ النّٰصِرِیۡنَ ﴿۱۵۰﴾ ",
              urdu_="یہ تمہارے مددگار نہیں ہیں بلکہ اللہ تمہارا مددگار ہے اور وہ سب سے بہتر مددگار ہے۔",
          ),

      Verse(
              id_=151,
              arabic_="سَنُلۡقِیۡ فِیۡ قُلُوۡبِ الَّذِیۡنَ  کَفَرُوا الرُّعۡبَ بِمَاۤ اَشۡرَکُوۡا بِاللّٰہِ مَا لَمۡ یُنَزِّلۡ بِہٖ سُلۡطٰنًا ۚ وَ مَاۡوٰٮہُمُ النَّارُ ؕ وَ بِئۡسَ مَثۡوَی الظّٰلِمِیۡنَ ﴿۱۵۱﴾ ",
              urdu_="ہم عنقریب کافروں کے دلوں میں رعب بٹھا دیں گے کیونکہ یہ اللہ کے ساتھ شرک کرتے ہیں جس کی اس نے کوئی بھی دلیل نازل نہیں کی۔ اور ان کا ٹھکانہ دوزخ ہے وہ ظالموں کا بہت برا ٹھکانہ ہے۔",
          ),

      Verse(
              id_=152,
              arabic_="وَ لَقَدۡ صَدَقَکُمُ اللّٰہُ وَعۡدَہٗۤ  اِذۡ تَحُسُّوۡنَہُمۡ بِاِذۡنِہٖ ۚ حَتّٰۤی  اِذَا فَشِلۡتُمۡ وَ تَنَازَعۡتُمۡ فِی الۡاَمۡرِ وَ عَصَیۡتُمۡ مِّنۡۢ بَعۡدِ مَاۤ اَرٰٮکُمۡ مَّا تُحِبُّوۡنَ ؕ مِنۡکُمۡ مَّنۡ یُّرِیۡدُ الدُّنۡیَا وَ مِنۡکُمۡ مَّنۡ یُّرِیۡدُ الۡاٰخِرَۃَ ۚ  ثُمَّ صَرَفَکُمۡ عَنۡہُمۡ لِیَبۡتَلِیَکُمۡ ۚ وَ لَقَدۡ عَفَا عَنۡکُمۡ  ؕ وَ اللّٰہُ ذُوۡ فَضۡلٍ عَلَی الۡمُؤۡمِنِیۡنَ ﴿۱۵۲﴾ ",
              urdu_="اور اللہ نے تم سے اپنا وعدہ سچا کردیا یعنی اس وقت جب کہ تم کافروں کو اس کے حکم سے قتل کر رہے تھے۔ یہاں تک کہ جو تم چاہتے تھے اللہ نے تم کو دکھا دیا۔ اس کے بعد تم نے ہمت ہار دی اور پیغمبر کے حکم کی تعمیل میں جھگڑا کرنے لگے اور تم نے اس کی نافرمانی کی۔ بعض تو تم میں سے دنیا کے طلبگار تھے اور بعض آخرت کے طالب۔ اس وقت اللہ نے تم کو ان کے سامنے سے ہٹا دیا تا کہ تمہاری آزمائش کرے اور اس نے تمہارا قصور معاف کر دیا۔ اور اللہ مومنوں پر بڑا فضل کرنے والا ہے۔",
          ),

      Verse(
              id_=153,
              arabic_="اِذۡ تُصۡعِدُوۡنَ وَ لَا تَلۡوٗنَ عَلٰۤی اَحَدٍ وَّ الرَّسُوۡلُ یَدۡعُوۡکُمۡ فِیۡۤ  اُخۡرٰٮکُمۡ فَاَثَابَکُمۡ غَمًّۢا بِغَمٍّ لِّکَیۡلَا تَحۡزَنُوۡا عَلٰی مَا فَاتَکُمۡ وَ لَا مَاۤ  اَصَابَکُمۡ ؕ وَ اللّٰہُ خَبِیۡرٌۢ  بِمَا تَعۡمَلُوۡنَ ﴿۱۵۳﴾ ",
              urdu_="وہ وقت بھی یاد کرنے کے لائق ہے جب تم لوگ دور بھاگے جاتے تھے اور کسی کو پیچھے پھر کر نہیں دیکھتے تھے اور رسول اللہ ﷺ تم کو تمہارے پیچھے کھڑے بلا رہے تھے تو اللہ نے تم کو غم پر غم پہنچایا تا کہ جو چیز تمہارے ہاتھ سے جاتی رہی یا جو مصیبت تم پر واقع ہوئی ہے اس سے تم غمگین نہ ہو اور اللہ تمہارے سب اعمال سے خبردار ہے۔",
          ),

      Verse(
              id_=154,
              arabic_="ثُمَّ اَنۡزَلَ عَلَیۡکُمۡ مِّنۡۢ بَعۡدِ الۡغَمِّ اَمَنَۃً نُّعَاسًا یَّغۡشٰی طَآئِفَۃً مِّنۡکُمۡ ۙ وَ طَآئِفَۃٌ قَدۡ اَہَمَّتۡہُمۡ  اَنۡفُسُہُمۡ  یَظُنُّوۡنَ بِاللّٰہِ غَیۡرَ الۡحَقِّ ظَنَّ الۡجَاہِلِیَّۃِ ؕ یَقُوۡلُوۡنَ ہَلۡ لَّنَا مِنَ الۡاَمۡرِ  مِنۡ شَیۡءٍ ؕ قُلۡ   اِنَّ الۡاَمۡرَ  کُلَّہٗ  لِلّٰہِ ؕ یُخۡفُوۡنَ فِیۡۤ اَنۡفُسِہِمۡ مَّا لَا یُبۡدُوۡنَ لَکَ ؕ یَقُوۡلُوۡنَ لَوۡ کَانَ لَنَا مِنَ الۡاَمۡرِ شَیۡءٌ مَّا قُتِلۡنَا ہٰہُنَا ؕ قُلۡ لَّوۡ کُنۡتُمۡ فِیۡ بُیُوۡتِکُمۡ لَبَرَزَ الَّذِیۡنَ کُتِبَ عَلَیۡہِمُ الۡقَتۡلُ اِلٰی مَضَاجِعِہِمۡ ۚ وَ لِیَبۡتَلِیَ اللّٰہُ مَا فِیۡ صُدُوۡرِکُمۡ وَ لِیُمَحِّصَ مَا فِیۡ قُلُوۡبِکُمۡ ؕ وَ اللّٰہُ عَلِیۡمٌۢ بِذَاتِ الصُّدُوۡرِ ﴿۱۵۴﴾ ",
              urdu_="پھر اللہ نے اس پریشانی کے بعد تم پر تسلی نازل فرمائی۔ ( یعنی ) نیند کہ تم میں سے ایک جماعت پر طاری ہو گئ اور کچھ لوگ جنکو جان کے لالے پڑ رہے تھے اللہ کے بارے میں ناحق ایّام کفر کے سے گمان کرتے تھے۔ اور کہتے تھے بھلا ہمارے اختیار کی کچھ بات ہے؟ تم کہدو کہ بیشک سب باتیں اللہ ہی کے اختیار میں ہیں۔ یہ لوگ بہت سی باتیں دلوں میں مخفی رکھتے ہیں جو تم پر ظاہر نہیں کرتے تھے۔ کہتے تھے کہ ہمارے بس کی بات ہوتی تو ہم یہاں قتل ہی نہ کئے جاتے۔  کہدو کہ اگر تم اپنے گھروں میں بھی ہوتے تو جنکی تقدیر میں مارا جانا لکھا تھا وہ اپنی اپنی قتل گاہوں کی طرف ضرور نکل آتے۔ اس سے غرض یہ تھی کہ اللہ تمہارے سینوں کی باتوں کو آزمائے اور جو کچھ تمہارے دلوں میں ہے اس کو صاف کر دے۔ اور اللہ دلوں کی باتوں سے خوب واقف ہے۔",
          ),

      Verse(
              id_=155,
              arabic_="اِنَّ الَّذِیۡنَ تَوَلَّوۡا مِنۡکُمۡ یَوۡمَ الۡتَقَی الۡجَمۡعٰنِ ۙ اِنَّمَا اسۡتَزَلَّہُمُ الشَّیۡطٰنُ بِبَعۡضِ مَا کَسَبُوۡا ۚ وَ لَقَدۡ عَفَا اللّٰہُ عَنۡہُمۡ ؕ اِنَّ اللّٰہَ  غَفُوۡرٌ  حَلِیۡمٌ ﴿۱۵۵﴾٪ ",
              urdu_="جو لوگ تم میں سے احد کے دن جبکہ مومنوں اور کافروں کی دو جماعتیں ایک دوسرے سے گتھ گئیں جنگ سے بھاگ گئے تو ان کے بعض افعال کے سبب شیطان نے ان کو پھسلا دیا تھا۔ مگر اللہ نے ان کا قصور معاف کر دیا۔ بیشک اللہ بخشنے والا ہے بردبار ہے۔",
          ),

      Verse(
              id_=156,
              arabic_="یٰۤاَیُّہَا الَّذِیۡنَ اٰمَنُوۡا لَا تَکُوۡنُوۡا کَالَّذِیۡنَ کَفَرُوۡا وَ قَالُوۡا لِاِخۡوَانِہِمۡ اِذَا ضَرَبُوۡا فِی الۡاَرۡضِ اَوۡ کَانُوۡا غُزًّی لَّوۡ کَانُوۡا عِنۡدَنَا مَا مَاتُوۡا وَ مَا قُتِلُوۡا ۚ لِیَجۡعَلَ اللّٰہُ ذٰلِکَ حَسۡرَۃً فِیۡ قُلُوۡبِہِمۡ ؕ وَ اللّٰہُ یُحۡیٖ وَ یُمِیۡتُ ؕ وَ اللّٰہُ بِمَا تَعۡمَلُوۡنَ  بَصِیۡرٌ ﴿۱۵۶﴾ ",
              urdu_="مومنو! ان لوگوں جیسے نہ ہونا جو کفر کرتے ہیں اور ان کے مسلمان بھائی جب اللہ کی راہ میں سفر کریں اور مرجائیں یا جہاد کو نکلیں اور مارے جائیں تو ان کی نسبت کہتے ہیں کہ اگر وہ ہمارے پاس رہتے تو نہ مرتے اور نہ مارے جاتے یہ اسلئے کہ اللہ ان لوگوں کے دلوں میں افسوس پیدا کر دے۔ اور زندگی اور موت تو اللہ ہی دیتا ہے اور اللہ تمہارے سب کاموں کو دیکھ رہا ہے۔",
          ),

      Verse(
              id_=157,
              arabic_="وَ لَئِنۡ قُتِلۡتُمۡ فِیۡ سَبِیۡلِ اللّٰہِ اَوۡ مُتُّمۡ لَمَغۡفِرَۃٌ مِّنَ اللّٰہِ وَ رَحۡمَۃٌ خَیۡرٌ مِّمَّا یَجۡمَعُوۡنَ ﴿۱۵۷﴾ ",
              urdu_="اور اگر تم اللہ کے رستے میں مارے جاؤ یا مر جاؤ تو جو مال و متاع لوگ جمع کرتے ہیں اس سے اللہ کی بخشش اور رحمت کہیں بہتر ہے۔",
          ),

      Verse(
              id_=158,
              arabic_="وَ لَئِنۡ مُّتُّمۡ اَوۡ قُتِلۡتُمۡ لَاِالَی اللّٰہِ تُحۡشَرُوۡنَ ﴿۱۵۸﴾ ",
              urdu_="اور اگر تم مر جاؤ یا مارے جاؤ تو اللہ کے حضور میں ضرور اکٹھے کئے جاؤ گے۔",
          ),

      Verse(
              id_=159,
              arabic_="فَبِمَا رَحۡمَۃٍ مِّنَ اللّٰہِ لِنۡتَ لَہُمۡ ۚ وَ لَوۡ کُنۡتَ فَظًّا غَلِیۡظَ الۡقَلۡبِ لَانۡفَضُّوۡا مِنۡ حَوۡلِکَ ۪ فَاعۡفُ عَنۡہُمۡ وَ اسۡتَغۡفِرۡ لَہُمۡ وَ شَاوِرۡہُمۡ فِی الۡاَمۡرِ ۚ فَاِذَا عَزَمۡتَ فَتَوَکَّلۡ عَلَی اللّٰہِ ؕ اِنَّ اللّٰہَ یُحِبُّ الۡمُتَوَکِّلِیۡنَ ﴿۱۵۹﴾ ",
              urdu_="پھر اے نبی اللہ کی مہربانی سے تم ان لوگوں کے لئے نرم مزاج واقع ہوئے ہو اور اگر تم بدخو اور سخت دل ہوتے تو یہ تمہارے پاس سے بھاگ کھڑے ہوتے۔  سو ان کو معاف کر دو اور ان کے لئے اللہ سے مغفرت مانگو اور اپنے کاموں میں ان سے مشورت لیا کرو۔ اور جب کسی کام کا پختہ ارادہ کر لو تو اللہ پر بھروسہ رکھو۔ بیشک اللہ بھروسہ رکھنے والوں کو دوست رکھتا ہے۔",
          ),

      Verse(
              id_=160,
              arabic_="اِنۡ یَّنۡصُرۡکُمُ اللّٰہُ فَلَا غَالِبَ لَکُمۡ ۚ وَ اِنۡ یَّخۡذُلۡکُمۡ فَمَنۡ ذَا الَّذِیۡ یَنۡصُرُکُمۡ مِّنۡۢ بَعۡدِہٖ ؕ وَ عَلَی اللّٰہِ فَلۡیَتَوَکَّلِ الۡمُؤۡمِنُوۡنَ ﴿۱۶۰﴾",
              urdu_="اگر اللہ تمہارا مددگار ہے تو تم پر کوئی غالب نہیں آ سکتا اور اگر وہ تمہیں چھوڑ دے تو پھر کون ہے اسکے بعد تمہاری مدد کرے اور مومنوں کو تو چاہیے کہ اللہ ہی پر بھروسہ رکھیں۔",
          ),

      Verse(
              id_=161,
              arabic_="وَ مَا کَانَ لِنَبِیٍّ اَنۡ یَّغُلَّ ؕ وَ مَنۡ یَّغۡلُلۡ یَاۡتِ بِمَا غَلَّ  یَوۡمَ الۡقِیٰمَۃِ ۚ ثُمَّ  تُوَفّٰی کُلُّ نَفۡسٍ مَّا کَسَبَتۡ وَ ہُمۡ لَا یُظۡلَمُوۡنَ ﴿۱۶۱﴾ ",
              urdu_="اور کبھی نہیں ہو سکتا کہ کوئی پیغمبر خیانت کرے۔ اور خیانت کرنے والوں کو قیامت کے دن خیانت کی ہوئی چیز اللہ کے روبرو لا حاضر کرنی ہو گی۔ پھر ہر شخص کو اس کے اعمال کا پورا پورا بدلہ دیا جائے گا اور انکے ساتھ بے انصافی نہیں کی جائے گی۔",
          ),

      Verse(
              id_=162,
              arabic_="اَفَمَنِ اتَّبَعَ رِضۡوَانَ اللّٰہِ کَمَنۡۢ بَآءَ بِسَخَطٍ مِّنَ اللّٰہِ وَ مَاۡوٰٮہُ جَہَنَّمُ ؕ وَ بِئۡسَ الۡمَصِیۡرُ  ﴿۱۶۲﴾",
              urdu_="بھلا جو شخص اللہ کی خوشنودی کا تابع ہو وہ اس شخص کی طرح ہو سکتا ہے جو اللہ کی ناخوشی میں گرفتار ہو اور جس کا ٹھکانہ دوزخ ہے اور وہ برا ٹھکانہ ہے۔",
          ),

      Verse(
              id_=163,
              arabic_="ہُمۡ دَرَجٰتٌ عِنۡدَ اللّٰہِ ؕ وَ اللّٰہُ بَصِیۡرٌۢ  بِمَا یَعۡمَلُوۡنَ ﴿۱۶۳﴾ ",
              urdu_="سب لوگوں کے اللہ کے ہاں مختلف درجے ہیں اور اللہ ان کے سب اعمال دیکھ رہا ہے۔",
          ),

      Verse(
              id_=164,
              arabic_="لَقَدۡ مَنَّ اللّٰہُ عَلَی الۡمُؤۡمِنِیۡنَ اِذۡ بَعَثَ فِیۡہِمۡ رَسُوۡلًا مِّنۡ اَنۡفُسِہِمۡ یَتۡلُوۡا عَلَیۡہِمۡ اٰیٰتِہٖ وَ یُزَکِّیۡہِمۡ وَ یُعَلِّمُہُمُ الۡکِتٰبَ وَ الۡحِکۡمَۃَ ۚ وَ  اِنۡ کَانُوۡا مِنۡ قَبۡلُ لَفِیۡ ضَلٰلٍ مُّبِیۡنٍ ﴿۱۶۴﴾ ",
              urdu_="اللہ نے مومنوں پر یقینًا احسان کیا ہے کہ ان میں انہیں میں سے ایک پیغمبر بھیجے جو ان کو اللہ کی آیتیں پڑھ پڑھ کو سناتے اور ان کو پاک کرتے اور انہیں کتاب و حکمت کی تعلیم دیتے ہیں۔ اور پہلے تو یہ لوگ کھلی گمراہی میں تھے۔",
          ),

      Verse(
              id_=165,
              arabic_="اَوَ لَمَّاۤ اَصَابَتۡکُمۡ مُّصِیۡبَۃٌ قَدۡ اَصَبۡتُمۡ مِّثۡلَیۡہَا ۙ قُلۡتُمۡ اَنّٰی ہٰذَا ؕ قُلۡ ہُوَ مِنۡ عِنۡدِ اَنۡفُسِکُمۡ ؕ اِنَّ اللّٰہَ عَلٰی کُلِّ شَیۡءٍ  قَدِیۡرٌ ﴿۱۶۵﴾ ",
              urdu_="بھلا یہ کیا بات ہے کہ جب احد کے دن کفار کے ہاتھ سے تم پر مصیبت واقع ہوئی حالانکہ جنگ بدر میں اس سے دگنی مصیبت تمہارے ہاتھ سے ان پر پڑ چکی ہے تو تم کہنے لگے کہ یہ آفت ہم پر کہاں سے آپڑی کہدو کہ یہ تمہاری ہی شامت اعمال ہے کہ تم نے پیغمبر کے حکم کے خلاف کیا بیشک اللہ ہر چیز پر قادر ہے۔",
          ),

      Verse(
              id_=166,
              arabic_="وَ مَاۤ اَصَابَکُمۡ یَوۡمَ الۡتَقَی الۡجَمۡعٰنِ فَبِاِذۡنِ اللّٰہِ وَ لِیَعۡلَمَ الۡمُؤۡمِنِیۡنَ ﴿۱۶۶﴾ۙ ",
              urdu_="اور جو مصیبت تم پر دونوں جماعتوں کے مقابلے کے دن واقع ہوئی سو اللہ کے حکم سے واقع ہوئی اور اس سے یہ مقصود تھا کہ اللہ مومنوں کو اچھی طرح معلوم کر لے۔",
          ),

      Verse(
              id_=167,
              arabic_="وَ لِیَعۡلَمَ الَّذِیۡنَ نَافَقُوۡا ۚۖ وَ قِیۡلَ لَہُمۡ تَعَالَوۡا قَاتِلُوۡا فِیۡ سَبِیۡلِ اللّٰہِ اَوِ ادۡفَعُوۡا ؕ قَالُوۡا لَوۡ نَعۡلَمُ قِتَالًا لَّا تَّبَعۡنٰکُمۡ ؕ ہُمۡ  لِلۡکُفۡرِ یَوۡمَئِذٍ اَقۡرَبُ مِنۡہُمۡ لِلۡاِیۡمَانِ ۚ یَقُوۡلُوۡنَ بِاَفۡوَاہِہِمۡ مَّا لَیۡسَ فِیۡ قُلُوۡبِہِمۡ ؕ وَ اللّٰہُ اَعۡلَمُ  بِمَا یَکۡتُمُوۡنَ ﴿۱۶۷﴾ۚ ",
              urdu_="اور منافقوں کو بھی معلوم کر لے اور جب ان سے کہا گیا کہ آؤ اللہ کے رستے میں جنگ کرو یا کافروں کے حملوں کو روکو۔ تو کہنے لگے کہ اگر ہم کو لڑائی کی خبر ہوتی تو ہم ضرور تمہارے ساتھ رہتے۔ یہ اس دن ایمان کی نسبت کفر سے زیادہ قریب تھے۔ منہ سے وہ باتیں کہتے ہیں جو ان کے دل میں نہیں ہیں۔ اور جو کچھ یہ چھپاتے ہیں اللہ اس سے خوب واقف ہے۔",
          ),

      Verse(
              id_=168,
              arabic_="اَلَّذِیۡنَ قَالُوۡا لِاِخۡوَانِہِمۡ وَ قَعَدُوۡا لَوۡ اَطَاعُوۡنَا مَا قُتِلُوۡا ؕ قُلۡ فَادۡرَءُوۡا عَنۡ اَنۡفُسِکُمُ الۡمَوۡتَ اِنۡ کُنۡتُمۡ صٰدِقِیۡنَ  ﴿۱۶۸﴾",
              urdu_="یہ خود تو جنگ سے بچکر بیٹھ ہی رہے تھے مگر جنہوں نےاللہ کی راہ میں جانیں قربان کر دیں اپنے ان بھائیوں کے بارے میں بھی کہتے ہیں کہ اگر ہمارا کہا مانتے تو قتل نہ ہوتے کہدو کہ اگر سچے ہو تو اپنے اوپر سے موت کو ٹال دینا۔",
          ),

      Verse(
              id_=169,
              arabic_="وَ لَا تَحۡسَبَنَّ الَّذِیۡنَ قُتِلُوۡا فِیۡ سَبِیۡلِ اللّٰہِ اَمۡوَاتًا ؕ بَلۡ اَحۡیَآءٌ عِنۡدَ رَبِّہِمۡ یُرۡزَقُوۡنَ ﴿۱۶۹﴾ۙ ",
              urdu_="جو لوگ اللہ کی راہ میں مارے گئے ان کو مرے ہوئے نہ سمجھنا وہ مرے ہوئے نہیں ہیں بلکہ اللہ کے نزدیک زندہ ہیں اور ان کو رزق مل رہا ہے۔",
          ),

      Verse(
              id_=170,
              arabic_="فَرِحِیۡنَ بِمَاۤ  اٰتٰہُمُ اللّٰہُ مِنۡ فَضۡلِہٖ ۙ وَ یَسۡتَبۡشِرُوۡنَ بِالَّذِیۡنَ لَمۡ یَلۡحَقُوۡا بِہِمۡ مِّنۡ خَلۡفِہِمۡ ۙ اَلَّا خَوۡفٌ عَلَیۡہِمۡ وَ لَا ہُمۡ یَحۡزَنُوۡنَ ﴿۱۷۰﴾ۘ ",
              urdu_="جو کچھ اللہ نے ان کو اپنے فضل سے بخش رکھا ہے اس میں خوش ہیں اور جو لوگ ان کے پیچھے رہ گئے اور شہید ہو کر ان میں شامل نہیں ہو سکے ان کی نسبت خوشیاں منا رہے ہیں کہ قیامت کے دن ان کو بھی نہ کچھ خوف ہو گا اور نہ وہ غمناک ہوں گے۔",
          ),

      Verse(
              id_=171,
              arabic_="یَسۡتَبۡشِرُوۡنَ بِنِعۡمَۃٍ مِّنَ اللّٰہِ وَ فَضۡلٍ ۙ وَّ اَنَّ اللّٰہَ لَا یُضِیۡعُ اَجۡرَ الۡمُؤۡمِنِیۡنَ   ﴿۱۷۱﴾ۚ٪ۛ",
              urdu_="اور اللہ کے انعامات اور فضل سے خوش ہو رہے ہیں اور اس سے کہ اللہ مومنوں کا اجر ضائع نہیں کرتا۔",
          ),

      Verse(
              id_=172,
              arabic_="اَلَّذِیۡنَ اسۡتَجَابُوۡا لِلّٰہِ وَ الرَّسُوۡلِ مِنۡۢ بَعۡدِ مَاۤ  اَصَابَہُمُ الۡقَرۡحُ ؕۛ لِلَّذِیۡنَ اَحۡسَنُوۡا مِنۡہُمۡ وَ اتَّقَوۡا اَجۡرٌ  عَظِیۡمٌ  ﴿۱۷۲﴾ۚ",
              urdu_="جنہوں نے باوجود زخم کھانے کے اللہ اور رسول کے حکم کو قبول کیا۔ جو لوگ ان میں نیکوکار اور پرہیزگار ہیں ان کے لئے بڑا ثواب ہے۔",
          ),

      Verse(
              id_=173,
              arabic_="اَلَّذِیۡنَ  قَالَ لَہُمُ النَّاسُ  اِنَّ النَّاسَ قَدۡ جَمَعُوۡا لَکُمۡ فَاخۡشَوۡہُمۡ فَزَادَہُمۡ اِیۡمَانًا ٭ۖ وَّ قَالُوۡا حَسۡبُنَا اللّٰہُ وَ نِعۡمَ الۡوَکِیۡلُ ﴿۱۷۳﴾ ",
              urdu_="جب ان سے لوگوں نے آ کر بیان کیا کہ کفار نے تمہارے مقابلے کے لئے لشکر کثیر جمع کیا ہے تو ان سے ڈرو۔ تو ان کا ایمان اور زیادہ ہو گیا۔ اور کہنے لگے ہم کو اللہ کافی ہے اور وہ بہت اچھا کارساز ہے۔",
          ),

      Verse(
              id_=174,
              arabic_="فَانۡقَلَبُوۡا بِنِعۡمَۃٍ مِّنَ اللّٰہِ وَ فَضۡلٍ لَّمۡ یَمۡسَسۡہُمۡ سُوۡٓءٌ ۙ وَّ اتَّبَعُوۡا رِضۡوَانَ اللّٰہِ ؕ وَ اللّٰہُ ذُوۡ فَضۡلٍ  عَظِیۡمٍ ﴿۱۷۴﴾ ",
              urdu_="پھر وہ اللہ کی نعمتوں اور اس کے فضل کے ساتھ خوش و خرم واپس آئے ان کو کسی طرح کا ضرر نہ پہنچا اور وہ اللہ کی خوشنودی کے تابع رہے اور اللہ بڑے فضل کا مالک ہے۔",
          ),

      Verse(
              id_=175,
              arabic_="اِنَّمَا ذٰلِکُمُ الشَّیۡطٰنُ یُخَوِّفُ اَوۡلِیَآءَہٗ ۪ فَلَا تَخَافُوۡہُمۡ وَ خَافُوۡنِ  اِنۡ کُنۡتُمۡ  مُّؤۡمِنِیۡنَ  ﴿۱۷۵﴾",
              urdu_="یہ خوف دلانے والا تو شیطان ہے جو اپنے دوستوں سے ڈراتا ہے۔ تو اگر تم مومن ہو تو ان سے مت ڈرنا اور مجھی سے ڈرتے رہنا۔",
          ),

      Verse(
              id_=176,
              arabic_="وَ لَا یَحۡزُنۡکَ الَّذِیۡنَ یُسَارِعُوۡنَ فِی الۡکُفۡرِ ۚ اِنَّہُمۡ لَنۡ یَّضُرُّوا اللّٰہَ شَیۡئًا ؕ یُرِیۡدُ اللّٰہُ اَلَّا یَجۡعَلَ لَہُمۡ حَظًّا فِی الۡاٰخِرَۃِ ۚ وَ لَہُمۡ عَذَابٌ عَظِیۡمٌ ﴿۱۷۶﴾ ",
              urdu_="اور جو لوگ کفر میں جلدی کرتے ہیں ان کی وجہ سے غمگین نہ ہونا۔ یہ اللہ کا کچھ نقصان نہیں کر سکتے اللہ چاہتا ہے کہ آخرت میں ان کو کچھ حصہ نہ دے اور ان کے لئے بڑا عذاب تیار ہے۔",
          ),

      Verse(
              id_=177,
              arabic_="اِنَّ الَّذِیۡنَ اشۡتَرَوُا الۡکُفۡرَ بِالۡاِیۡمَانِ لَنۡ یَّضُرُّوا اللّٰہَ شَیۡئًا ۚ وَ لَہُمۡ عَذَابٌ اَلِیۡمٌ ﴿۱۷۷﴾ ",
              urdu_="جن لوگوں نے ایمان کے بدلے کفر خریدا وہ اللہ کا کچھ نہیں بگاڑ سکتے اور ان کو دکھ دینے والا عذاب ہو گا۔",
          ),

      Verse(
              id_=178,
              arabic_="وَ لَا یَحۡسَبَنَّ الَّذِیۡنَ کَفَرُوۡۤا اَنَّمَا نُمۡلِیۡ لَہُمۡ خَیۡرٌ لِّاَنۡفُسِہِمۡ ؕ اِنَّمَا نُمۡلِیۡ لَہُمۡ لِیَزۡدَادُوۡۤا اِثۡمًا ۚ وَ لَہُمۡ عَذَابٌ مُّہِیۡنٌ ﴿۱۷۸﴾ ",
              urdu_="اور کافر لوگ یہ نہ خیال کریں کہ ہم جو ان کو مہلت دئیے جاتے ہیں تو یہ ان کے حق میں اچھا ہے نہیں بلکہ ہم انکو اس لئے مہلت دیتے ہیں کہ اور گناہ کر لیں آخر کار ان کو ذلیل کرنے والا عذاب ہو گا۔",
          ),

      Verse(
              id_=179,
              arabic_="مَا  کَانَ اللّٰہُ لِیَذَرَ الۡمُؤۡمِنِیۡنَ عَلٰی مَاۤ  اَنۡتُمۡ عَلَیۡہِ حَتّٰی یَمِیۡزَ  الۡخَبِیۡثَ مِنَ الطَّیِّبِ ؕ وَ مَا کَانَ اللّٰہُ لِیُطۡلِعَکُمۡ عَلَی الۡغَیۡبِ وَ لٰکِنَّ اللّٰہَ یَجۡتَبِیۡ مِنۡ رُّسُلِہٖ مَنۡ یَّشَآءُ ۪ فَاٰمِنُوۡا بِاللّٰہِ وَ رُسُلِہٖ ۚ وَ  اِنۡ تُؤۡمِنُوۡا وَ تَتَّقُوۡا فَلَکُمۡ  اَجۡرٌ  عَظِیۡمٌ ﴿۱۷۹﴾ ",
              urdu_="لوگو جب تک اللہ ناپاک کو پاک سے الگ نہ کر دے گا مومنوں کو اس حال میں جس میں تم ہو ہرگز نہیں رہنے دے گا۔ اور اللہ تم کو غیب کی باتوں سے بھی مطلع نہیں کرے گا۔ البتہ اللہ اپنے پیغمبروں میں سے جسے چاہتا ہے منتخب کر لیتا ہے سو تم اللہ پر اور اس کے رسولوں پر ایمان رکھو۔ اور اگر ایمان  پر قائم رہو گے اور پرہیزگاری کرو گے تو تم کو اجر عظیم ملے گا۔",
          ),

      Verse(
              id_=180,
              arabic_="وَ لَا یَحۡسَبَنَّ الَّذِیۡنَ یَبۡخَلُوۡنَ بِمَاۤ اٰتٰہُمُ اللّٰہُ مِنۡ فَضۡلِہٖ ہُوَ خَیۡرًا لَّہُمۡ ؕ بَلۡ ہُوَ شَرٌّ  لَّہُمۡ ؕ سَیُطَوَّقُوۡنَ مَا بَخِلُوۡا بِہٖ یَوۡمَ الۡقِیٰمَۃِ ؕ وَ لِلّٰہِ مِیۡرَاثُ السَّمٰوٰتِ وَ الۡاَرۡضِ ؕ وَ اللّٰہُ بِمَا تَعۡمَلُوۡنَ خَبِیۡرٌ ﴿۱۸۰﴾٪ ",
              urdu_="جو لوگ مال میں جو اللہ نے اپنے فضل سے ان کو عطا فرمایا ہے بخل کرتے ہیں وہ اس بخل کو اپنے حق میں اچھا نہ سمجھیں وہ اچھا نہیں بلکہ ان کے لئے برا ہے وہ جس مال میں بخل کرتے ہیں قیامت کے دن اس کا طوق بنا کر ان کی گردنوں میں ڈالا جائے گا اور آسمانوں اور زمین کا وارث اللہ ہی ہے اور جو عمل تم کرتے ہو اللہ کو معلوم ہے۔",
          ),

      Verse(
              id_=181,
              arabic_="لَقَدۡ سَمِعَ اللّٰہُ قَوۡلَ الَّذِیۡنَ قَالُوۡۤا اِنَّ اللّٰہَ فَقِیۡرٌ وَّ نَحۡنُ اَغۡنِیَآءُ ۘ سَنَکۡتُبُ مَا قَالُوۡا وَ قَتۡلَہُمُ الۡاَنۡۢبِیَآءَ بِغَیۡرِ حَقٍّ ۙ وَّ نَقُوۡلُ ذُوۡقُوۡا عَذَابَ الۡحَرِیۡقِ ﴿۱۸۱﴾ ",
              urdu_="اللہ نے ان لوگوں کا قول سن لیا ہے جو کہتے ہیں کہ اللہ فقیر ہے اور ہم مالدار ہیں۔ یہ جو کچھ کہتے ہیں ہم اس کو لکھ لیں گے اور پیغمبروں کو جو یہ ناحق قتل کرتے رہے ہیں اس کو بھی قلمبند کر رکھیں گے اور قیامت کے روز کہیں گے کہ سخت جلن والی آگ کے عذاب کے مزے چکھتے رہو۔",
          ),

      Verse(
              id_=182,
              arabic_="ذٰلِکَ بِمَا قَدَّمَتۡ اَیۡدِیۡکُمۡ وَ اَنَّ اللّٰہَ لَیۡسَ بِظَلَّامٍ  لِّلۡعَبِیۡدِ  ﴿۱۸۲﴾ۚ",
              urdu_="یہ ان کاموں کی سزا ہے جو تمہاے ہاتھ آگے بھیجتے رہے ہیں اور اللہ تو بندوں پر مطلق ظلم نہیں کرتا۔",
          ),

      Verse(
              id_=183,
              arabic_="اَلَّذِیۡنَ قَالُوۡۤا اِنَّ اللّٰہَ عَہِدَ اِلَیۡنَاۤ اَلَّا نُؤۡمِنَ لِرَسُوۡلٍ حَتّٰی یَاۡتِیَنَا بِقُرۡبَانٍ تَاۡکُلُہُ النَّارُ ؕ قُلۡ قَدۡ جَآءَکُمۡ رُسُلٌ مِّنۡ قَبۡلِیۡ بِالۡبَیِّنٰتِ وَ بِالَّذِیۡ قُلۡتُمۡ فَلِمَ قَتَلۡتُمُوۡہُمۡ  اِنۡ کُنۡتُمۡ صٰدِقِیۡنَ ﴿۱۸۳﴾ ",
              urdu_="جو لوگ کہتے ہیں کہ اللہ نے ہمیں حکم بھیجا کہ جب تک کوئی پیغمبر ہمارے پاس ایسی نیاز لے کر نہ آئے جس کو آگ آ کر کھا جائے تب تک ہم اس پر ایمان نہ لائیں گے اے پیغمبر ان سے کہدو کہ مجھ سے پہلے کئ پیغمبر تمہارے پاس کھلی ہو ئی نشانیاں لے کر آئے اور وہ معجزہ بھی لائے جو تم کہتے ہو تو اگر سچے ہو تو تم نے ان کو کیوں قتل کیا؟",
          ),

      Verse(
              id_=184,
              arabic_="فَاِنۡ کَذَّبُوۡکَ فَقَدۡ کُذِّبَ رُسُلٌ مِّنۡ قَبۡلِکَ جَآءُوۡ بِالۡبَیِّنٰتِ وَ الزُّبُرِ وَ الۡکِتٰبِ  الۡمُنِیۡرِ ﴿۱۸۴﴾ ",
              urdu_="پھر اگر یہ لوگ تم کو جھٹلا رہے ہیں تو تم سے پہلے بہت سے پیغمبر کھلی ہوئی نشانیاں اور صحیفے اور روشن کتابیں لے کر آ چکے ہیں کہ انکو بھی جھٹلایا گیا تھا۔",
          ),

      Verse(
              id_=185,
              arabic_="کُلُّ نَفۡسٍ ذَآئِقَۃُ الۡمَوۡتِ ؕ وَ اِنَّمَا تُوَفَّوۡنَ اُجُوۡرَکُمۡ یَوۡمَ الۡقِیٰمَۃِ ؕ فَمَنۡ زُحۡزِحَ عَنِ النَّارِ وَ اُدۡخِلَ الۡجَنَّۃَ فَقَدۡ فَازَ ؕ وَ مَا الۡحَیٰوۃُ الدُّنۡیَاۤ اِلَّا مَتَاعُ  الۡغُرُوۡرِ ﴿۱۸۵﴾ ",
              urdu_="ہر متنفس کو موت کا مزا چکھنا ہے۔ اور تم کو قیامت کے دن تمہارے اعمال کا پورا پورا بدلہ دیا جائے گا تو جو شخص دوزخ سے دور رکھا گیا اور بہشت میں داخل کیا گیا وہ مراد کو پہنچ گیا۔ اور دنیا کی زندگی تو دھوکے کا سامان ہے۔",
          ),

      Verse(
              id_=186,
              arabic_="لَتُبۡلَوُنَّ فِیۡۤ اَمۡوَالِکُمۡ وَ اَنۡفُسِکُمۡ ۟ وَ لَتَسۡمَعُنَّ مِنَ الَّذِیۡنَ اُوۡتُوا الۡکِتٰبَ مِنۡ قَبۡلِکُمۡ وَ مِنَ الَّذِیۡنَ اَشۡرَکُوۡۤا اَذًی کَثِیۡرًا ؕ وَ اِنۡ تَصۡبِرُوۡا وَ تَتَّقُوۡا فَاِنَّ ذٰلِکَ مِنۡ عَزۡمِ الۡاُمُوۡرِ ﴿۱۸۶﴾ ",
              urdu_="اے اہل ایمان تمہارے مال و جان میں تمہاری آزمائش ضرور کی جائے گی اور تم اہل کتاب سے اور ان لوگوں سے جو مشرک ہیں بہت سی ایذا کی باتیں سنو گے۔ تو اگر صبر اور پرہیز گاری کرتے رہو گے۔ تو یہ بڑی ہمت کے کام ہیں۔",
          ),

      Verse(
              id_=187,
              arabic_="وَ اِذۡ اَخَذَ اللّٰہُ مِیۡثَاقَ الَّذِیۡنَ اُوۡتُوا الۡکِتٰبَ لَتُبَیِّنُنَّہٗ لِلنَّاسِ وَ لَا تَکۡتُمُوۡنَہٗ ۫ فَنَبَذُوۡہُ  وَرَآءَ ظُہُوۡرِہِمۡ وَ اشۡتَرَوۡا بِہٖ ثَمَنًا قَلِیۡلًا ؕ فَبِئۡسَ مَا یَشۡتَرُوۡنَ ﴿۱۸۷﴾ ",
              urdu_="اور جب اللہ نے ان لوگوں سے جنکو کتاب عنایت کی گئ تھی اقرار لیا کہ اس میں جو کچھ لکھا ہے اسے لوگوں سے صاف صاف بیان کرتے رہنا اور اس کی کسی بات کو نہ چھپانا تو انہوں نے اس کو پس پشت پھینک دیا اور اسکے بدلے تھوڑی سی قیمت حاصل کی۔ یہ جو کچھ حاصل کرتے ہیں برا ہے۔",
          ),

      Verse(
              id_=188,
              arabic_="لَا تَحۡسَبَنَّ الَّذِیۡنَ یَفۡرَحُوۡنَ بِمَاۤ اَتَوۡا وَّ یُحِبُّوۡنَ اَنۡ یُّحۡمَدُوۡا بِمَا لَمۡ یَفۡعَلُوۡا فَلَا تَحۡسَبَنَّہُمۡ بِمَفَازَۃٍ مِّنَ الۡعَذَابِ ۚ وَ لَہُمۡ عَذَابٌ اَلِیۡمٌ ﴿۱۸۸﴾ ",
              urdu_="جو لوگ اپنے کئے ہوئے کاموں سے خوش ہوتے ہیں اور وہ کام جو کرتے نہیں ان کیلئے چاہتے ہیں کہ ان کی تعریف کی جائے ان کی نسبت خیال نہ کرنا کہ وہ عذاب سے چھٹکارا پائیں گے۔ اور انہیں درد دینے والا عذاب ہو گا۔",
          ),

      Verse(
              id_=189,
              arabic_="وَ لِلّٰہِ مُلۡکُ السَّمٰوٰتِ وَ الۡاَرۡضِ ؕ وَ اللّٰہُ عَلٰی کُلِّ شَیۡءٍ  قَدِیۡرٌ ﴿۱۸۹﴾٪ ",
              urdu_="اور آسمانوں اور  زمین کی بادشاہی اللہ ہی کو ہے اور اللہ ہر چیز پر قادر ہے۔",
          ),

      Verse(
              id_=190,
              arabic_="اِنَّ فِیۡ خَلۡقِ السَّمٰوٰتِ وَ الۡاَرۡضِ وَ اخۡتِلَافِ الَّیۡلِ وَ النَّہَارِ لَاٰیٰتٍ  لِّاُولِی الۡاَلۡبَابِ ﴿۱۹۰﴾ۚۙ ",
              urdu_="بیشک آسمانوں اور زمین کی پیدائش اور رات اور دن کے بدل بدل کر آنے جانے میں عقل والوں کے لئے نشانیاں ہیں۔",
          ),

      Verse(
              id_=191,
              arabic_="الَّذِیۡنَ یَذۡکُرُوۡنَ اللّٰہَ  قِیٰمًا وَّ قُعُوۡدًا وَّ عَلٰی جُنُوۡبِہِمۡ وَ یَتَفَکَّرُوۡنَ فِیۡ خَلۡقِ السَّمٰوٰتِ وَ الۡاَرۡضِ ۚ رَبَّنَا مَا خَلَقۡتَ ہٰذَا بَاطِلًا ۚ سُبۡحٰنَکَ فَقِنَا عَذَابَ النَّارِ ﴿۱۹۱﴾ ",
              urdu_="جو کھڑے اور بیٹھے اور لیٹے ہر حال میں اللہ کو یاد کرتے اور آسمان اور زمین کی پیدائش میں غور کرتے اور کہتے ہیں کہ اے پروردگار تو نے اس کائنات کو بے فائدہ نہیں پیدا کیا۔ تو پاک ہے۔ تو قیامت کے دن ہمیں دوزخ کے عذاب سے بچائیو۔",
          ),

      Verse(
              id_=192,
              arabic_="رَبَّنَاۤ اِنَّکَ مَنۡ تُدۡخِلِ النَّارَ فَقَدۡ اَخۡزَیۡتَہٗ ؕ وَ مَا لِلظّٰلِمِیۡنَ مِنۡ اَنۡصَارٍ ﴿۱۹۲﴾ ",
              urdu_="اے پروردگار جس کو تو نے دوزخ میں ڈالا۔ اسے رسوا کیا اور ظالموں کا کوئی مددگار نہیں ہو گا۔",
          ),

      Verse(
              id_=193,
              arabic_="رَبَّنَاۤ اِنَّنَا سَمِعۡنَا مُنَادِیًا یُّنَادِیۡ لِلۡاِیۡمَانِ اَنۡ اٰمِنُوۡا بِرَبِّکُمۡ  فَاٰمَنَّا ٭ۖ رَبَّنَا فَاغۡفِرۡ لَنَا ذُنُوۡبَنَا وَ کَفِّرۡ عَنَّا سَیِّاٰتِنَا وَ تَوَفَّنَا مَعَ  الۡاَبۡرَارِ ﴿۱۹۳﴾ۚ ",
              urdu_="اے پروردگار ہم نے ایک پکار کرنے والے کو سنا کہ ایمان کے لئے پکار رہا تھا کہ اپنے پروردگار پر ایمان لاؤ تو ہم ایمان لے آئے سو اے پروردگار ہمارے گناہ معاف فرما۔ اور ہماری برائیوں کو ہم سے دور کر دے اور ہم کو دنیا سے نیک بندوں کے ساتھ اٹھائیو۔",
          ),

      Verse(
              id_=194,
              arabic_="رَبَّنَا وَ اٰتِنَا مَا وَعَدۡتَّنَا عَلٰی رُسُلِکَ وَ لَا تُخۡزِنَا یَوۡمَ الۡقِیٰمَۃِ ؕ اِنَّکَ لَا تُخۡلِفُ الۡمِیۡعَادَ ﴿۱۹۴﴾ ",
              urdu_="اے پرودگار تو نے جن جن چیزوں کے ہم سے اپنے پیغمبروں کے واسطے سے وعدے کئے ہیں وہ ہمیں عطا فرما اور قیامت کے دن ہمیں رسوا نہ کیجیو کچھ شک نہیں کہ تو خلاف وعدہ نہیں کرے گا۔",
          ),

      Verse(
              id_=195,
              arabic_="فَاسۡتَجَابَ لَہُمۡ رَبُّہُمۡ اَنِّیۡ لَاۤ اُضِیۡعُ عَمَلَ عَامِلٍ مِّنۡکُمۡ مِّنۡ ذَکَرٍ اَوۡ اُنۡثٰی ۚ بَعۡضُکُمۡ مِّنۡۢ  بَعۡضٍ ۚ فَالَّذِیۡنَ ہَاجَرُوۡا وَ اُخۡرِجُوۡا مِنۡ دِیَارِہِمۡ وَ اُوۡذُوۡا فِیۡ سَبِیۡلِیۡ وَ قٰتَلُوۡا وَ قُتِلُوۡا لَاُکَفِّرَنَّ عَنۡہُمۡ سَیِّاٰتِہِمۡ وَ لَاُدۡخِلَنَّہُمۡ جَنّٰتٍ تَجۡرِیۡ مِنۡ تَحۡتِہَا الۡاَنۡہٰرُ ۚ ثَوَابًا مِّنۡ عِنۡدِ اللّٰہِ ؕ وَ اللّٰہُ عِنۡدَہٗ حُسۡنُ الثَّوَابِ ﴿۱۹۵﴾ ",
              urdu_="تو ان کے پروردگار نے ان کی دعا قبول کر لی اور فرمایا کہ میں کسی عمل کرنے والے کے عمل کو مرد ہو یا عورت ضائع نہیں کرتا۔ تم ایک دوسرے کے ہم جنس ہو۔ تو جو لوگ میرے لئے وطن چھوڑ گئے اور اپنے گھروں سے نکالے گئے اور ستائے گئے اور لڑے اور قتل کئے گئے میں ان کے گناہ دور کر دوں گا اور ان کو بہشتوں میں داخل کروں گا جن کے نیچے نہریں بہ رہی ہیں۔ یہ اللہ کے ہاں سے بدلہ ہے اور اللہ کے ہاں اچھا بدلہ ہے۔",
          ),

      Verse(
              id_=196,
              arabic_="لَا یَغُرَّنَّکَ تَقَلُّبُ الَّذِیۡنَ کَفَرُوۡا فِی الۡبِلَادِ  ﴿۱۹۶﴾ؕ",
              urdu_="اے پیغمبر کافروں کا شہروں میں ٹھاٹ سے گھومنا تمہیں دھوکا نہ دے۔",
          ),

      Verse(
              id_=197,
              arabic_="مَتَاعٌ قَلِیۡلٌ ۟ ثُمَّ مَاۡوٰٮہُمۡ جَہَنَّمُ ؕ وَ بِئۡسَ الۡمِہَادُ ﴿۱۹۷﴾ ",
              urdu_="یہ دنیا کا تھوڑا سا فائدہ ہے۔ پھر آخرت میں تو ان کا ٹھکانہ دوزخ ہے۔ اور وہ بری جگہ ہے۔",
          ),

      Verse(
              id_=198,
              arabic_="لٰکِنِ الَّذِیۡنَ اتَّقَوۡا رَبَّہُمۡ لَہُمۡ جَنّٰتٌ تَجۡرِیۡ مِنۡ تَحۡتِہَا الۡاَنۡہٰرُ خٰلِدِیۡنَ فِیۡہَا نُزُلًا مِّنۡ عِنۡدِ اللّٰہِ ؕ وَ مَا عِنۡدَ اللّٰہِ خَیۡرٌ  لِّلۡاَبۡرَارِ ﴿۱۹۸﴾ ",
              urdu_="لیکن جو لوگ اپنے پروردگار سے ڈرتے رہے ان کے لئے باغ ہیں جن کے نیچے نہریں بہ رہی ہیں اور وہ ان میں ہمیشہ رہیں گے یہ اللہ کے ہاں سے ان کی مہمانی ہے اور جو کچھ اللہ کے ہاں ہے وہ نیکوکاروں کے لئے بہت اچھا ہے۔",
          ),

      Verse(
              id_=199,
              arabic_="وَ اِنَّ مِنۡ اَہۡلِ  الۡکِتٰبِ لَمَنۡ یُّؤۡمِنُ بِاللّٰہِ وَ مَاۤ اُنۡزِلَ اِلَیۡکُمۡ وَ مَاۤ  اُنۡزِلَ اِلَیۡہِمۡ خٰشِعِیۡنَ  لِلّٰہِ ۙ لَا  یَشۡتَرُوۡنَ بِاٰیٰتِ اللّٰہِ ثَمَنًا قَلِیۡلًا ؕ اُولٰٓئِکَ لَہُمۡ اَجۡرُہُمۡ عِنۡدَ رَبِّہِمۡ ؕ اِنَّ اللّٰہَ سَرِیۡعُ الۡحِسَابِ ﴿۱۹۹﴾ ",
              urdu_="اور بعض اہل کتاب ایسے بھی ہیں جو اللہ پر اور اس کتاب پر جو تم پر نازل ہوئی اور اس پر جو ان پر نازل ہوئی ایمان رکھتے ہیں اور اللہ کے آگے عاجزی کرتے ہیں اور اللہ کی آیتوں کے بدلے تھوڑی سی قیمت نہیں لیتے۔ یہی لوگ ہیں جنکا صلہ ان کے پروردگار کے ہاں تیار ہے اور اللہ جلد حساب لینے والا ہے۔",
          ),

      Verse(
              id_=200,
              arabic_="یٰۤاَیُّہَا الَّذِیۡنَ اٰمَنُوا اصۡبِرُوۡا وَ صَابِرُوۡا وَ رَابِطُوۡا ۟ وَ اتَّقُوا اللّٰہَ لَعَلَّکُمۡ  تُفۡلِحُوۡنَ ﴿۲۰۰﴾٪",
              urdu_="اے اہل ایمان کفار کے مقابلے میں ثابت قدم رہو اور استقامت رکھو اور مورچوں پر جمے رہو۔ اور اللہ سے ڈرو۔ تاکہ مراد حاصل کرو۔",
          ),
    ),
