      info_=""
    ),
  Surah(
      id_=7,
      titleEnglish_="Al-Araf ",
      titleArabic_="الاعراف",
      verses_=listOf(
      Verse(
              id_=1,
              arabic_="الٓـمّٓصٓ ۚ﴿۱﴾ ",
              urdu_="المص۔",
          ),

      Verse(
              id_=2,
              arabic_="کِتٰبٌ اُنۡزِلَ  اِلَیۡکَ  فَلَا  یَکُنۡ فِیۡ صَدۡرِکَ حَرَجٌ مِّنۡہُ لِتُنۡذِرَ بِہٖ وَ ذِکۡرٰی لِلۡمُؤۡمِنِیۡنَ ﴿۲﴾ ",
              urdu_="اے نبی یہ کتاب جو تم پر نازل ہوئی ہے۔ اس سے تمہیں تنگدل نہیں ہونا چاہیے یہ نازل اس لئے ہوئی ہے کہ تم اس کے ذریعے سے لوگوں کو ڈر سناؤ اور یہ ایمان والوں کے لئے نصیحت ہے۔",
          ),

      Verse(
              id_=3,
              arabic_="اِتَّبِعُوۡا مَاۤ  اُنۡزِلَ  اِلَیۡکُمۡ  مِّنۡ  رَّبِّکُمۡ  وَ لَا تَتَّبِعُوۡا مِنۡ دُوۡنِہٖۤ  اَوۡلِیَآءَ ؕ قَلِیۡلًا مَّا  تَذَکَّرُوۡنَ ﴿۳﴾ ",
              urdu_="لوگو جو کتاب تم پر تمہارے پروردگار کے ہاں سے نازل ہوئی ہے اسکی پیروی کرو۔ اور اسکے سوا اور رفیقوں کی پیروی نہ کرو اور تم کم ہی نصیحت قبول کرتے ہو۔",
          ),

      Verse(
              id_=4,
              arabic_="وَ کَمۡ مِّنۡ قَرۡیَۃٍ اَہۡلَکۡنٰہَا فَجَآءَہَا بَاۡسُنَا  بَیَاتًا  اَوۡ  ہُمۡ  قَآئِلُوۡنَ ﴿۴﴾ ",
              urdu_="اور کتنی ہی بستیاں ہیں کہ ہم نے تباہ کر ڈالیں جن پر ہمارا عذاب یا تو رات میں آتا تھا جبکہ وہ سوتے تھے یا دن کو جب وہ قیلولہ یعنی دوپہر کو آرام کرتے تھے۔",
          ),

      Verse(
              id_=5,
              arabic_="فَمَا کَانَ دَعۡوٰٮہُمۡ اِذۡ  جَآءَہُمۡ  بَاۡسُنَاۤ  اِلَّاۤ  اَنۡ  قَالُوۡۤا  اِنَّا کُنَّا ظٰلِمِیۡنَ ﴿۵﴾ ",
              urdu_="تو جس وقت ان پر عذاب آتا تھا انکے منہ سے یہی نکلتا تھا کہ ہائے ہم اپنے اوپر ظلم کرتے رہے۔",
          ),

      Verse(
              id_=6,
              arabic_="فَلَنَسۡـَٔلَنَّ الَّذِیۡنَ اُرۡسِلَ اِلَیۡہِمۡ وَ لَنَسۡـَٔلَنَّ  الۡمُرۡسَلِیۡنَ ۙ﴿۶﴾ ",
              urdu_="تو جن لوگوں کی طرف پیغمبر بھیجے گئے ہم ان سے بھی پرسش کریں گے اور پیغمبروں سے بھی پوچھیں گے۔",
          ),

      Verse(
              id_=7,
              arabic_="فَلَنَقُصَّنَّ عَلَیۡہِمۡ بِعِلۡمٍ  وَّ مَا کُنَّا غَآئِبِیۡنَ ﴿۷﴾ ",
              urdu_="پھر اپنے علم سے ان کے حالات بیان کریں گے اور ہم کہیں غائب تو نہیں تھے۔",
          ),

      Verse(
              id_=8,
              arabic_="وَ الۡوَزۡنُ یَوۡمَئِذِ ۣالۡحَقُّ ۚ فَمَنۡ ثَقُلَتۡ مَوَازِیۡنُہٗ  فَاُولٰٓئِکَ  ہُمُ  الۡمُفۡلِحُوۡنَ ﴿۸﴾",
              urdu_="اور اس روز اعمال کا تلنا برحق ہے۔ تو جن لوگوں کی تولیں بھاری ہوں گی وہ تو کامیاب ہیں۔",
          ),

      Verse(
              id_=9,
              arabic_="وَ مَنۡ خَفَّتۡ مَوَازِیۡنُہٗ فَاُولٰٓئِکَ الَّذِیۡنَ خَسِرُوۡۤا اَنۡفُسَہُمۡ بِمَا کَانُوۡا بِاٰیٰتِنَا یَظۡلِمُوۡنَ ﴿۹﴾ ",
              urdu_="اور جنکی تولیں ہلکی ہوں گی تو یہی لوگ ہیں جنہوں نے اپنے آپکو خسارے میں ڈالا اس لئے کہ ہماری آیتوں کے بارے میں بے انصافی کرتے تھے۔",
          ),

      Verse(
              id_=10,
              arabic_="وَ لَقَدۡ مَکَّنّٰکُمۡ فِی الۡاَرۡضِ وَ جَعَلۡنَا لَکُمۡ فِیۡہَا مَعَایِشَ ؕ قَلِیۡلًا  مَّا تَشۡکُرُوۡنَ ﴿٪۱۰﴾ ",
              urdu_="اور ہم ہی نے زمین میں تمہارا ٹھکانہ بنایا اور اس میں تمہارے لئے اسباب معیشت پیدا کئے مگر تم کم ہی شکر کرتے ہو۔",
          ),

      Verse(
              id_=11,
              arabic_="وَ لَقَدۡ خَلَقۡنٰکُمۡ ثُمَّ صَوَّرۡنٰکُمۡ ثُمَّ قُلۡنَا لِلۡمَلٰٓئِکَۃِ اسۡجُدُوۡا  لِاٰدَمَ ٭ۖ فَسَجَدُوۡۤا  اِلَّاۤ  اِبۡلِیۡسَ ؕ لَمۡ  یَکُنۡ مِّنَ السّٰجِدِیۡنَ ﴿۱۱﴾ ",
              urdu_="اور ہم ہی نے تم کو ابتدا میں مٹی سے پیدا کیا پھر تمہاری شکل و صورت بنائی پھر فرشتوں کو حکم دیا کہ آدم کے آگے سجدہ کرو تو سب نے سجدہ کیا۔ لیکن ابلیس۔ کہ وہ سجدہ کرنے والوں میں شامل نہ ہوا۔",
          ),

      Verse(
              id_=12,
              arabic_="قَالَ مَا مَنَعَکَ  اَلَّا  تَسۡجُدَ   اِذۡ   اَمَرۡتُکَ ؕ قَالَ  اَنَا خَیۡرٌ  مِّنۡہُ ۚ خَلَقۡتَنِیۡ مِنۡ نَّارٍ  وَّ  خَلَقۡتَہٗ  مِنۡ  طِیۡنٍ ﴿۱۲﴾ ",
              urdu_="اللہ نے فرمایا جب میں نے تجھ کو بھی حکم دیا تھا تو کس چیز نے تجھے سجدہ کرنے سے باز رکھا اس نے کہا کہ میں اس سے افضل ہوں۔ مجھے تو نے آگ سے پیدا کیا ہے اور اسے مٹی سے بنایا ہے۔",
          ),

      Verse(
              id_=13,
              arabic_="قَالَ فَاہۡبِطۡ مِنۡہَا فَمَا یَکُوۡنُ لَکَ اَنۡ تَتَکَبَّرَ فِیۡہَا فَاخۡرُجۡ  اِنَّکَ مِنَ الصّٰغِرِیۡنَ ﴿۱۳﴾ ",
              urdu_="فرمایا تو بہشت سے اتر جا تجھے شایاں نہیں کہ یہاں تکبر کرے پس نکل جا۔ کہ تو ذلیل لوگوں میں سے ہے۔",
          ),

      Verse(
              id_=14,
              arabic_="قَالَ  اَنۡظِرۡنِیۡۤ   اِلٰی  یَوۡمِ  یُبۡعَثُوۡنَ ﴿۱۴﴾ ",
              urdu_="اس نے کہا کہ مجھے اس دن تک مہلت عطا فرما جس دن لوگ قبروں سے اٹھائے جائیں گے۔",
          ),

      Verse(
              id_=15,
              arabic_="قَالَ   اِنَّکَ  مِنَ  الۡمُنۡظَرِیۡنَ ﴿۱۵﴾ ",
              urdu_="فرمایا اچھا تجھ کو مہلت دی جاتی ہے۔",
          ),

      Verse(
              id_=16,
              arabic_="قَالَ فَبِمَاۤ  اَغۡوَیۡتَنِیۡ لَاَقۡعُدَنَّ  لَہُمۡ صِرَاطَکَ  الۡمُسۡتَقِیۡمَ ﴿ۙ۱۶﴾ ",
              urdu_="بولا کہ مجھے تو تو نے گمراہ کیا ہی ہے میں بھی تیرے سیدھے رستے پر ان کو گمراہ کرنے کے لئے بیٹھوں گا۔",
          ),

      Verse(
              id_=17,
              arabic_="ثُمَّ لَاٰتِیَنَّہُمۡ مِّنۡۢ بَیۡنِ اَیۡدِیۡہِمۡ وَ مِنۡ خَلۡفِہِمۡ  وَ عَنۡ اَیۡمَانِہِمۡ وَ عَنۡ شَمَآئِلِہِمۡ ؕ وَ لَا  تَجِدُ اَکۡثَرَہُمۡ شٰکِرِیۡنَ ﴿۱۷﴾ ",
              urdu_="پھر ان کے آگے سے اور پیچھے سے دائیں سے اور بائیں سے غرض ہر طرف سے آؤں گا اور تو ان میں سے اکثر کو شکرگذار نہیں پائے گا۔",
          ),

      Verse(
              id_=18,
              arabic_="قَالَ اخۡرُجۡ مِنۡہَا مَذۡءُوۡمًا مَّدۡحُوۡرًا ؕ لَمَنۡ تَبِعَکَ مِنۡہُمۡ لَاَمۡلَـَٔنَّ جَہَنَّمَ  مِنۡکُمۡ  اَجۡمَعِیۡنَ ﴿۱۸﴾ ",
              urdu_="اللہ نے فرمایا نکل جا یہاں سے پاجی۔ مردود جو لوگ ان میں سے تیری پیروی کریں گے میں ان کو اور تجھ کو جہنم میں ڈال کر تم سب سے جہنم کو بھر دوں گا۔",
          ),

      Verse(
              id_=19,
              arabic_="وَ یٰۤاٰدَمُ  اسۡکُنۡ اَنۡتَ وَ زَوۡجُکَ الۡجَنَّۃَ فَکُلَا مِنۡ حَیۡثُ شِئۡتُمَا وَ لَا تَقۡرَبَا ہٰذِہِ الشَّجَرَۃَ  فَتَکُوۡنَا مِنَ  الظّٰلِمِیۡنَ ﴿۱۹﴾ ",
              urdu_="اور اے آدم  تم اور تمہاری بیوی بہشت میں رہو سہو اور جہاں سے چاہو اور جو چاہو نوش جان کرو مگر اس درخت کے پاس نہ جانا۔ ورنہ گناہگار ہو جاؤ گے۔",
          ),

      Verse(
              id_=20,
              arabic_="فَوَسۡوَسَ لَہُمَا الشَّیۡطٰنُ لِیُبۡدِیَ لَہُمَا مَا وٗرِیَ عَنۡہُمَا مِنۡ سَوۡاٰتِہِمَا وَ قَالَ مَا نَہٰکُمَا رَبُّکُمَا عَنۡ ہٰذِہِ الشَّجَرَۃِ  اِلَّاۤ اَنۡ  تَکُوۡنَا مَلَکَیۡنِ اَوۡ تَکُوۡنَا مِنَ  الۡخٰلِدِیۡنَ ﴿۲۰﴾",
              urdu_="تو شیطان دونوں کو بہکانے لگا تاکہ انکی شرمگاہیں جو ان سے پوشیدہ تھیں ان پر کھول دے اور کہنے لگا کہ تم کو تمہارے پروردگار نے اس درخت سے صرف اس لئے منع کیا ہے کہ تم فرشتے نہ بن جاؤ یا ہمیشہ جیتے نہ رہو۔",
          ),

      Verse(
              id_=21,
              arabic_="وَ قَاسَمَہُمَاۤ  اِنِّیۡ لَکُمَا لَمِنَ النّٰصِحِیۡنَ ﴿ۙ۲۱﴾ ",
              urdu_="اور ان سے قسم کھا کر کہا کہ میں تو تمہارا خیرخواہ ہوں۔",
          ),

      Verse(
              id_=22,
              arabic_="فَدَلّٰٮہُمَا بِغُرُوۡرٍ ۚ فَلَمَّا ذَاقَا الشَّجَرَۃَ بَدَتۡ لَہُمَا سَوۡاٰتُہُمَا وَ طَفِقَا یَخۡصِفٰنِ عَلَیۡہِمَا مِنۡ وَّرَقِ الۡجَنَّۃِ ؕ وَ نَادٰٮہُمَا رَبُّہُمَاۤ  اَلَمۡ اَنۡہَکُمَا عَنۡ تِلۡکُمَا الشَّجَرَۃِ  وَ اَقُلۡ لَّکُمَاۤ  اِنَّ الشَّیۡطٰنَ لَکُمَا عَدُوٌّ مُّبِیۡنٌ ﴿۲۲﴾ ",
              urdu_="غرض مردود نے دھوکا دے کر انکو معصیت کی طرف کھینچ ہی لیا سو جب انہوں نے اس درخت کے پھل کو کھالیا تو انکی شرمگاہیں کھل گئیں اور وہ بہشت کے درختوں کے پتے توڑ توڑ کر اپنے اوپر چپکانے اور ستر چھپانے لگے اور انکے پروردگار نے انکو پکارا کہ کیا میں نے تم کو اس درخت کے پاس جانے سے منع نہیں کیا تھا اور جتا نہیں دیا تھا کہ شیطان تمہارا کھلم کھلا دشمن ہے۔",
          ),

      Verse(
              id_=23,
              arabic_="قَالَا رَبَّنَا ظَلَمۡنَاۤ  اَنۡفُسَنَا ٜ وَ  اِنۡ  لَّمۡ تَغۡفِرۡ لَنَا وَ تَرۡحَمۡنَا لَنَکُوۡنَنَّ مِنَ الۡخٰسِرِیۡنَ ﴿۲۳﴾ ",
              urdu_="دونوں عرض کرنے لگے کہ پروردگار ہم نے اپنی جانوں پر ظلم کیا اور اگر تو ہمیں نہیں بخشے گا اور ہم پر رحم نہیں کرے گا تو ہم تباہ ہو جائیں گے۔",
          ),

      Verse(
              id_=24,
              arabic_="قَالَ اہۡبِطُوۡا بَعۡضُکُمۡ  لِبَعۡضٍ عَدُوٌّ ۚ وَ لَکُمۡ فِی الۡاَرۡضِ مُسۡتَقَرٌّ  وَّ مَتَاعٌ اِلٰی  حِیۡنٍ ﴿۲۴﴾ ",
              urdu_="فرمایا تم سب بہشت سے اتر جاؤ اب سے تم ایک دوسرے کے دشمن ہو اور تمہارے لئے ایک وقت خاص تک زمین میں ٹھکانہ اور زندگی کا سامان کر دیا گیا ہے۔",
          ),

      Verse(
              id_=25,
              arabic_="قَالَ فِیۡہَا تَحۡیَوۡنَ وَ فِیۡہَا تَمُوۡتُوۡنَ وَ مِنۡہَا  تُخۡرَجُوۡنَ ﴿٪۲۵﴾ ",
              urdu_="فرمایا کہ اسی میں تمہارا جینا ہوگا اور اسی میں مرنا اور اسی میں سے قیامت کو زندہ کر کے نکالے جاؤ گے۔",
          ),

      Verse(
              id_=26,
              arabic_="یٰبَنِیۡۤ  اٰدَمَ  قَدۡ  اَنۡزَلۡنَا عَلَیۡکُمۡ  لِبَاسًا یُّوَارِیۡ سَوۡاٰتِکُمۡ وَ رِیۡشًا ؕ وَ لِبَاسُ التَّقۡوٰی ۙ ذٰلِکَ خَیۡرٌ ؕ ذٰلِکَ مِنۡ اٰیٰتِ اللّٰہِ  لَعَلَّہُمۡ  یَذَّکَّرُوۡنَ ﴿۲۶﴾ ",
              urdu_="اے بنی آدم ہم نے تم پر پوشاک اتاری کہ تمہاری شرمگاہوں کو چھپائے اور تمہارے بدن کو زینت دے اور جو پرہیزگاری کا لباس ہے وہ سب سے اچھا ہے یہ اللہ کی نشانیاں ہیں تاکہ لوگ نصیحت حاصل کریں۔",
          ),

      Verse(
              id_=27,
              arabic_="یٰبَنِیۡۤ  اٰدَمَ  لَا یَفۡتِنَنَّکُمُ الشَّیۡطٰنُ کَمَاۤ اَخۡرَجَ  اَبَوَیۡکُمۡ  مِّنَ الۡجَنَّۃِ یَنۡزِعُ عَنۡہُمَا لِبَاسَہُمَا لِیُرِیَہُمَا سَوۡاٰتِہِمَا ؕ اِنَّہٗ یَرٰٮکُمۡ ہُوَ وَ قَبِیۡلُہٗ مِنۡ حَیۡثُ لَا تَرَوۡنَہُمۡ ؕ اِنَّا جَعَلۡنَا الشَّیٰطِیۡنَ اَوۡلِیَآءَ  لِلَّذِیۡنَ لَا یُؤۡمِنُوۡنَ ﴿۲۷﴾ ",
              urdu_="اے بنی آدم دیکھنا کہیں شیطان تمہیں بہکا نہ دے جس طرح تمہارے ماں باپ کو بہکا کر بہشت سے نکلوا دیا اور ان سے ان کے کپڑے اتروا دیئے تاکہ ان کے ستر انکو کھول کر دکھا دے۔ وہ اور اسکے بھائی بند تم کو ایسی جگہ سے دیکھتے رہتے ہیں جہاں سے تم انکو نہیں دیکھ سکتے ہم نے شیطانوں کو انہی لوگوں کا رفیق بنایا ہے جو ایمان نہیں رکھتے۔",
          ),

      Verse(
              id_=28,
              arabic_="وَ اِذَا فَعَلُوۡا فَاحِشَۃً  قَالُوۡا وَجَدۡنَا عَلَیۡہَاۤ  اٰبَآءَنَا وَ اللّٰہُ اَمَرَنَا بِہَا ؕ قُلۡ اِنَّ اللّٰہَ  لَا  یَاۡمُرُ  بِالۡفَحۡشَآءِ ؕ اَتَقُوۡلُوۡنَ عَلَی اللّٰہِ  مَا  لَا  تَعۡلَمُوۡنَ ﴿۲۸﴾ ",
              urdu_="اور یہ لوگ جب کوئی بے حیائی کا کام کرتے ہیں تو کہتے ہیں کہ ہم نے اپنے بزرگوں کو اسی طرح کرتے دیکھا ہے اور اللہ نے بھی ہم کو یہی حکم دیا ہے کہدو کہ اللہ بے حیائی کے کام کرنے کا تو حکم نہیں دیتا کیا تم اللہ کی نسبت ایسی بات کہتے ہو جس کا تمہیں علم نہیں۔",
          ),

      Verse(
              id_=29,
              arabic_="قُلۡ اَمَرَ رَبِّیۡ  بِالۡقِسۡطِ ۟ وَ اَقِیۡمُوۡا وُجُوۡہَکُمۡ عِنۡدَ کُلِّ مَسۡجِدٍ  وَّ ادۡعُوۡہُ مُخۡلِصِیۡنَ لَہُ  الدِّیۡنَ ۬ؕ کَمَا بَدَاَکُمۡ تَعُوۡدُوۡنَ ﴿ؕ۲۹﴾ ",
              urdu_="کہدو کہ میرے پروردگار نے تو انصاف کرنے کا حکم دیا ہے اور یہ کہ ہر نماز کے وقت سیدھا قبلے کی طرف رخ کیا کرو۔ اور صرف اسی کی عبادت کرو اور اسی کو پکارو۔ اس نے جس طرح تم کو ابتدا میں پیدا کیا تھا اسی طرح تم پھر پیدا ہو گے۔",
          ),

      Verse(
              id_=30,
              arabic_="فَرِیۡقًا ہَدٰی وَ فَرِیۡقًا حَقَّ عَلَیۡہِمُ الضَّلٰلَۃُ ؕ اِنَّہُمُ اتَّخَذُوا الشَّیٰطِیۡنَ اَوۡلِیَآءَ مِنۡ دُوۡنِ اللّٰہِ وَ یَحۡسَبُوۡنَ  اَنَّہُمۡ مُّہۡتَدُوۡنَ ﴿۳۰﴾ ",
              urdu_="ایک فریق کو تو اس نے ہدایت دی اور ایک فریق پر گمراہی ثابت ہو چکی۔ ان لوگوں نے اللہ کو چھوڑ کر شیطانوں کو رفیق بنا لیا اور سمجھتے یہ ہیں کہ ہدایت یاب ہیں۔",
          ),

      Verse(
              id_=31,
              arabic_="یٰبَنِیۡۤ  اٰدَمَ خُذُوۡا زِیۡنَتَکُمۡ عِنۡدَ کُلِّ مَسۡجِدٍ  وَّ کُلُوۡا وَ اشۡرَبُوۡا وَ لَا  تُسۡرِفُوۡا ۚ اِنَّہٗ لَا  یُحِبُّ الۡمُسۡرِفِیۡنَ ﴿٪۳۱﴾ ",
              urdu_="اے بنی آدم! ہر نماز کے وقت اپنے آپکو مزین کیا کرو اور کھاؤ اور پیئو اور بے جا نہ اڑاؤ کہ اللہ بے جا اڑانے والوں کو دوست نہیں رکھتا۔",
          ),

      Verse(
              id_=32,
              arabic_="قُلۡ مَنۡ حَرَّمَ زِیۡنَۃَ اللّٰہِ الَّتِیۡۤ  اَخۡرَجَ لِعِبَادِہٖ  وَ الطَّیِّبٰتِ مِنَ الرِّزۡقِ ؕ قُلۡ ہِیَ لِلَّذِیۡنَ اٰمَنُوۡا فِی الۡحَیٰوۃِ الدُّنۡیَا خَالِصَۃً  یَّوۡمَ الۡقِیٰمَۃِ ؕ  کَذٰلِکَ  نُفَصِّلُ الۡاٰیٰتِ  لِقَوۡمٍ  یَّعۡلَمُوۡنَ ﴿۳۲﴾ ",
              urdu_="پوچھو تو کہ جو زینت و آرائش اور کھانے پینے کی پاکیزہ چیزیں اللہ نے اپنے بندوں کے لئے پیدا کی ہیں ان کو حرام کس نے کیا ہے؟ کہدو کہ یہ چیزیں دنیا کی زندگی میں بھی ایمان والوں کے لئے ہیں اور قیامت کے دن صرف انہی کا حصہ ہوں گی۔ اسی طرح اللہ اپنی آیتیں سمجھنے والوں کے لئے کھول کھول کر بیان فرماتا ہے۔",
          ),

      Verse(
              id_=33,
              arabic_="قُلۡ  اِنَّمَا حَرَّمَ رَبِّیَ الۡفَوَاحِشَ مَا ظَہَرَ  مِنۡہَا وَ  مَا بَطَنَ وَ الۡاِثۡمَ وَ الۡبَغۡیَ بِغَیۡرِ الۡحَقِّ وَ اَنۡ تُشۡرِکُوۡا بِاللّٰہِ مَا لَمۡ یُنَزِّلۡ بِہٖ سُلۡطٰنًا وَّ اَنۡ تَقُوۡلُوۡا عَلَی  اللّٰہِ  مَا لَا  تَعۡلَمُوۡنَ ﴿۳۳﴾ ",
              urdu_="کہدو کہ میرے پروردگار نے تو بے حیائی کی باتوں کو ظاہر ہوں یا پوشیدہ اور گناہ کو اور ناحق زیادتی کرنے کو حرام کیا ہے۔ اور اسکو بھی کہ تم کسی کو اللہ کا شریک بناؤ جسکی اس نے کوئی سند نازل نہیں کی اور اسکو بھی کہ اللہ کے بارے میں ایسی باتیں کہو جنکا تمہیں کچھ علم نہیں۔",
          ),

      Verse(
              id_=34,
              arabic_="وَ لِکُلِّ اُمَّۃٍ  اَجَلٌ ۚ فَاِذَا  جَآءَ  اَجَلُہُمۡ  لَا یَسۡتَاۡخِرُوۡنَ سَاعَۃً  وَّ لَا یَسۡتَقۡدِمُوۡنَ ﴿۳۴﴾ ",
              urdu_="اور ہر ایک امت کے لئے موت کا ایک وقت مقرر ہے۔ جب وہ آ جاتا ہے تو نہ تو ایک گھڑی دیر کر سکتے ہیں اور نہ جلدی ہی کر سکتے ہیں۔",
          ),

      Verse(
              id_=35,
              arabic_="یٰبَنِیۡۤ  اٰدَمَ  اِمَّا یَاۡتِیَنَّکُمۡ رُسُلٌ مِّنۡکُمۡ یَقُصُّوۡنَ عَلَیۡکُمۡ اٰیٰتِیۡ ۙ فَمَنِ اتَّقٰی وَ اَصۡلَحَ فَلَا خَوۡفٌ عَلَیۡہِمۡ وَ لَا ہُمۡ یَحۡزَنُوۡنَ ﴿۳۵﴾ ",
              urdu_="اے بنی آدم ہم تم کو یہ نصیحت ہمیشہ کرتے رہے ہیں کہ جب ہمارے پیغمبر تمہارے پاس آیا کریں اور ہماری آیتیں تم کو سنایا کریں تو ان پر ایمان لایا کرو کہ اب جو شخص ان پر ایمان لا کر اللہ سے ڈرتا رہے گا اور اپنی حالت درست رکھے گا تو ایسے لوگوں کو نہ کچھ خوف ہو گا اور نہ وہ غمناک ہوں گے۔",
          ),

      Verse(
              id_=36,
              arabic_="وَ الَّذِیۡنَ کَذَّبُوۡا بِاٰیٰتِنَا وَ اسۡتَکۡبَرُوۡا عَنۡہَاۤ  اُولٰٓئِکَ اَصۡحٰبُ النَّارِ ۚ ہُمۡ فِیۡہَا خٰلِدُوۡنَ ﴿۳۶﴾   ",
              urdu_="اور جنہوں نے ہماری آیتوں کو جھٹلایا اور ان سے سرتابی کی وہی دوزخی ہیں کہ ہمیشہ اس میں جلتے رہیں گے۔",
          ),

      Verse(
              id_=37,
              arabic_="فَمَنۡ اَظۡلَمُ مِمَّنِ افۡتَرٰی عَلَی اللّٰہِ  کَذِبًا اَوۡ کَذَّبَ بِاٰیٰتِہٖ ؕ اُولٰٓئِکَ یَنَالُہُمۡ نَصِیۡبُہُمۡ مِّنَ الۡکِتٰبِ ؕ حَتّٰۤی  اِذَا جَآءَتۡہُمۡ  رُسُلُنَا یَتَوَفَّوۡنَہُمۡ ۙ قَالُوۡۤا اَیۡنَ مَا  کُنۡتُمۡ تَدۡعُوۡنَ مِنۡ دُوۡنِ اللّٰہِ ؕ قَالُوۡا ضَلُّوۡا عَنَّا وَ شَہِدُوۡا عَلٰۤی اَنۡفُسِہِمۡ  اَنَّہُمۡ  کَانُوۡا کٰفِرِیۡنَ ﴿۳۷﴾ ",
              urdu_="پس اس سے زیادہ ظالم کون جو اللہ پر جھوٹ باندھے یا اسکی آیتوں کو جھٹلائے۔ انکو ان کے نصیب کا لکھا ملتا ہی رہے گا۔ یہاں تک کہ جب انکے پاس ہمارے بھیجے ہوئے فرشتے جان نکالنے آئیں گے۔ تو کہیں گے کہ جنکو تم اللہ کے سوا پکارا کرتے تھے۔ وہ اب کہاں ہیں؟ وہ کہیں گے کہ وہ ہم سے گم ہو گئے اور اقرار کریں گے کہ بیشک وہ کافر تھے۔",
          ),

      Verse(
              id_=38,
              arabic_="قَالَ ادۡخُلُوۡا فِیۡۤ  اُمَمٍ قَدۡ خَلَتۡ مِنۡ قَبۡلِکُمۡ مِّنَ الۡجِنِّ وَ الۡاِنۡسِ فِی النَّارِ ؕ کُلَّمَا دَخَلَتۡ اُمَّۃٌ  لَّعَنَتۡ اُخۡتَہَا ؕ حَتّٰۤی اِذَا ادَّارَکُوۡا فِیۡہَا جَمِیۡعًا ۙ قَالَتۡ اُخۡرٰٮہُمۡ  لِاُوۡلٰٮہُمۡ رَبَّنَا ہٰۤؤُلَآءِ اَضَلُّوۡنَا فَاٰتِہِمۡ عَذَابًا ضِعۡفًا مِّنَ النَّارِ ۬ؕ قَالَ لِکُلٍّ ضِعۡفٌ وَّ لٰکِنۡ  لَّا  تَعۡلَمُوۡنَ ﴿۳۸﴾ ",
              urdu_="تو اللہ فرمائے گا کہ جنوں اور انسانوں کی جو جماعتیں تم سے پہلے ہو گذری ہیں ان ہی کے ساتھ تم بھی داخل جہنم ہو جاؤ۔ جب ایک جماعت وہاں جا داخل ہو گی تو اپنے جیسی دوسری جماعت پر لعنت کریگی۔ یہاں تک کہ جب سب اس میں داخل ہو جائیں گے تو پچھلی جماعت پہلی کی نسبت کہے گی کہ اے پروردگار ان ہی لوگوں نے ہم کو گمراہ کیا تھا تو انکو آتش جہنم کا دگنا عذاب دے اللہ فرمائے گا کہ تم سب کو دگنا عذاب دیا جائے گا مگر تم نہیں جانتے۔",
          ),

      Verse(
              id_=39,
              arabic_="وَ قَالَتۡ اُوۡلٰٮہُمۡ لِاُخۡرٰٮہُمۡ فَمَا کَانَ لَکُمۡ عَلَیۡنَا مِنۡ فَضۡلٍ فَذُوۡقُوا الۡعَذَابَ بِمَا کُنۡتُمۡ تَکۡسِبُوۡنَ ﴿٪۳۹﴾ ",
              urdu_="اور پہلی جماعت پچھلی سے کہے گی کہ تم کو ہم پر کچھ بھی فضیلت نہ ہوئی تو جو عمل تم کیا کرتے تھے اسکے بدلے میں عذاب کے مزے چکھو۔",
          ),

      Verse(
              id_=40,
              arabic_="اِنَّ الَّذِیۡنَ  کَذَّبُوۡا بِاٰیٰتِنَا وَ اسۡتَکۡبَرُوۡا عَنۡہَا لَا تُفَتَّحُ لَہُمۡ  اَبۡوَابُ السَّمَآءِ  وَ لَا یَدۡخُلُوۡنَ الۡجَنَّۃَ حَتّٰی یَلِجَ الۡجَمَلُ فِیۡ سَمِّ الۡخِیَاطِ ؕ وَ کَذٰلِکَ نَجۡزِی الۡمُجۡرِمِیۡنَ ﴿۴۰﴾ ",
              urdu_="جن لوگوں نے ہماری آیتوں کو جھٹلایا اور ان سے سرتابی کی ان کے لئے نہ آسمان کے دروازے کھولے جائیں گے اور نہ وہ بہشت میں داخل ہوں گے یہاں تک کہ اونٹ سوئی کے ناکے میں سے نکل جائے اور گنہگاروں کو ہم ایسی ہی سزا دیا کرتے ہیں۔",
          ),

      Verse(
              id_=41,
              arabic_="لَہُمۡ مِّنۡ جَہَنَّمَ مِہَادٌ  وَّ مِنۡ فَوۡقِہِمۡ غَوَاشٍ ؕ وَ کَذٰلِکَ نَجۡزِی الظّٰلِمِیۡنَ ﴿۴۱﴾ ",
              urdu_="ایسے لوگوں کے لئے نیچے بچھونا بھی آتش جہنم کا ہوگا اور اوپر سے اوڑھنا بھی اسی کا اور ظالموں کو ہم ایسی ہی سزا دیا کرتے ہیں۔",
          ),

      Verse(
              id_=42,
              arabic_="وَ الَّذِیۡنَ اٰمَنُوۡا وَ عَمِلُوا الصّٰلِحٰتِ لَا نُکَلِّفُ نَفۡسًا اِلَّا وُسۡعَہَاۤ ۫ اُولٰٓئِکَ اَصۡحٰبُ  الۡجَنَّۃِ ۚ ہُمۡ   فِیۡہَا خٰلِدُوۡنَ ﴿۴۲﴾ ",
              urdu_="اور جو لوگ ایمان لائے اور عمل نیک کرتے رہے اور ہم کسی شخص کو اسکی طاقت سے زیادہ ذمہ دار بناتے ہی نہیں ایسے ہی لوگ اہل بہشت ہیں کہ اس میں ہمیشہ رہیں گے۔",
          ),

      Verse(
              id_=43,
              arabic_="وَ نَزَعۡنَا مَا فِیۡ صُدُوۡرِہِمۡ مِّنۡ غِلٍّ تَجۡرِیۡ مِنۡ تَحۡتِہِمُ الۡاَنۡہٰرُ ۚ وَ قَالُوا الۡحَمۡدُ لِلّٰہِ الَّذِیۡ ہَدٰٮنَا لِہٰذَا ۟ وَ مَا کُنَّا لِنَہۡتَدِیَ لَوۡ لَاۤ  اَنۡ ہَدٰٮنَا اللّٰہُ ۚ لَقَدۡ جَآءَتۡ رُسُلُ رَبِّنَا بِالۡحَقِّ ؕ وَ نُوۡدُوۡۤا اَنۡ تِلۡکُمُ الۡجَنَّۃُ  اُوۡرِثۡتُمُوۡہَا بِمَا کُنۡتُمۡ تَعۡمَلُوۡنَ ﴿۴۳﴾ ",
              urdu_="اور جو کینے انکے سینوں میں ہوں گے ہم سب نکال ڈالیں گے ان کے محلوں کے نیچے نہریں بہ رہی ہوں گی اور کہیں گے کہ اللہ کا شکر ہے جس نے ہم کو یہاں کا رستہ دکھایا اور اگر اللہ ہم کو رستہ نہ دکھاتا تو ہم رستہ نہ پاسکتے۔ بیشک ہمارے پروردگار کے رسول حق بات لے کر آئے تھے۔ اور اس روز منادی کر دی جائے گی کہ تم ان اعمال کے صلے میں جو دنیا میں کرتے تھے اس بہشت کے مالک بنا دیئے گئے ہو۔",
          ),

      Verse(
              id_=44,
              arabic_="وَ نَادٰۤی  اَصۡحٰبُ الۡجَنَّۃِ اَصۡحٰبَ النَّارِ اَنۡ قَدۡ وَجَدۡنَا مَا وَعَدَنَا رَبُّنَا حَقًّا فَہَلۡ وَجَدۡتُّمۡ مَّا وَعَدَ رَبُّکُمۡ حَقًّا ؕ قَالُوۡا نَعَمۡ ۚ فَاَذَّنَ مُؤَذِّنٌۢ بَیۡنَہُمۡ اَنۡ لَّعۡنَۃُ  اللّٰہِ  عَلَی  الظّٰلِمِیۡنَ ﴿ۙ۴۴﴾ ",
              urdu_="اور اہل بہشت دوزخیوں سے پکار کر کہیں گے کہ جو وعدہ ہمارے پروردگار نے ہم سے کیا تھا ہم نے تو اسے سچا پالیا۔ بھلا جو وعدہ تمہارے پروردگار نے تم سے کیا تھا تم نے بھی اسے سچا پایا؟ وہ کہیں گے ہاں۔ تو اس وقت ان میں ایک پکارنے والا پکار دے گا کہ بے انصافوں پر اللہ کی لعنت۔",
          ),

      Verse(
              id_=45,
              arabic_="الَّذِیۡنَ یَصُدُّوۡنَ عَنۡ سَبِیۡلِ اللّٰہِ وَ یَبۡغُوۡنَہَا عِوَجًا ۚ وَ ہُمۡ بِالۡاٰخِرَۃِ کٰفِرُوۡنَ ﴿ۘ۴۵﴾ ",
              urdu_="جو اللہ کی راہ سے روکتے اور اس میں کجی ڈھونڈتے اور آخرت سے انکار کرتے تھے۔",
          ),

      Verse(
              id_=46,
              arabic_="وَ بَیۡنَہُمَا حِجَابٌ ۚ وَ عَلَی الۡاَعۡرَافِ رِجَالٌ یَّعۡرِفُوۡنَ کُلًّۢا بِسِیۡمٰہُمۡ ۚ وَ  نَادَوۡا اَصۡحٰبَ الۡجَنَّۃِ  اَنۡ سَلٰمٌ  عَلَیۡکُمۡ ۟  لَمۡ یَدۡخُلُوۡہَا وَ  ہُمۡ  یَطۡمَعُوۡنَ ﴿۴۶﴾ ",
              urdu_="ان دونوں یعنی بہشت اور دوزخ کے درمیان اعراف نام کی ایک دیوار ہو گی اور اعراف پر کچھ آدمی ہوں گے جو سب کو انکی صورتوں سے پہچان لیں گے اور وہ اہل بہشت کو پکار کر کہیں گے کہ تم پر سلامتی ہو یہ لوگ ابھی بہشت میں داخل تو نہیں ہوئے ہونگے مگر امید رکھتے ہوں گے۔",
          ),

      Verse(
              id_=47,
              arabic_="وَ اِذَا صُرِفَتۡ اَبۡصَارُہُمۡ تِلۡقَآءَ اَصۡحٰبِ النَّارِ ۙ قَالُوۡا رَبَّنَا لَا تَجۡعَلۡنَا مَعَ الۡقَوۡمِ  الظّٰلِمِیۡنَ ﴿٪۴۷﴾ ",
              urdu_="اور جب انکی نگاہیں پلٹ کر اہل دوزخ کی طرف جائیں گی تو عرض کریں گے کہ اے ہمارے پروردگار ہم کو ظالم لوگوں کے ساتھ شامل نہ کیجیو۔",
          ),

      Verse(
              id_=48,
              arabic_="وَ نَادٰۤی اَصۡحٰبُ الۡاَعۡرَافِ رِجَالًا یَّعۡرِفُوۡنَہُمۡ بِسِیۡمٰہُمۡ قَالُوۡا مَاۤ  اَغۡنٰی عَنۡکُمۡ جَمۡعُکُمۡ وَ مَا کُنۡتُمۡ تَسۡتَکۡبِرُوۡنَ ﴿۴۸﴾ ",
              urdu_="اور اہل اعراف کافر لوگوں کو جنہیں انکی صورتوں سے شناخت کرتے ہونگے پکاریں گے اور کہیں گے کہ آج نہ تو تمہاری جماعت ہی تمہارے کچھ کام آئی اور نہ تمہارا  تکبر ہی سود مند ہوا۔",
          ),

      Verse(
              id_=49,
              arabic_="اَہٰۤؤُلَآءِ  الَّذِیۡنَ  اَقۡسَمۡتُمۡ  لَا  یَنَالُہُمُ اللّٰہُ بِرَحۡمَۃٍ ؕ اُدۡخُلُوا الۡجَنَّۃَ  لَا خَوۡفٌ عَلَیۡکُمۡ  وَ لَاۤ  اَنۡتُمۡ  تَحۡزَنُوۡنَ ﴿۴۹﴾ ",
              urdu_="پھر مومنوں کی طرف اشارہ کر کے کہیں گے کیا یہ وہی لوگ ہیں جنکے بارے میں تم قسمیں کھایا کرتے تھے کہ اللہ اپنی رحمت سے انکی دستگیری نہ کرے گا تو مومنو تم بہشت میں داخل ہو جاؤ تمہیں کچھ خوف نہیں اور نہ تم کو کچھ رنج و غم ہو گا۔",
          ),

      Verse(
              id_=50,
              arabic_="وَ نَادٰۤی اَصۡحٰبُ النَّارِ  اَصۡحٰبَ الۡجَنَّۃِ اَنۡ اَفِیۡضُوۡا عَلَیۡنَا مِنَ الۡمَآءِ اَوۡ مِمَّا رَزَقَکُمُ  اللّٰہُ ؕ قَالُوۡۤا  اِنَّ اللّٰہَ حَرَّمَہُمَا عَلَی الۡکٰفِرِیۡنَ ﴿ۙ۵۰﴾ ",
              urdu_="اور دوزخی بہشتیوں سے گڑگڑا کر کہیں گے کہ کسی قدر ہم پر پانی بہاؤ یا جو رزق اللہ نے تمہیں عنایت فرمایا ہے اس میں سے کچھ ہمیں بھی دو وہ جواب دیں گے کہ اللہ نے یہ دونوں نعمتیں کافروں پر حرام کر دی ہیں۔",
          ),

      Verse(
              id_=51,
              arabic_="الَّذِیۡنَ اتَّخَذُوۡا دِیۡنَہُمۡ لَہۡوًا وَّ لَعِبًا وَّ غَرَّتۡہُمُ الۡحَیٰوۃُ الدُّنۡیَا ۚ فَالۡیَوۡمَ نَنۡسٰہُمۡ  کَمَا نَسُوۡا لِقَآءَ یَوۡمِہِمۡ ہٰذَا ۙ وَ مَا  کَانُوۡا بِاٰیٰتِنَا  یَجۡحَدُوۡنَ ﴿۵۱﴾ ",
              urdu_="جنہوں نے اپنے دین کو تماشا اور کھیل بنا رکھا تھا اور دنیا کی زندگی نے انکو دھوکے میں ڈال رکھا تھا۔ تو جس طرح یہ لوگ اپنے اس دن کے آنے کو بھولے ہوئے اور ہماری آیتوں سے منکر ہو رہے تھے اسی طرح آج ہم بھی انہیں بھلا دیں گے۔",
          ),

      Verse(
              id_=52,
              arabic_="وَ لَقَدۡ جِئۡنٰہُمۡ بِکِتٰبٍ فَصَّلۡنٰہُ عَلٰی عِلۡمٍ ہُدًی وَّ رَحۡمَۃً  لِّقَوۡمٍ  یُّؤۡمِنُوۡنَ ﴿۵۲﴾ ",
              urdu_="اور ہم نے انکے پاس کتاب پہنچا دی ہے جسکو علم و دانش کے ساتھ کھول کھول کر بیان کر دیا ہے اور وہ مومن لوگوں کے لئے ہدایت اور رحمت ہے۔",
          ),

      Verse(
              id_=53,
              arabic_="ہَلۡ یَنۡظُرُوۡنَ  اِلَّا تَاۡوِیۡلَہٗ ؕ یَوۡمَ یَاۡتِیۡ تَاۡوِیۡلُہٗ یَقُوۡلُ الَّذِیۡنَ نَسُوۡہُ مِنۡ قَبۡلُ قَدۡ جَآءَتۡ رُسُلُ رَبِّنَا بِالۡحَقِّ ۚ فَہَلۡ لَّنَا مِنۡ شُفَعَآءَ  فَیَشۡفَعُوۡا  لَنَاۤ  اَوۡ  نُرَدُّ فَنَعۡمَلَ غَیۡرَ الَّذِیۡ کُنَّا نَعۡمَلُ ؕ قَدۡ خَسِرُوۡۤا اَنۡفُسَہُمۡ وَ ضَلَّ عَنۡہُمۡ مَّا کَانُوۡا  یَفۡتَرُوۡنَ ﴿٪۵۳﴾ ",
              urdu_="کیا یہ لوگ اسکے مضمون ظاہر ہونے کے منتظر ہیں۔ جس دن اس کا مضمون ظاہر ہو جائے گا۔ تو جو لوگ اسکو پہلے سے بھولے ہوئے ہونگے وہ بول اٹھیں گے کہ بیشک ہمارے پروردگار کے رسول حق لے کر آئے تھے بھلا آج ہمارے کوئی سفارشی ہیں کہ ہماری سفارش کریں یا ہم دنیا میں پھر لوٹا دیئے جائیں کہ جو عمل بد ہم پہلے کرتے تھے۔ وہ نہ کریں بلکہ انکے سوا دوسرے عمل کریں۔ بیشک ان لوگوں نے اپنا نقصان کیا اور جو کچھ یہ افترا کیا کرتے تھے ان سے سب جاتا رہا۔",
          ),

      Verse(
              id_=54,
              arabic_="اِنَّ رَبَّکُمُ اللّٰہُ الَّذِیۡ خَلَقَ السَّمٰوٰتِ وَ الۡاَرۡضَ فِیۡ سِتَّۃِ اَیَّامٍ ثُمَّ اسۡتَوٰی عَلَی الۡعَرۡشِ ۟ یُغۡشِی الَّیۡلَ النَّہَارَ یَطۡلُبُہٗ حَثِیۡثًا ۙ وَّ الشَّمۡسَ وَ الۡقَمَرَ وَ النُّجُوۡمَ مُسَخَّرٰتٍۭ بِاَمۡرِہٖ ؕ اَلَا لَہُ  الۡخَلۡقُ وَ الۡاَمۡرُ ؕ تَبٰرَکَ اللّٰہُ رَبُّ الۡعٰلَمِیۡنَ ﴿۵۴﴾ ",
              urdu_="کچھ شک نہیں کہ تمہارا پروردگار اللہ ہی ہے جس نے آسمانوں اور زمین کو چھ دن میں پیدا کیا پھر عرش پر جلوہ افروز ہوا وہی رات کو دن کا لباس پہناتا ہے کہ وہ اسکے پیچھے دوڑتا چلا آتا ہے اور اسی نے سورج اور چاند اور ستاروں کو پیدا کیا سب اسکے حکم کے مطابق کام میں لگے ہوئے ہیں۔ دیکھو سب مخلوق بھی اسی کی ہے اور حکم بھی اسی کا ہے اللہ رب العالمین بڑی برکت والا ہے۔",
          ),

      Verse(
              id_=55,
              arabic_="اُدۡعُوۡا رَبَّکُمۡ  تَضَرُّعًا  وَّ خُفۡیَۃً ؕ اِنَّہٗ  لَا یُحِبُّ  الۡمُعۡتَدِیۡنَ ﴿ۚ۵۵﴾ ",
              urdu_="لوگو اپنے پروردگار سے عاجزی سے اور چپکے چپکے دعائیں مانگا کرو وہ حد سے بڑھنے والوں کو دوست نہیں رکھتا۔",
          ),

      Verse(
              id_=56,
              arabic_="وَ لَا تُفۡسِدُوۡا فِی الۡاَرۡضِ بَعۡدَ اِصۡلَاحِہَا وَ ادۡعُوۡہُ خَوۡفًا وَّ طَمَعًا ؕ اِنَّ رَحۡمَتَ اللّٰہِ قَرِیۡبٌ مِّنَ الۡمُحۡسِنِیۡنَ ﴿۵۶﴾ ",
              urdu_="اور ملک میں اصلاح کے بعد خرابی نہ کرنا اور اللہ سے خوف رکھتے ہوئے اور امید رکھ کر دعائیں مانگتے رہنا۔ کچھ شک نہیں کہ اللہ کی رحمت نیکی کرنے والوں سے قریب ہے۔",
          ),

      Verse(
              id_=57,
              arabic_="وَ ہُوَ الَّذِیۡ یُرۡسِلُ الرِّیٰحَ بُشۡرًۢا بَیۡنَ یَدَیۡ رَحۡمَتِہٖ ؕ حَتّٰۤی  اِذَاۤ   اَقَلَّتۡ  سَحَابًا ثِقَالًا سُقۡنٰہُ لِبَلَدٍ مَّیِّتٍ فَاَنۡزَلۡنَا بِہِ الۡمَآءَ فَاَخۡرَجۡنَا بِہٖ مِنۡ کُلِّ الثَّمَرٰتِ ؕ کَذٰلِکَ نُخۡرِجُ الۡمَوۡتٰی لَعَلَّکُمۡ  تَذَکَّرُوۡنَ ﴿۵۷﴾ ",
              urdu_="اور وہی تو ہے جو اپنی رحمت یعنی بارش سے پہلے ہواؤں کو خوشخبری بنا کر بھیجتا ہے۔ یہاں تک کہ جب وہ بھاری بھاری بادلوں کو اٹھا لاتی ہیں تو ہم اس کو ایک مری ہوئی بستی کی طرف ہانک دیتے ہیں۔ پھر بادل سے بارش برساتے ہیں پھر بارش سے ہر طرح کے پھل پیدا کرتے ہیں اسی طرح ہم مردوں کو زمین سے زندہ کر کے باہر نکالیں گے۔ یہ آیات اس لئے بیان کی جاتی ہیں تاکہ تم نصیحت حاصل کرو۔",
          ),

      Verse(
              id_=58,
              arabic_="وَ الۡبَلَدُ الطَّیِّبُ یَخۡرُجُ نَبَاتُہٗ بِاِذۡنِ رَبِّہٖ ۚ وَ الَّذِیۡ خَبُثَ لَا یَخۡرُجُ  اِلَّا نَکِدًا ؕ کَذٰلِکَ نُصَرِّفُ الۡاٰیٰتِ لِقَوۡمٍ یَّشۡکُرُوۡنَ ﴿٪۵۸﴾ ",
              urdu_="اور جو زمین پاکیزہ ہے اس میں سے سبزہ بھی پروردگار کے حکم سے نفیس ہی نکلتا ہے اور جو خراب ہے اس میں سے جو کچھ نکلتا ہے ناقص ہوتا ہے۔ اسی طرح ہم آیتوں کو شکرگذار لوگوں کے لئے پھیر پھیر کر بیان کرتے ہیں۔",
          ),

      Verse(
              id_=59,
              arabic_="لَقَدۡ اَرۡسَلۡنَا نُوۡحًا اِلٰی قَوۡمِہٖ فَقَالَ یٰقَوۡمِ اعۡبُدُوا اللّٰہَ مَا  لَکُمۡ مِّنۡ  اِلٰہٍ غَیۡرُہٗ ؕ اِنِّیۡۤ   اَخَافُ عَلَیۡکُمۡ  عَذَابَ یَوۡمٍ عَظِیۡمٍ ﴿۵۹﴾ ",
              urdu_="ہم نے نوح کو انکی قوم کی طرف بھیجا۔ تو انہوں نے ان سے کہا اے میری برادری کے لوگو اللہ کی عبادت کرو اسکے سوا تمہارا کوئی معبود نہیں مجھے تمہارے بارے میں بڑے دن کے عذاب کا بہت ہی ڈر ہے۔",
          ),

      Verse(
              id_=60,
              arabic_="قَالَ الۡمَلَاُ مِنۡ قَوۡمِہٖۤ  اِنَّا لَنَرٰٮکَ فِیۡ ضَلٰلٍ  مُّبِیۡنٍ ﴿۶۰﴾ ",
              urdu_="تو جو انکی قوم میں سردار تھے وہ کہنے لگے کہ ہم تمہیں کھلی گمراہی میں مبتلا دیکھتے ہیں۔",
          ),

      Verse(
              id_=61,
              arabic_="قَالَ یٰقَوۡمِ لَیۡسَ بِیۡ ضَلٰلَۃٌ  وَّ لٰکِنِّیۡ رَسُوۡلٌ  مِّنۡ  رَّبِّ  الۡعٰلَمِیۡنَ ﴿۶۱﴾",
              urdu_="فرمایا اے قوم مجھ میں کسی طرح کی گمراہی نہیں ہے بلکہ میں پروردگار عالم کا پیغمبر ہوں۔",
          ),

      Verse(
              id_=62,
              arabic_="اُبَلِّغُکُمۡ رِسٰلٰتِ رَبِّیۡ وَ اَنۡصَحُ لَکُمۡ وَ اَعۡلَمُ مِنَ اللّٰہِ  مَا  لَا  تَعۡلَمُوۡنَ ﴿۶۲﴾ ",
              urdu_="تمہیں اپنے  پروردگار کے پیغام پہنچاتا ہوں اور تمہاری خیرخواہی کرتا ہوں اور مجھ کو اللہ کی طرف سے ایسی باتیں معلوم ہیں جن سے تم بے خبر ہو۔",
          ),

      Verse(
              id_=63,
              arabic_="اَوَ عَجِبۡتُمۡ اَنۡ جَآءَکُمۡ ذِکۡرٌ  مِّنۡ رَّبِّکُمۡ عَلٰی رَجُلٍ مِّنۡکُمۡ لِیُنۡذِرَکُمۡ وَ لِتَتَّقُوۡا وَ لَعَلَّکُمۡ  تُرۡحَمُوۡنَ ﴿۶۳﴾ ",
              urdu_="کیا تم کو اس بات سے تعجب ہوا ہے کہ تم میں سے ایک شخص کے ذریعے تمہارے پروردگار کی طرف سے تمہارے پاس نصیحت آئی تاکہ وہ تم کو ڈرائے اور تاکہ تم پرہیزگار بنو اور تاکہ تم پر رحم کیا جائے۔",
          ),

      Verse(
              id_=64,
              arabic_="فَکَذَّبُوۡہُ  فَاَنۡجَیۡنٰہُ وَ الَّذِیۡنَ مَعَہٗ فِی الۡفُلۡکِ وَ اَغۡرَقۡنَا الَّذِیۡنَ کَذَّبُوۡا بِاٰیٰتِنَا ؕ اِنَّہُمۡ  کَانُوۡا  قَوۡمًا  عَمِیۡنَ ﴿٪۶۴﴾",
              urdu_="مگر ان لوگوں نے انکی تکذیب کی۔ تو ہم نے نوح کو اور جو انکے ساتھ کشتی میں سوار تھے انکو تو بچا لیا اور جن لوگوں نے ہماری آیتوں کو جھٹلایا تھا انہیں غرق کر دیا کچھ شک نہیں کہ وہ عقل کے اندھے لوگ تھے۔",
          ),

      Verse(
              id_=65,
              arabic_="وَ اِلٰی عَادٍ  اَخَاہُمۡ ہُوۡدًا ؕ قَالَ یٰقَوۡمِ اعۡبُدُوا اللّٰہَ مَا  لَکُمۡ  مِّنۡ  اِلٰہٍ  غَیۡرُہٗ ؕ اَفَلَا  تَتَّقُوۡنَ ﴿۶۵﴾ ",
              urdu_="اور اسی طرح قوم عاد کی طرف انکے بھائی ہود کو بھیجا انہوں نے کہا کہ بھائیو اللہ ہی کی عبادت کرو۔ اسکے سوا تمہارا کوئی معبود نہیں کیا تم ڈرتے نہیں؟",
          ),

      Verse(
              id_=66,
              arabic_="قَالَ الۡمَلَاُ الَّذِیۡنَ  کَفَرُوۡا مِنۡ قَوۡمِہٖۤ  اِنَّا لَنَرٰٮکَ فِیۡ سَفَاہَۃٍ  وَّ اِنَّا لَنَظُنُّکَ مِنَ الۡکٰذِبِیۡنَ ﴿۶۶﴾ ",
              urdu_="تو انکی قوم کے سردار جو کافر تھے کہنے لگے کہ تم ہمیں احمق نظر آتے ہو اور ہم تمہیں جھوٹا خیال کرتے ہیں۔",
          ),

      Verse(
              id_=67,
              arabic_="قَالَ یٰقَوۡمِ لَیۡسَ بِیۡ سَفَاہَۃٌ   وَّ لٰکِنِّیۡ رَسُوۡلٌ مِّنۡ  رَّبِّ  الۡعٰلَمِیۡنَ ﴿۶۷﴾",
              urdu_="فرمایا اے میری قوم! مجھ میں حماقت کی کوئی بات نہیں ہے بلکہ میں رب العالمین کا پیغمبر ہوں۔",
          ),

      Verse(
              id_=68,
              arabic_="اُبَلِّغُکُمۡ رِسٰلٰتِ رَبِّیۡ وَ اَنَا لَکُمۡ نَاصِحٌ  اَمِیۡنٌ ﴿۶۸﴾ ",
              urdu_="میں تمہیں اللہ کے پیغام پہنچاتا ہوں اور تمہارا امانتدار خیرخواہ ہوں۔",
          ),

      Verse(
              id_=69,
              arabic_="اَوَ عَجِبۡتُمۡ اَنۡ جَآءَکُمۡ ذِکۡرٌ مِّنۡ رَّبِّکُمۡ عَلٰی رَجُلٍ مِّنۡکُمۡ لِیُنۡذِرَکُمۡ ؕ وَ اذۡکُرُوۡۤا  اِذۡ  جَعَلَکُمۡ  خُلَفَآءَ مِنۡۢ بَعۡدِ قَوۡمِ نُوۡحٍ وَّ زَادَکُمۡ فِی الۡخَلۡقِ بَصۜۡطَۃً ۚ فَاذۡکُرُوۡۤا اٰلَآءَ اللّٰہِ لَعَلَّکُمۡ تُفۡلِحُوۡنَ ﴿۶۹﴾ ",
              urdu_="اور کیا تم کو اس بات سے تعجب ہوا ہے کہ تم میں سے ایک شخص کے ذریعے تمہارے پروردگار کی طرف سے تمہارے پاس نصیحت آئی تاکہ وہ تمہیں ڈرائے۔ اور یاد تو کرو جب اس نے تم کو قوم نوح کے بعد جانشین بنایا اور تم کو پھیلاؤ زیادہ دیا۔ پس اللہ کی نعمتوں کو یاد کرو تاکہ کامیاب ہو جاؤ۔",
          ),

      Verse(
              id_=70,
              arabic_="قَالُوۡۤا اَجِئۡتَنَا لِنَعۡبُدَ اللّٰہَ وَحۡدَہٗ وَ نَذَرَ مَا کَانَ یَعۡبُدُ اٰبَآؤُنَا ۚ فَاۡتِنَا بِمَا تَعِدُنَاۤ  اِنۡ  کُنۡتَ  مِنَ  الصّٰدِقِیۡنَ ﴿۷۰﴾ ",
              urdu_="وہ کہنے لگے کیا تم ہمارے پاس اس لئے آئے ہو کہ ہم اکیلے اللہ ہی کی عبادت کریں اور جنکو ہمارے باپ دادا پوجتے چلے آئے ہیں انکو چھوڑ دیں؟ تو اگر سچے ہو تو جس چیز سے ہمیں ڈراتے ہو اسے لے آؤ۔",
          ),

      Verse(
              id_=71,
              arabic_="قَالَ قَدۡ وَقَعَ عَلَیۡکُمۡ مِّنۡ رَّبِّکُمۡ رِجۡسٌ وَّ غَضَبٌ ؕ اَتُجَادِلُوۡنَنِیۡ فِیۡۤ  اَسۡمَآءٍ سَمَّیۡتُمُوۡہَاۤ  اَنۡتُمۡ  وَ اٰبَآؤُکُمۡ مَّا نَزَّلَ اللّٰہُ بِہَا مِنۡ سُلۡطٰنٍ ؕ فَانۡتَظِرُوۡۤا  اِنِّیۡ مَعَکُمۡ  مِّنَ الۡمُنۡتَظِرِیۡنَ ﴿۷۱﴾ ",
              urdu_="ہود نے کہا کہ تمہارے پروردگار کی طرف سے تم پر عذاب اور غضب کا نازل ہونا مقرر ہو چکا ہے۔ کیا تم مجھ سے چند ناموں کے بارے میں جھگڑتے ہو جو تم نے اور تمہارے باپ دادا نے اپنی طرف سے رکھ لئے ہیں۔ جنکی اللہ نے کوئی سند نازل نہیں کی۔ تو تم بھی انتظار کرو میں بھی تمہارے ساتھ انتظار کرتا ہوں۔",
          ),

      Verse(
              id_=72,
              arabic_="فَاَنۡجَیۡنٰہُ وَ الَّذِیۡنَ مَعَہٗ بِرَحۡمَۃٍ مِّنَّا وَ قَطَعۡنَا دَابِرَ الَّذِیۡنَ کَذَّبُوۡا بِاٰیٰتِنَا وَ مَا  کَانُوۡا مُؤۡمِنِیۡنَ ﴿٪۷۲﴾ ",
              urdu_="پھر ہم نے ہود کو اور جو لوگ انکے ساتھ تھے انکو اپنی مہربانی سے نجات بخشی اور جنہوں نے ہماری آیتوں کو جھٹلایا تھا انکی جڑ کاٹ دی۔ اور وہ ایمان لانے والے تھے ہی نہیں۔",
          ),

      Verse(
              id_=73,
              arabic_="وَ  اِلٰی ثَمُوۡدَ  اَخَاہُمۡ  صٰلِحًا ۘ قَالَ یٰقَوۡمِ اعۡبُدُوا اللّٰہَ  مَا  لَکُمۡ  مِّنۡ  اِلٰہٍ غَیۡرُہٗ ؕ قَدۡ جَآءَتۡکُمۡ  بَیِّنَۃٌ مِّنۡ رَّبِّکُمۡ ؕ ہٰذِہٖ نَاقَۃُ اللّٰہِ لَکُمۡ اٰیَۃً فَذَرُوۡہَا تَاۡکُلۡ فِیۡۤ  اَرۡضِ اللّٰہِ وَ لَا تَمَسُّوۡہَا بِسُوۡٓءٍ فَیَاۡخُذَکُمۡ عَذَابٌ اَلِیۡمٌ ﴿۷۳﴾ ",
              urdu_="اور قوم ثمود کی طرف انکے بھائی صالح کو بھیجا تو صالح نے کہا کہ اے قوم! اللہ ہی کی عبادت کرو اسکے سوا تمہارا کوئی معبود نہیں۔ تمہارے پاس تمہارے پروردگار کی طرف سے ایک معجزہ آ چکا ہے یعنی یہی اللہ کی اونٹنی تمہارے لئے معجزہ ہے۔ تو اسے آزاد چھوڑ دو کہ اللہ کی زمین میں چرتی پھرے اور تم اسے بری نیت سے ہاتھ بھی نہ لگانا۔ ورنہ عذاب الیم تمہیں پکڑ لے گا۔",
          ),

      Verse(
              id_=74,
              arabic_="وَ اذۡکُرُوۡۤا اِذۡ جَعَلَکُمۡ خُلَفَآءَ مِنۡۢ بَعۡدِ عَادٍ  وَّ بَوَّاَکُمۡ فِی الۡاَرۡضِ تَتَّخِذُوۡنَ مِنۡ سُہُوۡلِہَا قُصُوۡرًا وَّ تَنۡحِتُوۡنَ الۡجِبَالَ بُیُوۡتًا ۚ فَاذۡکُرُوۡۤا اٰلَآءَ اللّٰہِ وَ لَا تَعۡثَوۡا فِی الۡاَرۡضِ مُفۡسِدِیۡنَ ﴿۷۴﴾ ",
              urdu_="اور یاد تو کرو جب اس نے تم کو قوم عاد کے بعد سردار بنایا اور زمین پر آباد کیا۔ کہ نرم زمین میں محل تعمیر کرتے ہو اور پہاڑوں کر تراش تراش کر گھر بناتے ہو۔ پس اللہ کی نعمتوں کو یاد کرو اور زمین میں فساد نہ کرتے پھرو۔",
          ),

      Verse(
              id_=75,
              arabic_="قَالَ الۡمَلَاُ الَّذِیۡنَ اسۡتَکۡبَرُوۡا مِنۡ قَوۡمِہٖ لِلَّذِیۡنَ اسۡتُضۡعِفُوۡا لِمَنۡ اٰمَنَ مِنۡہُمۡ اَتَعۡلَمُوۡنَ اَنَّ  صٰلِحًا  مُّرۡسَلٌ  مِّنۡ رَّبِّہٖ ؕ قَالُوۡۤا اِنَّا بِمَاۤ  اُرۡسِلَ بِہٖ مُؤۡمِنُوۡنَ ﴿۷۵﴾ ",
              urdu_="تو انکی قوم میں سردار لوگ جو تکبر رکھتے تھے غریب لوگوں سے جو ان میں سے ایمان لے آئے تھے کہنے لگے بھلا تم یقین کرتے ہو کہ صالح اپنے پروردگار کی طرف سے بھیجے گئے ہیں؟ انہوں نے کہا ہاں جو چیز وہ دے کر بھیجے گئے ہیں ہم اس پر بلاشبہ ایمان رکھتے ہیں۔",
          ),

      Verse(
              id_=76,
              arabic_="قَالَ الَّذِیۡنَ اسۡتَکۡبَرُوۡۤا اِنَّا بِالَّذِیۡۤ اٰمَنۡتُمۡ  بِہٖ  کٰفِرُوۡنَ ﴿۷۶﴾ ",
              urdu_="تو سرداران مغرور کہنے لگے کہ جس چیز پر تم ایمان لائے ہو ہم تو اسکو نہیں مانتے۔",
          ),

      Verse(
              id_=77,
              arabic_="فَعَقَرُوا النَّاقَۃَ  وَ عَتَوۡا عَنۡ  اَمۡرِ  رَبِّہِمۡ وَ قَالُوۡا یٰصٰلِحُ ائۡتِنَا بِمَا تَعِدُنَاۤ  اِنۡ  کُنۡتَ مِنَ  الۡمُرۡسَلِیۡنَ ﴿۷۷﴾ ",
              urdu_="آخر انہوں نے اس اونٹنی کو مار ڈالا اور اپنے پروردگار کے حکم سے سرکشی کی اور کہنے لگے کہ صالح جس چیز سے تم ہمیں ڈراتے تھے اگر تم اللہ کے پیغمبر ہو تو اسے ہم پر لے آؤ۔",
          ),

      Verse(
              id_=78,
              arabic_="فَاَخَذَتۡہُمُ الرَّجۡفَۃُ فَاَصۡبَحُوۡا فِیۡ  دَارِہِمۡ  جٰثِمِیۡنَ ﴿۷۸﴾ ",
              urdu_=" پس انکو زلزلے نے آ پکڑا سو وہ اپنے گھروں میں اوندھے پڑے رہ گئے۔",
          ),

      Verse(
              id_=79,
              arabic_="فَتَوَلّٰی عَنۡہُمۡ وَ قَالَ یٰقَوۡمِ لَقَدۡ اَبۡلَغۡتُکُمۡ رِسَالَۃَ رَبِّیۡ وَ نَصَحۡتُ لَکُمۡ  وَ لٰکِنۡ لَّا  تُحِبُّوۡنَ النّٰصِحِیۡنَ ﴿۷۹﴾",
              urdu_="پھر صالح ان سے ناامید ہو کر پھرے اور کہا کہ میری قوم! میں نے تم کو اللہ کا پیغام پہنچا دیا اور تمہاری خیرخواہی کی مگر تم ایسے ہو کہ خیرخواہوں کو دوست ہی نہیں رکھتے۔",
          ),

      Verse(
              id_=80,
              arabic_="وَ لُوۡطًا اِذۡ قَالَ لِقَوۡمِہٖۤ اَتَاۡتُوۡنَ الۡفَاحِشَۃَ مَا سَبَقَکُمۡ بِہَا مِنۡ اَحَدٍ مِّنَ  الۡعٰلَمِیۡنَ ﴿۸۰﴾ ",
              urdu_="اور اسی طرح جب ہم نے لوط کو پیغمبر بنا کر بھیجا تو اس وقت انہوں نے اپنی قوم سے کہا کہ تم ایسی بے حیائی کا کام کیوں کرتے ہو کہ تم سے پہلے اہل عالم میں سے کسی نے اس طرح کا کام نہیں کیا۔",
          ),

      Verse(
              id_=81,
              arabic_="اِنَّکُمۡ لَتَاۡتُوۡنَ الرِّجَالَ شَہۡوَۃً مِّنۡ دُوۡنِ النِّسَآءِ ؕ بَلۡ  اَنۡتُمۡ  قَوۡمٌ  مُّسۡرِفُوۡنَ ﴿۸۱﴾ ",
              urdu_="یعنی خواہش نفسانی پورا کرنے کے لئے عورتوں کو چھوڑ کر مردوں کے پاس آتے ہو۔ حقیقت یہ ہے کہ تم لوگ حد سے نکل جانے والے ہو۔",
          ),

      Verse(
              id_=82,
              arabic_="وَ مَا کَانَ جَوَابَ قَوۡمِہٖۤ  اِلَّاۤ اَنۡ قَالُوۡۤا اَخۡرِجُوۡہُمۡ مِّنۡ قَرۡیَتِکُمۡ ۚ اِنَّہُمۡ  اُنَاسٌ یَّتَطَہَّرُوۡنَ ﴿۸۲﴾ ",
              urdu_="اور ان سے اسکا جواب کچھ نہ بن پڑا اور بولے تو یہ بولے کہ ان لوگوں یعنی لوط اور اسکے گھر والوں کو اپنے گاؤں سے نکال دو کہ یہ لوگ پاک بننا چاہتے ہیں۔",
          ),

      Verse(
              id_=83,
              arabic_="فَاَنۡجَیۡنٰہُ وَ اَہۡلَہٗۤ  اِلَّا امۡرَاَتَہٗ ۫ۖ کَانَتۡ مِنَ  الۡغٰبِرِیۡنَ ﴿۸۳﴾ ",
              urdu_="تو ہم نے انکو اور انکے گھر والوں کو بچا لیا مگر انکی بیوی نہ بچی کہ وہ پیچھے رہنے والوں میں ہو گئ۔",
          ),

      Verse(
              id_=84,
              arabic_="وَ  اَمۡطَرۡنَا عَلَیۡہِمۡ  مَّطَرًا ؕ فَانۡظُرۡ  کَیۡفَ کَانَ عَاقِبَۃُ  الۡمُجۡرِمِیۡنَ ﴿٪۸۴﴾ ",
              urdu_="اور ہم نے ان پر پتھروں کی بارش برسائی سو دیکھ لو کہ گنہگاروں کا کیسا انجام ہوا۔",
          ),

      Verse(
              id_=85,
              arabic_="وَ اِلٰی مَدۡیَنَ اَخَاہُمۡ شُعَیۡبًا ؕ قَالَ یٰقَوۡمِ اعۡبُدُوا اللّٰہَ مَا  لَکُمۡ مِّنۡ  اِلٰہٍ غَیۡرُہٗ ؕ قَدۡ جَآءَتۡکُمۡ بَیِّنَۃٌ مِّنۡ رَّبِّکُمۡ فَاَوۡفُوا الۡکَیۡلَ وَ الۡمِیۡزَانَ وَ لَا تَبۡخَسُوا النَّاسَ اَشۡیَآءَہُمۡ وَ لَا تُفۡسِدُوۡا فِی الۡاَرۡضِ بَعۡدَ  اِصۡلَاحِہَا ؕ ذٰلِکُمۡ خَیۡرٌ لَّکُمۡ  اِنۡ کُنۡتُمۡ مُّؤۡمِنِیۡنَ ﴿ۚ۸۵﴾ ",
              urdu_="اور مدین کی طرف انکے بھائی شعیب کو بھیجا تو انہوں نے کہا اے قوم! اللہ ہی کی عبادت کرو اسکے سوا تمہار کوئی معبود نہیں۔ تمہارے پاس تمہارے پروردگار کی طرف سے نشانی آچکی ہے تو تم ناپ اور تول پوری کیا کرو اور لوگوں کو چیزیں کم نہ دیا کرو اور زمین میں اصلاح کے بعد خرابی نہ کرو اگر تم صاحب ایمان ہو تو سمجھ لو کہ یہ بات تمہارے حق میں بہتر ہے۔",
          ),

      Verse(
              id_=86,
              arabic_="وَ لَا تَقۡعُدُوۡا بِکُلِّ صِرَاطٍ تُوۡعِدُوۡنَ وَ تَصُدُّوۡنَ عَنۡ  سَبِیۡلِ اللّٰہِ  مَنۡ  اٰمَنَ بِہٖ وَ تَبۡغُوۡنَہَا عِوَجًا ۚ وَ اذۡکُرُوۡۤا اِذۡ کُنۡتُمۡ قَلِیۡلًا فَکَثَّرَکُمۡ ۪ وَ انۡظُرُوۡا کَیۡفَ  کَانَ عَاقِبَۃُ  الۡمُفۡسِدِیۡنَ ﴿۸۶﴾ ",
              urdu_="اور ہر رستے پر مت بیٹھا کرو کہ جو شخص اللہ پر ایمان لاتا ہے اسے تم ڈراتے اور اللہ کی راہ سے روکتے اور اس میں کجی ڈھونڈتے ہو اور اس وقت کو یاد کرو جب تم تھوڑے سے تھے تو اللہ نے تم کو جماعت کثیر کر دیا اور دیکھ لو کہ خرابی کرنے والوں کا انجام کیسا ہوا۔",
          ),

      Verse(
              id_=87,
              arabic_="وَ اِنۡ کَانَ طَآئِفَۃٌ  مِّنۡکُمۡ اٰمَنُوۡا بِالَّذِیۡۤ اُرۡسِلۡتُ بِہٖ وَ طَآئِفَۃٌ  لَّمۡ یُؤۡمِنُوۡا فَاصۡبِرُوۡا حَتّٰی یَحۡکُمَ اللّٰہُ بَیۡنَنَا ۚ وَ ہُوَ  خَیۡرُ  الۡحٰکِمِیۡنَ ﴿۸۷﴾ ",
              urdu_="اور اگر تم میں سے ایک جماعت میری رسالت پر ایمان لے آئی ہے اور ایک جماعت ایمان نہیں لائی تو صبر کئے رہو یہاں تک کہ اللہ ہمارے تمہارے درمیان فیصلہ کر دے۔ اور وہ سب سے بہتر فیصلہ کرنے والا ہے۔",
          ),

      Verse(
              id_=88,
              arabic_="قَالَ الۡمَلَاُ الَّذِیۡنَ اسۡتَکۡبَرُوۡا مِنۡ قَوۡمِہٖ لَنُخۡرِجَنَّکَ یٰشُعَیۡبُ وَ الَّذِیۡنَ اٰمَنُوۡا مَعَکَ مِنۡ قَرۡیَتِنَاۤ  اَوۡ  لَتَعُوۡدُنَّ فِیۡ مِلَّتِنَا ؕ قَالَ اَوَ لَوۡ  کُنَّا کٰرِہِیۡنَ ﴿۟۸۸﴾",
              urdu_="تو انکی قوم میں جو لوگ سردار تھے جو تکبر کرتے تھے وہ کہنے لگے۔ کہ شعیب یا تو ہم تم کو اور جو لوگ تمہارے ساتھ ایمان لائے ہیں ان کو اپنے شہر سے نکال دیں گے۔ یا تم ہمارے مذہب میں واپس آ جاؤ۔ انہوں نے کہا خواہ ہم تمہارے دین سے بیزار ہی ہوں تو بھی؟",
          ),

      Verse(
              id_=89,
              arabic_="قَدِ افۡتَرَیۡنَا عَلَی اللّٰہِ  کَذِبًا اِنۡ عُدۡنَا فِیۡ مِلَّتِکُمۡ  بَعۡدَ  اِذۡ  نَجّٰنَا اللّٰہُ مِنۡہَا ؕ وَ مَا یَکُوۡنُ  لَنَاۤ  اَنۡ نَّعُوۡدَ  فِیۡہَاۤ  اِلَّاۤ  اَنۡ یَّشَآءَ اللّٰہُ رَبُّنَا ؕ وَسِعَ رَبُّنَا کُلَّ شَیۡءٍ عِلۡمًا ؕ عَلَی اللّٰہِ تَوَکَّلۡنَا ؕ رَبَّنَا افۡتَحۡ بَیۡنَنَا وَ بَیۡنَ قَوۡمِنَا بِالۡحَقِّ وَ اَنۡتَ خَیۡرُ  الۡفٰتِحِیۡنَ ﴿۸۹﴾",
              urdu_="اگر ہم اسکے بعد کہ اللہ ہمیں اس سے نجات بخش چکا ہے تمہارے مذہب میں لوٹ جائیں تو بیشک ہم نے اللہ پر جھوٹ باندھا اور ہمیں شایاں نہیں کہ ہم اس میں لوٹ جائیں ہاں اللہ جو ہمارا پروردگار ہے وہ چاہے تو ہم مجبور ہیں ہمارے پروردگار کا علم ہر چیز کا احاطہ کئے ہوئے ہے۔ ہمارا اللہ ہی پر بھروسہ ہے اے پروردگار ہم میں اور ہماری قوم میں انصاف کے ساتھ فیصلہ کر دے اور تو سب سے بہتر فیصلہ کرنے والا ہے۔",
          ),

      Verse(
              id_=90,
              arabic_="وَ قَالَ الۡمَلَاُ الَّذِیۡنَ  کَفَرُوۡا مِنۡ قَوۡمِہٖ لَئِنِ اتَّبَعۡتُمۡ شُعَیۡبًا اِنَّکُمۡ  اِذًا لَّخٰسِرُوۡنَ ﴿۹۰﴾ ",
              urdu_="اور انکی قوم میں سے سردار لوگ جو کافر تھے کہنے لگے کہ بھائیو اگر تم نے شعیب کی پیروی کی تو بیشک تم خسارے میں پڑ گئے۔",
          ),

      Verse(
              id_=91,
              arabic_="فَاَخَذَتۡہُمُ الرَّجۡفَۃُ فَاَصۡبَحُوۡا فِیۡ دَارِہِمۡ جٰثِمِیۡنَ ﴿ۚۖۛ۹۱﴾ ",
              urdu_="تو انکو زلزلے نے آ پکڑا اور وہ اپنے گھروں میں اوندھے پڑے رہ گئے۔",
          ),

      Verse(
              id_=92,
              arabic_="الَّذِیۡنَ کَذَّبُوۡا شُعَیۡبًا کَاَنۡ  لَّمۡ  یَغۡنَوۡا فِیۡہَا ۚۛ اَلَّذِیۡنَ  کَذَّبُوۡا شُعَیۡبًا  کَانُوۡا ہُمُ الۡخٰسِرِیۡنَ ﴿۹۲﴾ ",
              urdu_="یہ لوگ جنہوں نے شعیب کی تکذیب کی تھی ایسے برباد ہوئے تھے کہ گویا وہ ان میں کبھی آباد ہی نہیں ہوئے تھے غرض جنہوں نے شعیب کو جھٹلایا وہ خسارے میں پڑ گئے۔",
          ),

      Verse(
              id_=93,
              arabic_="فَتَوَلّٰی عَنۡہُمۡ وَ قَالَ یٰقَوۡمِ لَقَدۡ اَبۡلَغۡتُکُمۡ رِسٰلٰتِ رَبِّیۡ وَ نَصَحۡتُ لَکُمۡ ۚ فَکَیۡفَ  اٰسٰی عَلٰی قَوۡمٍ کٰفِرِیۡنَ ﴿٪۹۳﴾ ",
              urdu_="پھر شعیب ان میں سے نکل آئے اور کہا کہ بھائیو میں نے تم کو اپنے پروردگار کے پیغام پہنچا دیئے اور تمہاری خیرخواہی کی تھی تو میں کافروں پر عذاب نازل ہونے سے رنج و غم کیوں کروں۔",
          ),

      Verse(
              id_=94,
              arabic_="وَ مَاۤ  اَرۡسَلۡنَا فِیۡ  قَرۡیَۃٍ  مِّنۡ نَّبِیٍّ  اِلَّاۤ اَخَذۡنَاۤ  اَہۡلَہَا بِالۡبَاۡسَآءِ  وَ الضَّرَّآءِ لَعَلَّہُمۡ  یَضَّرَّعُوۡنَ ﴿۹۴﴾",
              urdu_="اور ہم نے کسی شہر میں کوئی پیغمبر نہیں بھیجا۔ مگر وہاں کے رہنے والوں کو جو ایمان نہ لائے دکھوں اور مصیبتوں میں مبتلا کیا تاکہ وہ عاجزی اور زاری کریں۔",
          ),

      Verse(
              id_=95,
              arabic_="ثُمَّ بَدَّلۡنَا مَکَانَ السَّیِّئَۃِ الۡحَسَنَۃَ حَتّٰی عَفَوۡا وَّ قَالُوۡا قَدۡ مَسَّ اٰبَآءَنَا الضَّرَّآءُ وَ السَّرَّآءُ  فَاَخَذۡنٰہُمۡ  بَغۡتَۃً   وَّ ہُمۡ  لَا  یَشۡعُرُوۡنَ ﴿۹۵﴾ ",
              urdu_="پھر ہم نے تکلیف کو آسودگی سے بدل دیا یہاں تک کہ وہ مال و اولاد میں زیادہ ہوگئے تو کہنے لگے کہ اسی طرح کا رنج و راحت ہمارے بڑوں کو بھی پہنچتا رہا ہے تو ہم نے انکو ناگہاں پکڑ لیا اور وہ اپنے حال میں بیخبر تھے۔",
          ),

      Verse(
              id_=96,
              arabic_="وَ لَوۡ اَنَّ  اَہۡلَ الۡقُرٰۤی اٰمَنُوۡا  وَ اتَّقَوۡا لَفَتَحۡنَا عَلَیۡہِمۡ بَرَکٰتٍ مِّنَ السَّمَآءِ وَ الۡاَرۡضِ وَ لٰکِنۡ  کَذَّبُوۡا فَاَخَذۡنٰہُمۡ بِمَا  کَانُوۡا  یَکۡسِبُوۡنَ ﴿۹۶﴾",
              urdu_="اور اگر ان بستیوں کے لوگ ایمان لے آتے اور پرہیزگار ہو جاتے تو ہم ان پر آسمان اور زمین کی برکات کے دروازے کھول دیتے مگر انہوں نے تو تکذیب کی۔ سو انکے اعمال کی سزا میں ہم نے انکو پکڑ لیا۔",
          ),

      Verse(
              id_=97,
              arabic_="اَفَاَمِنَ اَہۡلُ الۡقُرٰۤی اَنۡ  یَّاۡتِیَہُمۡ  بَاۡسُنَا بَیَاتًا  وَّ ہُمۡ  نَآئِمُوۡنَ ﴿ؕ۹۷﴾ ",
              urdu_="کیا بستیوں میں رہنے والے اس سے بے خوف ہیں۔ کہ ان پر ہمارا عذاب رات کو واقع ہو اور وہ بیخبر سو رہے ہوں۔",
          ),

      Verse(
              id_=98,
              arabic_="اَوَ  اَمِنَ  اَہۡلُ الۡقُرٰۤی اَنۡ یَّاۡتِیَہُمۡ بَاۡسُنَا ضُحًی  وَّ ہُمۡ  یَلۡعَبُوۡنَ ﴿۹۸﴾ ",
              urdu_="اور کیا اہل شہر اس سے نڈر ہیں کہ ان پر ہمارا عذاب دن چڑھے آ نازل ہو اور وہ کھیل رہے ہوں۔",
          ),

      Verse(
              id_=99,
              arabic_="اَفَاَمِنُوۡا مَکۡرَ اللّٰہِ ۚ فَلَا  یَاۡمَنُ مَکۡرَ اللّٰہِ   اِلَّا الۡقَوۡمُ  الۡخٰسِرُوۡنَ ﴿٪۹۹﴾ ",
              urdu_="کیا یہ لوگ اللہ کے داؤں کا ڈر نہیں رکھتے سن لو کہ اللہ کے داؤں سے وہی لوگ نڈر ہوتے ہیں جو خسارہ پانے والے ہیں۔",
          ),

      Verse(
              id_=100,
              arabic_="اَوَ لَمۡ  یَہۡدِ  لِلَّذِیۡنَ یَرِثُوۡنَ الۡاَرۡضَ مِنۡۢ  بَعۡدِ  اَہۡلِہَاۤ  اَنۡ  لَّوۡ نَشَآءُ  اَصَبۡنٰہُمۡ بِذُنُوۡبِہِمۡ ۚ وَ نَطۡبَعُ عَلٰی قُلُوۡبِہِمۡ  فَہُمۡ  لَا  یَسۡمَعُوۡنَ ﴿۱۰۰﴾ ",
              urdu_="کیا ان لوگوں کو جو اہل زمین کے مر جانے کے بعد زمین کے مالک ہوتے ہیں یہ امر موجب ہدایت نہیں ہوا کہ اگر ہم چاہیں تو ان کے گناہوں کے سبب ان پر مصیبت ڈال دیں اور ان کے دلوں پر مہر لگا دیں کہ کچھ سن ہی نہ سکیں۔",
          ),

      Verse(
              id_=101,
              arabic_="تِلۡکَ الۡقُرٰی نَقُصُّ عَلَیۡکَ مِنۡ اَنۡۢبَآئِہَا ۚ  وَ لَقَدۡ جَآءَتۡہُمۡ رُسُلُہُمۡ بِالۡبَیِّنٰتِ ۚ فَمَا کَانُوۡا لِیُؤۡمِنُوۡا بِمَا کَذَّبُوۡا مِنۡ قَبۡلُ ؕ کَذٰلِکَ یَطۡبَعُ اللّٰہُ عَلٰی قُلُوۡبِ  الۡکٰفِرِیۡنَ ﴿۱۰۱﴾ ",
              urdu_="یہ بستیاں ہیں جن کے کچھ حالات ہم تم کو سناتے ہیں اور ان کے پاس انکے پیغمبر نشانیاں لے کر آئے۔ مگر وہ ایسے نہیں تھے کہ جس چیز کو پہلے جھٹلا چکے ہوں اسے مان لیں اسی طرح اللہ کافروں کے دلوں پر مہر لگا دیتا ہے۔",
          ),

      Verse(
              id_=102,
              arabic_="وَ مَا  وَجَدۡنَا  لِاَکۡثَرِہِمۡ  مِّنۡ عَہۡدٍ ۚ وَ  اِنۡ  وَّجَدۡنَاۤ   اَکۡثَرَہُمۡ  لَفٰسِقِیۡنَ ﴿۱۰۲﴾ ",
              urdu_="اور ہم نے ان میں سے اکثر لوگوں میں عہد کا پاس نہیں دیکھا۔ اور ان میں اکثروں کو دیکھا تو بدکار ہی دیکھا۔",
          ),

      Verse(
              id_=103,
              arabic_="ثُمَّ بَعَثۡنَا مِنۡۢ بَعۡدِہِمۡ مُّوۡسٰی بِاٰیٰتِنَاۤ  اِلٰی فِرۡعَوۡنَ وَ مَلَا۠ئِہٖ فَظَلَمُوۡا بِہَا ۚ فَانۡظُرۡ  کَیۡفَ کَانَ عَاقِبَۃُ الۡمُفۡسِدِیۡنَ ﴿۱۰۳﴾ ",
              urdu_="پھر ان پیغمبروں کے بعد ہم نے موسٰی کو نشانیاں دے کر فرعون اور اسکے درباریوں کے پاس بھیجا۔ تو انہوں نے انکے ساتھ کفر کیا سو دیکھ لو کہ خرابی کرنے والوں کا انجام کیا ہوا۔",
          ),

      Verse(
              id_=104,
              arabic_="وَ قَالَ مُوۡسٰی یٰفِرۡعَوۡنُ  اِنِّیۡ  رَسُوۡلٌ مِّنۡ رَّبِّ  الۡعٰلَمِیۡنَ ﴿۱۰۴﴾ۙ ",
              urdu_="اور موسٰی نے کہا کہ اے فرعون میں رب العالمین کا پیغمبر ہوں۔",
          ),

      Verse(
              id_=105,
              arabic_="حَقِیۡقٌ عَلٰۤی اَنۡ  لَّاۤ  اَقُوۡلَ عَلَی اللّٰہِ  اِلَّا الۡحَقَّ ؕ قَدۡ جِئۡتُکُمۡ بِبَیِّنَۃٍ مِّنۡ رَّبِّکُمۡ فَاَرۡسِلۡ مَعِیَ بَنِیۡۤ  اِسۡرَآءِیۡلَ ﴿۱۰۵﴾ؕ ",
              urdu_="مجھ پر واجب ہے کہ اللہ کی طرف سے جو کچھ کہوں سچ ہی کہوں۔ میں تمہارے پاس تمہارے پروردگار کی طرف سے نشانی لے کر آیا ہوں سو بنی اسرائیل کو میرے ساتھ جانے کی رخصت دے دیجئے۔",
          ),

      Verse(
              id_=106,
              arabic_="قَالَ  اِنۡ کُنۡتَ جِئۡتَ بِاٰیَۃٍ  فَاۡتِ بِہَاۤ  اِنۡ  کُنۡتَ  مِنَ  الصّٰدِقِیۡنَ ﴿۱۰۶﴾ ",
              urdu_="فرعون نے کہا اگر تم نشانی لے کر آئے ہو تو اگر سچے ہو تو لاؤ دکھاؤ۔",
          ),

      Verse(
              id_=107,
              arabic_="فَاَلۡقٰی عَصَاہُ  فَاِذَا ہِیَ ثُعۡبَانٌ  مُّبِیۡنٌ ﴿۱۰۷﴾ۚۖ ",
              urdu_="موسٰی نے اپنی لاٹھی زمین پر ڈال دی تو وہ اسی وقت ایک بڑا اژدھا بن گیا۔",
          ),

      Verse(
              id_=108,
              arabic_="وَّ نَزَعَ یَدَہٗ  فَاِذَا ہِیَ بَیۡضَآءُ  لِلنّٰظِرِیۡنَ ﴿۱۰۸﴾٪ ",
              urdu_="اور اپنا ہاتھ گریبان سے نکالا تو اسی دم دیکھنے والوں کی نگاہوں میں سفید براق یعنی روشن ہو گیا۔",
          ),

      Verse(
              id_=109,
              arabic_="قَالَ الۡمَلَاُ مِنۡ  قَوۡمِ  فِرۡعَوۡنَ   اِنَّ ہٰذَا لَسٰحِرٌ  عَلِیۡمٌ ﴿۱۰۹﴾ۙ ",
              urdu_="تو قوم فرعون میں جو سردار تھے وہ کہنے لگے کہ یہ بڑا ماہر جادوگر ہے۔",
          ),

      Verse(
              id_=110,
              arabic_="یُّرِیۡدُ  اَنۡ یُّخۡرِجَکُمۡ مِّنۡ  اَرۡضِکُمۡ ۚ فَمَا ذَا  تَاۡمُرُوۡنَ ﴿۱۱۰﴾ ",
              urdu_="اس کا ارادہ یہ ہے کہ تم کو تمہارے ملک سے نکال دے۔ بھلا تمہاری کیا صلاح ہے؟",
          ),

      Verse(
              id_=111,
              arabic_="قَالُوۡۤا اَرۡجِہۡ  وَ اَخَاہُ  وَ اَرۡسِلۡ فِی الۡمَدَآئِنِ  حٰشِرِیۡنَ ﴿۱۱۱﴾ۙ ",
              urdu_="انہوں نے فرعون  سے کہا کہ فی الحال موسٰی اور اسکے بھائی کے معاملے کو موقوف رکھئے اور شہروں میں نقیب روانہ کر دیجئے۔",
          ),

      Verse(
              id_=112,
              arabic_="یَاۡتُوۡکَ  بِکُلِّ  سٰحِرٍ  عَلِیۡمٍ ﴿۱۱۲﴾ ",
              urdu_="کہ تمام ماہر جادوگروں کو آپ کے پاس لے آئیں۔",
          ),

      Verse(
              id_=113,
              arabic_="وَ  جَآءَ  السَّحَرَۃُ  فِرۡعَوۡنَ  قَالُوۡۤا اِنَّ  لَنَا  لَاَجۡرًا  اِنۡ  کُنَّا نَحۡنُ الۡغٰلِبِیۡنَ ﴿۱۱۳﴾ ",
              urdu_="چنانچہ ایسا ہی کیا گیا اور جادوگر فرعون کے پاس آ پہنچے اور کہنے لگے کہ اگر ہم جیت گئے تو ہمیں صلہ تو عطا کیجئے گا ناں۔",
          ),

      Verse(
              id_=114,
              arabic_="قَالَ  نَعَمۡ  وَ  اِنَّکُمۡ لَمِنَ الۡمُقَرَّبِیۡنَ ﴿۱۱۴﴾ ",
              urdu_="فرعون نے کہا ہاں ضرور اور اسکے علاوہ تم مقربوں میں داخل کر لئے جاؤ گے۔",
          ),

      Verse(
              id_=115,
              arabic_="قَالُوۡا یٰمُوۡسٰۤی  اِمَّاۤ  اَنۡ تُلۡقِیَ وَ  اِمَّاۤ  اَنۡ نَّکُوۡنَ  نَحۡنُ الۡمُلۡقِیۡنَ ﴿۱۱۵﴾ ",
              urdu_="جب فریقین روز مقررہ پر جمع ہوئے تو جادوگروں نے کہا کہ موسٰی یا تو تم جادو کی چیز ڈالو یا ہم ڈالتے ہیں۔",
          ),

      Verse(
              id_=116,
              arabic_="قَالَ  اَلۡقُوۡا ۚ فَلَمَّاۤ  اَلۡقَوۡا سَحَرُوۡۤا  اَعۡیُنَ النَّاسِ وَ اسۡتَرۡہَبُوۡہُمۡ  وَ جَآءُوۡ  بِسِحۡرٍ عَظِیۡمٍ ﴿۱۱۶﴾ ",
              urdu_="موسٰی نے فرمایا تم ہی ڈالو۔ سو جب انہوں نے جادو کی چیزیں ڈالیں تو لوگوں کی آنکھوں پر جادو کر دیا یعنی نظربندی کر دی اور لاٹھیوں اور رسیوں کے سانپ بنا بنا کر انہیں ڈرا ڈرا دیا اور بہت بڑا جادو دکھایا۔",
          ),

      Verse(
              id_=117,
              arabic_="وَ اَوۡحَیۡنَاۤ  اِلٰی مُوۡسٰۤی اَنۡ اَلۡقِ عَصَاکَ ۚ فَاِذَا  ہِیَ تَلۡقَفُ  مَا  یَاۡفِکُوۡنَ ﴿۱۱۷﴾ۚ ",
              urdu_="اور اس وقت ہم نے موسٰی کی طرف وحی بھیجی کہ تم بھی اپنی لاٹھی ڈال دو تب وہ فوراً سانپ بن کر جادگروں کے بنائے ہوئے سانپوں کو ایک ایک کر کے نگل جائے گی۔",
          ),

      Verse(
              id_=118,
              arabic_="فَوَقَعَ الۡحَقُّ وَ بَطَلَ مَا کَانُوۡا یَعۡمَلُوۡنَ ﴿۱۱۸﴾ۚ ",
              urdu_="آخر حق ثابت ہو گیا اور جو کچھ وہ لوگ کرتے تھے باطل ہو گیا۔",
          ),

      Verse(
              id_=119,
              arabic_="فَغُلِبُوۡا ہُنَالِکَ وَ انۡقَلَبُوۡا صٰغِرِیۡنَ ﴿۱۱۹﴾ۚ ",
              urdu_="چنانچہ اس موقع پر وہ مغلوب ہو گئے اور ذلیل ہو کر رہ گئے۔",
          ),

      Verse(
              id_=120,
              arabic_="وَ اُلۡقِیَ  السَّحَرَۃُ  سٰجِدِیۡنَ ﴿۱۲۰﴾ۚۖ ",
              urdu_="اور یہ کیفیت دیکھ کر جادوگر سجدے میں گر پڑے۔",
          ),

      Verse(
              id_=121,
              arabic_="قَالُوۡۤا  اٰمَنَّا  بِرَبِّ الۡعٰلَمِیۡنَ ﴿۱۲۱﴾ۙ ",
              urdu_="کہنے لگے کہ ہم سب جہانوں کے پروردگار پر ایمان لائے۔",
          ),

      Verse(
              id_=122,
              arabic_="رَبِّ  مُوۡسٰی  وَ ہٰرُوۡنَ ﴿۱۲۲﴾ ",
              urdu_="یعنی موسٰی اور ہارون کے پروردگار پر۔",
          ),

      Verse(
              id_=123,
              arabic_="قَالَ فِرۡعَوۡنُ اٰمَنۡتُمۡ بِہٖ قَبۡلَ اَنۡ اٰذَنَ لَکُمۡ ۚ اِنَّ ہٰذَا لَمَکۡرٌ  مَّکَرۡتُمُوۡہُ فِی الۡمَدِیۡنَۃِ  لِتُخۡرِجُوۡا مِنۡہَاۤ  اَہۡلَہَا ۚ فَسَوۡفَ تَعۡلَمُوۡنَ ﴿۱۲۳﴾ ",
              urdu_="فرعون نے کہا پیشتر اسکے کہ میں تمہیں اجازت دوں تم اس پر ایمان لے آئے؟ بیشک یہ فریب ہے جو تم نے مل کر شہر میں کیا ہے تاکہ اہل شہر کو یہاں سے نکال دو۔ سو عنقریب اس کا نتیجہ معلوم کر لو گے۔",
          ),

      Verse(
              id_=124,
              arabic_="لَاُقَطِّعَنَّ اَیۡدِیَکُمۡ وَ اَرۡجُلَکُمۡ مِّنۡ خِلَافٍ ثُمَّ لَاُصَلِّبَنَّکُمۡ اَجۡمَعِیۡنَ ﴿۱۲۴﴾ ",
              urdu_="میں پہلے تو تمہارے ایک طرف کے ہاتھ اور دوسری طرف کے پاؤں کاٹ دوں گا پھر تم سب کو سولی چڑھا دوں گا۔",
          ),

      Verse(
              id_=125,
              arabic_="قَالُوۡۤا اِنَّاۤ  اِلٰی رَبِّنَا مُنۡقَلِبُوۡنَ ﴿۱۲۵﴾ۚ ",
              urdu_="وہ بولے کہ ہم تو اپنے پروردگار کی طرف لوٹ کر جانے والے ہیں۔",
          ),

      Verse(
              id_=126,
              arabic_="وَ مَا تَنۡقِمُ مِنَّاۤ  اِلَّاۤ  اَنۡ  اٰمَنَّا بِاٰیٰتِ رَبِّنَا لَمَّا جَآءَتۡنَا ؕ رَبَّنَاۤ  اَفۡرِغۡ عَلَیۡنَا صَبۡرًا  وَّ تَوَفَّنَا مُسۡلِمِیۡنَ ﴿۱۲۶﴾٪ ",
              urdu_="اور اسکے سوا تجھ کو ہماری کون سی بات بری لگی ہے کہ جب ہمارے پروردگار کی نشانیاں ہمارے پاس آگئیں تو ہم ان پر ایمان لے آئے۔ اے پروردگار ہم پر صبر و استقامت کے دہانے کھولدے اور ہمیں فرمانبرداروں کی حالت میں وفات دیجئو۔",
          ),

      Verse(
              id_=127,
              arabic_="وَ قَالَ الۡمَلَاُ مِنۡ قَوۡمِ فِرۡعَوۡنَ اَتَذَرُ مُوۡسٰی وَ قَوۡمَہٗ  لِیُفۡسِدُوۡا فِی الۡاَرۡضِ وَ یَذَرَکَ  وَ اٰلِہَتَکَ ؕ قَالَ سَنُقَتِّلُ اَبۡنَآءَہُمۡ وَ نَسۡتَحۡیٖ نِسَآءَہُمۡ ۚ وَ اِنَّا فَوۡقَہُمۡ  قٰہِرُوۡنَ ﴿۱۲۷﴾ ",
              urdu_="اور قوم فرعون میں جو سردار تھے کہنے لگے کہ کیا آپ موسٰی اور اسکی قوم کو چھوڑ دیجئے گا کہ ملک میں خرابی کریں اور آپ کو اور آپ کے معبودوں کو چھوڑ دیں۔ اس نے کہا کہ ہم ان کے لڑکوں کو تو قتل کر ڈالیں گے اور لڑکیوں کو زندہ رہنے دیں گے اور بلاشبہ ہم ان پر غالب ہیں۔",
          ),

      Verse(
              id_=128,
              arabic_="قَالَ مُوۡسٰی  لِقَوۡمِہِ اسۡتَعِیۡنُوۡا بِاللّٰہِ وَ اصۡبِرُوۡا ۚ اِنَّ  الۡاَرۡضَ  لِلّٰہِ ۟ۙ یُوۡرِثُہَا مَنۡ یَّشَآءُ مِنۡ عِبَادِہٖ ؕ وَ الۡعَاقِبَۃُ  لِلۡمُتَّقِیۡنَ ﴿۱۲۸﴾ ",
              urdu_="موسٰی نے اپنی قوم سے کہا کہ اللہ سے مدد مانگو اور ثابت قدم رہو زمین تو اللہ کی ہے۔ وہ اپنے بندوں میں سے جسے چاہتا ہے اسکا مالک بناتا ہے۔ اور انجام تو پرہیزگاروں ہی کا بھلا ہوتا ہے۔",
          ),

      Verse(
              id_=129,
              arabic_="قَالُوۡۤا اُوۡذِیۡنَا مِنۡ قَبۡلِ اَنۡ تَاۡتِیَنَا وَ مِنۡۢ بَعۡدِ مَا جِئۡتَنَا ؕ قَالَ عَسٰی رَبُّکُمۡ اَنۡ یُّہۡلِکَ عَدُوَّکُمۡ وَ یَسۡتَخۡلِفَکُمۡ  فِی الۡاَرۡضِ فَیَنۡظُرَ  کَیۡفَ تَعۡمَلُوۡنَ ﴿۱۲۹﴾٪ ",
              urdu_="وہ بولے کہ تمہارے آنے سے پہلے بھی ہم کو اذیتیں پہنچتی رہیں اور تمہارے آنے کے بعد بھی موسٰی نے کہا کہ قریب ہے کہ تمہارا پروردگار تمہارے دشمن کو ہلاک کر دے اور تمہیں زمین میں خلیفہ بنائے پھر دیکھے کہ تم کیسے عمل کرتے ہو۔",
          ),

      Verse(
              id_=130,
              arabic_="وَ لَقَدۡ اَخَذۡنَاۤ  اٰلَ فِرۡعَوۡنَ بِالسِّنِیۡنَ وَ نَقۡصٍ مِّنَ الثَّمَرٰتِ لَعَلَّہُمۡ یَذَّکَّرُوۡنَ ﴿۱۳۰﴾ ",
              urdu_="اور ہم نے فرعونیوں کو خشک سالیوں اور میووں کے نقصان میں پکڑا تاکہ وہ نصیحت حاصل کریں۔",
          ),

      Verse(
              id_=131,
              arabic_="فَاِذَا جَآءَتۡہُمُ الۡحَسَنَۃُ  قَالُوۡا  لَنَا ہٰذِہٖ ۚ وَ  اِنۡ  تُصِبۡہُمۡ سَیِّئَۃٌ  یَّطَّیَّرُوۡا بِمُوۡسٰی وَ مَنۡ مَّعَہٗ ؕ اَلَاۤ  اِنَّمَا طٰٓئِرُہُمۡ عِنۡدَ اللّٰہِ وَ لٰکِنَّ  اَکۡثَرَہُمۡ  لَا یَعۡلَمُوۡنَ ﴿۱۳۱﴾ ",
              urdu_="سو جب ان کو آسائش حاصل ہوتی تو کہتے کہ ہم اسکے مستحق ہیں اور اگر سختی پہنچتی تو اسے موسٰی اور انکے رفیقوں کی بدشگونی بتاتے دیکھو ان کی بدشگونی اللہ کے ہاں مقدر ہے لیکن ان میں سے اکثر نہیں جانتے۔",
          ),

      Verse(
              id_=132,
              arabic_="وَ قَالُوۡا مَہۡمَا تَاۡتِنَا بِہٖ مِنۡ اٰیَۃٍ لِّتَسۡحَرَنَا بِہَا ۙ فَمَا نَحۡنُ لَکَ بِمُؤۡمِنِیۡنَ ﴿۱۳۲﴾ ",
              urdu_="اور کہنے لگے کہ تم ہمارے پاس خواہ کوئی بھی نشانی لاؤ تاکہ اس سے ہم پر جادو کرو۔ مگر ہم تم پر ایمان لانے والے نہیں ہیں۔",
          ),

      Verse(
              id_=133,
              arabic_="فَاَرۡسَلۡنَا عَلَیۡہِمُ الطُّوۡفَانَ وَ الۡجَرَادَ وَ الۡقُمَّلَ وَ الضَّفَادِعَ وَ الدَّمَ  اٰیٰتٍ مُّفَصَّلٰتٍ ۟ فَاسۡتَکۡبَرُوۡا وَ کَانُوۡا  قَوۡمًا مُّجۡرِمِیۡنَ ﴿۱۳۳﴾ ",
              urdu_="تو ہم نے ان پر طوفان اور ٹڈیاں اور جوئیں اور مینڈک اور خون کتنی کھلی ہوئی نشانیاں بھیجیں مگر وہ تکبر ہی کرتے رہے اور وہ لوگ تھے ہی گناہگار۔",
          ),

      Verse(
              id_=134,
              arabic_="وَ لَمَّا وَقَعَ عَلَیۡہِمُ  الرِّجۡزُ  قَالُوۡا یٰمُوۡسَی ادۡعُ لَنَا رَبَّکَ بِمَا عَہِدَ عِنۡدَکَ ۚ  لَئِنۡ کَشَفۡتَ عَنَّا الرِّجۡزَ لَنُؤۡمِنَنَّ  لَکَ  وَ لَنُرۡسِلَنَّ  مَعَکَ  بَنِیۡۤ اِسۡرَآءِیۡلَ ﴿۱۳۴﴾ۚ ",
              urdu_="اور جب ان پر عذاب واقع ہوتا تو کہتے کہ موسٰی ہمارے لئے اپنے پروردگار سے دعا کرو۔ جیسا اس نے تم سے عہد کر رکھا ہے اگر تم ہم سے عذاب کو ٹال دو گے تو ہم تم پر ایمان بھی لے آئیں گے اور بنی اسرائیل کو بھی تمہارے ساتھ جانے کی اجازت دینگے۔",
          ),

      Verse(
              id_=135,
              arabic_="فَلَمَّا کَشَفۡنَا عَنۡہُمُ الرِّجۡزَ اِلٰۤی اَجَلٍ ہُمۡ  بٰلِغُوۡہُ  اِذَا ہُمۡ  یَنۡکُثُوۡنَ ﴿۱۳۵﴾ ",
              urdu_="پھر جب ہم ایک مدت کے لئے جس تک ان کو پہنچنا تھا ان سے عذاب دور کر دیتے تو وہ فورًا عہد کو توڑ ڈالتے۔",
          ),

      Verse(
              id_=136,
              arabic_="فَانۡتَقَمۡنَا مِنۡہُمۡ فَاَغۡرَقۡنٰہُمۡ  فِی الۡیَمِّ بِاَنَّہُمۡ کَذَّبُوۡا بِاٰیٰتِنَا وَ کَانُوۡا عَنۡہَا غٰفِلِیۡنَ ﴿۱۳۶﴾ ",
              urdu_="تو ہم نے ان سے بدلہ لے کر ہی چھوڑا کہ انکو سمندر میں ڈبو دیا اس لئے کہ وہ ہماری آیتوں کو جھٹلاتے۔ اور ان سے بے پروائی کرتے تھے۔",
          ),

      Verse(
              id_=137,
              arabic_="وَ اَوۡرَثۡنَا الۡقَوۡمَ  الَّذِیۡنَ کَانُوۡا یُسۡتَضۡعَفُوۡنَ  مَشَارِقَ  الۡاَرۡضِ وَ مَغَارِبَہَا الَّتِیۡ  بٰرَکۡنَا فِیۡہَا ؕ وَ تَمَّتۡ کَلِمَتُ رَبِّکَ الۡحُسۡنٰی عَلٰی بَنِیۡۤ اِسۡرَآءِیۡلَ ۬ۙ بِمَا صَبَرُوۡا ؕ وَ دَمَّرۡنَا مَا کَانَ یَصۡنَعُ فِرۡعَوۡنُ وَ قَوۡمُہٗ  وَ مَا کَانُوۡا  یَعۡرِشُوۡنَ ﴿۱۳۷﴾ ",
              urdu_="اور جو لوگ کمزور بنائے جاتے رہے انکو ہم نے زمین شام کے مشرق و مغرب کا جس میں ہم نے برکت دی تھی وارث کر دیا اور بنی اسرائیل کے بارے میں ان کے صبر کی وجہ سے تمہارے پروردگار کا وعدہ نیک پورا ہوا اور فرعون اور قوم فرعون جو محل بناتے اور انگور کے باغ جو چھتریوں پر چڑھاتے تھے سب کو ہم نے تباہ کر دیا۔",
          ),

      Verse(
              id_=138,
              arabic_="وَ جٰوَزۡنَا بِبَنِیۡۤ  اِسۡرَآءِیۡلَ الۡبَحۡرَ فَاَتَوۡا عَلٰی قَوۡمٍ یَّعۡکُفُوۡنَ عَلٰۤی  اَصۡنَامٍ لَّہُمۡ ۚ قَالُوۡا یٰمُوۡسَی اجۡعَلۡ لَّنَاۤ  اِلٰـہًا کَمَا لَہُمۡ  اٰلِـہَۃٌ ؕ قَالَ  اِنَّکُمۡ  قَوۡمٌ  تَجۡہَلُوۡنَ ﴿۱۳۸﴾ ",
              urdu_="اور ہم نے بنی اسرائیل کو سمندر کے پار اتارا تو وہ ایسے لوگوں کے پاس جا پہنچے جو اپنے بتوں کی عبادت کے لئے بیٹھے رہتے تھے۔ بنی اسرائیل کہنے لگے کہ موسٰی جیسے ان لوگوں کے معبود ہیں ہمارے لئے بھی ایک معبود بنا دو۔ فرمایا کہ تم بڑے ہی جاہل لوگ ہو۔",
          ),

      Verse(
              id_=139,
              arabic_="اِنَّ  ہٰۤؤُلَآءِ  مُتَبَّرٌ  مَّا ہُمۡ  فِیۡہِ  وَ  بٰطِلٌ مَّا  کَانُوۡا  یَعۡمَلُوۡنَ ﴿۱۳۹﴾ ",
              urdu_="یہ لوگ جس شغل میں پھنسے ہوئے ہیں وہ برباد ہونے والا ہے اور جو کام یہ کرتے ہیں سب بیہودہ ہیں۔",
          ),

      Verse(
              id_=140,
              arabic_="قَالَ  اَغَیۡرَ اللّٰہِ  اَبۡغِیۡکُمۡ  اِلٰـہًا وَّ ہُوَ فَضَّلَکُمۡ  عَلَی  الۡعٰلَمِیۡنَ ﴿۱۴۰﴾ ",
              urdu_="فرمایا کیا میں اللہ کے سوا تمہارے لئے کوئی اور معبود تلاش کروں حالانکہ اس نے تمکو تمام اہل عالم پر فضیلت بخشی ہے۔",
          ),

      Verse(
              id_=141,
              arabic_="وَ اِذۡ  اَنۡجَیۡنٰکُمۡ مِّنۡ اٰلِ فِرۡعَوۡنَ یَسُوۡمُوۡنَکُمۡ  سُوۡٓءَ  الۡعَذَابِ ۚ یُقَتِّلُوۡنَ اَبۡنَآءَکُمۡ  وَ یَسۡتَحۡیُوۡنَ نِسَآءَکُمۡ ؕ وَ فِیۡ ذٰلِکُمۡ  بَلَآءٌ  مِّنۡ  رَّبِّکُمۡ   عَظِیۡمٌ ﴿۱۴۱﴾٪",
              urdu_="اور ہمارے ان احسانوں کو یاد کرو جب ہم نے تمکو فرعونیوں کے ہاتھ سے نجات بخشی وہ لوگ تم کو بڑا دکھ دیتے تھے۔ تمہارے بیٹوں کو تو قتل کر ڈالتے تھے اور بیٹیوں کو زندہ رہنے دیتے تھے اور اس میں تمہارے پروردگار کی طرف سے بڑی آزمائش تھی۔",
          ),

      Verse(
              id_=142,
              arabic_="وَ وٰعَدۡنَا  مُوۡسٰی ثَلٰثِیۡنَ  لَیۡلَۃً وَّ اَتۡمَمۡنٰہَا بِعَشۡرٍ فَتَمَّ  مِیۡقَاتُ رَبِّہٖۤ اَرۡبَعِیۡنَ  لَیۡلَۃً ۚ وَ قَالَ مُوۡسٰی  لِاَخِیۡہِ ہٰرُوۡنَ  اخۡلُفۡنِیۡ  فِیۡ  قَوۡمِیۡ وَ اَصۡلِحۡ  وَ لَا تَتَّبِعۡ  سَبِیۡلَ  الۡمُفۡسِدِیۡنَ ﴿۱۴۲﴾ ",
              urdu_="اور ہم نے موسٰی سے تیس رات کی معیاد مقرر کی اور دس راتیں اور ملا کر اسے پورا چلہ کر دیا تو اسکے پروردگار کی چالیس رات کی معیاد پوری ہو گئ۔ اور موسٰی نے اپنی بھائی ہارون سے کہا کہ میرے کوہ طور پر جانے کے بعد تم میری قوم میں میرے جانشین ہو۔ اور ان کی اصلاح کرتے رہنا اور شریروں کے رستے پر نہ چلنا۔",
          ),

      Verse(
              id_=143,
              arabic_="وَ لَمَّا جَآءَ مُوۡسٰی لِمِیۡقَاتِنَا وَ کَلَّمَہٗ رَبُّہٗ ۙ قَالَ رَبِّ اَرِنِیۡۤ   اَنۡظُرۡ   اِلَیۡکَ ؕ قَالَ لَنۡ تَرٰٮنِیۡ  وَ لٰکِنِ  انۡظُرۡ  اِلَی  الۡجَبَلِ فَاِنِ اسۡتَقَرَّ مَکَانَہٗ فَسَوۡفَ تَرٰٮنِیۡ ۚ فَلَمَّا تَجَلّٰی رَبُّہٗ  لِلۡجَبَلِ جَعَلَہٗ  دَکًّا وَّ خَرَّ مُوۡسٰی صَعِقًا ۚ فَلَمَّاۤ  اَفَاقَ قَالَ سُبۡحٰنَکَ تُبۡتُ  اِلَیۡکَ  وَ اَنَا  اَوَّلُ الۡمُؤۡمِنِیۡنَ ﴿۱۴۳﴾ ",
              urdu_="اور جب موسٰی ہمارے مقرر کئے ہوئے وقت پر کوہ طور پر پہنچے اور ان کے پروردگار نے ان سے کلام کیا۔ تو کہنے لگے کہ اے پروردگار مجھے اپنا جلوہ دکھا کہ میں تیرا دیدار کروں فرمایا کہ تم مجھے ہرگز نہ دیکھ سکو گے۔ ہاں پہاڑ کی طرف دیکھتے رہو اگر یہ اپنی جگہ پر قائم رہا تو تم مجھ کو دیکھ سکو گے۔ پھر جب انکے پروردگار نے پہاڑ پر تجلی کی تو اسکو ریزہ ریزہ کر دیا۔ اور موسٰی بے ہوش ہو کر گر پڑے۔ پھر جب ہوش میں آئے تو کہنے لگے کہ تیری ذات پاک ہے اور میں تیرے حضور میں توبہ کرتا ہوں اور جو ایمان لانے والے ہیں ان میں سب سے اول ہوں۔",
          ),

      Verse(
              id_=144,
              arabic_="قَالَ یٰمُوۡسٰۤی  اِنِّی  اصۡطَفَیۡتُکَ عَلَی النَّاسِ بِرِسٰلٰتِیۡ  وَ بِکَلَامِیۡ ۫ۖ فَخُذۡ مَاۤ اٰتَیۡتُکَ  وَ  کُنۡ  مِّنَ  الشّٰکِرِیۡنَ ﴿۱۴۴﴾ ",
              urdu_="فرمایا اے موسٰی میں نے تم کو اپنے پیغام اور اپنے کلام سے نواز کر لوگوں سے ممتاز کیا ہے۔ تو جو میں نے تم کو عطا کیا ہے اسے تھامے رکھو اور میرا شکر بجا لاؤ۔",
          ),

      Verse(
              id_=145,
              arabic_="وَ کَتَبۡنَا لَہٗ  فِی الۡاَلۡوَاحِ  مِنۡ کُلِّ شَیۡءٍ مَّوۡعِظَۃً  وَّ تَفۡصِیۡلًا  لِّکُلِّ شَیۡءٍ ۚ فَخُذۡہَا بِقُوَّۃٍ  وَّ اۡمُرۡ قَوۡمَکَ یَاۡخُذُوۡا بِاَحۡسَنِہَا ؕ سَاُورِیۡکُمۡ  دَارَ الۡفٰسِقِیۡنَ ﴿۱۴۵﴾ ",
              urdu_="اور ہم نے تورات کی تختیوں میں انکے لئے ہر قسم کی نصیحت اور ہر چیز کی تفصیل لکھ دی پھر ارشاد فرمایا کہ اسے مضبوطی سے تھامے رہو اور اپنی قوم سے بھی کہہ دو کہ ان باتوں پر بہت اچھی طرح عمل کریں میں عنقریب تم کو نافرمان لوگوں کا گھر دکھاؤں گا۔",
          ),

      Verse(
              id_=146,
              arabic_="سَاَصۡرِفُ عَنۡ اٰیٰتِیَ الَّذِیۡنَ یَتَکَبَّرُوۡنَ فِی الۡاَرۡضِ بِغَیۡرِ الۡحَقِّ ؕ وَ  اِنۡ یَّرَوۡا کُلَّ اٰیَۃٍ  لَّا یُؤۡمِنُوۡا بِہَا ۚ وَ اِنۡ یَّرَوۡا سَبِیۡلَ الرُّشۡدِ لَا یَتَّخِذُوۡہُ سَبِیۡلًا ۚ وَ اِنۡ  یَّرَوۡا سَبِیۡلَ الۡغَیِّ یَتَّخِذُوۡہُ سَبِیۡلًا ؕ ذٰلِکَ بِاَنَّہُمۡ کَذَّبُوۡا بِاٰیٰتِنَا وَ کَانُوۡا عَنۡہَا غٰفِلِیۡنَ ﴿۱۴۶﴾ ",
              urdu_="جو لوگ زمین میں ناحق تکبر کرتے ہیں انکو اپنی آیتوں سے پھیر دوں گا۔ اگر یہ سب نشانیاں بھی دیکھ لیں تب بھی ان پر ایمان نہ لائیں اور اگر بھلائی کا رستہ دیکھیں تو اسے اپنا رستہ نہ بنائیں۔ اور اگر برائی کی راہ دیکھیں تو اسے رستہ بنا لیں۔ یہ اس لئے کہ انہوں نے ہماری آیات کو جھٹلایا اور ان سے غفلت کرتے رہے۔",
          ),

      Verse(
              id_=147,
              arabic_="وَ الَّذِیۡنَ  کَذَّبُوۡا بِاٰیٰتِنَا وَ  لِقَآءِ  الۡاٰخِرَۃِ حَبِطَتۡ اَعۡمَالُہُمۡ ؕ ہَلۡ  یُجۡزَوۡنَ  اِلَّا  مَا  کَانُوۡا  یَعۡمَلُوۡنَ ﴿۱۴۷﴾٪ ",
              urdu_="اور جن لوگوں نے ہماری آیتوں اور آخرت کی پیشی کو جھٹلایا انکے اعمال ضائع ہو جائیں گے۔ یہ جیسے عمل کرتے ہیں ویسا ہی انکو بدلہ ملے گا۔",
          ),

      Verse(
              id_=148,
              arabic_="وَ اتَّخَذَ قَوۡمُ  مُوۡسٰی مِنۡۢ بَعۡدِہٖ مِنۡ حُلِیِّہِمۡ عِجۡلًا جَسَدًا لَّہٗ خُوَارٌ ؕ اَلَمۡ یَرَوۡا اَنَّہٗ  لَا یُکَلِّمُہُمۡ وَ لَا یَہۡدِیۡہِمۡ سَبِیۡلًا ۘ اِتَّخَذُوۡہُ   وَ کَانُوۡا ظٰلِمِیۡنَ ﴿۱۴۸﴾ ",
              urdu_="اور موسٰی کے بعد انکی قوم نے اپنے زیور سے ایک بچھڑا بنا لیا وہ ایک جسم تھا جس میں سے بیل کی آواز نکلتی تھی۔ ان لوگوں نے یہ نہ دیکھا کہ وہ نہ ان سے بات کر سکتا ہے اور نہ انکو رستہ دکھا سکتا ہے۔ اسکو انہوں نے معبود بنا لیا اور اپنے حق میں ظلم کیا۔",
          ),

      Verse(
              id_=149,
              arabic_="وَ لَمَّا سُقِطَ فِیۡۤ  اَیۡدِیۡہِمۡ وَ رَاَوۡا اَنَّہُمۡ قَدۡ ضَلُّوۡا ۙ قَالُوۡا لَئِنۡ لَّمۡ  یَرۡحَمۡنَا رَبُّنَا وَ یَغۡفِرۡ لَنَا لَنَکُوۡنَنَّ مِنَ الۡخٰسِرِیۡنَ ﴿۱۴۹﴾ ",
              urdu_="اور جب وہ نادم ہوئے اور دیکھا کہ گمراہ ہو گئے ہیں تو کہنے لگے کہ اگر ہمارا پروردگار ہم پر رحم نہیں کرے گا اور ہم کو معاف نہیں فرمائے گا تو ہم برباد ہو جائیں گے۔",
          ),

      Verse(
              id_=150,
              arabic_="وَ لَمَّا رَجَعَ مُوۡسٰۤی اِلٰی قَوۡمِہٖ غَضۡبَانَ اَسِفًا ۙ قَالَ بِئۡسَمَا خَلَفۡتُمُوۡنِیۡ  مِنۡۢ بَعۡدِیۡ ۚ اَعَجِلۡتُمۡ اَمۡرَ رَبِّکُمۡ ۚ وَ اَلۡقَی الۡاَلۡوَاحَ وَ اَخَذَ بِرَاۡسِ اَخِیۡہِ یَجُرُّہٗۤ اِلَیۡہِ ؕ قَالَ ابۡنَ اُمَّ  اِنَّ  الۡقَوۡمَ اسۡتَضۡعَفُوۡنِیۡ  وَ کَادُوۡا یَقۡتُلُوۡنَنِیۡ ۫ۖ فَلَا تُشۡمِتۡ بِیَ الۡاَعۡدَآءَ وَ لَا تَجۡعَلۡنِیۡ مَعَ  الۡقَوۡمِ  الظّٰلِمِیۡنَ ﴿۱۵۰﴾ ",
              urdu_="اور جب موسٰی اپنی قوم میں نہایت غصے اور افسوس کی حالت میں واپس آئے تو کہنے لگے کہ تم نے میرے بعد بہت ہی برا کام کیا۔ کیا تم نے اپنے پروردگار کا حکم جلد چاہا یہ کہا اور شدت غضب سے تورات کی تختیاں ڈالدیں اور اپنے بھائی کا سر پکڑ کر اپنی طرف کھینچنے لگے۔ انہوں نے کہا کہ بھائی جان لوگ تو مجھے کمزور سمجھتے تھے اور قریب تھا کہ قتل کر دیں۔ تو ایسا کام نہ کیجئے کہ دشمن مجھ پر ہنسیں اور مجھے ظالم لوگوں میں شامل نہ سمجھئے۔",
          ),

      Verse(
              id_=151,
              arabic_="قَالَ رَبِّ اغۡفِرۡ لِیۡ وَ لِاَخِیۡ وَ اَدۡخِلۡنَا فِیۡ رَحۡمَتِکَ ۫ۖ وَ اَنۡتَ اَرۡحَمُ الرّٰحِمِیۡنَ ﴿۱۵۱﴾٪",
              urdu_="انہوں نے دعا کی کہ اے پروردگار مجھے اور میرے بھائی کو معاف کر دے اور ہمیں اپنی رحمت میں داخل کر تو سب سے بڑھ کر رحم کرنے والا ہے۔",
          ),

      Verse(
              id_=152,
              arabic_="اِنَّ الَّذِیۡنَ اتَّخَذُوا الۡعِجۡلَ سَیَنَالُہُمۡ غَضَبٌ مِّنۡ رَّبِّہِمۡ وَ ذِلَّۃٌ فِی الۡحَیٰوۃِ الدُّنۡیَا ؕ وَ کَذٰلِکَ نَجۡزِی الۡمُفۡتَرِیۡنَ ﴿۱۵۲﴾ ",
              urdu_="جن لوگوں نے بچھڑے کو معبود بنا لیا تھا ان پر انکے پروردگار کا غضب واقع ہو گا اور دنیا کی زندگی میں ذلت نصیب ہوگی اور ہم افتراپردازوں کو ایسا ہی بدلہ دیا کرتے ہیں۔",
          ),

      Verse(
              id_=153,
              arabic_="وَ الَّذِیۡنَ عَمِلُوا السَّیِّاٰتِ ثُمَّ  تَابُوۡا مِنۡۢ بَعۡدِہَا وَ اٰمَنُوۡۤا ۫ اِنَّ رَبَّکَ مِنۡۢ بَعۡدِہَا لَغَفُوۡرٌ رَّحِیۡمٌ ﴿۱۵۳﴾ ",
              urdu_="اور جنہوں نے برے کام کئے پھر اسکے بعد توبہ کر لی اور ایمان لے آئے۔ تو کچھ شک نہیں کہ تمہارا پروردگار اس کے بعد بخش دے گا کہ وہ بخشنے والا مہربان ہے۔",
          ),

      Verse(
              id_=154,
              arabic_="وَ لَمَّا سَکَتَ عَنۡ  مُّوۡسَی الۡغَضَبُ  اَخَذَ الۡاَلۡوَاحَ ۚۖ وَ فِیۡ نُسۡخَتِہَا ہُدًی وَّ رَحۡمَۃٌ  لِّلَّذِیۡنَ ہُمۡ  لِرَبِّہِمۡ یَرۡہَبُوۡنَ ﴿۱۵۴﴾",
              urdu_="اور جب موسٰی کا غصہ ٹھنڈا ہوا تو تورات کی تختیاں اٹھا لیں۔ اور جو کچھ ان میں لکھا تھا وہ ان لوگوں کے لئے جو اپنے پروردگار سے ڈرتے ہیں ہدایت اور رحمت تھی۔",
          ),

      Verse(
              id_=155,
              arabic_="وَ اخۡتَارَ مُوۡسٰی قَوۡمَہٗ سَبۡعِیۡنَ رَجُلًا لِّمِیۡقَاتِنَا ۚ فَلَمَّاۤ  اَخَذَتۡہُمُ الرَّجۡفَۃُ قَالَ رَبِّ لَوۡ شِئۡتَ اَہۡلَکۡتَہُمۡ مِّنۡ قَبۡلُ وَ اِیَّایَ ؕ اَتُہۡلِکُنَا بِمَا فَعَلَ السُّفَہَآءُ مِنَّا ۚ اِنۡ ہِیَ  اِلَّا فِتۡنَتُکَ ؕ تُضِلُّ بِہَا مَنۡ تَشَآءُ وَ تَہۡدِیۡ مَنۡ تَشَآءُ ؕ اَنۡتَ وَلِیُّنَا فَاغۡفِرۡ لَنَا وَ ارۡحَمۡنَا وَ  اَنۡتَ  خَیۡرُ  الۡغٰفِرِیۡنَ ﴿۱۵۵﴾",
              urdu_="اور موسٰی نے اس معیاد پر جو ہم نے مقرر کی تھی اپنی قوم کے ستر آدمی منتخب کر کے کوہ طور پر حاضر کئے۔ پھر جب انکو زلزلے نے آ پکڑا تو موسٰی نے کہا کہ اے پروردگار اگر تو چاہتا تو ان کو اور مجھ کو پہلے ہی سے ہلاک کر دیتا۔ کیا تو اس فعل کی سزا میں جو ہم میں سے بے عقل لوگوں نے کیا ہے ہمیں ہلاک کر دے گا یہ تو تیری آزمائش ہے اس سے تو جس کو چاہے گمراہ رہنے دے اور جسے چاہے ہدایت بخشے تو ہی ہمارا کارساز ہے سو ہمیں ہمارے گناہ بخش دے اور ہم پر رحم کر اور تو سب سے بہتر بخشنے والا ہے۔",
          ),

      Verse(
              id_=156,
              arabic_="وَ اکۡتُبۡ لَنَا فِیۡ ہٰذِہِ الدُّنۡیَا حَسَنَۃً  وَّ  فِی الۡاٰخِرَۃِ   اِنَّا ہُدۡنَاۤ   اِلَیۡکَ ؕ قَالَ عَذَابِیۡۤ  اُصِیۡبُ  بِہٖ  مَنۡ  اَشَآءُ ۚ وَ رَحۡمَتِیۡ وَسِعَتۡ کُلَّ شَیۡءٍ ؕ فَسَاَکۡتُبُہَا لِلَّذِیۡنَ یَتَّقُوۡنَ وَ یُؤۡتُوۡنَ الزَّکٰوۃَ وَ الَّذِیۡنَ ہُمۡ بِاٰیٰتِنَا یُؤۡمِنُوۡنَ ﴿۱۵۶﴾ۚ ",
              urdu_="اور ہمارے لئے اس دنیا میں بھی بھلائی لکھ دے اور آخرت میں بھی۔ ہم تیری طرف رجوع ہو چکے۔ فرمایا کہ جو میرا عذاب ہے اسے تو جس پر چاہتا ہوں نازل کرتا ہوں اور جو میری رحمت ہے وہ ہر چیز کو شامل ہے۔ میں اسکو ان لوگوں کے لئے لکھ دوں گا جو پرہیزگاری کرتے اور زکوٰۃ دیتے اور ہماری آیتوں پر ایمان رکھتے ہیں۔",
          ),

      Verse(
              id_=157,
              arabic_="اَلَّذِیۡنَ یَتَّبِعُوۡنَ الرَّسُوۡلَ النَّبِیَّ الۡاُمِّیَّ الَّذِیۡ یَجِدُوۡنَہٗ مَکۡتُوۡبًا عِنۡدَہُمۡ فِی التَّوۡرٰٮۃِ وَ الۡاِنۡجِیۡلِ ۫ یَاۡمُرُہُمۡ بِالۡمَعۡرُوۡفِ وَ یَنۡہٰہُمۡ عَنِ الۡمُنۡکَرِ وَ یُحِلُّ لَہُمُ الطَّیِّبٰتِ وَ یُحَرِّمُ عَلَیۡہِمُ الۡخَبٰٓئِثَ وَ یَضَعُ عَنۡہُمۡ اِصۡرَہُمۡ وَ الۡاَغۡلٰلَ الَّتِیۡ کَانَتۡ عَلَیۡہِمۡ ؕ فَالَّذِیۡنَ اٰمَنُوۡا بِہٖ  وَ عَزَّرُوۡہُ وَ نَصَرُوۡہُ  وَ اتَّبَعُوا النُّوۡرَ الَّذِیۡۤ اُنۡزِلَ مَعَہٗۤ ۙ اُولٰٓئِکَ  ہُمُ  الۡمُفۡلِحُوۡنَ ﴿۱۵۷﴾٪ ",
              urdu_="وہ لوگ جو اس رسول یعنی نبی امّی کی پیروی کرتے ہیں جنکے اوصاف کو وہ اپنے ہاں تورات اور انجیل میں لکھا ہوا پاتے ہیں۔ وہ انہیں نیک کام کا حکم دیتے ہیں۔ اور برے کام سے روکتے ہیں۔ اور پاک چیزوں کو انکے لئے حلال کرتے ہیں اور ناپاک چیزوں کو ان پر حرام ٹھیراتے ہیں اور ان پر سے بوجھ اور طوق جو انکے سر پر اور گلے میں تھے اتارتے ہیں۔ تو جو لوگ ان پر ایمان لائے اور ان کی رفاقت کی اور انہیں مدد دی اور جو نور ان کے ساتھ نازل ہوا ہے اسکی پیروی کی وہی مراد پانے والے ہیں۔",
          ),

      Verse(
              id_=158,
              arabic_="قُلۡ یٰۤاَیُّہَا النَّاسُ اِنِّیۡ رَسُوۡلُ اللّٰہِ اِلَیۡکُمۡ جَمِیۡعَۨا الَّذِیۡ لَہٗ  مُلۡکُ السَّمٰوٰتِ  وَ الۡاَرۡضِ ۚ لَاۤ اِلٰہَ  اِلَّا ہُوَ یُحۡیٖ وَ یُمِیۡتُ ۪ فَاٰمِنُوۡا  بِاللّٰہِ وَ رَسُوۡلِہِ النَّبِیِّ  الۡاُمِّیِّ  الَّذِیۡ یُؤۡمِنُ بِاللّٰہِ وَ کَلِمٰتِہٖ وَ اتَّبِعُوۡہُ  لَعَلَّکُمۡ تَہۡتَدُوۡنَ ﴿۱۵۸﴾ ",
              urdu_="اے نبی کہدو کہ لوگو میں تم سب کی طرف اس اللہ کا بھیجا ہوا ہوں جو آسمانوں اور زمین کا بادشاہ ہے۔ اسکے سوا کوئی معبود نہیں وہی زندگانی بخشتا ہے اور وہی موت دیتا ہے۔ تو اللہ پر اور اسکے رسول پیغمبر امی پر جو اللہ پر اور اسکے تمام کلام پر ایمان رکھتے ہیں ایمان لاؤ اور انکی پیروی کرو تاکہ ہدایت پاؤ۔",
          ),

      Verse(
              id_=159,
              arabic_="وَ  مِنۡ قَوۡمِ مُوۡسٰۤی اُمَّۃٌ یَّہۡدُوۡنَ بِالۡحَقِّ وَ بِہٖ  یَعۡدِلُوۡنَ ﴿۱۵۹﴾ ",
              urdu_="اور قوم موسٰی میں کچھ لوگ ایسے بھی ہیں جو حق کا رستہ بتاتے ہیں اور اسی کے مطابق انصاف کرتے ہیں۔",
          ),

      Verse(
              id_=160,
              arabic_="وَ قَطَّعۡنٰہُمُ اثۡنَتَیۡ عَشۡرَۃَ  اَسۡبَاطًا اُمَمًا ؕ وَ  اَوۡحَیۡنَاۤ  اِلٰی مُوۡسٰۤی  اِذِ  اسۡتَسۡقٰٮہُ قَوۡمُہٗۤ  اَنِ اضۡرِبۡ بِّعَصَاکَ الۡحَجَرَ ۚ فَانۡۢبَجَسَتۡ مِنۡہُ اثۡنَتَا عَشۡرَۃَ عَیۡنًا ؕ قَدۡ عَلِمَ کُلُّ اُنَاسٍ مَّشۡرَبَہُمۡ ؕ وَ ظَلَّلۡنَا عَلَیۡہِمُ الۡغَمَامَ وَ اَنۡزَلۡنَا عَلَیۡہِمُ الۡمَنَّ وَ السَّلۡوٰی ؕ کُلُوۡا مِنۡ طَیِّبٰتِ مَا رَزَقۡنٰکُمۡ ؕ وَ مَا ظَلَمُوۡنَا وَ لٰکِنۡ  کَانُوۡۤا  اَنۡفُسَہُمۡ یَظۡلِمُوۡنَ ﴿۱۶۰﴾ ",
              urdu_="اور ہم نے انکو یعنی بنی اسرائیل کو الگ الگ کر کے بارہ قبیلے اور بڑی بڑی جماعتیں بنا دیا اور جب موسٰی سے انکی قوم نے پانی طلب کیا تو ہم نے ان کی طرف وحی بھیجی کہ اپنی لاٹھی پتھر پر مار دو۔ تو اس میں سے بارہ چشمے پھوٹ نکلے اور سب لوگوں نے اپنا اپنا گھاٹ معلوم کر لیا۔ اور ہم نے ان کے سروں پر بادل کو سائبان بنائے رکھا اور ان پر من و سلوٰی اتارتے رہے۔ اور ان سے کہا کہ جو پاکیزہ چیزیں ہم تمہیں دیتے ہیں انہیں کھاؤ۔ اور ان لوگوں نے ہمارا کچھ نقصان نہیں کیا بلکہ جو نقصان کیا اپنا ہی کیا۔",
          ),

      Verse(
              id_=161,
              arabic_="وَ اِذۡ قِیۡلَ لَہُمُ اسۡکُنُوۡا ہٰذِہِ الۡقَرۡیَۃَ وَ کُلُوۡا مِنۡہَا حَیۡثُ شِئۡتُمۡ وَ قُوۡلُوۡا حِطَّۃٌ وَّ ادۡخُلُوا الۡبَابَ سُجَّدًا نَّغۡفِرۡ لَکُمۡ خَطِیۡٓـٰٔتِکُمۡ ؕ سَنَزِیۡدُ الۡمُحۡسِنِیۡنَ ﴿۱۶۱﴾ ",
              urdu_="اور یاد کرو جب ان سے کہا گیا کہ اس شہر میں سکونت اختیار کر لو اور اس میں جہاں سے جی چاہے کھانا پینا اور ہاں شہر میں جانا تو حِطَّۃٌ کہنا اور دروازے میں داخل ہونا تو سجدہ کرتے ہوئے۔ تو ہم تمہارے گناہ معاف کردیں گے اور نیکی کرنے والوں کو اور زیادہ دیں گے۔",
          ),

      Verse(
              id_=162,
              arabic_="فَبَدَّلَ الَّذِیۡنَ ظَلَمُوۡا مِنۡہُمۡ  قَوۡلًا غَیۡرَ الَّذِیۡ قِیۡلَ لَہُمۡ فَاَرۡسَلۡنَا عَلَیۡہِمۡ رِجۡزًا مِّنَ السَّمَآءِ بِمَا کَانُوۡا یَظۡلِمُوۡنَ ﴿۱۶۲﴾٪ ",
              urdu_="مگر جو ان میں ظالم تھے انہوں نے اس لفظ کو جس کا انکو حکم دیا گیا تھا بدل کر اسکی جگہ اور لفظ کہنا شروع کیا تو ہم نے ان پر آسمان سے عذاب بھیجا اس لئے کہ وہ ظلم کرتے تھے۔",
          ),

      Verse(
              id_=163,
              arabic_="وَ سۡـَٔلۡہُمۡ عَنِ الۡقَرۡیَۃِ  الَّتِیۡ کَانَتۡ حَاضِرَۃَ  الۡبَحۡرِ ۘ اِذۡ یَعۡدُوۡنَ فِی السَّبۡتِ اِذۡ تَاۡتِیۡہِمۡ حِیۡتَانُہُمۡ یَوۡمَ سَبۡتِہِمۡ شُرَّعًا وَّ یَوۡمَ لَا یَسۡبِتُوۡنَ ۙ لَا  تَاۡتِیۡہِمۡ ۚۛ کَذٰلِکَ ۚۛ نَبۡلُوۡہُمۡ بِمَا کَانُوۡا یَفۡسُقُوۡنَ ﴿۱۶۳﴾ ",
              urdu_="اور ان سے اس گاؤں کا حال تو پوچھو جو لب دریا واقع تھا۔ جب وہ لوگ ہفتے کے دن کے بارے میں حد سے تجاوز کرنے لگے جبکہ انکے تعطیل کے دن یعنی سنیچر کو مچھلیاں انکے سامنے پانی کے اوپر آتیں اور جب ہفتے کا دن نہ ہوتا تو نہ آتیں۔ اسی طرح ہم ان لوگوں کو انکی نافرمانیوں کے سبب آزمائش میں ڈالنے لگے۔",
          ),

      Verse(
              id_=164,
              arabic_="وَ اِذۡ قَالَتۡ اُمَّۃٌ مِّنۡہُمۡ لِمَ تَعِظُوۡنَ قَوۡمَۨا ۙ اللّٰہُ مُہۡلِکُہُمۡ اَوۡ مُعَذِّبُہُمۡ عَذَابًا شَدِیۡدًا ؕ قَالُوۡا مَعۡذِرَۃً  اِلٰی رَبِّکُمۡ  وَ لَعَلَّہُمۡ  یَتَّقُوۡنَ ﴿۱۶۴﴾ ",
              urdu_="اور جب ان میں سے ایک جماعت نے کہا کہ تم ایسے لوگوں کو کیوں نصیحت کرتے ہو جنکو اللہ ہلاک کرنے والا یا سخت عذاب دینے والا ہے تو انہوں نے کہا اس لئے کہ تمہارے پروردگار کے سامنے معذرت کر سکیں اور عجب نہیں کہ وہ پرہیزگاری اختیار کریں۔",
          ),

      Verse(
              id_=165,
              arabic_="فَلَمَّا نَسُوۡا مَا ذُکِّرُوۡا بِہٖۤ اَنۡجَیۡنَا الَّذِیۡنَ یَنۡہَوۡنَ عَنِ السُّوۡٓءِ وَ اَخَذۡنَا الَّذِیۡنَ ظَلَمُوۡا بِعَذَابٍۭ بَئِیۡسٍۭ بِمَا کَانُوۡا  یَفۡسُقُوۡنَ ﴿۱۶۵﴾ ",
              urdu_="پھر جب انہوں نے ان باتوں کر فراموش کر دیا جنکی انکو نصیحت کی گئ تھی تو جو لوگ برائی سے منع کرتے تھے انکو ہم نے نجات دی اور جو ظلم کرتے تھے انکو برے عذاب میں پکڑ لیا کہ نافرمانی کئے جاتے تھے۔",
          ),

      Verse(
              id_=166,
              arabic_="فَلَمَّا عَتَوۡا عَنۡ مَّا نُہُوۡا عَنۡہُ قُلۡنَا لَہُمۡ   کُوۡنُوۡا  قِرَدَۃً  خٰسِئِیۡنَ ﴿۱۶۶﴾ ",
              urdu_="غرض جن اعمال بد سے انکو منع کیا گیا تھا جب وہ ان پر اصرار اور سرکشی کرنے لگے تو ہم نے انکو حکم دیا کہ ذلیل بندر ہو جاؤ۔",
          ),

      Verse(
              id_=167,
              arabic_="وَ اِذۡ تَاَذَّنَ رَبُّکَ لَیَبۡعَثَنَّ عَلَیۡہِمۡ اِلٰی یَوۡمِ الۡقِیٰمَۃِ مَنۡ یَّسُوۡمُہُمۡ سُوۡٓءَ الۡعَذَابِ ؕ اِنَّ  رَبَّکَ  لَسَرِیۡعُ  الۡعِقَابِ ۚۖ وَ  اِنَّہٗ  لَغَفُوۡرٌ  رَّحِیۡمٌ ﴿۱۶۷﴾ ",
              urdu_="اور اس وقت کو یاد کرو جب تمہارے پروردگار نے یہود کو آگاہ کر دیا تھا کہ وہ ان پر قیامت تک ایسے شخص کو مسلط رکھے گا جو انکو بری بری تکلیفیں دیتا رہے۔ بیشک تمہارا پروردگار جلد عذاب کرنے والا ہے اور وہ بخشنے والا مہربان بھی ہے۔",
          ),

      Verse(
              id_=168,
              arabic_="وَ قَطَّعۡنٰہُمۡ فِی الۡاَرۡضِ اُمَمًا ۚ مِنۡہُمُ الصّٰلِحُوۡنَ وَ مِنۡہُمۡ دُوۡنَ ذٰلِکَ ۫ وَ بَلَوۡنٰہُمۡ بِالۡحَسَنٰتِ وَ السَّیِّاٰتِ لَعَلَّہُمۡ یَرۡجِعُوۡنَ ﴿۱۶۸﴾ ",
              urdu_="اور ہم نے انکو جماعت جماعت کر کے ملک میں منتشر کر دیا۔ بعض ان میں سے نیکوکار ہیں اور بعض اور طرح کے یعنی بدکار اور ہم آسائشوں اور تکلیفوں دونوں سے انکی آزمائش کرتے رہے تاکہ ہماری طرف رجوع کریں۔",
          ),

      Verse(
              id_=169,
              arabic_="فَخَلَفَ مِنۡۢ بَعۡدِہِمۡ خَلۡفٌ وَّرِثُوا الۡکِتٰبَ یَاۡخُذُوۡنَ عَرَضَ ہٰذَا الۡاَدۡنٰی وَ یَقُوۡلُوۡنَ سَیُغۡفَرُ لَنَا ۚ وَ اِنۡ یَّاۡتِہِمۡ عَرَضٌ مِّثۡلُہٗ یَاۡخُذُوۡہُ ؕ اَلَمۡ یُؤۡخَذۡ عَلَیۡہِمۡ مِّیۡثَاقُ الۡکِتٰبِ اَنۡ لَّا یَقُوۡلُوۡا عَلَی اللّٰہِ  اِلَّا الۡحَقَّ وَ دَرَسُوۡا مَا فِیۡہِ  ؕ وَ الدَّارُ  الۡاٰخِرَۃُ  خَیۡرٌ لِّلَّذِیۡنَ  یَتَّقُوۡنَ ؕ اَفَلَا  تَعۡقِلُوۡنَ ﴿۱۶۹﴾ ",
              urdu_="پھر انکے بعد ناخلف انکے قائم مقام ہوئے جو کتاب کے وارث بنے یہ لوگ اس حقیر دنیا کا مال و متاع لے لیتے ہیں اور کہتے ہیں کہ ہم بخش دیئے جائیں گے۔ اور اگر انکے سامنے بھی ویسا ہی مال آ جاتا ہے تو وہ بھی اسے لے لیتے ہیں۔ کیا ان سے کتاب کی نسبت عہد نہیں لیا گیا تھا کہ اللہ پر سچ کے سوا اور کچھ نہیں کہیں گے۔ اور جو کچھ اس کتاب میں ہے اسکو انہوں نے پڑھ بھی لیا ہے۔ اور آخرت کا گھر پرہیزگاروں کے لئے بہتر ہے کیا تم سمجھتے نہیں۔",
          ),

      Verse(
              id_=170,
              arabic_="وَ الَّذِیۡنَ یُمَسِّکُوۡنَ بِالۡکِتٰبِ وَ اَقَامُوا الصَّلٰوۃَ ؕ اِنَّا لَا نُضِیۡعُ اَجۡرَ الۡمُصۡلِحِیۡنَ ﴿۱۷۰﴾ ",
              urdu_="اور جو لوگ کتاب کو مضبوط پکڑے ہوئے ہیں اور نماز کا اہتمام کرتے ہیں انکو ہم اجر دیں گے کہ ہم نیکوکاروں کا اجر ضائع نہیں کرتے۔",
          ),

      Verse(
              id_=171,
              arabic_="وَ اِذۡ نَتَقۡنَا الۡجَبَلَ فَوۡقَہُمۡ کَاَنَّہٗ ظُلَّۃٌ وَّ ظَنُّوۡۤا اَنَّہٗ وَاقِعٌۢ بِہِمۡ ۚ خُذُوۡا مَاۤ اٰتَیۡنٰکُمۡ بِقُوَّۃٍ وَّ اذۡکُرُوۡا مَا فِیۡہِ لَعَلَّکُمۡ  تَتَّقُوۡنَ ﴿۱۷۱﴾٪ ",
              urdu_="اور جب ہم نے ان کے سروں پر پہاڑ اٹھا کھڑا کیا گویا وہ سائبان تھا اور انہوں نے خیال کیا کہ وہ ان پر گرتا ہے۔ تو ہم نے کہا کہ جو ہم نے تمہیں دیا ہے اسے زور سے پکڑے رہو اور جو اس میں لکھا ہے اسے یاد رکھو تاکہ پرہیزگار بن جاؤ۔",
          ),

      Verse(
              id_=172,
              arabic_="وَ اِذۡ اَخَذَ رَبُّکَ مِنۡۢ بَنِیۡۤ اٰدَمَ مِنۡ ظُہُوۡرِہِمۡ ذُرِّیَّتَہُمۡ وَ اَشۡہَدَہُمۡ عَلٰۤی اَنۡفُسِہِمۡ ۚ اَلَسۡتُ بِرَبِّکُمۡ ؕ قَالُوۡا بَلٰی ۚۛ شَہِدۡنَا ۚۛ اَنۡ تَقُوۡلُوۡا یَوۡمَ  الۡقِیٰمَۃِ  اِنَّا کُنَّا عَنۡ  ہٰذَا غٰفِلِیۡنَ ﴿۱۷۲﴾ۙ ",
              urdu_="اور جب تمہارے پروردگار نے بنی آدم سے یعنی انکی پیٹھوں سے انکی اولاد نکالی تو ان سے خود انکے مقابلے میں اقرار کرا لیا یعنی ان سے پوچھا کیا میں تمہارا پروردگار نہیں ہوں۔ وہ کہنے لگے کیوں نہیں ہم گواہ ہیں کہ تو ہمارا پروردگار ہے یہ اقرار اس لئے کرایا تھا کہ قیامت کے دن کہیں یوں نہ کہنے لگو کہ ہم کو تو اسکی خبر ہی نہ تھی۔",
          ),

      Verse(
              id_=173,
              arabic_="اَوۡ تَقُوۡلُوۡۤا  اِنَّمَاۤ  اَشۡرَکَ  اٰبَآؤُنَا مِنۡ  قَبۡلُ وَ کُنَّا ذُرِّیَّۃً مِّنۡۢ بَعۡدِہِمۡ ۚ اَفَتُہۡلِکُنَا بِمَا فَعَلَ الۡمُبۡطِلُوۡنَ ﴿۱۷۳﴾ ",
              urdu_="یا یہ نہ کہو کہ شرک تو پہلے ہمارے بڑوں نے کیا تھا اور ہم تو انکی اولاد تھے جو ان کے بعد پیدا ہوئے تو کیا جو کام اہل باطل کرتے رہے اسکے بدلے تو ہمیں ہلاک کرتا ہے؟",
          ),

      Verse(
              id_=174,
              arabic_="وَ کَذٰلِکَ نُفَصِّلُ الۡاٰیٰتِ وَ لَعَلَّہُمۡ یَرۡجِعُوۡنَ ﴿۱۷۴﴾ ",
              urdu_="اور اسی طرح ہم اپنی آیتیں کھول کھول کر بیان کرتے ہیں تاکہ یہ رجوع کریں۔",
          ),

      Verse(
              id_=175,
              arabic_="وَ اتۡلُ عَلَیۡہِمۡ  نَبَاَ الَّذِیۡۤ  اٰتَیۡنٰہُ  اٰیٰتِنَا فَانۡسَلَخَ مِنۡہَا فَاَتۡبَعَہُ الشَّیۡطٰنُ فَکَانَ مِنَ  الۡغٰوِیۡنَ ﴿۱۷۵﴾ ",
              urdu_="اور انکو اس شخص کا حال پڑھ کر سنا دو جسکو ہم نے اپنی آیتیں عطا فرمائیں اور اسکو علم کے لبادے سے آراستہ کیا تو اس نے اسکو اتار پھینکا پھر شیطان اسکے پیچھے لگا تو وہ گمراہوں میں ہو گیا۔",
          ),

      Verse(
              id_=176,
              arabic_="وَ لَوۡ شِئۡنَا لَرَفَعۡنٰہُ بِہَا وَ لٰکِنَّہٗۤ اَخۡلَدَ اِلَی الۡاَرۡضِ وَ اتَّبَعَ ہَوٰٮہُ ۚ فَمَثَلُہٗ  کَمَثَلِ الۡکَلۡبِ ۚ اِنۡ  تَحۡمِلۡ عَلَیۡہِ یَلۡہَثۡ اَوۡ تَتۡرُکۡہُ یَلۡہَثۡ ؕ ذٰلِکَ مَثَلُ الۡقَوۡمِ الَّذِیۡنَ کَذَّبُوۡا بِاٰیٰتِنَا ۚ فَاقۡصُصِ الۡقَصَصَ لَعَلَّہُمۡ یَتَفَکَّرُوۡنَ ﴿۱۷۶﴾ ",
              urdu_="اور اگر ہم چاہتے تو ان آیتوں سے اس کے درجے کو بلند کر دیتے۔ مگر وہ تو پستی کی طرف مائل ہو گیا اور اپنی خواہش کے پیچھے چل پڑا۔ تو اسکی مثال کتے کی سی ہو گئ کہ اگر اس پر حملہ کرو زبان نکالے رہے یا اسے یونہی چھوڑ دو تو بھی زبان نکالے رہے۔ یہی مثال ان لوگوں کی ہے جنہوں نے ہماری آیتوں کو جھٹلایا تو ان سے یہ قصہ بیان کر دو۔ تاکہ وہ فکر کریں۔",
          ),

      Verse(
              id_=177,
              arabic_="سَآءَ مَثَلَاۨ الۡقَوۡمُ الَّذِیۡنَ کَذَّبُوۡا بِاٰیٰتِنَا وَ اَنۡفُسَہُمۡ کَانُوۡا یَظۡلِمُوۡنَ ﴿۱۷۷﴾ ",
              urdu_="جن لوگوں نے ہماری آیتوں کی تکذیب کی انکی مثال بری ہے اور انہوں نے نقصان کیا تو اپنا ہی کیا۔",
          ),

      Verse(
              id_=178,
              arabic_="مَنۡ یَّہۡدِ اللّٰہُ فَہُوَ الۡمُہۡتَدِیۡ ۚ وَ مَنۡ یُّضۡلِلۡ  فَاُولٰٓئِکَ  ہُمُ  الۡخٰسِرُوۡنَ ﴿۱۷۸﴾ ",
              urdu_="جسکو اللہ ہدایت دے وہی راہ یاب ہے۔ اور جسکو گمراہ رہنے دے تو ایسے ہی لوگ نقصان اٹھانے والے ہیں۔",
          ),

      Verse(
              id_=179,
              arabic_="وَ لَقَدۡ ذَرَاۡنَا لِجَہَنَّمَ کَثِیۡرًا مِّنَ الۡجِنِّ وَ الۡاِنۡسِ ۫ۖ  لَہُمۡ قُلُوۡبٌ لَّا یَفۡقَہُوۡنَ بِہَا ۫ وَ لَہُمۡ اَعۡیُنٌ لَّا یُبۡصِرُوۡنَ بِہَا ۫ وَ لَہُمۡ اٰذَانٌ لَّا یَسۡمَعُوۡنَ بِہَا ؕ اُولٰٓئِکَ کَالۡاَنۡعَامِ بَلۡ ہُمۡ اَضَلُّ ؕ اُولٰٓئِکَ ہُمُ الۡغٰفِلُوۡنَ ﴿۱۷۹﴾ ",
              urdu_="اور ہم نے بہت سے جن اور انسان دوزخ کے لئے پیدا کئے ہیں۔ انکے دل ہیں لیکن ان سے سمجھتے نہیں اور انکی آنکھیں ہیں مگر ان سے دیکھتے نہیں۔ اور ان کے کان ہیں پر ان سے سنتے نہیں۔ یہ لوگ بالکل چوپایوں کی طرح ہیں بلکہ ان سے بھی زیادہ بھٹکے ہوئے۔ یہی وہ ہیں جو غفلت میں پڑے ہوئے ہیں۔",
          ),

      Verse(
              id_=180,
              arabic_="وَ لِلّٰہِ الۡاَسۡمَآءُ الۡحُسۡنٰی فَادۡعُوۡہُ بِہَا ۪ وَ ذَرُوا الَّذِیۡنَ یُلۡحِدُوۡنَ فِیۡۤ  اَسۡمَآئِہٖ ؕ سَیُجۡزَوۡنَ مَا کَانُوۡا یَعۡمَلُوۡنَ ﴿۱۸۰﴾",
              urdu_="اور اللہ کے سب نام اچھے ہی اچھے ہیں تو اسکو انہی ناموں سے پکارا کرو اور جو لوگ اسکے ناموں میں کجی اختیار کرتے ہیں انکو چھوڑ دو۔ وہ جو کچھ کر رہے ہیں عنقریب اسکی سزا پائیں گے۔",
          ),

      Verse(
              id_=181,
              arabic_="وَ مِمَّنۡ خَلَقۡنَاۤ  اُمَّۃٌ یَّہۡدُوۡنَ بِالۡحَقِّ وَ بِہٖ  یَعۡدِلُوۡنَ ﴿۱۸۱﴾٪ ",
              urdu_="اور ہماری مخلوقات میں سے وہ لوگ بھی ہیں جو حق کا رستہ بتاتے ہیں اور اسی کے مطابق انصاف کرتے ہیں۔",
          ),

      Verse(
              id_=182,
              arabic_="وَ الَّذِیۡنَ کَذَّبُوۡا بِاٰیٰتِنَا سَنَسۡتَدۡرِجُہُمۡ مِّنۡ حَیۡثُ لَا  یَعۡلَمُوۡنَ ﴿۱۸۲﴾ۚۖ ",
              urdu_="اور جن لوگوں نے ہماری آیتوں کو جھٹلایا انکو ہم بتدریج اس طرح پکڑیں گے کہ انکو معلوم ہی نہیں ہو گا۔",
          ),

      Verse(
              id_=183,
              arabic_="وَ  اُمۡلِیۡ   لَہُمۡ ؕ۟ اِنَّ  کَیۡدِیۡ مَتِیۡنٌ ﴿۱۸۳﴾ ",
              urdu_="اور میں انکو مہلت دیئے جاتا ہوں۔ میری تدبیر بڑی مضبوط ہے۔",
          ),

      Verse(
              id_=184,
              arabic_="اَوَ لَمۡ  یَتَفَکَّرُوۡا ٜ مَا بِصَاحِبِہِمۡ مِّنۡ جِنَّۃٍ ؕ اِنۡ ہُوَ  اِلَّا  نَذِیۡرٌ  مُّبِیۡنٌ  ﴿۱۸۴﴾",
              urdu_="کیا انہوں نے غور نہیں کیا کہ انکے رفیق یعنی پیغمبر کو کسی طرح کا بھی جنون نہیں ہے۔ وہ تو صاف صاف خبردار کرنے والے ہیں۔",
          ),

      Verse(
              id_=185,
              arabic_="اَوَ لَمۡ یَنۡظُرُوۡا فِیۡ مَلَکُوۡتِ السَّمٰوٰتِ وَ الۡاَرۡضِ وَ مَا خَلَقَ اللّٰہُ مِنۡ شَیۡءٍ ۙ وَّ اَنۡ عَسٰۤی اَنۡ یَّکُوۡنَ قَدِ اقۡتَرَبَ اَجَلُہُمۡ ۚ فَبِاَیِّ  حَدِیۡثٍۭ  بَعۡدَہٗ  یُؤۡمِنُوۡنَ ﴿۱۸۵﴾ ",
              urdu_="کیا انہوں نے آسمان اور زمین کی بادشاہت میں اور جو چیزیں اللہ نے پیدا کی ہیں ان پر نظر نہیں کی۔ اور اس بات پر خیال نہیں کیا کہ عجب نہیں کہ انکی موت کا وقت نزدیک پہنچ گیا ہو۔ تو اسکے بعد وہ اور کس بات پر ایمان لائیں گے؟",
          ),

      Verse(
              id_=186,
              arabic_="مَنۡ یُّضۡلِلِ اللّٰہُ فَلَا ہَادِیَ لَہٗ  ؕ وَ یَذَرُہُمۡ  فِیۡ  طُغۡیَانِہِمۡ  یَعۡمَہُوۡنَ ﴿۱۸۶﴾ ",
              urdu_="جس شخص کو اللہ گمراہ رہنے دے اسکو کوئی ہدایت دینے والا نہیں۔ اور وہ ان گمراہوں کو چھوڑے رکھتا ہے کہ اپنی سرکشی میں پڑے بہکتے رہیں۔",
          ),

      Verse(
              id_=187,
              arabic_="یَسۡـَٔلُوۡنَکَ عَنِ السَّاعَۃِ اَیَّانَ مُرۡسٰہَا ؕ قُلۡ اِنَّمَا عِلۡمُہَا عِنۡدَ رَبِّیۡ ۚ لَا یُجَلِّیۡہَا لِوَقۡتِہَاۤ اِلَّا ہُوَ ؕۘؔ ثَقُلَتۡ فِی السَّمٰوٰتِ وَ الۡاَرۡضِ ؕ لَا تَاۡتِیۡکُمۡ  اِلَّا بَغۡتَۃً ؕ یَسۡـَٔلُوۡنَکَ کَاَنَّکَ حَفِیٌّ عَنۡہَا ؕ قُلۡ اِنَّمَا عِلۡمُہَا عِنۡدَ اللّٰہِ وَ لٰکِنَّ اَکۡثَرَ  النَّاسِ لَا  یَعۡلَمُوۡنَ ﴿۱۸۷﴾",
              urdu_="یہ لوگ تم سے قیامت کے بارے میں پوچھتے ہیں کہ اسکے واقع ہونے کا وقت کب ہے کہدو کہ اس کا علم تو میرے پروردگار ہی کو ہے۔ وہی اسے اسکے وقت پر ظاہر کر دے گا۔ وہ آسمان اور زمین میں ایک بھاری بات ہوگی۔ اور ناگہاں تم پر آ جائے گی۔ یہ تم سے اس طرح دریافت کرتے ہیں کہ گویا تم اس سے بخوبی واقف ہو۔ کہو کہ اس کا علم تو اللہ ہی کو ہے لیکن اکثر لوگ یہ نہیں جانتے۔",
          ),

      Verse(
              id_=188,
              arabic_="قُلۡ لَّاۤ  اَمۡلِکُ  لِنَفۡسِیۡ  نَفۡعًا وَّ لَا  ضَرًّا  اِلَّا مَا شَآءَ اللّٰہُ ؕ وَ لَوۡ کُنۡتُ اَعۡلَمُ الۡغَیۡبَ لَاسۡتَکۡثَرۡتُ مِنَ الۡخَیۡرِۚۖۛ وَ مَا مَسَّنِیَ  السُّوۡٓءُ ۚۛ اِنۡ  اَنَا  اِلَّا  نَذِیۡرٌ وَّ بَشِیۡرٌ  لِّقَوۡمٍ  یُّؤۡمِنُوۡنَ ﴿۱۸۸﴾٪ ",
              urdu_="کہدو کہ میں اپنے فائدے اور نقصان کا کچھ بھی اختیار نہیں رکھتا مگر وہ جو اللہ چاہے اور اگر میں غیب کی باتیں جانتا ہوتا تو بہت سے فائدے جمع کر لیتا اور مجھ کو کوئی تکلیف نہ پہنچتی۔ میں تو مومنوں کو ڈر اور خوشخبری سنانے والا ہوں۔",
          ),

      Verse(
              id_=189,
              arabic_="ہُوَ الَّذِیۡ خَلَقَکُمۡ مِّنۡ نَّفۡسٍ وَّاحِدَۃٍ وَّ جَعَلَ مِنۡہَا زَوۡجَہَا لِیَسۡکُنَ اِلَیۡہَا ۚ فَلَمَّا تَغَشّٰہَا حَمَلَتۡ حَمۡلًا خَفِیۡفًا فَمَرَّتۡ بِہٖ ۚ فَلَمَّاۤ  اَثۡقَلَتۡ دَّعَوَا اللّٰہَ رَبَّہُمَا لَئِنۡ اٰتَیۡتَنَا صَالِحًا  لَّنَکُوۡنَنَّ  مِنَ  الشّٰکِرِیۡنَ ﴿۱۸۹﴾ ",
              urdu_="وہ اللہ ہی تو ہے جس نے تم کو ایک شخص سے پیدا کیا۔ اور اس سے اس کا جوڑا بنایا تاکہ اس سے راحت حاصل کرے سو جب وہ اسکے پاس جاتا ہے تو اسے ہلکا سا حمل رہ جاتا ہے اور وہ اسکے ساتھ چلتی پھرتی ہے۔ پھر جب وہ بوجھ محسوس کرتی ہے یعنی بچہ پیٹ میں بڑا ہوتا ہے تو دونوں میاں بیوی اپنے پروردگار اللہ سے التجا کرتے ہیں کہ اگر تو ہمیں صحیح و سالم بچہ دے گا تو ہم تیرے شکرگذار ہوں گے۔",
          ),

      Verse(
              id_=190,
              arabic_="فَلَمَّاۤ  اٰتٰہُمَا صَالِحًا جَعَلَا لَہٗ  شُرَکَآءَ فِیۡمَاۤ  اٰتٰہُمَا ۚ فَتَعٰلَی اللّٰہُ عَمَّا یُشۡرِکُوۡنَ ﴿۱۹۰﴾ ",
              urdu_="پھر جب وہ انکو صحیح و سالم بچہ دیتا ہے تو اس بچے میں جو وہ ان کو دیتا ہے اس کا شریک مقرر کرتے ہیں۔ سو جو شرک وہ کرتے ہیں اللہ کا رتبہ اس سے بلند ہے۔",
          ),

      Verse(
              id_=191,
              arabic_="اَیُشۡرِکُوۡنَ مَا لَا یَخۡلُقُ شَیۡئًا وَّ ہُمۡ یُخۡلَقُوۡنَ ﴿۱۹۱﴾۫ۖ ",
              urdu_="کیا وہ ایسوں کو شریک بناتے ہیں جو کچھ بھی پیدا نہیں کر سکتے اور خود پیدا کئے جاتے ہیں۔",
          ),

      Verse(
              id_=192,
              arabic_="وَ لَا یَسۡتَطِیۡعُوۡنَ لَہُمۡ نَصۡرًا وَّ لَاۤ اَنۡفُسَہُمۡ  یَنۡصُرُوۡنَ  ﴿۱۹۲﴾",
              urdu_="اور نہ انکی مدد کی طاقت رکھتے ہیں اور نہ اپنی ہی مدد کر سکتے ہیں۔",
          ),

      Verse(
              id_=193,
              arabic_="وَ اِنۡ تَدۡعُوۡہُمۡ اِلَی الۡہُدٰی لَا یَتَّبِعُوۡکُمۡ ؕ سَوَآءٌ عَلَیۡکُمۡ اَدَعَوۡتُمُوۡہُمۡ  اَمۡ  اَنۡتُمۡ  صَامِتُوۡنَ ﴿۱۹۳﴾ ",
              urdu_="اور اگر تم انکو سیدھے رستے کی طرف بلاؤ تو تمہارا کہا نہ مانیں۔ تمہارے لئے برابر ہے کہ تم انکو بلاؤ یا چپکے ہو رہو۔",
          ),

      Verse(
              id_=194,
              arabic_="اِنَّ الَّذِیۡنَ تَدۡعُوۡنَ مِنۡ دُوۡنِ اللّٰہِ عِبَادٌ اَمۡثَالُکُمۡ فَادۡعُوۡہُمۡ فَلۡیَسۡتَجِیۡبُوۡا لَکُمۡ  اِنۡ کُنۡتُمۡ صٰدِقِیۡنَ ﴿۱۹۴﴾ ",
              urdu_="مشرکو جنکو تم اللہ کے سوا پکارتے ہو تمہاری طرح کے بندے ہی ہیں اچھا تم انکو پکارو اگر سچے ہو تو چاہیے کہ وہ تم کو جواب بھی دیں۔",
          ),

      Verse(
              id_=195,
              arabic_="اَلَہُمۡ  اَرۡجُلٌ  یَّمۡشُوۡنَ  بِہَاۤ  ۫ اَمۡ  لَہُمۡ  اَیۡدٍ یَّبۡطِشُوۡنَ بِہَاۤ  ۫ اَمۡ  لَہُمۡ اَعۡیُنٌ یُّبۡصِرُوۡنَ  بِہَاۤ  ۫ اَمۡ  لَہُمۡ اٰذَانٌ یَّسۡمَعُوۡنَ بِہَا ؕ قُلِ ادۡعُوۡا شُرَکَآءَکُمۡ ثُمَّ   کِیۡدُوۡنِ  فَلَا  تُنۡظِرُوۡنِ ﴿۱۹۵﴾ ",
              urdu_="بھلا انکے پاؤں ہیں جن سے چلیں یا ہاتھ ہیں جن سے پکڑیں یا آنکھیں ہیں جن سے دیکھیں یا کان ہیں جن سے سنیں؟ کہدو کہ اپنے شریکوں کو بلا لو اور میرے بارے میں جو تدبیر کرنی ہو کر لو اور مجھے کچھ مہلت بھی نہ دو پھر دیکھو کہ وہ میرا کیا کر سکتے ہیں۔",
          ),

      Verse(
              id_=196,
              arabic_="اِنَّ  وَلِیِّ ۦَ اللّٰہُ الَّذِیۡ نَزَّلَ الۡکِتٰبَ ۫ۖ وَ ہُوَ  یَتَوَلَّی  الصّٰلِحِیۡنَ ﴿۱۹۶﴾ ",
              urdu_="میرا مددگار تو اللہ ہی ہے جس نے کتاب برحق نازل کی اور وہ نیک لوگوں کو دوست رکھتا ہے۔",
          ),

      Verse(
              id_=197,
              arabic_="وَ الَّذِیۡنَ تَدۡعُوۡنَ مِنۡ دُوۡنِہٖ لَا یَسۡتَطِیۡعُوۡنَ نَصۡرَکُمۡ وَ لَاۤ  اَنۡفُسَہُمۡ یَنۡصُرُوۡنَ ﴿۱۹۷﴾ ",
              urdu_="اور جنکو تم اللہ کے سوا پکارتے ہو وہ نہ تمہاری مدد کی طاقت رکھتے ہیں اور نہ خود اپنی ہی مدد کر سکتے ہیں۔",
          ),

      Verse(
              id_=198,
              arabic_="وَ  اِنۡ تَدۡعُوۡہُمۡ  اِلَی الۡہُدٰی لَا یَسۡمَعُوۡا ؕ وَ تَرٰٮہُمۡ یَنۡظُرُوۡنَ  اِلَیۡکَ وَ ہُمۡ لَا یُبۡصِرُوۡنَ ﴿۱۹۸﴾ ",
              urdu_="اور اگر تم انکو سیدھے رستے کی طرف بلاؤ تو سن نہ سکیں اور تم انہیں دیکھتے ہو کہ بظاہر آنکھیں کھولے تمہاری طرف دیکھ رہے ہیں مگر فی الواقع کچھ نہیں دیکھتے۔",
          ),

      Verse(
              id_=199,
              arabic_="خُذِ الۡعَفۡوَ وَ اۡمُرۡ بِالۡعُرۡفِ وَ اَعۡرِضۡ عَنِ  الۡجٰہِلِیۡنَ ﴿۱۹۹﴾ ",
              urdu_="اے نبی عفو و درگذر اختیار کرو اور نیک کام کرنے کا حکم دو اور جاہلوں سے کنارہ کر لو۔",
          ),

      Verse(
              id_=200,
              arabic_="وَ اِمَّا یَنۡزَغَنَّکَ مِنَ الشَّیۡطٰنِ نَزۡغٌ فَاسۡتَعِذۡ  بِاللّٰہِ ؕ اِنَّہٗ  سَمِیۡعٌ  عَلِیۡمٌ ﴿۲۰۰﴾ ",
              urdu_="اور اگر شیطان کی طرف سے تمہارے دل میں کسی طرح کا وسوسہ پیدا ہو تو اللہ سے پناہ مانگو۔ بیشک وہ سننے والا ہے سب کچھ جاننے والا ہے۔",
          ),

      Verse(
              id_=201,
              arabic_="اِنَّ الَّذِیۡنَ اتَّقَوۡا اِذَا مَسَّہُمۡ طٰٓئِفٌ مِّنَ الشَّیۡطٰنِ تَذَکَّرُوۡا فَاِذَا ہُمۡ مُّبۡصِرُوۡنَ ﴿۲۰۱﴾ۚ ",
              urdu_="جو لوگ پرہیزگار ہیں جب انکو شیطان کی طرف سے کوئی وسوسہ پیدا ہوتا ہے تو چونک پڑتے ہیں اور دل کی آنکھیں کھول کر دیکھنے لگتے ہیں۔",
          ),

      Verse(
              id_=202,
              arabic_="وَ اِخۡوَانُہُمۡ یَمُدُّوۡنَہُمۡ فِی الۡغَیِّ ثُمَّ لَا یُقۡصِرُوۡنَ ﴿۲۰۲﴾ ",
              urdu_="اور ان کفار کے بھائی انہیں گمراہی میں کھینچے جاتے ہیں پھر اس میں کسی طرح کی کوتاہی نہیں کرتے۔",
          ),

      Verse(
              id_=203,
              arabic_="وَ اِذَا لَمۡ تَاۡتِہِمۡ بِاٰیَۃٍ قَالُوۡا لَوۡ لَا اجۡتَبَیۡتَہَا ؕ قُلۡ  اِنَّمَاۤ  اَتَّبِعُ  مَا یُوۡحٰۤی اِلَیَّ مِنۡ رَّبِّیۡ ۚ ہٰذَا بَصَآئِرُ  مِنۡ رَّبِّکُمۡ وَ ہُدًی وَّ رَحۡمَۃٌ   لِّقَوۡمٍ  یُّؤۡمِنُوۡنَ ﴿۲۰۳﴾ ",
              urdu_="اور جب تم انکے پاس کچھ دنوں تک کوئی آیت نہیں لاتے تو کہتے ہیں کہ تم نے اپنی طرف سے کیوں نہیں بنا لی۔ کہدو کہ میں تو اسی حکم کی پیروی کرتا ہوں جو میرے پروردگار کی طرف سے میرے پاس آتا ہے یہ قرآن تمہارے پروردگار کی جانب سے دانش و بصیرت اور مومنوں کے لئے ہدایت اور رحمت ہے۔",
          ),

      Verse(
              id_=204,
              arabic_="وَ اِذَا قُرِیٴَ الۡقُرۡاٰنُ فَاسۡتَمِعُوۡا لَہٗ وَ اَنۡصِتُوۡا  لَعَلَّکُمۡ  تُرۡحَمُوۡنَ ﴿۲۰۴﴾",
              urdu_="اور جب قرآن پڑھا جائے تو توجہ سے سنا کرو۔ اور خاموش رہا کرو۔ تاکہ تم پر رحم کیا جائے۔",
          ),

      Verse(
              id_=205,
              arabic_="وَ اذۡکُرۡ رَّبَّکَ فِیۡ نَفۡسِکَ تَضَرُّعًا وَّ  خِیۡفَۃً وَّ دُوۡنَ الۡجَہۡرِ مِنَ الۡقَوۡلِ بِالۡغُدُوِّ وَ الۡاٰصَالِ  وَ لَا  تَکُنۡ  مِّنَ  الۡغٰفِلِیۡنَ ﴿۲۰۵﴾ ",
              urdu_="اور اپنے پروردگار کو دل ہی دل میں عاجزی اور خوف سے اور پست آواز سے صبح و شام یاد کرتے رہو اور دیکھنا غافل نہ ہونا۔",
          ),

      Verse(
              id_=206,
              arabic_="اِنَّ  الَّذِیۡنَ عِنۡدَ رَبِّکَ لَا یَسۡتَکۡبِرُوۡنَ عَنۡ عِبَادَتِہٖ وَ یُسَبِّحُوۡنَہٗ وَ لَہٗ یَسۡجُدُوۡنَ ﴿۲۰۶﴾٪ٛ",
              urdu_="جو لوگ تمہارے پروردگار کے پاس ہیں وہ اسکی عبادت سے روگردانی نہیں کرتے۔ اور اس پاک ذات کو یاد کرتے اور اسکے آگے سجدے کرتے رہتے ہیں۔",
          ),
    ),
