    ),
  Surah(
      id_=12,
      titleEnglish_="Yusuf ",
      titleArabic_="یوسف",
      verses_=listOf(
      Verse(
              id_=1,
              arabic_="الٓرٰ ۟ تِلۡکَ اٰیٰتُ الۡکِتٰبِ الۡمُبِیۡنِ ۟﴿۱﴾ ",
              urdu_="الٓرا۔ یہ کتاب روشن کی آیتیں ہیں۔",
          ),

      Verse(
              id_=2,
              arabic_="اِنَّاۤ اَنۡزَلۡنٰہُ  قُرۡءٰنًا عَرَبِیًّا لَّعَلَّکُمۡ تَعۡقِلُوۡنَ ﴿۲﴾ ",
              urdu_="ہم نے اس قرآن کو عربی میں نازل کیا ہے تاکہ تم لوگ سمجھ سکو۔",
          ),

      Verse(
              id_=3,
              arabic_="نَحۡنُ نَقُصُّ عَلَیۡکَ اَحۡسَنَ الۡقَصَصِ بِمَاۤ اَوۡحَیۡنَاۤ اِلَیۡکَ ہٰذَا الۡقُرۡاٰنَ ٭ۖ وَ اِنۡ   کُنۡتَ  مِنۡ  قَبۡلِہٖ  لَمِنَ  الۡغٰفِلِیۡنَ ﴿۳﴾ ",
              urdu_="اے پیغمبر ہم اس قرآن کے ذریعے سے جو ہم نے تمہاری طرف بھیجا ہے تمہیں ایک نہایت اچھا قصہ سناتے ہیں۔ اور تم اس سے پہلے بےخبر تھے۔",
          ),

      Verse(
              id_=4,
              arabic_="اِذۡ  قَالَ یُوۡسُفُ لِاَبِیۡہِ یٰۤاَبَتِ اِنِّیۡ  رَاَیۡتُ اَحَدَعَشَرَ کَوۡکَبًا وَّ الشَّمۡسَ وَ الۡقَمَرَ رَاَیۡتُہُمۡ  لِیۡ  سٰجِدِیۡنَ ﴿۴﴾ ",
              urdu_="جب یوسف نے اپنے والد سے کہا کہ ابا میں نے خواب میں گیارہ ستاروں اور سورج اور چاند کو دیکھا ہے۔ دیکھتا کیا ہوں کہ وہ مجھے سجدہ کر رہے ہیں۔",
          ),

      Verse(
              id_=5,
              arabic_="قَالَ یٰبُنَیَّ  لَا تَقۡصُصۡ رُءۡیَاکَ عَلٰۤی اِخۡوَتِکَ فَیَکِیۡدُوۡا لَکَ کَیۡدًا ؕ اِنَّ الشَّیۡطٰنَ  لِلۡاِنۡسَانِ  عَدُوٌّ  مُّبِیۡنٌ ﴿۵﴾ ",
              urdu_="انہوں نے کہا کہ بیٹا! اپنے خواب کا ذکر اپنے بھائیوں سے نہ کرنا نہیں تو وہ تمہارے حق میں کوئی چال چلیں گے۔ کچھ شک نہیں کہ شیطان انسان کا کھلا دشمن ہے۔",
          ),

      Verse(
              id_=6,
              arabic_="وَ کَذٰلِکَ یَجۡتَبِیۡکَ رَبُّکَ وَ یُعَلِّمُکَ مِنۡ تَاۡوِیۡلِ الۡاَحَادِیۡثِ وَ یُتِمُّ نِعۡمَتَہٗ عَلَیۡکَ وَ عَلٰۤی اٰلِ یَعۡقُوۡبَ کَمَاۤ  اَتَمَّہَا عَلٰۤی  اَبَوَیۡکَ  مِنۡ قَبۡلُ  اِبۡرٰہِیۡمَ وَ  اِسۡحٰقَ ؕ اِنَّ رَبَّکَ عَلِیۡمٌ  حَکِیۡمٌ ٪﴿۶﴾ ",
              urdu_="اور اسی طرح اللہ تمہیں برگزیدہ و ممتاز کرے گا اور خواب کی باتوں کی تعبیر کا علم سکھائے گا۔ اور جس طرح اس نے اپنی نعمت پہلے تمہارے دادا پردادا ابراہیم اور اسحٰق پر پوری کی تھی اسی طرح تم پر اور اولاد یعقوب پر پوری کرے گا بیشک تمہارا پروردگار سب کچھ جاننے والا ہے حکمت والا ہے۔",
          ),

      Verse(
              id_=7,
              arabic_="لَقَدۡ کَانَ فِیۡ یُوۡسُفَ وَ اِخۡوَتِہٖۤ  اٰیٰتٌ لِّلسَّآئِلِیۡنَ ﴿۷﴾ ",
              urdu_="ہاں یوسف اور انکے بھائیوں کے قصے میں پوچھنے والوں کے لئے بہت سی نشانیاں ہیں۔",
          ),

      Verse(
              id_=8,
              arabic_="اِذۡ قَالُوۡا لَیُوۡسُفُ وَ اَخُوۡہُ  اَحَبُّ اِلٰۤی اَبِیۡنَا مِنَّا وَ نَحۡنُ عُصۡبَۃٌ ؕ اِنَّ اَبَانَا لَفِیۡ ضَلٰلٍ مُّبِیۡنِۣ ۚ﴿ۖ۸﴾",
              urdu_="جب انہوں نے آپس میں تذکرہ کیا کہ یوسف اور اس کا بھائی ابا کو ہم سے زیادہ پیارے ہیں حالانکہ ہم جماعت کی جماعت ہیں۔ کچھ شک نہیں کہ ابا کھلی غلطی پر ہیں۔",
          ),

      Verse(
              id_=9,
              arabic_="اقۡتُلُوۡا یُوۡسُفَ اَوِ اطۡرَحُوۡہُ  اَرۡضًا یَّخۡلُ لَکُمۡ وَجۡہُ  اَبِیۡکُمۡ وَ تَکُوۡنُوۡا مِنۡۢ  بَعۡدِہٖ  قَوۡمًا  صٰلِحِیۡنَ ﴿۹﴾ ",
              urdu_="یوسف کو یا تو جان سے مار ڈالو یا کسی ملک پر پھینک آؤ۔ پھر ابا کی توجہ صرف تمہاری طرف ہو جائے گی اور اسکے بعد تم نیک لوگوں میں ہو جاؤ گے۔",
          ),

      Verse(
              id_=10,
              arabic_="قَالَ قَآئِلٌ مِّنۡہُمۡ لَا تَقۡتُلُوۡا یُوۡسُفَ وَ اَلۡقُوۡہُ فِیۡ غَیٰبَتِ الۡجُبِّ یَلۡتَقِطۡہُ بَعۡضُ السَّیَّارَۃِ   اِنۡ  کُنۡتُمۡ  فٰعِلِیۡنَ ﴿۱۰﴾ ",
              urdu_="ان میں سے ایک کہنے والے نے کہا کہ یوسف کو جان سے نہ مارو۔ کسی اندھے کنویں میں ڈال دو کہ کوئی راہ گیر اسے نکال کر کسی دوسرے ملک میں لے جائے گا۔ اگر تم کو کرنا ہی ہے تو یوں کرو۔",
          ),

      Verse(
              id_=11,
              arabic_="قَالُوۡا یٰۤاَبَانَا مَا لَکَ لَا تَاۡمَنَّا عَلٰی یُوۡسُفَ  وَ  اِنَّا  لَہٗ   لَنٰصِحُوۡنَ ﴿۱۱﴾ ",
              urdu_="یہ مشورہ کر کے وہ اپنے والد سے کہنے لگے کہ اباجان کیا سبب ہے کہ آپ یوسف کے بارے میں ہمارا اعتبار نہیں کرتے حالانکہ ہم اسکے خیرخواہ ہیں۔",
          ),

      Verse(
              id_=12,
              arabic_="اَرۡسِلۡہُ  مَعَنَا غَدًا یَّرۡتَعۡ وَ یَلۡعَبۡ وَ  اِنَّا لَہٗ   لَحٰفِظُوۡنَ ﴿۱۲﴾ ",
              urdu_="کل اسے ہمارے ساتھ بھیج دیجئے کہ خوب میوے کھائے اور کھیلے کودے ہم اسکے نگہبان ہیں۔",
          ),

      Verse(
              id_=13,
              arabic_="قَالَ اِنِّیۡ لَیَحۡزُنُنِیۡۤ  اَنۡ تَذۡہَبُوۡا بِہٖ وَ اَخَافُ اَنۡ یَّاۡکُلَہُ الذِّئۡبُ وَ اَنۡتُمۡ عَنۡہُ غٰفِلُوۡنَ ﴿۱۳﴾ ",
              urdu_="انہوں نے کہا کہ یہ امر مجھے غمناک کئے دیتا ہے کہ تم اسے لے جاؤ یعنی وہ مجھ سے جدا ہو جائے اور مجھے یہ خوف بھی ہے کہ تم کھیل میں اس سے غافل ہو جاؤ اور اسے بھیڑیا کھا جائے۔",
          ),

      Verse(
              id_=14,
              arabic_="قَالُوۡا  لَئِنۡ  اَکَلَہُ  الذِّئۡبُ وَ نَحۡنُ عُصۡبَۃٌ  اِنَّاۤ   اِذًا  لَّخٰسِرُوۡنَ ﴿۱۴﴾ ",
              urdu_="وہ کہنے لگے کہ اگر ہماری موجودگی میں کہ ہم ایک طاقتور جماعت ہیں اسے بھیڑیا کھا گیا تو ہم بڑے نقصان میں پڑ گئے۔",
          ),

      Verse(
              id_=15,
              arabic_="فَلَمَّا ذَہَبُوۡا بِہٖ  وَ  اَجۡمَعُوۡۤا اَنۡ یَّجۡعَلُوۡہُ  فِیۡ غَیٰبَتِ  الۡجُبِّ ۚ وَ  اَوۡحَیۡنَاۤ  اِلَیۡہِ لَتُنَبِّئَنَّہُمۡ بِاَمۡرِہِمۡ ہٰذَا وَ ہُمۡ لَا یَشۡعُرُوۡنَ ﴿۱۵﴾",
              urdu_="غرض جب وہ اسکو لے گئے اور اس بات پر اتفاق کر لیا کہ اسکو اندھے کنوئیں میں ڈالدیں اور ہم نے یوسف کی طرف وحی بھیجی کہ ایک وقت ایسا آئے گا کہ تم انہیں انکے اس سلوک سے آگاہ کرو گے اور انکو اس وحی کی کچھ خبر نہ ہو گی۔",
          ),

      Verse(
              id_=16,
              arabic_="وَ جَآءُوۡۤ  اَبَاہُمۡ  عِشَآءً  یَّبۡکُوۡنَ ؕ﴿۱۶﴾ ",
              urdu_="اور یہ حرکت کر کے وہ رات کے وقت باپ کے پاس روتے ہوئے آئے۔",
          ),

      Verse(
              id_=17,
              arabic_="قَالُوۡا یٰۤاَبَانَاۤ  اِنَّا ذَہَبۡنَا نَسۡتَبِقُ وَ تَرَکۡنَا یُوۡسُفَ عِنۡدَ مَتَاعِنَا فَاَکَلَہُ  الذِّئۡبُ ۚ وَ مَاۤ  اَنۡتَ بِمُؤۡمِنٍ لَّنَا وَ لَوۡ کُنَّا صٰدِقِیۡنَ ﴿۱۷﴾ ",
              urdu_="کہنے لگے کہ ابا جان ہم تو دوڑنے اور ایک دوسرے سے آگے نکلنے میں مصروف ہو گئے اور یوسف کو اپنے اسباب کے پاس چھوڑ گئے تو اسے بھیڑیا کھا گیا۔ اور آپ ہماری بات کو گو ہم سچ ہی کہتے ہیں باور نہیں کریں گے۔",
          ),

      Verse(
              id_=18,
              arabic_="وَ جَآءُوۡ عَلٰی  قَمِیۡصِہٖ بِدَمٍ  کَذِبٍ ؕ قَالَ بَلۡ سَوَّلَتۡ لَکُمۡ اَنۡفُسُکُمۡ اَمۡرًا ؕ فَصَبۡرٌ  جَمِیۡلٌ ؕ وَ اللّٰہُ  الۡمُسۡتَعَانُ عَلٰی  مَا  تَصِفُوۡنَ ﴿۱۸﴾ ",
              urdu_="اور انکے کرتے پر جھوٹ موٹ کا لہو بھی لگا لائے یعقوب نے کہا کہ یوں نہیں ہے بلکہ تم اپنے دل سے یہ بات بنا لائے ہو اچھا صبر کہ وہی خوب ہے اور جو تم بیان کرتے ہو اسکے بارے میں اللہ ہی سے مدد چاہئیے۔",
          ),

      Verse(
              id_=19,
              arabic_="وَ جَآءَتۡ سَیَّارَۃٌ  فَاَرۡسَلُوۡا وَارِدَہُمۡ فَاَدۡلٰی دَلۡوَہٗ ؕ قَالَ یٰبُشۡرٰی ہٰذَا غُلٰمٌ ؕ وَ اَسَرُّوۡہُ بِضَاعَۃً ؕ وَ اللّٰہُ عَلِیۡمٌۢ بِمَا یَعۡمَلُوۡنَ ﴿۱۹﴾ ",
              urdu_="اور اب اللہ کی شان دیکھو کہ اس کنوئیں کے قریب ایک قافلہ آ وارد ہوا اور انہوں نے پانی کے لئے اپنا سقا بھیجا تو اس نے کنوئیں میں ڈول لٹکایا یوسف اس سے لٹک گئے وہ بولا زہے قسمت یہ تو ایک لڑکا ہے اور اسکو قیمتی سمجھ کر چھپا لیا اور جو کچھ وہ کرتے تھے اللہ کو خوب معلوم تھا۔",
          ),

      Verse(
              id_=20,
              arabic_="وَ شَرَوۡہُ بِثَمَنٍۭ بَخۡسٍ دَرَاہِمَ مَعۡدُوۡدَۃٍ ۚ وَ کَانُوۡا  فِیۡہِ  مِنَ  الزَّاہِدِیۡنَ ﴿٪۲۰﴾ ",
              urdu_="اور انہوں نے اسکو تھوڑی سی قیمت یعنی معدودے چند درہموں پر بیچ ڈالا اور وہ اس سے بیزار ہو رہے تھے۔",
          ),

      Verse(
              id_=21,
              arabic_="وَ قَالَ الَّذِی اشۡتَرٰٮہُ مِنۡ مِّصۡرَ لِامۡرَاَتِہٖۤ  اَکۡرِمِیۡ مَثۡوٰٮہُ عَسٰۤی اَنۡ یَّنۡفَعَنَاۤ  اَوۡ نَتَّخِذَہٗ  وَلَدًا ؕ وَ کَذٰلِکَ مَکَّنَّا لِیُوۡسُفَ فِی الۡاَرۡضِ ۫ وَ لِنُعَلِّمَہٗ مِنۡ تَاۡوِیۡلِ الۡاَحَادِیۡثِ ؕ وَ اللّٰہُ غَالِبٌ عَلٰۤی اَمۡرِہٖ وَ لٰکِنَّ اَکۡثَرَ النَّاسِ لَا  یَعۡلَمُوۡنَ ﴿۲۱﴾ ",
              urdu_="اور مصر میں جس شخص نے اسکو خریدا اسنے اپنی بیوی سے جس کا نام زلیخا تھا کہا کہ اسکو عزت و اکرام سے رکھو۔ عجب نہیں کہ یہ ہمیں فائدہ دے یا ہم اسے بیٹا بنا لیں اس طرح ہم نے یوسف کو سرزمین مصر میں جگہ دی اور غرض یہ تھی کہ ہم انکو خواب کی باتوں کی تعبیر سکھائیں۔ اور اللہ اپنے کام پر غالب ہے لیکن اکثر لوگ نہیں جانتے۔",
          ),

      Verse(
              id_=22,
              arabic_="وَ لَمَّا بَلَغَ اَشُدَّہٗۤ  اٰتَیۡنٰہُ حُکۡمًا وَّ عِلۡمًا ؕ وَ کَذٰلِکَ نَجۡزِی الۡمُحۡسِنِیۡنَ ﴿۲۲﴾ ",
              urdu_="اور جب وہ اپنی جوانی کو پہنچے تو ہم نے انکو دانائی اور علم عطا کیا اور نیکوکاروں کو ہم اسی طرح بدلہ دیا کرتے ہیں۔",
          ),

      Verse(
              id_=23,
              arabic_="وَ رَاوَدَتۡہُ الَّتِیۡ ہُوَ فِیۡ بَیۡتِہَا عَنۡ نَّفۡسِہٖ  وَ غَلَّقَتِ الۡاَبۡوَابَ وَ قَالَتۡ ہَیۡتَ لَکَ ؕ قَالَ مَعَاذَ اللّٰہِ  اِنَّہٗ  رَبِّیۡۤ  اَحۡسَنَ مَثۡوَایَ ؕ اِنَّہٗ  لَا یُفۡلِحُ الظّٰلِمُوۡنَ ﴿۲۳﴾ ",
              urdu_="اور جس عورت کے گھر میں وہ رہتے تھے اس نے انکو اپنی طرف مائل کرنا چاہا اور دروازے بند کر کے کہنے لگی یوسف جلدی آؤ انہوں نے کہا کہ اللہ پناہ میں رکھے وہ یعنی تمہارے میاں تو میرے آقا ہیں انہوں نے مجھے اچھی طرح سے رکھا ہے میں ایسا ظلم نہیں کر سکتا بیشک ظالم لوگ فلاح نہیں پائیں گے۔",
          ),

      Verse(
              id_=24,
              arabic_="وَ لَقَدۡ ہَمَّتۡ بِہٖ ۚ وَ ہَمَّ بِہَا لَوۡ لَاۤ  اَنۡ رَّاٰ بُرۡہَانَ رَبِّہٖ ؕ کَذٰلِکَ لِنَصۡرِفَ عَنۡہُ السُّوۡٓءَ  وَ الۡفَحۡشَآءَ ؕ اِنَّہٗ  مِنۡ  عِبَادِنَا الۡمُخۡلَصِیۡنَ ﴿۲۴﴾ ",
              urdu_="اور اس عورت نے ان کا قصد کیا اور انہوں نے اس کا قصد کیا اگر وہ اپنے پروردگار کی نشانی نہ دیکھتے تو جو ہوتا سو ہوتا۔ یوں اس لئے کیا گیا کہ ہم ان سے برائی اور بےحیائی کو روک دیں۔ بیشک وہ ہمارے چنے ہوئے بندوں میں سے تھے۔",
          ),

      Verse(
              id_=25,
              arabic_="وَ اسۡتَبَقَا الۡبَابَ وَ قَدَّتۡ قَمِیۡصَہٗ مِنۡ دُبُرٍ وَّ اَلۡفَیَا سَیِّدَہَا لَدَا الۡبَابِ ؕ قَالَتۡ مَا جَزَآءُ مَنۡ اَرَادَ بِاَہۡلِکَ سُوۡٓءًا اِلَّاۤ  اَنۡ یُّسۡجَنَ اَوۡ عَذَابٌ اَلِیۡمٌ ﴿۲۵﴾ ",
              urdu_="اور دونوں دروازے کی طرف بھاگے آگے یوسف پیچھے زلیخا اور عورت نے ان کا کرتہ پیچھے سے پکڑ کو جو کھینچا تو پھاڑ ڈالا اور دونوں کو دروازے کے پاس عورت کا خاوند مل گیا تو عورت بولی کہ جو شخص تمہاری بیوی کے ساتھ برا ارادہ کرے اسکی اسکے سوا کیا سزا ہے کہ یا تو قید کیا جائے یا اسے دکھ کا عذاب دیا جائے۔",
          ),

      Verse(
              id_=26,
              arabic_="قَالَ ہِیَ رَاوَدَتۡنِیۡ عَنۡ نَّفۡسِیۡ وَ شَہِدَ شَاہِدٌ مِّنۡ اَہۡلِہَا ۚ اِنۡ کَانَ قَمِیۡصُہٗ  قُدَّ مِنۡ قُبُلٍ فَصَدَقَتۡ وَ ہُوَ مِنَ الۡکٰذِبِیۡنَ ﴿۲۶﴾ ",
              urdu_="یوسف نے کہا اسی نے مجھ کو اپنی طرف مائل کرنا چاہا تھا اور اسکے خاندان میں سے ایک گواہ نے گواہی دی  کہ اگر اس کا کرتا آگے سے پھٹا ہو تو یہ سچی اور یوسف جھوٹا ہے۔",
          ),

      Verse(
              id_=27,
              arabic_="وَ  اِنۡ  کَانَ  قَمِیۡصُہٗ  قُدَّ مِنۡ دُبُرٍ فَکَذَبَتۡ  وَ ہُوَ  مِنَ  الصّٰدِقِیۡنَ ﴿۲۷﴾ ",
              urdu_="اور اگر کرتا پیچھے سے پھٹا ہو تو یہ جھوٹی اور وہ سچا ہے۔",
          ),

      Verse(
              id_=28,
              arabic_="فَلَمَّا رَاٰ  قَمِیۡصَہٗ  قُدَّ مِنۡ دُبُرٍ قَالَ  اِنَّہٗ مِنۡ  کَیۡدِکُنَّ ؕ اِنَّ  کَیۡدَکُنَّ  عَظِیۡمٌ ﴿۲۸﴾ ",
              urdu_="سو جب اس کا کرتہ دیکھا تو پیچھے سے پھٹا تھا تب اس نے زلیخا سے کہا کہ یہ تمہارا ہی فریب ہے۔ اور کچھ شک نہیں کہ تم عورتوں کے فریب بڑے بھاری ہوتے ہیں۔",
          ),

      Verse(
              id_=29,
              arabic_="یُوۡسُفُ اَعۡرِضۡ عَنۡ ہٰذَا ٜ وَ اسۡتَغۡفِرِیۡ  لِذَنۡۢبِکِ ۚۖ اِنَّکِ  کُنۡتِ مِنَ الۡخٰطِئِیۡنَ ﴿٪۲۹﴾ ",
              urdu_="یوسف! اس بات کا خیال نہ کر۔ اور زلیخا تو اپنے گناہوں کی بخشش مانگ۔ بیشک خطا تیری ہی ہے۔",
          ),

      Verse(
              id_=30,
              arabic_="وَ قَالَ نِسۡوَۃٌ  فِی الۡمَدِیۡنَۃِ  امۡرَاَتُ الۡعَزِیۡزِ  تُرَاوِدُ  فَتٰٮہَا عَنۡ  نَّفۡسِہٖ ۚ قَدۡ شَغَفَہَا حُبًّا ؕ اِنَّا لَنَرٰٮہَا فِیۡ ضَلٰلٍ مُّبِیۡنٍ ﴿۳۰﴾ ",
              urdu_="اور شہر میں عورتیں گفتگوئیں کرنے لگیں کہ عزیز کی بیوی اپنے غلام کو اپنی طرف مائل کرنا چاہتی ہے اور اسکی محبت اسکے دل میں گھر کر گئ ہے۔ ہم دیکھتی ہیں کہ وہ کھلی گمراہی میں ہے۔",
          ),

      Verse(
              id_=31,
              arabic_="فَلَمَّا سَمِعَتۡ بِمَکۡرِہِنَّ  اَرۡسَلَتۡ اِلَیۡہِنَّ وَ اَعۡتَدَتۡ لَہُنَّ مُتَّکَاً  وَّ اٰتَتۡ کُلَّ وَاحِدَۃٍ  مِّنۡہُنَّ سِکِّیۡنًا وَّ قَالَتِ اخۡرُجۡ عَلَیۡہِنَّ ۚ فَلَمَّا رَاَیۡنَہٗۤ  اَکۡبَرۡنَہٗ وَ قَطَّعۡنَ اَیۡدِیَہُنَّ وَ قُلۡنَ حَاشَ لِلّٰہِ مَا ہٰذَا بَشَرًا ؕ اِنۡ  ہٰذَاۤ  اِلَّا مَلَکٌ  کَرِیۡمٌ ﴿۳۱﴾ ",
              urdu_="پھر جب زلیخا نے ان عورتوں کی گفتگو جو حقیقت میں دیدار یوسف کے لئے ایک چال تھی سنی تو انکے پاس دعوت کا پیغام بھیجا اور انکے لئے ایک محفل مرتب کی۔ اور پھل تراشنے کے لئے ہر ایک کو ایک ایک چھری دی اور یوسف سے کہا کہ انکے سامنے آؤ۔ جب عورتوں نے انکو دیکھا تو انکا رعب حسن ان پر ایسا چھا گیا کہ پھل تراشتے تراشتے اپنے ہاتھ کاٹ لئے اور بیساختہ بول اٹھیں کہ اللہ پاک ہے یہ آدمی نہیں یہ تو کوئی بزرگ فرشتہ ہے۔",
          ),

      Verse(
              id_=32,
              arabic_="قَالَتۡ فَذٰلِکُنَّ الَّذِیۡ لُمۡتُنَّنِیۡ فِیۡہِ ؕ وَ لَقَدۡ رَاوَدۡتُّہٗ عَنۡ نَّفۡسِہٖ فَاسۡتَعۡصَمَ ؕ وَ لَئِنۡ لَّمۡ  یَفۡعَلۡ  مَاۤ  اٰمُرُہٗ   لَیُسۡجَنَنَّ وَ لَیَکُوۡنًا مِّنَ  الصّٰغِرِیۡنَ ﴿۳۲﴾ ",
              urdu_="زلیخا نے کہا تو یہ وہی ہے جسکے بارے میں تم مجھے طعنے دیتی تھیں اور بیشک میں نے اسکو اپنی طرف مائل کرنا چاہا مگر یہ بچا رہا اور اگر یہ وہ کام نہ کرے گا جو میں اسے کہتی ہوں تو قید کر دیا جائے گا اور ذلیل ہو گا۔",
          ),

      Verse(
              id_=33,
              arabic_="قَالَ رَبِّ السِّجۡنُ اَحَبُّ اِلَیَّ مِمَّا یَدۡعُوۡنَنِیۡۤ  اِلَیۡہِ ۚ وَ اِلَّا تَصۡرِفۡ عَنِّیۡ کَیۡدَہُنَّ اَصۡبُ  اِلَیۡہِنَّ وَ اَکُنۡ مِّنَ الۡجٰہِلِیۡنَ ﴿۳۳﴾ ",
              urdu_="یوسف نے دعا کی کہ پروردگار جس کام کی طرف یہ مجھے بلا تی ہیں اسکی نسبت مجھے قید پسند ہے۔ اور اگر تو مجھ سے انکے فریب کو نہ ہٹائے گا تو میں انکی طرف مائل ہو جاؤں گا اور نادانوں میں سے ہو جاؤں گا۔",
          ),

      Verse(
              id_=34,
              arabic_="فَاسۡتَجَابَ لَہٗ  رَبُّہٗ  فَصَرَفَ عَنۡہُ کَیۡدَہُنَّ ؕ اِنَّہٗ  ہُوَ السَّمِیۡعُ الۡعَلِیۡمُ ﴿۳۴﴾ ",
              urdu_="پس انکے رب نے انکی دعا قبول کر لی اور ان سے عورتوں کا داؤ ٹال دیا۔ بیشک وہی تو سننے والا ہے جاننے والا ہے۔",
          ),

      Verse(
              id_=35,
              arabic_="ثُمَّ بَدَا لَہُمۡ مِّنۡۢ بَعۡدِ مَا رَاَوُا الۡاٰیٰتِ لَیَسۡجُنُنَّہٗ حَتّٰی حِیۡنٍ ﴿٪۳۵﴾ ",
              urdu_="پھر باوجود اسکے کہ وہ لوگ انکی بیگناہی کے نشان دیکھ چکے تھے۔ انکی رائے یہی ٹھہری کہ کچھ عرصے کے لئے ان کو قید ہی کر دیں۔",
          ),

      Verse(
              id_=36,
              arabic_="وَ دَخَلَ مَعَہُ السِّجۡنَ فَتَیٰنِ ؕ قَالَ اَحَدُہُمَاۤ  اِنِّیۡۤ  اَرٰٮنِیۡۤ  اَعۡصِرُ خَمۡرًا ۚ وَ قَالَ الۡاٰخَرُ اِنِّیۡۤ  اَرٰٮنِیۡۤ  اَحۡمِلُ فَوۡقَ رَاۡسِیۡ خُبۡزًا تَاۡکُلُ الطَّیۡرُ مِنۡہُ ؕ نَبِّئۡنَا بِتَاۡوِیۡلِہٖ ۚ اِنَّا نَرٰٮکَ مِنَ الۡمُحۡسِنِیۡنَ ﴿۳۶﴾ ",
              urdu_="اور انکے ساتھ دو اور جوان بھی داخل زنداں ہوئے ایک نے ان میں سے کہا کہ میں نے خواب دیکھا ہے دیکھتا کیا ہوں کہ شراب کے لئے انگور نچوڑ رہا ہوں دوسرے نے کہا کہ میں نے بھی خواب دیکھا ہے میں یہ دیکھتا ہوں کہ اپنے سر پر روٹیاں اٹھائے ہوئے ہوں اور پرندے ان میں سے کھا رہے ہیں تو ہمیں انکی تعبیر بتا دیجئے کہ ہم تمہیں نیکوکار دیکھتے ہیں۔",
          ),

      Verse(
              id_=37,
              arabic_="قَالَ لَا یَاۡتِیۡکُمَا طَعَامٌ تُرۡزَقٰنِہٖۤ  اِلَّا نَبَّاۡتُکُمَا بِتَاۡوِیۡلِہٖ  قَبۡلَ اَنۡ یَّاۡتِیَکُمَا ؕ ذٰلِکُمَا مِمَّا عَلَّمَنِیۡ رَبِّیۡ ؕ اِنِّیۡ تَرَکۡتُ مِلَّۃَ  قَوۡمٍ لَّا یُؤۡمِنُوۡنَ بِاللّٰہِ  وَ ہُمۡ  بِالۡاٰخِرَۃِ  ہُمۡ  کٰفِرُوۡنَ ﴿۳۷﴾ ",
              urdu_="یوسف نے کہا کہ جو کھانا تم کو ملنے والا ہے وہ آنے نہیں پائے گا کہ میں اس سے پہلے تم کو اس کی تعبیر بتا دوں گا۔ یہ ان باتوں میں سے ہے جو میرے پروردگار نے مجھے سکھائی ہیں جو لوگ اللہ پر ایمان نہیں لاتے اور آخرت کا انکار کرتے ہیں میں نے انکا مذہب چھوڑ دیا ہے۔",
          ),

      Verse(
              id_=38,
              arabic_="وَ اتَّبَعۡتُ مِلَّۃَ  اٰبَآءِیۡۤ   اِبۡرٰہِیۡمَ  وَ اِسۡحٰقَ وَ یَعۡقُوۡبَ ؕ مَا کَانَ لَنَاۤ  اَنۡ نُّشۡرِکَ بِاللّٰہِ  مِنۡ  شَیۡءٍ ؕ ذٰلِکَ مِنۡ فَضۡلِ اللّٰہِ عَلَیۡنَا وَ عَلَی النَّاسِ وَ لٰکِنَّ اَکۡثَرَ  النَّاسِ لَا  یَشۡکُرُوۡنَ ﴿۳۸﴾",
              urdu_="اور میں اپنے باپ دادا ابراہیم اور اسحٰق اور یعقوب کے مذہب پر چلتا ہوں۔ ہمیں شایاں نہیں ہے کہ کسی چیز کو اللہ کے ساتھ شریک بنائیں یہ اللہ کا فضل ہے ہم پر بھی اور لوگوں پر بھی۔ لیکن اکثر لوگ شکر نہیں کرتے۔",
          ),

      Verse(
              id_=39,
              arabic_="یٰصَاحِبَیِ السِّجۡنِ ءَاَرۡبَابٌ مُّتَفَرِّقُوۡنَ خَیۡرٌ  اَمِ  اللّٰہُ   الۡوَاحِدُ  الۡقَہَّارُ ﴿ؕ۳۹﴾ ",
              urdu_="اے میرے قیدخانے کے رفیقو! بھلا کئی جدا جدا آقا اچھے یا اللہ جو اکیلا ہے سب پر غالب ہے؟",
          ),

      Verse(
              id_=40,
              arabic_="مَا تَعۡبُدُوۡنَ مِنۡ دُوۡنِہٖۤ  اِلَّاۤ  اَسۡمَآءً سَمَّیۡتُمُوۡہَاۤ  اَنۡتُمۡ وَ اٰبَآؤُکُمۡ مَّاۤ اَنۡزَلَ اللّٰہُ  بِہَا مِنۡ سُلۡطٰنٍ ؕ اِنِ الۡحُکۡمُ  اِلَّا لِلّٰہِ ؕ اَمَرَ اَلَّا تَعۡبُدُوۡۤا اِلَّاۤ اِیَّاہُ ؕ ذٰلِکَ الدِّیۡنُ الۡقَیِّمُ  وَ لٰکِنَّ  اَکۡثَرَ  النَّاسِ لَا  یَعۡلَمُوۡنَ ﴿۴۰﴾",
              urdu_="جن چیزوں کی تم اللہ کے سوا پرستش کرتے ہو وہ صرف نام ہی نام ہیں۔ جو تم نے اور تمہارے باپ دادا نے رکھ لئے ہیں۔ اللہ نے انکی کوئی سند نازل نہیں کی سن رکھو کہ اللہ کے سوا کسی کی حکومت نہیں ہے اس نے ارشاد فرمایا ہے کہ اسکے سوا کسی کی عبادت نہ کرو یہی سیدھا دین ہے لیکن اکثر لوگ نہیں جانتے۔",
          ),

      Verse(
              id_=41,
              arabic_="یٰصَاحِبَیِ السِّجۡنِ اَمَّاۤ  اَحَدُ کُمَا فَیَسۡقِیۡ رَبَّہٗ خَمۡرًا ۚ وَ اَمَّا الۡاٰخَرُ فَیُصۡلَبُ فَتَاۡکُلُ الطَّیۡرُ مِنۡ رَّاۡسِہٖ ؕ قُضِیَ الۡاَمۡرُ  الَّذِیۡ  فِیۡہِ تَسۡتَفۡتِیٰنِ ﴿ؕ۴۱﴾ ",
              urdu_="اے میرے قید خانے کے رفیقو تم میں سے ایک جو پہلا خواب بیان کرنے والا ہے وہ تو اپنے آقا کو شراب پلایا کرے گا۔ اور جو دوسرا ہے وہ سولی دیا جائے گا اور پرندے اس کا سر نوچیں گے۔ جو بات تم مجھ سے پوچھتے تھے وہ فیصل ہو چکی ہے۔",
          ),

      Verse(
              id_=42,
              arabic_="وَ قَالَ لِلَّذِیۡ ظَنَّ اَنَّہٗ نَاجٍ مِّنۡہُمَا اذۡکُرۡنِیۡ عِنۡدَ رَبِّکَ ۫ فَاَنۡسٰہُ الشَّیۡطٰنُ ذِکۡرَ رَبِّہٖ فَلَبِثَ فِی السِّجۡنِ بِضۡعَ سِنِیۡنَ ﴿ؕ٪۴۲﴾ ",
              urdu_="اور ان دونوں  میں سے جس کی نسبت یوسف نے خیال کیا کہ وہ رہائی پا جائے گا اس سے کہا کہ اپنے آقا سے میرا ذکر بھی کر دینا۔ پھر شیطان نے اسے ان کا اپنے آقا سے ذکر کرنا بھلا دیا اور یوسف کئ برس قید خانے ہی میں رہے۔",
          ),

      Verse(
              id_=43,
              arabic_="وَ قَالَ الۡمَلِکُ اِنِّیۡۤ  اَرٰی سَبۡعَ بَقَرٰتٍ سِمَانٍ یَّاۡکُلُہُنَّ سَبۡعٌ عِجَافٌ وَّ سَبۡعَ سُنۡۢبُلٰتٍ خُضۡرٍ وَّ اُخَرَ  یٰبِسٰتٍ ؕ یٰۤاَیُّہَا الۡمَلَاُ  اَفۡتُوۡنِیۡ فِیۡ رُءۡیَایَ اِنۡ کُنۡتُمۡ لِلرُّءۡیَا تَعۡبُرُوۡنَ ﴿۴۳﴾ ",
              urdu_="اور بادشاہ نے کہا کہ میں نے خواب دیکھا ہے دیکھتا کیا ہوں کہ سات موٹی گائیں ہیں جنکو سات دبلی گائیں کھا رہی ہیں۔ اور سات خوشے سبز ہیں اور سات خشک۔ اے درباریو اگر تم خوابوں کی تعبیر دے سکتے ہو تو مجھے میرے خواب کی تعبیر بتاؤ۔",
          ),

      Verse(
              id_=44,
              arabic_="قَالُوۡۤا اَضۡغَاثُ اَحۡلَامٍ ۚ وَ مَا نَحۡنُ بِتَاۡوِیۡلِ  الۡاَحۡلَامِ  بِعٰلِمِیۡنَ ﴿۴۴﴾ ",
              urdu_="انہوں نے کہا یہ خواب تو پریشان خیالی سے ہیں اور ہمیں ایسے خوابوں کی تعبیر نہیں آتی۔",
          ),

      Verse(
              id_=45,
              arabic_="وَ قَالَ الَّذِیۡ نَجَا مِنۡہُمَا وَ ادَّکَرَ  بَعۡدَ اُمَّۃٍ  اَنَا  اُنَبِّئُکُمۡ  بِتَاۡوِیۡلِہٖ فَاَرۡسِلُوۡنِ ﴿۴۵﴾ ",
              urdu_="اور وہ شخص جو دونوں قیدیوں میں سے رہائی پا گیا تھا اور جسے مدت کے بعد وہ بات یاد آ گئ بول اٹھا کہ میں آپ کو اسکی تعبیر لا بتاتا ہوں مجھے قید خانے جانے کی اجازت دیجئے۔",
          ),

      Verse(
              id_=46,
              arabic_="یُوۡسُفُ اَیُّہَا الصِّدِّیۡقُ اَفۡتِنَا فِیۡ سَبۡعِ بَقَرٰتٍ سِمَانٍ یَّاۡکُلُہُنَّ سَبۡعٌ عِجَافٌ وَّ سَبۡعِ سُنۡۢبُلٰتٍ خُضۡرٍ وَّ اُخَرَ  یٰبِسٰتٍ ۙ لَّعَلِّیۡۤ  اَرۡجِعُ  اِلَی النَّاسِ لَعَلَّہُمۡ یَعۡلَمُوۡنَ ﴿۴۶﴾ ",
              urdu_="غرض وہ یوسف کے پاس آیا اور کہنے لگا یوسف اے بڑے سچے یوسف ہمیں اس خواب کی تعبیر بتایئے کہ سات موٹی گائیوں کو سات دبلی گائیں کھا رہی ہیں۔ اور سات خوشے سبز ہیں اور سات سوکھے تاکہ میں لوگوں کے پاس واپس جا کر تعبیر بتاؤں عجب نہیں کہ وہ تمہاری قدر جانیں۔",
          ),

      Verse(
              id_=47,
              arabic_="قَالَ تَزۡرَعُوۡنَ سَبۡعَ سِنِیۡنَ دَاَبًا ۚ فَمَا حَصَدۡتُّمۡ  فَذَرُوۡہُ  فِیۡ  سُنۡۢبُلِہٖۤ  اِلَّا قَلِیۡلًا  مِّمَّا  تَاۡکُلُوۡنَ ﴿۴۷﴾ ",
              urdu_="انہوں نے کہا کہ تم لوگ سات سال متواتر کھیتی کرتے رہو گے تو جو غلہ کاٹو تو تھوڑے سے غلے کے سوا جو کھانے میں آئے باقی کو خوشوں میں ہی رہنے دینا۔",
          ),

      Verse(
              id_=48,
              arabic_="ثُمَّ یَاۡتِیۡ مِنۡۢ بَعۡدِ ذٰلِکَ سَبۡعٌ شِدَادٌ یَّاۡکُلۡنَ مَا قَدَّمۡتُمۡ لَہُنَّ اِلَّا قَلِیۡلًا مِّمَّا تُحۡصِنُوۡنَ ﴿۴۸﴾ ",
              urdu_="پھر اسکے بعد خشک سالی کے سات سخت سال آئیں گے کہ جو غلہ تم نے جمع کر رکھا ہو گا وہ اس سب کو کھا جائیں گے۔ صرف وہی تھوڑا سا رہ جائے گا جو تم احتیاط سے رکھ چھوڑو گے۔",
          ),

      Verse(
              id_=49,
              arabic_="ثُمَّ  یَاۡتِیۡ مِنۡۢ  بَعۡدِ ذٰلِکَ  عَامٌ فِیۡہِ یُغَاثُ  النَّاسُ  وَ فِیۡہِ  یَعۡصِرُوۡنَ ﴿٪۴۹﴾ ",
              urdu_="پھر اسکے بعد ایک سال آئے گا کہ خوب مینہ برسے گا اور لوگ اس میں رس نچوڑیں گے۔",
          ),

      Verse(
              id_=50,
              arabic_="وَ  قَالَ الۡمَلِکُ ائۡتُوۡنِیۡ بِہٖ ۚ فَلَمَّا جَآءَہُ الرَّسُوۡلُ قَالَ ارۡجِعۡ  اِلٰی رَبِّکَ فَسۡـَٔلۡہُ مَا بَالُ النِّسۡوَۃِ  الّٰتِیۡ قَطَّعۡنَ اَیۡدِیَہُنَّ ؕ اِنَّ رَبِّیۡ بِکَیۡدِہِنَّ عَلِیۡمٌ ﴿۵۰﴾ ",
              urdu_="اور یہ تعبیر سن کر بادشاہ نے حکم دیا کہ یوسف کو میرے پاس لے آؤ۔ سو جب قاصد انکے پاس گیا تو انہوں نے کہا کہ اپنے آقا کے پاس واپس جاؤ اور اس سے پوچھو کہ ان عورتوں کا کیا حال ہے جنہوں نے اپنے ہاتھ کاٹ لئے تھے۔ بیشک میرا پروردگار انکے فریب سے خوب واقف ہے۔",
          ),

      Verse(
              id_=51,
              arabic_="قَالَ مَا خَطۡبُکُنَّ  اِذۡ  رَاوَدۡتُّنَّ یُوۡسُفَ عَنۡ نَّفۡسِہٖ ؕ قُلۡنَ حَاشَ لِلّٰہِ  مَا عَلِمۡنَا عَلَیۡہِ مِنۡ سُوۡٓءٍ ؕ قَالَتِ امۡرَاَتُ الۡعَزِیۡزِ الۡـٰٔنَ حَصۡحَصَ الۡحَقُّ ۫ اَنَا رَاوَدۡتُّہٗ عَنۡ  نَّفۡسِہٖ  وَ  اِنَّہٗ   لَمِنَ  الصّٰدِقِیۡنَ ﴿۵۱﴾ ",
              urdu_="بادشاہ نے عورتوں سے پوچھا کہ تمہارا کیا قصہ تھا جب تم نے یوسف کو اپنی طرف مائل کرنا چاہا؟ سب بول اٹھیں کہ اللہ پاک ہے ہم نے اس میں کوئی برائی معلوم نہیں کی عزیز کی بیوی نے کہا اب سچی بات تو ظاہر ہو ہی گئ ہے اصل یہ ہے کہ میں نے اسکو اپنی طرف مائل کرنا چاہا تھا اور وہ بیشک سچا ہے۔",
          ),

      Verse(
              id_=52,
              arabic_="ذٰلِکَ لِیَعۡلَمَ اَنِّیۡ لَمۡ اَخُنۡہُ بِالۡغَیۡبِ وَ اَنَّ  اللّٰہَ  لَا یَہۡدِیۡ  کَیۡدَ  الۡخَآئِنِیۡنَ ﴿۵۲﴾ ",
              urdu_="یوسف نے کہا کہ میں نے یہ بات اس لئے پوچھی ہے کہ عزیز کو یقین ہو جائے کہ میں نے اسکی پیٹھ پیچھے اسکی امانت میں خیانت نہیں کی اور اللہ خیانت کرنے والوں کی چال کو چلنے نہیں دیتا۔",
          ),

      Verse(
              id_=53,
              arabic_="وَ مَاۤ  اُبَرِّیٴُ نَفۡسِیۡ ۚ اِنَّ  النَّفۡسَ لَاَمَّارَۃٌۢ بِالسُّوۡٓءِ  اِلَّا مَا رَحِمَ  رَبِّیۡ ؕ اِنَّ رَبِّیۡ  غَفُوۡرٌ  رَّحِیۡمٌ ﴿۵۳﴾",
              urdu_="اور میں اپنے آپکو پاک صاف نہیں کہتا کیونکہ نفس امارہ انسان کو برائی ہی سکھاتا رہتا ہے۔ مگر یہ کہ میرا پروردگار رحم فرمائے۔ بیشک میرا پروردگار بخشنے والا ہے مہربان ہے۔",
          ),

      Verse(
              id_=54,
              arabic_="وَ قَالَ الۡمَلِکُ ائۡتُوۡنِیۡ بِہٖۤ  اَسۡتَخۡلِصۡہُ لِنَفۡسِیۡ ۚ فَلَمَّا  کَلَّمَہٗ  قَالَ  اِنَّکَ الۡیَوۡمَ  لَدَیۡنَا مَکِیۡنٌ  اَمِیۡنٌ ﴿۵۴﴾ ",
              urdu_="اور بادشاہ نے حکم دیا کہ اسے میرے پاس لاؤ میں اسے اپنا مصاحب خاص بناؤں گا۔ پھر جب ان سے گفتگو کی تو کہا کہ آج سے تم ہمارے ہاں صاحب منزلت ہو اور صاحب اعتبار ہو۔",
          ),

      Verse(
              id_=55,
              arabic_="قَالَ اجۡعَلۡنِیۡ عَلٰی خَزَآئِنِ الۡاَرۡضِ ۚ اِنِّیۡ  حَفِیۡظٌ  عَلِیۡمٌ ﴿۵۵﴾ ",
              urdu_="یوسف نے کہا مجھے زرعی پیداوار پر مقرر کر دیجئے کیونکہ میں حفاظت بھی کر سکتا ہوں اور اس کام سے واقف بھی ہوں۔",
          ),

      Verse(
              id_=56,
              arabic_="وَ کَذٰلِکَ مَکَّنَّا لِیُوۡسُفَ فِی الۡاَرۡضِ ۚ یَتَبَوَّاُ مِنۡہَا حَیۡثُ یَشَآءُ ؕ نُصِیۡبُ بِرَحۡمَتِنَا مَنۡ نَّشَآءُ  وَ لَا نُضِیۡعُ  اَجۡرَ الۡمُحۡسِنِیۡنَ ﴿۵۶﴾ ",
              urdu_="اس طرح ہم نے یوسف کو ملک مصر میں جگہ دی۔ اور وہ اس ملک میں جہاں چاہتے تھے رہتے تھے۔ ہم اپنی رحمت جس پر چاہتے ہیں کرتے ہیں اور نیکوکاروں کے اجر کو ضائع نہیں کرتے۔",
          ),

      Verse(
              id_=57,
              arabic_="وَ لَاَجۡرُ الۡاٰخِرَۃِ خَیۡرٌ لِّلَّذِیۡنَ اٰمَنُوۡا وَ کَانُوۡا  یَتَّقُوۡنَ ﴿٪۵۷﴾ ",
              urdu_="اور جو لوگ ایمان لائے اور ڈرتے رہے انکے لئے آخرت کا اجر بہت بہتر ہے۔",
          ),

      Verse(
              id_=58,
              arabic_="وَ جَآءَ اِخۡوَۃُ یُوۡسُفَ فَدَخَلُوۡا عَلَیۡہِ فَعَرَفَہُمۡ  وَ ہُمۡ  لَہٗ  مُنۡکِرُوۡنَ ﴿۵۸﴾ ",
              urdu_="اور یوسف کے بھائی کنعان سے مصر میں غلہ خریدنے کے لئے آئے تو یوسف کے پاس گئے تو یوسف نے انکو پہچان لیا اور وہ انکو نہ پہچان سکے۔",
          ),

      Verse(
              id_=59,
              arabic_="وَ لَمَّا جَہَّزَہُمۡ بِجَہَازِہِمۡ قَالَ ائۡتُوۡنِیۡ بِاَخٍ  لَّکُمۡ  مِّنۡ اَبِیۡکُمۡ ۚ اَلَا تَرَوۡنَ اَنِّیۡۤ اُوۡفِی الۡکَیۡلَ وَ اَنَا خَیۡرُ الۡمُنۡزِلِیۡنَ ﴿۵۹﴾ ",
              urdu_="اور جب یوسف نے انکے لئے ان کا سامان تیار کر دیا تو کہا کہ پھر آنا تو جو باپ کی طرف سے تمہارا ایک اور بھائی ہے اسے بھی میرے پاس لیتے آنا۔ کیا تم نہیں دیکھتے کہ میں ناپ بھی پوری پوری دیتا ہوں اور مہمانداری بھی خوب کرتا ہوں۔",
          ),

      Verse(
              id_=60,
              arabic_="فَاِنۡ لَّمۡ  تَاۡتُوۡنِیۡ بِہٖ  فَلَا کَیۡلَ  لَکُمۡ  عِنۡدِیۡ  وَ لَا  تَقۡرَبُوۡنِ  ﴿۶۰﴾",
              urdu_="اب اگر تم اسے میرے پاس نہ لاؤ گے تو نہ تمہیں میرے ہاں سے غلہ ملے گا اور نہ تم میرے پاس ہی آ سکو گے۔",
          ),

      Verse(
              id_=61,
              arabic_="قَالُوۡا سَنُرَاوِدُ عَنۡہُ اَبَاہُ وَ اِنَّا لَفٰعِلُوۡنَ ﴿۶۱﴾ ",
              urdu_="انہوں نے کہا کہ ہم اسکے بارے میں اسکے والد کو پھسلائیں گے۔ اور ہم یہ کام کر کے رہیں گے۔",
          ),

      Verse(
              id_=62,
              arabic_="وَ  قَالَ لِفِتۡیٰنِہِ اجۡعَلُوۡا بِضَاعَتَہُمۡ فِیۡ رِحَالِہِمۡ لَعَلَّہُمۡ یَعۡرِفُوۡنَہَاۤ اِذَا انۡقَلَبُوۡۤا اِلٰۤی اَہۡلِہِمۡ لَعَلَّہُمۡ یَرۡجِعُوۡنَ ﴿۶۲﴾ ",
              urdu_="اور یوسف نے اپنے خدام سے کہا کہ ان کا سرمایہ یعنی غلے کی قیمت انکے سامان میں رکھ دو عجب نہیں کہ جب یہ اپنے اہل و عیال میں جائیں تو اسے پہچان لیں اور عجب نہیں کہ یہ پھر یہاں آئیں۔",
          ),

      Verse(
              id_=63,
              arabic_="فَلَمَّا رَجَعُوۡۤا اِلٰۤی اَبِیۡہِمۡ قَالُوۡا یٰۤاَبَانَا مُنِعَ مِنَّا الۡکَیۡلُ فَاَرۡسِلۡ مَعَنَاۤ  اَخَانَا نَکۡتَلۡ  وَ  اِنَّا لَہٗ  لَحٰفِظُوۡنَ ﴿۶۳﴾ ",
              urdu_="پس جب وہ اپنے والد کے پاس واپس گئے تو کہنے لگے کہ ابا ہمارے لئے غلے کی بندش کر دی گئ ہے تو ہمارے ساتھ ہمارے بھائی کو بھیج دیجئے تاکہ ہم پھر غلہ لائیں اور ہم اسکے نگہبان ہیں۔",
          ),

      Verse(
              id_=64,
              arabic_="قَالَ ہَلۡ اٰمَنُکُمۡ عَلَیۡہِ اِلَّا کَمَاۤ اَمِنۡتُکُمۡ عَلٰۤی اَخِیۡہِ مِنۡ قَبۡلُ ؕ فَاللّٰہُ خَیۡرٌ حٰفِظًا ۪ وَّ ہُوَ اَرۡحَمُ الرّٰحِمِیۡنَ ﴿۶۴﴾ ",
              urdu_="یعقوب نے کہا کہ میں اسکے بارے میں تمہارا اعتبار نہیں کرتا مگر ویسا ہی جیسا پہلے اسکے بھائی کے بارے میں کیا تھا سو اللہ ہی بہتر نگہبان ہے اور وہ سب سے زیادہ رحم کرنے والا ہے۔",
          ),

      Verse(
              id_=65,
              arabic_="وَ لَمَّا فَتَحُوۡا مَتَاعَہُمۡ وَجَدُوۡا بِضَاعَتَہُمۡ رُدَّتۡ  اِلَیۡہِمۡ ؕ قَالُوۡا  یٰۤاَبَانَا مَا نَبۡغِیۡ ؕ ہٰذِہٖ  بِضَاعَتُنَا رُدَّتۡ اِلَیۡنَا ۚ وَ نَمِیۡرُ اَہۡلَنَا وَ نَحۡفَظُ اَخَانَا وَ نَزۡدَادُ کَیۡلَ  بَعِیۡرٍ ؕ ذٰلِکَ  کَیۡلٌ یَّسِیۡرٌ ﴿۶۵﴾ ",
              urdu_="اور جب انہوں نے اپنا اسباب کھولا تو دیکھا کہ ان کا سرمایہ واپس کر دیا گیا ہے۔ کہنے لگے ابا ہمیں اور کیا چاہیے دیکھئے یہ ہماری پونجی بھی ہمیں واپس کر دی گئ ہے۔ اور ہم اپنے اہل و عیال کے لئے پھر غلہ لائیں گے اور اپنے بھائی کی نگہبانی کریں گے اور ایک بار شتر زیادہ لائیں گے کہ یہ غلہ جو ہم لائے ہیں تھوڑا ہے۔",
          ),

      Verse(
              id_=66,
              arabic_="قَالَ لَنۡ اُرۡسِلَہٗ  مَعَکُمۡ حَتّٰی تُؤۡتُوۡنِ مَوۡثِقًا مِّنَ اللّٰہِ  لَتَاۡتُنَّنِیۡ بِہٖۤ اِلَّاۤ اَنۡ یُّحَاطَ بِکُمۡ ۚ فَلَمَّاۤ اٰتَوۡہُ مَوۡثِقَہُمۡ قَالَ اللّٰہُ عَلٰی مَا نَقُوۡلُ  وَکِیۡلٌ ﴿۶۶﴾ ",
              urdu_="یعقوب نے کہا کہ جب تک تم اللہ کا عہد نہ دو کہ اسکو میرے پاس صحیح سالم لے آؤ گے میں اسے ہرگز تمہارے ساتھ نہیں بھیجنے کا۔ مگر یہ کہ تم گھیر لئے جاؤ یعنی بےبس ہو جاؤ تو مجبوری ہے پس جب انہوں نے والد کو اپنا قول دیا  تو یعقوب نے کہا کہ جو قول و قرار ہم کر رہے ہیں اس کا اللہ ضامن ہے۔",
          ),

      Verse(
              id_=67,
              arabic_="وَ قَالَ یٰبَنِیَّ  لَا تَدۡخُلُوۡا مِنۡۢ بَابٍ وَّاحِدٍ  وَّ ادۡخُلُوۡا  مِنۡ  اَبۡوَابٍ  مُّتَفَرِّقَۃٍ ؕ وَ مَاۤ  اُغۡنِیۡ عَنۡکُمۡ  مِّنَ اللّٰہِ مِنۡ شَیۡءٍ ؕ اِنِ الۡحُکۡمُ  اِلَّا لِلّٰہِ ؕ عَلَیۡہِ تَوَکَّلۡتُ ۚ وَ عَلَیۡہِ فَلۡیَتَوَکَّلِ الۡمُتَوَکِّلُوۡنَ ﴿۶۷﴾",
              urdu_="اور فرمایا کہ اے میرے بیٹو ایک ہی دروازے سے داخل نہ ہونا بلکہ جدا جدا دروازوں سے داخل ہونا۔ اور میں اللہ کی تقدیر تو تم سے روک نہیں سکتا۔ بیشک حکم اللہ ہی کا ہے۔ میں اسی پر بھروسہ رکھتا ہوں اور اہل توکل کو اسی پر بھروسہ رکھنا چاہئیے۔",
          ),

      Verse(
              id_=68,
              arabic_="وَ لَمَّا دَخَلُوۡا مِنۡ حَیۡثُ اَمَرَہُمۡ  اَبُوۡہُمۡ ؕ مَا کَانَ یُغۡنِیۡ عَنۡہُمۡ مِّنَ اللّٰہِ مِنۡ شَیۡءٍ اِلَّا حَاجَۃً فِیۡ نَفۡسِ یَعۡقُوۡبَ قَضٰہَا ؕ وَ اِنَّہٗ  لَذُوۡ عِلۡمٍ لِّمَا عَلَّمۡنٰہُ  وَ لٰکِنَّ اَکۡثَرَ  النَّاسِ  لَا  یَعۡلَمُوۡنَ ﴿٪۶۸﴾",
              urdu_="اور جب وہ ان ان مقامات سے داخل ہوئے جہاں جہاں سے داخل ہونے کے لئے انکے والد نے ان سے کہا تھا تو وہ تدبیر اللہ کے حکم کو ذرا بھی ٹال نہیں سکتی تھی۔ ہاں وہ یعقوب کے دل کی خواہش تھی جو انہوں نے پوری کی تھی اور بیشک وہ صاحب علم تھے کیونکہ ہم نے انکو علم سکھایا تھا لیکن اکثر لوگ نہیں جانتے۔",
          ),

      Verse(
              id_=69,
              arabic_="وَ لَمَّا دَخَلُوۡا عَلٰی یُوۡسُفَ اٰوٰۤی اِلَیۡہِ اَخَاہُ  قَالَ  اِنِّیۡۤ   اَنَا  اَخُوۡکَ فَلَا تَبۡتَئِسۡ بِمَا  کَانُوۡا  یَعۡمَلُوۡنَ ﴿۶۹﴾ ",
              urdu_="اور جب وہ لوگ یوسف کے پاس پہنچے تو یوسف نے اپنے حقیقی بھائی کو اپنے پاس جگہ دی اور کہا کہ میں تمہارا بھائی ہوں تو جو سلوک یہ ہمارے ساتھ کرتے رہے ہیں اس پر افسوس نہ کرنا۔",
          ),

      Verse(
              id_=70,
              arabic_="فَلَمَّا جَہَّزَہُمۡ بِجَہَازِہِمۡ جَعَلَ السِّقَایَۃَ  فِیۡ رَحۡلِ اَخِیۡہِ  ثُمَّ اَذَّنَ مُؤَذِّنٌ اَیَّتُہَا الۡعِیۡرُ اِنَّکُمۡ لَسٰرِقُوۡنَ ﴿۷۰﴾ ",
              urdu_="پھر جب ان کا اسباب تیار کر دیا تو اپنے بھائی کے تھیلے میں آبخورہ رکھ دیا پھر جب وہ آبادی سے باہر نکل گئے تو ایک پکارنے والے نے آواز دی کہ قافلے والو! تم تو چور ہو۔",
          ),

      Verse(
              id_=71,
              arabic_="قَالُوۡا وَ اَقۡبَلُوۡا عَلَیۡہِمۡ  مَّا ذَا  تَفۡقِدُوۡنَ ﴿۷۱﴾ ",
              urdu_="وہ انکی طرف متوجہ ہو کر کہنے لگے کہ تمہاری کیا چیز کھوئی گئ ہے۔",
          ),

      Verse(
              id_=72,
              arabic_="قَالُوۡا نَفۡقِدُ صُوَاعَ  الۡمَلِکِ  وَ لِمَنۡ  جَآءَ بِہٖ  حِمۡلُ  بَعِیۡرٍ  وَّ اَنَا  بِہٖ  زَعِیۡمٌ ﴿۷۲﴾ ",
              urdu_="وہ بولے کہ بادشاہ کا کٹورہ کھویا گیا ہے اور جو شخص اسکو لے کے آئے اسکے لئے ایک بار شتر انعام اور میں اسکا ضامن ہوں۔",
          ),

      Verse(
              id_=73,
              arabic_="قَالُوۡا تَاللّٰہِ  لَقَدۡ عَلِمۡتُمۡ مَّا جِئۡنَا لِنُفۡسِدَ فِی الۡاَرۡضِ وَ مَا کُنَّا سٰرِقِیۡنَ ﴿۷۳﴾ ",
              urdu_="وہ کہنے لگے کہ اللہ کی قسم تم کو معلوم ہے کہ ہم اس ملک میں اس لئے نہیں آئے کہ خرابی کریں اور نہ ہم چوری کیا کرتے ہیں۔",
          ),

      Verse(
              id_=74,
              arabic_="قَالُوۡا  فَمَا  جَزَآؤُہٗۤ   اِنۡ  کُنۡتُمۡ کٰذِبِیۡنَ ﴿۷۴﴾ ",
              urdu_="وہ بولے کہ اگر تم جھوٹے نکلے یعنی چوری ثابت ہوئی تو اسکی سزا کیا؟",
          ),

      Verse(
              id_=75,
              arabic_="قَالُوۡا جَزَآؤُہٗ  مَنۡ وُّجِدَ فِیۡ  رَحۡلِہٖ  فَہُوَ جَزَآؤُہٗ ؕ  کَذٰلِکَ  نَجۡزِی  الظّٰلِمِیۡنَ ﴿۷۵﴾ ",
              urdu_="انہوں نے کہا کہ اسکی سزا یہ کہ جس کے تھیلے میں وہ دستیاب ہو وہی اس کا بدل قرار دیا جائے ہم ظالموں کو یہی سزا دیا کرتے ہیں۔",
          ),

      Verse(
              id_=76,
              arabic_="فَبَدَاَ بِاَوۡعِیَتِہِمۡ قَبۡلَ وِعَآءِ اَخِیۡہِ ثُمَّ اسۡتَخۡرَجَہَا مِنۡ وِّعَآءِ اَخِیۡہِ ؕ کَذٰلِکَ  کِدۡنَا لِیُوۡسُفَ ؕ مَا  کَانَ  لِیَاۡخُذَ اَخَاہُ فِیۡ  دِیۡنِ الۡمَلِکِ  اِلَّاۤ  اَنۡ  یَّشَآءَ  اللّٰہُ ؕ نَرۡفَعُ دَرَجٰتٍ مَّنۡ نَّشَآءُ ؕ وَ فَوۡقَ کُلِّ ذِیۡ  عِلۡمٍ  عَلِیۡمٌ ﴿۷۶﴾ ",
              urdu_="پھر یوسف نے اپنے بھائی کے تھیلے سے پہلے انکے تھیلوں کو دیکھنا شروع کیا۔ پھر اپنے بھائی کے تھیلے میں سے اسکو نکال لیا۔ اس طرح ہم نے یوسف کے لئے تدبیر کی ورنہ بادشاہ کے قانون کے مطابق وہ اپنے بھائی کو لے نہیں سکتے تھے مگر یہ کہ اللہ چاہے۔ ہم جس کے چاہتے ہیں درجے بلند کرتے ہیں اور ہر علم والے سے بڑھ کر ایک بڑا علم والا ہے۔",
          ),

      Verse(
              id_=77,
              arabic_="قَالُوۡۤا اِنۡ یَّسۡرِقۡ فَقَدۡ سَرَقَ اَخٌ لَّہٗ  مِنۡ قَبۡلُ ۚ فَاَسَرَّہَا یُوۡسُفُ فِیۡ نَفۡسِہٖ وَ لَمۡ یُبۡدِہَا لَہُمۡ ۚ قَالَ اَنۡتُمۡ شَرٌّ مَّکَانًا ۚ وَ اللّٰہُ  اَعۡلَمُ  بِمَا تَصِفُوۡنَ ﴿۷۷﴾ ",
              urdu_="برادران یوسف نے کہا کہ اگر اس نے چوری کی ہو تو کچھ عجب نہیں کہ اس کے ایک بھائی نے بھی پہلے چوری کی تھی۔ مگر یوسف نے اس بات کو اپنے دل میں مخفی رکھا اور ان پر ظاہر نہ ہونے دیا یعنی زیرلب کہا کہ تم بڑے برے ہو۔ اور جو تم بیان کرتے ہو اللہ اسے خوب جانتا ہے۔",
          ),

      Verse(
              id_=78,
              arabic_="قَالُوۡا یٰۤاَیُّہَا الۡعَزِیۡزُ اِنَّ لَہٗۤ  اَبًا شَیۡخًا کَبِیۡرًا فَخُذۡ اَحَدَنَا مَکَانَہٗ ۚ اِنَّا نَرٰٮکَ مِنَ  الۡمُحۡسِنِیۡنَ ﴿۷۸﴾ ",
              urdu_="وہ کہنے لگے کہ اے عزیز اسکے والد بہت بوڑھے ہیں اور اس سے بہت محبت رکھتے ہیں تو اسکو چھوڑ دیجئے اور اسکی جگہ ہم میں سے کسی کو رکھ لیجئے ہم دیکھتے ہیں کہ آپ احسان کرنے والے ہیں۔",
          ),

      Verse(
              id_=79,
              arabic_="قَالَ مَعَاذَ اللّٰہِ اَنۡ نَّاۡخُذَ اِلَّا مَنۡ وَّجَدۡنَا مَتَاعَنَا عِنۡدَہٗۤ ۙ اِنَّاۤ اِذًا لَّظٰلِمُوۡنَ ﴿٪۷۹﴾ ",
              urdu_="یوسف نے کہا کہ اللہ پناہ میں رکھے کہ جس شخص کے پاس ہم نے اپنی چیز پائی ہے اسکے سوا کسی اور کو پکڑ لیں ایسا کریں تو ہم بڑے بےانصاف ہیں۔",
          ),

      Verse(
              id_=80,
              arabic_="فَلَمَّا اسۡتَیۡـَٔسُوۡا مِنۡہُ خَلَصُوۡا نَجِیًّا ؕ قَالَ کَبِیۡرُہُمۡ اَلَمۡ تَعۡلَمُوۡۤا اَنَّ اَبَاکُمۡ قَدۡ اَخَذَ عَلَیۡکُمۡ  مَّوۡثِقًا مِّنَ اللّٰہِ وَ مِنۡ قَبۡلُ مَا فَرَّطۡتُّمۡ فِیۡ یُوۡسُفَ ۚ فَلَنۡ اَبۡرَحَ الۡاَرۡضَ حَتّٰی یَاۡذَنَ لِیۡۤ  اَبِیۡۤ  اَوۡ یَحۡکُمَ اللّٰہُ  لِیۡ ۚ وَ ہُوَ  خَیۡرُ  الۡحٰکِمِیۡنَ ﴿۸۰﴾ ",
              urdu_="آخر جب وہ اس سے ناامید ہو گئے تو الگ ہو کر صلاح کرنے لگے۔ سب سے بڑے نے کہا کیا تم نہیں جانتے کہ تمہارے والد نے تم سے اللہ کا عہد لیا ہے اور اس سے پہلے بھی تم یوسف کے بارے میں قصور کر چکے ہو تو جب تک والد صاحب مجھے اجازت نہ دیں میں تو اس جگہ سے ہلنے کا نہیں یا اللہ میرے لئے کوئی فیصلہ کرے۔ اور وہ سب سے بہتر فیصلہ کرنے والا ہے۔",
          ),

      Verse(
              id_=81,
              arabic_="اِرۡجِعُوۡۤا اِلٰۤی اَبِیۡکُمۡ  فَقُوۡلُوۡا یٰۤاَبَانَاۤ  اِنَّ ابۡنَکَ سَرَقَ ۚ وَ مَا شَہِدۡنَاۤ اِلَّا بِمَا عَلِمۡنَا وَ مَا کُنَّا لِلۡغَیۡبِ حٰفِظِیۡنَ ﴿۸۱﴾ ",
              urdu_="تم سب والد صاحب کے واپس پاس جاؤ اور کہو کہ ابا آپ کے صاحبزادے نے وہاں جا کر چوری کی۔ اور ہم نے تو اپنی دانست کے مطابق آپ سے اسکے لے آنے کا عہد کیا تھا مگر ہم غیب کی باتوں کے جاننے اور یاد رکھنے والے تو نہیں تھے۔",
          ),

      Verse(
              id_=82,
              arabic_="وَ سۡـَٔلِ الۡقَرۡیَۃَ  الَّتِیۡ  کُنَّا فِیۡہَا وَ الۡعِیۡرَ الَّتِیۡۤ  اَقۡبَلۡنَا فِیۡہَا ؕ وَ اِنَّا لَصٰدِقُوۡنَ ﴿۸۲﴾ ",
              urdu_="اور جس بستی میں ہم ٹھہرے تھے وہاں سے یعنی اہل مصر سے اور جس قافلے میں آئے ہیں اس سے دریافت کر لیجئے۔ اور ہم اس بیان میں بالکل سچے ہیں۔",
          ),

      Verse(
              id_=83,
              arabic_="قَالَ  بَلۡ  سَوَّلَتۡ  لَکُمۡ  اَنۡفُسُکُمۡ  اَمۡرًا ؕ فَصَبۡرٌ جَمِیۡلٌ ؕ عَسَی اللّٰہُ  اَنۡ یَّاۡتِـیَنِیۡ بِہِمۡ جَمِیۡعًا ؕ اِنَّہٗ  ہُوَ الۡعَلِیۡمُ الۡحَکِیۡمُ ﴿۸۳﴾ ",
              urdu_="جب انہوں نے یہ بات یعقوب سے آ کر کہی تو انہوں نے کہا کہ حقیقت یوں نہیں ہے بلکہ یہ بات تم نے اپنے دل سے بنا لی ہے تو صبر ہی بہتر ہے۔ عجب نہیں کہ اللہ ان سب کو میرے پاس لے آئے۔ بیشک وہ علم والا ہے حکمت والا ہے۔",
          ),

      Verse(
              id_=84,
              arabic_="وَ تَوَلّٰی عَنۡہُمۡ  وَ قَالَ یٰۤاَسَفٰی عَلٰی یُوۡسُفَ وَ ابۡیَضَّتۡ عَیۡنٰہُ مِنَ الۡحُزۡنِ فَہُوَ کَظِیۡمٌ ﴿۸۴﴾ ",
              urdu_="اور انکے پاس سے چلے گئے اور کہنے لگے کہ ہائے افسوس یوسف پر ہائے افسوس اور رنج و الم میں انکی آنکھیں سفید ہو گئیں اور ان کا دل غم سے بھر رہا تھا۔",
          ),

      Verse(
              id_=85,
              arabic_="قَالُوۡا تَاللّٰہِ تَفۡتَؤُا تَذۡکُرُ  یُوۡسُفَ حَتّٰی تَکُوۡنَ حَرَضًا اَوۡ تَکُوۡنَ مِنَ الۡہٰلِکِیۡنَ ﴿۸۵﴾ ",
              urdu_="بیٹے کہنے لگے کہ واللہ آپ تو یوسف کو اسی طرح یاد ہی کرتے رہیں گے یہاں تک کہ آپ گھل جائیں گے یا جان ہی دے دیں گے۔",
          ),

      Verse(
              id_=86,
              arabic_="قَالَ اِنَّمَاۤ اَشۡکُوۡا بَثِّیۡ وَ حُزۡنِیۡۤ   اِلَی اللّٰہِ وَ اَعۡلَمُ  مِنَ  اللّٰہِ  مَا  لَا  تَعۡلَمُوۡنَ ﴿۸۶﴾ ",
              urdu_="انہوں نے کہا کہ میں تو اپنے غم و اندوہ کا اظہار اللہ سے کرتا ہوں۔ اور اللہ کی طرف سے وہ باتیں جانتا ہوں جو تم نہیں جانتے۔",
          ),

      Verse(
              id_=87,
              arabic_="یٰبَنِیَّ اذۡہَبُوۡا فَتَحَسَّسُوۡا مِنۡ یُّوۡسُفَ وَ اَخِیۡہِ وَ لَا تَایۡـَٔسُوۡا مِنۡ رَّوۡحِ اللّٰہِ ؕ اِنَّہٗ  لَا یَایۡـَٔسُ مِنۡ رَّوۡحِ اللّٰہِ  اِلَّا الۡقَوۡمُ  الۡکٰفِرُوۡنَ ﴿۸۷﴾ ",
              urdu_="اے بیٹو  یوں کرو کہ ایک دفعہ پھر جاؤ اور یوسف اور اسکے بھائی کو تلاش کرو اور اللہ کی رحمت سے ناامید نہ ہونا کہ اللہ کی رحمت سے تو کافر لوگ ہی ناامید ہوا کرتے ہیں۔",
          ),

      Verse(
              id_=88,
              arabic_="فَلَمَّا دَخَلُوۡا عَلَیۡہِ قَالُوۡا یٰۤاَیُّہَا الۡعَزِیۡزُ مَسَّنَا وَ اَہۡلَنَا الضُّرُّ وَ جِئۡنَا بِبِضَاعَۃٍ مُّزۡجٰٮۃٍ فَاَوۡفِ لَنَا الۡکَیۡلَ وَ تَصَدَّقۡ عَلَیۡنَا ؕ اِنَّ اللّٰہَ یَجۡزِی  الۡمُتَصَدِّقِیۡنَ ﴿۸۸﴾ ",
              urdu_="پھر جب وہ یوسف کے پاس گئے تو کہنے لگے کہ اے عزیز ہمیں اور ہمارے اہل و عیال کو بڑی تکلیف ہو رہی ہے اور ہم یہ تھوڑا سا سرمایہ لائے ہیں آپ ہمیں اسکے عوض پورا غلہ دے دیجئے۔ اور ہم پر خیرات کیجئے کہ اللہ خیرات کرنے والوں کو ثواب دیتا ہے۔",
          ),

      Verse(
              id_=89,
              arabic_="قَالَ ہَلۡ عَلِمۡتُمۡ مَّا فَعَلۡتُمۡ بِیُوۡسُفَ وَ اَخِیۡہِ   اِذۡ  اَنۡتُمۡ  جٰہِلُوۡنَ ﴿۸۹﴾ ",
              urdu_="یوسف نے کہا تمہیں معلوم ہے کہ جب تم نادانی میں پھنسے ہوئے تھے تو تم نے یوسف اور اسکے بھائی کے ساتھ کیا کیا تھا؟",
          ),

      Verse(
              id_=90,
              arabic_="قَالُوۡۤا ءَاِنَّکَ لَاَنۡتَ یُوۡسُفُ ؕ قَالَ اَنَا یُوۡسُفُ وَ ہٰذَاۤ  اَخِیۡ ۫ قَدۡ مَنَّ اللّٰہُ عَلَیۡنَا ؕ اِنَّہٗ  مَنۡ یَّـتَّقِ وَ یَصۡبِرۡ  فَاِنَّ اللّٰہَ  لَا  یُضِیۡعُ  اَجۡرَ  الۡمُحۡسِنِیۡنَ ﴿۹۰﴾ ",
              urdu_="وہ بولے کیا واقعی تمہی یوسف ہو انہوں نے کہا ہاں میں ہی یوسف ہوں۔ اور بنیامین کی طرف اشارہ کر کے کہنے لگے یہ میرا بھائی ہے اللہ نے ہم پر بڑا احسان کیا ہے۔ جو شخص اللہ سے ڈرتا اور صبر کرتا ہے تو اللہ نیکوکاروں کا اجر ضائع نہیں کرتا۔",
          ),

      Verse(
              id_=91,
              arabic_="قَالُوۡا تَاللّٰہِ لَقَدۡ اٰثَرَکَ اللّٰہُ عَلَیۡنَا وَ  اِنۡ کُنَّا لَخٰطِئِیۡنَ ﴿۹۱﴾ ",
              urdu_="وہ بولے اللہ کی قسم اللہ نے تم کو ہم پر فضیلت بخشی ہے اور بیشک ہم خطاکار تھے۔",
          ),

      Verse(
              id_=92,
              arabic_="قَالَ لَا تَثۡرِیۡبَ عَلَیۡکُمُ الۡیَوۡمَ ؕ یَغۡفِرُ اللّٰہُ  لَکُمۡ ۫ وَ ہُوَ اَرۡحَمُ الرّٰحِمِیۡنَ ﴿۹۲﴾ ",
              urdu_="یوسف نے کہا کہ آج کے دن تم پر کوئی ملامت نہیں۔ اللہ تم کو معاف کرے۔ اور وہ سب سے بڑھ کر رحم کرنے والا ہے۔",
          ),

      Verse(
              id_=93,
              arabic_="اِذۡہَبُوۡا بِقَمِیۡصِیۡ ہٰذَا فَاَلۡقُوۡہُ عَلٰی وَجۡہِ اَبِیۡ یَاۡتِ بَصِیۡرًا ۚ وَ اۡتُوۡنِیۡ بِاَہۡلِکُمۡ  اَجۡمَعِیۡنَ ﴿٪۹۳﴾ ",
              urdu_="یہ میرا کرتہ لے جاؤ پھر اسے والد صاحب کے منہ پر ڈال دینا تو وہ بینا ہو جائیں گے۔ اور اپنے تمام اہل و عیال کو میرے پاس لے آؤ۔",
          ),

      Verse(
              id_=94,
              arabic_="وَ لَمَّا فَصَلَتِ الۡعِیۡرُ قَالَ اَبُوۡہُمۡ اِنِّیۡ لَاَجِدُ رِیۡحَ یُوۡسُفَ لَوۡ لَاۤ اَنۡ تُفَنِّدُوۡنِ ﴿۹۴﴾ ",
              urdu_="اور جب قافلہ مصر سے روانہ ہوا۔ تو انکے والد کہنے لگے کہ اگر مجھ کو یہ نہ کہو کہ بوڑھا بہک گیا ہے تو مجھے تو یوسف کی بو آ رہی ہے۔",
          ),

      Verse(
              id_=95,
              arabic_="قَالُوۡا تَاللّٰہِ  اِنَّکَ لَفِیۡ ضَلٰلِکَ الۡقَدِیۡمِ ﴿ٙ۹۵﴾ ",
              urdu_="وہ بولے کہ واللہ آپ اسی قدیم غلطی میں مبتلا ہیں۔",
          ),

      Verse(
              id_=96,
              arabic_="فَلَمَّاۤ  اَنۡ جَآءَ الۡبَشِیۡرُ اَلۡقٰٮہُ عَلٰی وَجۡہِہٖ فَارۡتَدَّ بَصِیۡرًا ۚ قَالَ اَلَمۡ اَقُلۡ لَّکُمۡ ۚۙ اِنِّیۡۤ اَعۡلَمُ مِنَ اللّٰہِ مَا لَا تَعۡلَمُوۡنَ ﴿۹۶﴾ ",
              urdu_="مگر جب خوشخبری دینے والا آ پہنچا تو اس نے کرتہ انکے منہ پر ڈال دیا سو وہ بینا ہو گئے۔ اور بیٹوں سے کہنے لگے۔ کیا میں نے تم سے نہیں کہا تھا کہ میں اللہ کی طرف سے وہ باتیں جانتا ہوں جو تم نہیں جانتے؟",
          ),

      Verse(
              id_=97,
              arabic_="قَالُوۡا یٰۤاَبَانَا اسۡتَغۡفِرۡ لَنَا ذُنُوۡبَنَاۤ اِنَّا کُنَّا خٰطِئِیۡنَ ﴿۹۷﴾ ",
              urdu_="بیٹوں نے کہا کہ ابا ہمارے لئے ہمارے گناہ کی مغفرت مانگیئے۔ بیشک ہم خطاکار تھے۔",
          ),

      Verse(
              id_=98,
              arabic_="قَالَ سَوۡفَ اَسۡتَغۡفِرُ لَکُمۡ رَبِّیۡ ؕ اِنَّہٗ ہُوَ  الۡغَفُوۡرُ  الرَّحِیۡمُ ﴿۹۸﴾ ",
              urdu_="فرمایا میں اپنے پروردگار سے تمہارے لئے بخشش مانگوں گا۔ بیشک وہ بخشنے والا ہے مہربان ہے۔",
          ),

      Verse(
              id_=99,
              arabic_="فَلَمَّا دَخَلُوۡا عَلٰی یُوۡسُفَ اٰوٰۤی اِلَیۡہِ اَبَوَیۡہِ وَ قَالَ ادۡخُلُوۡا مِصۡرَ اِنۡ شَآءَ اللّٰہُ   اٰمِنِیۡنَ ﴿ؕ۹۹﴾ ",
              urdu_="پھر جب یہ سب لوگ یوسف کے پاس پہنچے تو انہوں نے اپنے والدین کو اپنے پاس جگہ دی اور کہا مصر میں داخل ہو جایئے اللہ نے چاہا تو امن و امان سے رہیے گا۔",
          ),

      Verse(
              id_=100,
              arabic_="وَ رَفَعَ اَبَوَیۡہِ عَلَی الۡعَرۡشِ وَ خَرُّوۡا لَہٗ سُجَّدًا ۚ وَ قَالَ یٰۤاَبَتِ ہٰذَا تَاۡوِیۡلُ رُءۡیَایَ مِنۡ قَبۡلُ ۫ قَدۡ جَعَلَہَا رَبِّیۡ حَقًّا ؕ وَ قَدۡ  اَحۡسَنَ  بِیۡۤ   اِذۡ  اَخۡرَجَنِیۡ مِنَ السِّجۡنِ وَ جَآءَ بِکُمۡ مِّنَ الۡبَدۡوِ مِنۡۢ بَعۡدِ اَنۡ  نَّزَغَ  الشَّیۡطٰنُ  بَیۡنِیۡ وَ بَیۡنَ اِخۡوَتِیۡ ؕ اِنَّ رَبِّیۡ  لَطِیۡفٌ لِّمَا یَشَآءُ ؕ اِنَّہٗ  ہُوَ  الۡعَلِیۡمُ  الۡحَکِیۡمُ ﴿۱۰۰﴾",
              urdu_="اور اپنے والدین کو تخت پر بٹھایا اور سب انکے آگے سجدے میں گر پڑے اور اس وقت یوسف نے کہا ابا جان یہ میرے اس خواب کی تعبیر ہے جو میں نے پہلے بچپن میں دیکھا تھا۔ میرے پروردگار نے اسے سچ کر دیا۔ اور اس نے مجھ پر بہت سے احسان کئے ہیں کہ مجھ کو قید خانے سے نکالا۔ اور اسکے بعد کہ شیطان نے مجھ میں اور میرے بھائیوں میں فساد ڈال دیا تھا آپ کو گاؤں سے یہاں لایا۔ بیشک میرا پروردگار جو چاہتا ہے تدبیر سے کرتا ہے وہ علم والا ہے حکمت والا ہے۔",
          ),

      Verse(
              id_=101,
              arabic_="رَبِّ قَدۡ اٰتَیۡتَنِیۡ مِنَ الۡمُلۡکِ وَ عَلَّمۡتَنِیۡ مِنۡ تَاۡوِیۡلِ الۡاَحَادِیۡثِ ۚ فَاطِرَ السَّمٰوٰتِ وَ الۡاَرۡضِ ۟ اَنۡتَ وَلِیّٖ فِی الدُّنۡیَا وَ الۡاٰخِرَۃِ ۚ تَوَفَّنِیۡ مُسۡلِمًا وَّ اَلۡحِقۡنِیۡ  بِالصّٰلِحِیۡنَ ﴿۱۰۱﴾ ",
              urdu_="جب یہ سب باتیں ہو لیں تو یوسف نے اللہ سے دعا کی کہ اے میرے پروردگار تو نے مجھ کو حکومت سے نوازا اور خوابوں کی تعبیر کا علم بخشا۔ اے آسمانوں اور زمین کے پیدا کرنے والے تو ہی دنیا اور آخرت میں میرا کارساز ہے۔ تو مجھے دنیا سے اپنی اطاعت کی حالت میں اٹھائیو اور آخرت میں اپنے نیک بندوں میں داخل کیجیو۔",
          ),

      Verse(
              id_=102,
              arabic_="ذٰلِکَ  مِنۡ  اَنۡۢبَآءِ الۡغَیۡبِ نُوۡحِیۡہِ  اِلَیۡکَ ۚ وَ مَا کُنۡتَ لَدَیۡہِمۡ  اِذۡ  اَجۡمَعُوۡۤا  اَمۡرَہُمۡ  وَ ہُمۡ  یَمۡکُرُوۡنَ ﴿۱۰۲﴾ ",
              urdu_="اے پیغمبر یہ اخبار غیب میں سے ہیں جو ہم تمہاری طرف بھیجتے ہیں۔ اور جب برادران یوسف نے اپنی بات پر اتفاق کیا تھا اور وہ فریب کر رہے تھے تو تم انکے پاس تو نہ تھے۔",
          ),

      Verse(
              id_=103,
              arabic_="وَ مَاۤ  اَکۡثَرُ النَّاسِ وَ لَوۡ حَرَصۡتَ بِمُؤۡمِنِیۡنَ ﴿۱۰۳﴾ ",
              urdu_="اور بہت سے آدمی گو تم کتنی ہی خواہش کرو ایمان لانے والے نہیں ہیں۔",
          ),

      Verse(
              id_=104,
              arabic_="وَ مَا تَسۡـَٔلُہُمۡ عَلَیۡہِ مِنۡ اَجۡرٍ ؕ اِنۡ ہُوَ اِلَّا ذِکۡرٌ  لِّلۡعٰلَمِیۡنَ ﴿۱۰۴﴾٪ ",
              urdu_="اور تم ان سے اس خیرخواہی کا کچھ صلہ بھی تو نہیں مانگتے یہ قرآن اور کچھ نہیں تمام جہانوں کے لئے نصیحت ہے۔",
          ),

      Verse(
              id_=105,
              arabic_="وَ کَاَیِّنۡ مِّنۡ اٰیَۃٍ  فِی السَّمٰوٰتِ وَ الۡاَرۡضِ یَمُرُّوۡنَ عَلَیۡہَا وَ ہُمۡ عَنۡہَا مُعۡرِضُوۡنَ ﴿۱۰۵﴾ ",
              urdu_="اور آسمان و زمین میں بہت سی نشانیاں ہیں جن پر سے یہ گذرتے ہیں اور انکو نظرانداز کر دیتے ہیں۔",
          ),

      Verse(
              id_=106,
              arabic_="وَ مَا یُؤۡمِنُ اَکۡثَرُہُمۡ بِاللّٰہِ  اِلَّا وَ ہُمۡ مُّشۡرِکُوۡنَ ﴿۱۰۶﴾ ",
              urdu_="اور یہ اکثر اللہ پر ایمان نہیں رکھتے۔ مگر اسکے ساتھ شرک بھی کرتے ہیں۔",
          ),

      Verse(
              id_=107,
              arabic_="اَفَاَمِنُوۡۤا اَنۡ تَاۡتِیَہُمۡ غَاشِیَۃٌ مِّنۡ عَذَابِ اللّٰہِ  اَوۡ  تَاۡتِیَہُمُ السَّاعَۃُ بَغۡتَۃً   وَّ ہُمۡ  لَا  یَشۡعُرُوۡنَ ﴿۱۰۷﴾ ",
              urdu_="تو کیا یہ اس بات سے بےخوف ہیں کہ ان پر اللہ کا عذاب نازل ہو کر انکو ڈھانپ لے یا ان پر ناگہاں قیامت آ جائے اور انہیں خبر بھی نہ ہو۔",
          ),

      Verse(
              id_=108,
              arabic_="قُلۡ ہٰذِہٖ سَبِیۡلِیۡۤ  اَدۡعُوۡۤا  اِلَی اللّٰہِ ۟ؔ عَلٰی بَصِیۡرَۃٍ  اَنَا  وَ مَنِ اتَّبَعَنِیۡ ؕ وَ سُبۡحٰنَ اللّٰہِ  وَ مَاۤ   اَنَا مِنَ  الۡمُشۡرِکِیۡنَ ﴿۱۰۸﴾ ",
              urdu_="کہدو میرا رستہ تو یہ ہے۔ میں اللہ کی طرف بلاتا ہوں سمجھ بوجھ کر۔ میں بھی لوگوں کو اللہ کی طرف بلاتا ہوں اور میرے پیرو بھی۔ اور اللہ پاک ہے اور میں شرک کرنے والوں میں سے نہیں ہوں۔",
          ),

      Verse(
              id_=109,
              arabic_="وَ مَاۤ  اَرۡسَلۡنَا مِنۡ قَبۡلِکَ اِلَّا رِجَالًا نُّوۡحِیۡۤ  اِلَیۡہِمۡ مِّنۡ اَہۡلِ الۡقُرٰی ؕ اَفَلَمۡ یَسِیۡرُوۡا فِی الۡاَرۡضِ فَیَنۡظُرُوۡا کَیۡفَ کَانَ عَاقِبَۃُ  الَّذِیۡنَ مِنۡ قَبۡلِہِمۡ ؕ وَ لَدَارُ الۡاٰخِرَۃِ خَیۡرٌ لِّلَّذِیۡنَ اتَّقَوۡا ؕ اَفَلَا  تَعۡقِلُوۡنَ ﴿۱۰۹﴾ ",
              urdu_="اور ہم نے تم سے پہلے بستیوں کے رہنے والوں میں سے مرد ہی بھیجے تھے جنکی طرف ہم وحی بھیجتے تھے کیا ان لوگوں نے ملک میں سیروسیاحت نہیں کی کہ دیکھ لیتے کہ جو لوگ ان سے پہلے تھے ان کا انجام کیا ہوا۔ اور متقیوں کے لئے آخرت کا گھر بہت اچھا ہے کیا تم سمجھتے نہیں؟",
          ),

      Verse(
              id_=110,
              arabic_="حَتّٰۤی اِذَا اسۡتَیۡـَٔسَ الرُّسُلُ وَ ظَنُّوۡۤا اَنَّہُمۡ قَدۡ کُذِبُوۡا جَآءَہُمۡ نَصۡرُنَا ۙ فَنُجِّیَ مَنۡ  نَّشَآءُ ؕ وَلَا  یُرَدُّ بَاۡسُنَا عَنِ الۡقَوۡمِ  الۡمُجۡرِمِیۡنَ ﴿۱۱۰﴾ ",
              urdu_="یہاں تک کہ جب پیغمبر ناامید ہوگئے اور انہوں نے خیال کیا کہ اپنی نصرت کے بارے میں جو بات انہوں نے کہی تھی اس میں وہ سچے نہ نکلے تو انکے پاس ہماری مدد آ پہنچی۔ پھر جسے ہم نے چاہا بچا دیا اور ہمارا عذاب اتر کر گناہگار لوگوں سے پھرا نہیں کرتا۔",
          ),

      Verse(
              id_=111,
              arabic_="لَقَدۡ کَانَ فِیۡ قَصَصِہِمۡ عِبۡرَۃٌ  لِّاُولِی الۡاَلۡبَابِ ؕ مَا کَانَ حَدِیۡثًا یُّفۡتَرٰی وَ لٰکِنۡ تَصۡدِیۡقَ الَّذِیۡ بَیۡنَ یَدَیۡہِ وَ تَفۡصِیۡلَ کُلِّ شَیۡءٍ وَّ ہُدًی وَّ رَحۡمَۃً لِّقَوۡمٍ  یُّؤۡمِنُوۡنَ ﴿۱۱۱﴾٪",
              urdu_="ان کے قصے میں عقلمندوں کے لئے عبرت ہے۔ یہ قرآن ایسی بات نہیں ہے جو اپنے دل سے بنائی گئ ہو بلکہ جو کتابیں اس سے پہلے نازل ہوئی ہیں انکی تصدیق کرنے والا ہے اور ہر چیز کی تفصیل کرنے والا اور مومنوں کے لئے ہدایت اور رحمت ہے۔",
          ),
    ),
