  Surah(
      id_=15,
      titleEnglish_="Al-Hijr ",
      titleArabic_="الحجر",
      verses_=listOf(
      Verse(
              id_=1,
              arabic_="الٓرٰ ۟  تِلۡکَ اٰیٰتُ الۡکِتٰبِ وَ قُرۡاٰنٍ مُّبِیۡنٍ ﴿۱﴾ ",
              urdu_="الٓرا۔ یہ اللہ کی کتاب اور قرآن روشن کی آیتیں ہیں۔",
          ),

      Verse(
              id_=2,
              arabic_="رُبَمَا یَوَدُّ الَّذِیۡنَ کَفَرُوۡا لَوۡ کَانُوۡا مُسۡلِمِیۡنَ  ﴿۲﴾",
              urdu_="کسی وقت کافر لوگ آرزو کریں گے کہ اے کاش وہ مسلمان ہوتے۔",
          ),

      Verse(
              id_=3,
              arabic_="ذَرۡہُمۡ یَاۡکُلُوۡا وَ یَتَمَتَّعُوۡا وَ یُلۡہِہِمُ الۡاَمَلُ  فَسَوۡفَ  یَعۡلَمُوۡنَ ﴿۳﴾ ",
              urdu_="اے نبی ان کو انکے حال پر رہنے دو کہ کھا پی لیں اور فائدے اٹھا لیں اور لمبی لمبی آرزوئیں انکو دنیا میں مشغول کئے رکھیں آخرکار انکو اس کا انجام معلوم ہو جائے گا۔",
          ),

      Verse(
              id_=4,
              arabic_="وَ مَاۤ  اَہۡلَکۡنَا مِنۡ قَرۡیَۃٍ  اِلَّا وَ لَہَا کِتَابٌ  مَّعۡلُوۡمٌ ﴿۴﴾ ",
              urdu_="اور ہم نے کوئی بستی ہلاک نہیں کی۔ مگر اس کا وقت مقرر لکھا ہوا تھا۔",
          ),

      Verse(
              id_=5,
              arabic_="مَا تَسۡبِقُ مِنۡ اُمَّۃٍ  اَجَلَہَا وَ مَا یَسۡتَاۡخِرُوۡنَ ﴿۵﴾ ",
              urdu_="کوئی امت اپنی مدت مقررہ سے نہ آگے نکل سکتی ہے اور نہ پیچھے رہ سکتی ہے۔",
          ),

      Verse(
              id_=6,
              arabic_="وَ قَالُوۡا یٰۤاَیُّہَا الَّذِیۡ نُزِّلَ عَلَیۡہِ الذِّکۡرُ اِنَّکَ  لَمَجۡنُوۡنٌ ؕ﴿۶﴾ ",
              urdu_="اور کفار کہتے ہیں کہ اے شخص جس پر نصیحت کی کتاب نازل ہوئی ہے تو تو دیوانہ ہے۔",
          ),

      Verse(
              id_=7,
              arabic_="لَوۡ مَا تَاۡتِیۡنَا بِالۡمَلٰٓئِکَۃِ  اِنۡ کُنۡتَ مِنَ  الصّٰدِقِیۡنَ ﴿۷﴾ ",
              urdu_="اگر تو سچا ہے تو ہمارے پاس فرشتوں کو کیوں نہیں لے آتا۔",
          ),

      Verse(
              id_=8,
              arabic_="مَا نُنَزِّلُ الۡمَلٰٓئِکَۃَ  اِلَّا بِالۡحَقِّ وَ مَا کَانُوۡۤا  اِذًا  مُّنۡظَرِیۡنَ ﴿۸﴾ ",
              urdu_="کہدو ہم فرشتوں کو نازل نہیں کیا کرتے مگر حق کے ساتھ۔ اور اس وقت انکو مہلت نہیں ملے گی۔",
          ),

      Verse(
              id_=9,
              arabic_="اِنَّا نَحۡنُ نَزَّلۡنَا الذِّکۡرَ  وَ  اِنَّا  لَہٗ  لَحٰفِظُوۡنَ ﴿۹﴾ ",
              urdu_="بیشک یہ کتاب نصیحت ہمیں نے اتاری ہے اور یقینًا ہم اسکے نگہبان ہیں۔",
          ),

      Verse(
              id_=10,
              arabic_="وَ لَقَدۡ  اَرۡسَلۡنَا مِنۡ قَبۡلِکَ فِیۡ شِیَعِ الۡاَوَّلِیۡنَ ﴿۱۰﴾ ",
              urdu_="اور ہم نے تم سے پہلے کے مختلف گروہوں میں بھی پیغمبر بھیجے تھے۔",
          ),

      Verse(
              id_=11,
              arabic_="وَ مَا یَاۡتِیۡہِمۡ مِّنۡ رَّسُوۡلٍ  اِلَّا کَانُوۡا بِہٖ یَسۡتَہۡزِءُوۡنَ ﴿۱۱﴾ ",
              urdu_="اور انکے پاس کوئی پیغمبر نہیں آتا تھا مگر وہ اسکے ساتھ ہنسی کرتے تھے۔",
          ),

      Verse(
              id_=12,
              arabic_="کَذٰلِکَ نَسۡلُکُہٗ فِیۡ  قُلُوۡبِ الۡمُجۡرِمِیۡنَ ﴿ۙ۱۲﴾ ",
              urdu_="اسی طرح ہم اس گستاخی کو گنہگاروں کے دلوں میں ڈالے رکھتے ہیں۔",
          ),

      Verse(
              id_=13,
              arabic_="لَا یُؤۡمِنُوۡنَ بِہٖ وَ قَدۡ خَلَتۡ سُنَّۃُ الۡاَوَّلِیۡنَ ﴿۱۳﴾ ",
              urdu_="سو وہ اس پر ایمان نہیں لاتے اور پہلے لوگوں کی روش بھی یہی رہی ہے۔",
          ),

      Verse(
              id_=14,
              arabic_="وَ لَوۡ فَتَحۡنَا عَلَیۡہِمۡ بَابًا مِّنَ السَّمَآءِ فَظَلُّوۡا فِیۡہِ  یَعۡرُجُوۡنَ ﴿ۙ۱۴﴾ ",
              urdu_="اور اگر ہم آسمان کا کوئی دروازہ ان پر کھول دیں اور وہ اس میں چڑھنے بھی لگیں۔",
          ),

      Verse(
              id_=15,
              arabic_="لَقَالُوۡۤا اِنَّمَا سُکِّرَتۡ اَبۡصَارُنَا بَلۡ نَحۡنُ قَوۡمٌ  مَّسۡحُوۡرُوۡنَ ﴿٪۱۵﴾",
              urdu_="تو بھی وہ یہی کہیں کہ ہماری آنکھیں باندھ دی گئ ہیں بلکہ ہم پر جادو کر دیا گیا ہے۔",
          ),

      Verse(
              id_=16,
              arabic_="وَ لَقَدۡ جَعَلۡنَا فِی السَّمَآءِ بُرُوۡجًا وَّ زَیَّنّٰہَا  لِلنّٰظِرِیۡنَ ﴿ۙ۱۶﴾ ",
              urdu_="اور ہم نے آسمان میں برج بنائے ہیں اور دیکھنے والوں کے لئے اسکو سجا دیا ہے۔",
          ),

      Verse(
              id_=17,
              arabic_="وَ  حَفِظۡنٰہَا مِنۡ کُلِّ شَیۡطٰنٍ رَّجِیۡمٍ ﴿ۙ۱۷﴾ ",
              urdu_="اور ہر شیطان مردود سے اسے محفوظ کر دیا ہے۔",
          ),

      Verse(
              id_=18,
              arabic_="اِلَّا مَنِ اسۡتَرَقَ السَّمۡعَ فَاَتۡبَعَہٗ شِہَابٌ مُّبِیۡنٌ ﴿۱۸﴾ ",
              urdu_="ہاں اگر کوئی چوری سے سننا چاہے تو ایک روشن انگارہ اسکے پیچھے لپکتا ہے۔",
          ),

      Verse(
              id_=19,
              arabic_="وَ الۡاَرۡضَ مَدَدۡنٰہَا وَ اَلۡقَیۡنَا فِیۡہَا رَوَاسِیَ وَ اَنۡۢبَتۡنَا فِیۡہَا مِنۡ کُلِّ شَیۡءٍ مَّوۡزُوۡنٍ ﴿۱۹﴾ ",
              urdu_="اور زمین کو ہم نے پھیلایا اور اس پر پہاڑ بنا کر رکھ دیئے اور اس میں ہر ایک چیز اندازے سے اگائی۔",
          ),

      Verse(
              id_=20,
              arabic_="وَ جَعَلۡنَا لَکُمۡ  فِیۡہَا مَعَایِشَ وَ مَنۡ لَّسۡتُمۡ  لَہٗ  بِرٰزِقِیۡنَ ﴿۲۰﴾ ",
              urdu_="اور ہم نے تمہارے لئے اور ان لوگوں کے لئے جنکو تم روزی نہیں دیتے اس میں معاش کے سامان پیدا کئے۔",
          ),

      Verse(
              id_=21,
              arabic_="وَ  اِنۡ مِّنۡ شَیۡءٍ   اِلَّا عِنۡدَنَا خَزَآئِنُہٗ ۫ وَ  مَا  نُنَزِّلُہٗۤ  اِلَّا بِقَدَرٍ  مَّعۡلُوۡمٍ ﴿۲۱﴾ ",
              urdu_="اور ہمارے ہاں ہر چیز کے خزانے ہیں اور ہم اسکو بمقدار مناسب اتارتے رہتے ہیں۔",
          ),

      Verse(
              id_=22,
              arabic_="وَ اَرۡسَلۡنَا الرِّیٰحَ لَوَاقِحَ فَاَنۡزَلۡنَا مِنَ السَّمَآءِ مَآءً فَاَسۡقَیۡنٰکُمُوۡہُ ۚ وَ مَاۤ اَنۡتُمۡ  لَہٗ  بِخٰزِنِیۡنَ ﴿۲۲﴾ ",
              urdu_="اور ہم ہوائیں چلاتے ہیں جو پانی سے بھری ہوئی ہوتی ہیں سو ہم آسمان سے مینہ برساتے ہیں پھر ہم تمکو اس کا پانی پلاتے ہیں اور تم تو اس کا خزانہ نہیں رکھتے۔",
          ),

      Verse(
              id_=23,
              arabic_="وَ اِنَّا لَنَحۡنُ نُحۡیٖ وَ نُمِیۡتُ وَ نَحۡنُ الۡوٰرِثُوۡنَ ﴿۲۳﴾ ",
              urdu_="اور یقینًا ہم ہی حیات بخشتے اور ہم ہی موت دیتے ہیں اور ہم ہی سب کے وارث ہوں گے۔",
          ),

      Verse(
              id_=24,
              arabic_="وَ لَقَدۡ عَلِمۡنَا الۡمُسۡتَقۡدِمِیۡنَ مِنۡکُمۡ وَ لَقَدۡ  عَلِمۡنَا الۡمُسۡتَاۡخِرِیۡنَ ﴿۲۴﴾ ",
              urdu_="اور جو لوگ تم میں پہلے گذر چکے ہیں ہم کو معلوم ہیں اور جو پیچھے آنے والے ہیں وہ بھی ہم کو معلوم ہیں۔",
          ),

      Verse(
              id_=25,
              arabic_="وَ اِنَّ رَبَّکَ ہُوَ یَحۡشُرُہُمۡ ؕ اِنَّہٗ حَکِیۡمٌ عَلِیۡمٌ ﴿٪۲۵﴾ ",
              urdu_="اور تمہارا پروردگار قیامت کے دن ان سب کو جمع کرے گا وہ بڑا دانا ہے خبردار ہے۔",
          ),

      Verse(
              id_=26,
              arabic_="وَ لَقَدۡ خَلَقۡنَا الۡاِنۡسَانَ مِنۡ صَلۡصَالٍ مِّنۡ حَمَاٍ مَّسۡنُوۡنٍ ﴿ۚ۲۶﴾ ",
              urdu_="اور ہم نے انسان کو کھنکھناتے سڑے ہوئے گارے سے پیدا کیا ہے۔",
          ),

      Verse(
              id_=27,
              arabic_="وَ الۡجَآنَّ خَلَقۡنٰہُ مِنۡ قَبۡلُ مِنۡ نَّارِ السَّمُوۡمِ ﴿۲۷﴾ ",
              urdu_="اور جنوں کو اس سے بھی پہلے بے دھوئیں کی آگ سے پیدا کیا تھا۔",
          ),

      Verse(
              id_=28,
              arabic_="وَ اِذۡ قَالَ رَبُّکَ لِلۡمَلٰٓئِکَۃِ  اِنِّیۡ خَالِقٌۢ بَشَرًا مِّنۡ صَلۡصَالٍ مِّنۡ  حَمَاٍ  مَّسۡنُوۡنٍ ﴿۲۸﴾ ",
              urdu_="اور وہ وقت قابل ذکر ہے جب تمہارے پروردگار نے فرشتوں سے فرمایا کہ میں کھنکھناتے سڑے ہوئے گارے سے بشر بنانے والا ہوں۔",
          ),

      Verse(
              id_=29,
              arabic_="فَاِذَا سَوَّیۡتُہٗ  وَ نَفَخۡتُ فِیۡہِ  مِنۡ  رُّوۡحِیۡ فَقَعُوۡا  لَہٗ   سٰجِدِیۡنَ ﴿۲۹﴾ ",
              urdu_="جب میں اسکو درست کرلوں اور اس میں اپنی طرف سے روح پھونک دوں تو اسکے آگے سجدے میں گر پڑنا۔",
          ),

      Verse(
              id_=30,
              arabic_="فَسَجَدَ  الۡمَلٰٓئِکَۃُ  کُلُّہُمۡ  اَجۡمَعُوۡنَ ﴿ۙ۳۰﴾ ",
              urdu_="چنانچہ فرشتے تو سب کے سب سجدے میں گر پڑے۔",
          ),

      Verse(
              id_=31,
              arabic_="اِلَّاۤ  اِبۡلِیۡسَ ؕ اَبٰۤی اَنۡ یَّکُوۡنَ مَعَ السّٰجِدِیۡنَ ﴿۳۱﴾ ",
              urdu_="مگر شیطان کہ اس نے سجدہ کرنے والوں کے ساتھ ہونے سے انکار کیا۔",
          ),

      Verse(
              id_=32,
              arabic_="قَالَ یٰۤـاِبۡلِیۡسُ مَا لَکَ اَلَّا تَکُوۡنَ مَعَ السّٰجِدِیۡنَ ﴿۳۲﴾ ",
              urdu_="فرمایا اے ابلیس! تجھے کیا ہوا کہ تو سجدہ کرنے والوں میں شامل نہ ہوا۔",
          ),

      Verse(
              id_=33,
              arabic_="قَالَ لَمۡ  اَکُنۡ  لِّاَسۡجُدَ  لِبَشَرٍ  خَلَقۡتَہٗ مِنۡ  صَلۡصَالٍ  مِّنۡ  حَمَاٍ  مَّسۡنُوۡنٍ ﴿۳۳﴾ ",
              urdu_="اس نے کہا میں ایسا نہیں ہوں کہ انسان کو جس کو تو نے کھنکھناتے سڑے ہوئے گارے سے بنایا ہے سجدہ کروں۔",
          ),

      Verse(
              id_=34,
              arabic_="قَالَ  فَاخۡرُجۡ  مِنۡہَا فَاِنَّکَ  رَجِیۡمٌ ﴿ۙ۳۴﴾ ",
              urdu_="فرمایا یہاں سے نکل جا کہ اب تو مردود ہے۔",
          ),

      Verse(
              id_=35,
              arabic_="وَّ اِنَّ عَلَیۡکَ اللَّعۡنَۃَ اِلٰی یَوۡمِ الدِّیۡنِ ﴿۳۵﴾ ",
              urdu_="اور تجھ پر قیامت کے دن تک لعنت برسے گی۔",
          ),

      Verse(
              id_=36,
              arabic_="قَالَ رَبِّ فَاَنۡظِرۡنِیۡۤ  اِلٰی یَوۡمِ یُبۡعَثُوۡنَ ﴿۳۶﴾ ",
              urdu_="اس نے کہا کہ پروردگار مجھے اس دن تک مہلت دے جب لوگ مرنے کے بعد زندہ کئے جائیں گے۔",
          ),

      Verse(
              id_=37,
              arabic_="قَالَ فَاِنَّکَ مِنَ الۡمُنۡظَرِیۡنَ ﴿ۙ۳۷﴾ ",
              urdu_="فرمایا اچھا سو تجھے مہلت دی جاتی ہے۔",
          ),

      Verse(
              id_=38,
              arabic_="اِلٰی یَوۡمِ  الۡوَقۡتِ  الۡمَعۡلُوۡمِ ﴿۳۸﴾ ",
              urdu_="وقت مقرر یعنی قیامت کے دن تک۔",
          ),

      Verse(
              id_=39,
              arabic_="قَالَ رَبِّ بِمَاۤ  اَغۡوَیۡتَنِیۡ لَاُزَیِّنَنَّ  لَہُمۡ فِی الۡاَرۡضِ  وَ لَاُغۡوِیَنَّہُمۡ  اَجۡمَعِیۡنَ ﴿ۙ۳۹﴾ ",
              urdu_="اس نے کہا کہ پروردگار جیسا تو نے مجھے بھٹکا دیا ہے میں بھی زمین میں لوگوں کے لئے گناہوں کو آراستہ کر کے دکھاؤں گا اور سب کو بہکاؤں گا۔",
          ),

      Verse(
              id_=40,
              arabic_="اِلَّا عِبَادَکَ  مِنۡہُمُ  الۡمُخۡلَصِیۡنَ ﴿۴۰﴾ ",
              urdu_="ہاں ان میں جو تیرے خاص بندے ہیں ان پر میرا قابو چلنا مشکل ہے۔",
          ),

      Verse(
              id_=41,
              arabic_="قَالَ  ہٰذَا  صِرَاطٌ  عَلَیَّ  مُسۡتَقِیۡمٌ ﴿۴۱﴾ ",
              urdu_="فرمایا کہ مجھ تک پہنچنے کا یہی سیدھا رستہ ہے۔",
          ),

      Verse(
              id_=42,
              arabic_="اِنَّ عِبَادِیۡ لَیۡسَ لَکَ عَلَیۡہِمۡ سُلۡطٰنٌ  اِلَّا مَنِ اتَّبَعَکَ  مِنَ  الۡغٰوِیۡنَ ﴿۴۲﴾ ",
              urdu_="جو میرے خاص بندے ہیں ان پر تجھے کچھ قدرت نہیں کہ انکو گناہ میں ڈال سکے ہاں بدراہوں میں سے جو تیرے پیچھے چل پڑے۔",
          ),

      Verse(
              id_=43,
              arabic_="وَ  اِنَّ جَہَنَّمَ  لَمَوۡعِدُہُمۡ  اَجۡمَعِیۡنَ ﴿۟ۙ۴۳﴾ ",
              urdu_="اور ان سب کے وعدے کی جگہ جہنم ہے۔",
          ),

      Verse(
              id_=44,
              arabic_="لَہَا سَبۡعَۃُ  اَبۡوَابٍ ؕ لِکُلِّ بَابٍ مِّنۡہُمۡ جُزۡءٌ  مَّقۡسُوۡمٌ  ﴿٪۴۴﴾ ",
              urdu_="اسکے سات دروازے ہیں۔ ہر ایک دروازے کے لئے ان میں سے جماعتیں الگ الگ حصہ ہے۔",
          ),

      Verse(
              id_=45,
              arabic_="اِنَّ  الۡمُتَّقِیۡنَ  فِیۡ  جَنّٰتٍ  وَّ عُیُوۡنٍ ﴿ؕ۴۵﴾ ",
              urdu_="جو متقی ہیں وہ باغوں اور چشموں میں ہوں گے۔",
          ),

      Verse(
              id_=46,
              arabic_="اُدۡخُلُوۡہَا بِسَلٰمٍ  اٰمِنِیۡنَ ﴿۴۶﴾ ",
              urdu_="ان سے کہا جائے گا کہ ان میں سلامتی اور امن و اطمینان سے داخل ہو جاؤ۔",
          ),

      Verse(
              id_=47,
              arabic_="وَ نَزَعۡنَا مَا فِیۡ صُدُوۡرِہِمۡ مِّنۡ غِلٍّ اِخۡوَانًا عَلٰی  سُرُرٍ  مُّتَقٰبِلِیۡنَ ﴿۴۷﴾ ",
              urdu_="اور انکے دلوں میں جو کدورت ہو گی ہم اسے نکال دیں گے گویا بھائی بھائی مسندوں پر ایک دوسرے کے سامنے بیٹھے ہیں۔",
          ),

      Verse(
              id_=48,
              arabic_="لَا  یَمَسُّہُمۡ فِیۡہَا نَصَبٌ  وَّ  مَا ہُمۡ  مِّنۡہَا بِمُخۡرَجِیۡنَ ﴿۴۸﴾ ",
              urdu_="نہ انکو وہاں کوئی تکلیف پہنچے گی اور نہ وہ وہاں سے نکالے جائیں گے۔",
          ),

      Verse(
              id_=49,
              arabic_="نَبِّیٴۡ عِبَادِیۡۤ  اَنِّیۡۤ  اَنَا الۡغَفُوۡرُ الرَّحِیۡمُ ﴿ۙ۴۹﴾ ",
              urdu_="اے پیغمبر میرے بندوں کو بتا دو کہ میں بڑا بخشنے والا ہوں بڑا مہربان ہوں۔",
          ),

      Verse(
              id_=50,
              arabic_="وَ اَنَّ عَذَابِیۡ ہُوَ  الۡعَذَابُ  الۡاَلِیۡمُ ﴿۵۰﴾",
              urdu_="اور یہ کہ میرا عذاب بھی درد دینے والا عذاب ہے۔",
          ),

      Verse(
              id_=51,
              arabic_="وَ نَبِّئۡہُمۡ عَنۡ ضَیۡفِ  اِبۡرٰہِیۡمَ ﴿ۘ۵۱﴾ ",
              urdu_="اور انکو ابراہیم کے مہمانوں کو احوال سنا دو۔",
          ),

      Verse(
              id_=52,
              arabic_="اِذۡ دَخَلُوۡا عَلَیۡہِ فَقَالُوۡا سَلٰمًا ؕ قَالَ  اِنَّا مِنۡکُمۡ  وَجِلُوۡنَ ﴿۵۲﴾ ",
              urdu_="جب وہ ابراہیم کے پاس آئے تو سلام کہا۔ انہوں نے کہا کہ ہمیں تو تم سے ڈر لگتا ہے۔",
          ),

      Verse(
              id_=53,
              arabic_="قَالُوۡا لَا تَوۡجَلۡ  اِنَّا نُبَشِّرُکَ بِغُلٰمٍ عَلِیۡمٍ ﴿۵۳﴾ ",
              urdu_="مہمانوں نے کہا کہ ڈریئے نہیں ہم آپ کو ایک دانشمند لڑکے کی خوشخبری دیتے ہیں۔",
          ),

      Verse(
              id_=54,
              arabic_="قَالَ اَبَشَّرۡتُمُوۡنِیۡ عَلٰۤی اَنۡ مَّسَّنِیَ الۡکِبَرُ  فَبِمَ  تُبَشِّرُوۡنَ ﴿۵۴﴾ ",
              urdu_="وہ بولے کہ جب مجھے بڑھاپے نے آ پکڑا تو تم خوشخبری دینے لگے۔ اب کاہے کی خوشخبری دیتے ہو۔",
          ),

      Verse(
              id_=55,
              arabic_="قَالُوۡا بَشَّرۡنٰکَ بِالۡحَقِّ فَلَا تَکُنۡ مِّنَ الۡقٰنِطِیۡنَ ﴿۵۵﴾ ",
              urdu_="انہوں نے کہا کہ ہم آپ کو سچی خوشخبری دیتے ہیں سو آپ مایوس نہ ہوں۔",
          ),

      Verse(
              id_=56,
              arabic_="قَالَ وَ مَنۡ یَّقۡنَطُ مِنۡ رَّحۡمَۃِ  رَبِّہٖۤ  اِلَّا الضَّآلُّوۡنَ ﴿۵۶﴾ ",
              urdu_="ابراہیم نے کہا کہ اللہ کی رحمت سے مایوس ہونا تو گمراہوں کا کام ہے۔",
          ),

      Verse(
              id_=57,
              arabic_="قَالَ فَمَا خَطۡبُکُمۡ اَیُّہَا الۡمُرۡسَلُوۡنَ ﴿۵۷﴾ ",
              urdu_="پھر کہنے لگے کہ فرشتو! تمہار اصل کام کیا ہے۔",
          ),

      Verse(
              id_=58,
              arabic_="قَالُوۡۤا اِنَّاۤ اُرۡسِلۡنَاۤ  اِلٰی قَوۡمٍ مُّجۡرِمِیۡنَ ﴿ۙ۵۸﴾ ",
              urdu_="انہوں نے کہا ہم ایک گنہگار قوم کی طرف بھیجے گئے ہیں کہ اس کو عذاب کریں۔",
          ),

      Verse(
              id_=59,
              arabic_="اِلَّاۤ اٰلَ لُوۡطٍ ؕ اِنَّا لَمُنَجُّوۡہُمۡ اَجۡمَعِیۡنَ ﴿ۙ۵۹﴾ ",
              urdu_="مگر لوط کے گھر والے کہ ان سب کو ہم بچا لیں گے۔",
          ),

      Verse(
              id_=60,
              arabic_="اِلَّا امۡرَاَتَہٗ  قَدَّرۡنَاۤ ۙ اِنَّہَا  لَمِنَ الۡغٰبِرِیۡنَ ﴿٪۶۰﴾ ",
              urdu_="البتہ انکی بیوی کہ اسکے لئے ہم نے ٹھہرا دیا ہے کہ وہ پیچھے رہ جائے گی۔",
          ),

      Verse(
              id_=61,
              arabic_="فَلَمَّا جَآءَ  اٰلَ  لُوۡطِۣ الۡمُرۡسَلُوۡنَ ﴿ۙ۶۱﴾ ",
              urdu_="پھر جب فرشتے لوط کے گھر آئے۔",
          ),

      Verse(
              id_=62,
              arabic_="قَالَ  اِنَّکُمۡ  قَوۡمٌ  مُّنۡکَرُوۡنَ ﴿۶۲﴾ ",
              urdu_="تو انہوں نے کہا کہ تم تو نا آشنا سے لوگ ہو۔",
          ),

      Verse(
              id_=63,
              arabic_="قَالُوۡا بَلۡ جِئۡنٰکَ بِمَا کَانُوۡا فِیۡہِ یَمۡتَرُوۡنَ ﴿۶۳﴾ ",
              urdu_="وہ بولے کہ نہیں بلکہ ہم آپ کے پاس وہ چیز لے کر آئے ہیں جس میں لوگ شک کرتے تھے۔",
          ),

      Verse(
              id_=64,
              arabic_="وَ  اَتَیۡنٰکَ بِالۡحَقِّ وَ  اِنَّا  لَصٰدِقُوۡنَ ﴿۶۴﴾ ",
              urdu_="اور ہم آپ کے پاس یقینی بات لے کر آئے ہیں اور ہم سچ کہتے ہیں۔",
          ),

      Verse(
              id_=65,
              arabic_="فَاَسۡرِ بِاَہۡلِکَ بِقِطۡعٍ مِّنَ الَّیۡلِ وَ اتَّبِعۡ اَدۡبَارَہُمۡ وَ لَا یَلۡتَفِتۡ مِنۡکُمۡ  اَحَدٌ وَّ امۡضُوۡا  حَیۡثُ  تُؤۡمَرُوۡنَ ﴿۶۵﴾ ",
              urdu_="تو آپ کچھ رات رہے سے اپنے گھر والوں کو لے نکلیں اور خود انکے پیچھے چلیں اور آپ میں سے کوئی شخص پیچھے مڑ کر نہ دیکھے۔ اور جہاں آپ کو حکم ہو وہاں چلے جایئے۔",
          ),

      Verse(
              id_=66,
              arabic_="وَ قَضَیۡنَاۤ  اِلَیۡہِ ذٰلِکَ الۡاَمۡرَ اَنَّ  دَابِرَ ہٰۤؤُلَآءِ  مَقۡطُوۡعٌ  مُّصۡبِحِیۡنَ ﴿۶۶﴾ ",
              urdu_="اور ہم نے لوط کی طرف وحی بھیجی کہ ان لوگوں کی جڑ صبح ہوتے ہوتے کاٹ دی جائے گی۔",
          ),

      Verse(
              id_=67,
              arabic_="وَ جَآءَ اَہۡلُ الۡمَدِیۡنَۃِ یَسۡتَبۡشِرُوۡنَ ﴿۶۷﴾ ",
              urdu_="اور اہل شہر لوط کے پاس خوش خوش دوڑے آئے۔",
          ),

      Verse(
              id_=68,
              arabic_="قَالَ  اِنَّ ہٰۤؤُلَآءِ ضَیۡفِیۡ فَلَا تَفۡضَحُوۡنِ ﴿ۙ۶۸﴾ ",
              urdu_="لوط نے کہا کہ یہ میرے مہمان ہیں کہیں ان کے بارے میں مجھے رسوا نہ کرنا۔",
          ),

      Verse(
              id_=69,
              arabic_="وَ اتَّقُوا  اللّٰہَ  وَ لَا  تُخۡزُوۡنِ ﴿۶۹﴾ ",
              urdu_="اور اللہ سے ڈرو۔ اور میری بےآبروئی نہ کرو۔",
          ),

      Verse(
              id_=70,
              arabic_="قَالُوۡۤا  اَوَ لَمۡ  نَنۡہَکَ  عَنِ الۡعٰلَمِیۡنَ ﴿۷۰﴾ ",
              urdu_="وہ بولے کیا ہم نے تمکو سارے جہاں کی حمایت و طرفداری سے منع نہیں کیا۔",
          ),

      Verse(
              id_=71,
              arabic_="قَالَ ہٰۤؤُلَآءِ بَنٰتِیۡۤ  اِنۡ کُنۡتُمۡ فٰعِلِیۡنَ ﴿ؕ۷۱﴾ ",
              urdu_="انہوں نے کہا کہ اگر تمہیں کرنا ہی ہے تو یہ میری قوم کی لڑکیاں ہیں ان سے شادی کر لو۔",
          ),

      Verse(
              id_=72,
              arabic_="لَعَمۡرُکَ اِنَّہُمۡ لَفِیۡ سَکۡرَتِہِمۡ یَعۡمَہُوۡنَ ﴿۷۲﴾ ",
              urdu_="اے نبی تمہاری جان کی قسم وہ اپنی مستی میں مدہوش ہو رہے تھے۔",
          ),

      Verse(
              id_=73,
              arabic_="فَاَخَذَتۡہُمُ  الصَّیۡحَۃُ  مُشۡرِقِیۡنَ ﴿ۙ۷۳﴾ ",
              urdu_="سو ان کو سورج نکلتے نکلتے چنگھاڑ نے آ پکڑا۔",
          ),

      Verse(
              id_=74,
              arabic_="فَجَعَلۡنَا عَالِیَہَا سَافِلَہَا وَ اَمۡطَرۡنَا عَلَیۡہِمۡ  حِجَارَۃً  مِّنۡ  سِجِّیۡلٍ ﴿ؕ۷۴﴾ ",
              urdu_="اور ہم نے ان بستیوں کو الٹ کر نیچے اوپر کر دیا۔ اور ان پر کھنگر کی  پتھریاں برسائیں۔",
          ),

      Verse(
              id_=75,
              arabic_="اِنَّ  فِیۡ ذٰلِکَ  لَاٰیٰتٍ  لِّلۡمُتَوَسِّمِیۡنَ ﴿۷۵﴾ ",
              urdu_="بیشک اس قصے میں اہل فراست کے لئے نشانی ہے۔",
          ),

      Verse(
              id_=76,
              arabic_="وَ  اِنَّہَا لَبِسَبِیۡلٍ  مُّقِیۡمٍ ﴿۷۶﴾ ",
              urdu_="اور وہ بستیاں اب تک شام جانے والے سیدھے رستے پر موجود ہیں۔",
          ),

      Verse(
              id_=77,
              arabic_="اِنَّ فِیۡ ذٰلِکَ لَاٰیَۃً   لِّلۡمُؤۡمِنِیۡنَ ﴿ؕ۷۷﴾ ",
              urdu_="بیشک اس میں ایمان لانے والوں کے لئے نشانی ہے۔",
          ),

      Verse(
              id_=78,
              arabic_="وَ  اِنۡ کَانَ اَصۡحٰبُ الۡاَیۡکَۃِ لَظٰلِمِیۡنَ ﴿ۙ۷۸﴾ ",
              urdu_="اور بن کے رہنے والے یعنی قوم شعیب کے لوگ بھی گنہگار تھے۔",
          ),

      Verse(
              id_=79,
              arabic_="فَانۡتَقَمۡنَا مِنۡہُمۡ ۘ وَ اِنَّہُمَا لَبِاِمَامٍ مُّبِیۡنٍ ﴿ؕ٪۷۹﴾ ",
              urdu_="تو ہم نے ان سے بھی بدلہ لیا اور یہ دونوں شہر کھلے رستے پر موجود ہیں۔",
          ),

      Verse(
              id_=80,
              arabic_="وَ لَقَدۡ کَذَّبَ اَصۡحٰبُ الۡحِجۡرِ  الۡمُرۡسَلِیۡنَ ﴿ۙ۸۰﴾ ",
              urdu_="اور وادی حجر کے رہنے والوں نے بھی پیغمبروں کی تکذیب کی۔",
          ),

      Verse(
              id_=81,
              arabic_="وَ اٰتَیۡنٰہُمۡ اٰیٰتِنَا فَکَانُوۡا عَنۡہَا مُعۡرِضِیۡنَ ﴿ۙ۸۱﴾ ",
              urdu_="ہم نے ان کو اپنی نشانیاں دیں اور وہ ان سے منہ پھیرتے رہے۔",
          ),

      Verse(
              id_=82,
              arabic_="وَ کَانُوۡا یَنۡحِتُوۡنَ مِنَ الۡجِبَالِ بُیُوۡتًا اٰمِنِیۡنَ ﴿۸۲﴾ ",
              urdu_="اور وہ پہاڑوں کو تراش تراش کر گھر بناتے تھے کہ امن و اطمینان سے رہیں گے۔",
          ),

      Verse(
              id_=83,
              arabic_="فَاَخَذَتۡہُمُ  الصَّیۡحَۃُ  مُصۡبِحِیۡنَ ﴿ۙ۸۳﴾ ",
              urdu_="آخر زوردار آواز نے ان کو صبح ہوتے ہوتے آ پکڑا۔",
          ),

      Verse(
              id_=84,
              arabic_="فَمَاۤ  اَغۡنٰی عَنۡہُمۡ مَّا کَانُوۡا یَکۡسِبُوۡنَ ﴿ؕ۸۴﴾ ",
              urdu_="اور جو کام وہ کرتے تھے وہ ان کے کچھ بھی کام نہ آئے۔",
          ),

      Verse(
              id_=85,
              arabic_="وَ مَا خَلَقۡنَا السَّمٰوٰتِ وَ الۡاَرۡضَ وَ مَا بَیۡنَہُمَاۤ  اِلَّا بِالۡحَقِّ ؕ وَ اِنَّ السَّاعَۃَ لَاٰتِیَۃٌ  فَاصۡفَحِ  الصَّفۡحَ  الۡجَمِیۡلَ ﴿۸۵﴾ ",
              urdu_="اور ہم نے آسمانوں اور زمین کو اور جو مخلوقات ان میں ہے سب کو تدبیر کے ساتھ پیدا کیا ہے۔ اور قیامت تو ضرور آ کر رہے گی تو تم ان لوگوں سے اچھی طرح سے درگذر کرو۔",
          ),

      Verse(
              id_=86,
              arabic_="اِنَّ  رَبَّکَ ہُوَ  الۡخَلّٰقُ  الۡعَلِیۡمُ ﴿۸۶﴾ ",
              urdu_="کچھ شک نہیں کہ تمہارا پروردگار ہی سب کچھ پیدا کرنے والا ہے جاننے والا ہے۔",
          ),

      Verse(
              id_=87,
              arabic_="وَ لَقَدۡ اٰتَیۡنٰکَ سَبۡعًا مِّنَ الۡمَثَانِیۡ وَ الۡقُرۡاٰنَ  الۡعَظِیۡمَ ﴿۸۷﴾ ",
              urdu_="اور ہم نے تم کو سات آیتیں جو نماز میں دھرا کر پڑھی جاتی ہیں یعنی سورۃ الحمد اور عظمت والا کلام عطا فرمایا ہے۔",
          ),

      Verse(
              id_=88,
              arabic_="لَا تَمُدَّنَّ عَیۡنَیۡکَ اِلٰی مَا مَتَّعۡنَا بِہٖۤ اَزۡوَاجًا مِّنۡہُمۡ وَ لَا تَحۡزَنۡ عَلَیۡہِمۡ وَ اخۡفِضۡ جَنَاحَکَ  لِلۡمُؤۡمِنِیۡنَ  ﴿۸۸﴾",
              urdu_="ہم نے کفار کی کئ جماعتوں کو جنکو فوائد دنیاوی سے نوازا ہے تم ان کی طرف رغبت سے آنکھ اٹھا کر نہ دیکھنا اور نہ ان کے حال پر رنج و غم کرنا اور مومنوں کے ساتھ تواضع سے پیش آنا۔",
          ),

      Verse(
              id_=89,
              arabic_="وَ قُلۡ   اِنِّیۡۤ   اَنَا النَّذِیۡرُ  الۡمُبِیۡنُ ﴿ۚ۸۹﴾ ",
              urdu_="اور کہدو کہ میں تو علانیہ ڈر سنانے والا ہوں۔",
          ),

      Verse(
              id_=90,
              arabic_="کَمَاۤ   اَنۡزَلۡنَا عَلَی  الۡمُقۡتَسِمِیۡنَ ﴿ۙ۹۰﴾ ",
              urdu_="اور ہم ان کفار پر اسی طرح عذاب نازل کریں گے جس طرح ان لوگوں پر نازل کیا جنہوں نے تقسیم کر دیا۔",
          ),

      Verse(
              id_=91,
              arabic_="الَّذِیۡنَ  جَعَلُوا  الۡقُرۡاٰنَ  عِضِیۡنَ  ﴿۹۱﴾",
              urdu_="یعنی قرآن کو کچھ ماننے اور کچھ نہ ماننے سے ٹکڑے ٹکڑے کر ڈالا۔",
          ),

      Verse(
              id_=92,
              arabic_="فَوَ رَبِّکَ  لَنَسۡـَٔلَنَّہُمۡ  اَجۡمَعِیۡنَ ﴿ۙ۹۲﴾ ",
              urdu_="سو تمہارے پروردگار کی قسم ہم ان سے ضرور پرسش کریں گے۔",
          ),

      Verse(
              id_=93,
              arabic_="عَمَّا  کَانُوۡا  یَعۡمَلُوۡنَ ﴿ٙ۹۳﴾ ",
              urdu_="ان کاموں کی جو وہ کرتے رہے۔",
          ),

      Verse(
              id_=94,
              arabic_="فَاصۡدَعۡ بِمَا تُؤۡمَرُ وَ اَعۡرِضۡ عَنِ الۡمُشۡرِکِیۡنَ ﴿۹۴﴾ ",
              urdu_="پس جو حکم تم کو اللہ کی طرف سے ملا ہے وہ لوگوں کو بے دھڑک سنا دو اور مشرکوں کا ذرا خیال نہ کرو۔",
          ),

      Verse(
              id_=95,
              arabic_="اِنَّا کَفَیۡنٰکَ الۡمُسۡتَہۡزِءِیۡنَ ﴿ۙ۹۵﴾ ",
              urdu_="ہم تمہیں ان لوگوں کے شر سے بچانے کے لئے کافی ہیں جو تم سے ہنسی کرتے ہیں۔",
          ),

      Verse(
              id_=96,
              arabic_="الَّذِیۡنَ یَجۡعَلُوۡنَ مَعَ اللّٰہِ اِلٰہًا اٰخَرَ ۚ فَسَوۡفَ یَعۡلَمُوۡنَ ﴿۹۶﴾ ",
              urdu_="جو اللہ کے ساتھ کوئی اور معبود قرار دیتے ہیں سو عنقریب انکو ان باتوں کا انجام معلوم ہو جائے گا۔",
          ),

      Verse(
              id_=97,
              arabic_="وَ لَقَدۡ نَعۡلَمُ اَنَّکَ یَضِیۡقُ صَدۡرُکَ بِمَا یَقُوۡلُوۡنَ ﴿ۙ۹۷﴾ ",
              urdu_="اور ہم جانتے ہیں کہ انکی باتوں سے تمہارا دل تنگ ہوتا ہے۔",
          ),

      Verse(
              id_=98,
              arabic_="فَسَبِّحۡ بِحَمۡدِ رَبِّکَ وَ کُنۡ مِّنَ السّٰجِدِیۡنَ ﴿ۙ۹۸﴾",
              urdu_="تو تم اپنے پروردگار کی تسبیح کہتے اور اسکی خوبیاں بیان کرتے رہو اور سجدہ کرنے والوں میں سے ہو جاؤ۔",
          ),

      Verse(
              id_=99,
              arabic_="وَ اعۡبُدۡ رَبَّکَ حَتّٰی یَاۡتِیَکَ الۡیَقِیۡنُ ﴿٪۹۹﴾ ",
              urdu_="اور اپنے پروردگار کی عبادت کئے جاؤ یہاں تک کہ تمہاری موت کا وقت آ جائے۔",
          ),
    ),
