  Surah(
      id_=19,
      titleEnglish_="Maryam ",
      titleArabic_="مریم",
      verses_=listOf(
      Verse(
              id_=1,
              arabic_="کٓہٰیٰعٓصٓ ۟﴿ۚ۱﴾",
              urdu_="کہٰیٰعص۔",
          ),

      Verse(
              id_=2,
              arabic_="ذِکۡرُ  رَحۡمَتِ  رَبِّکَ  عَبۡدَہٗ   زَکَرِیَّا  ۖ﴿ۚ۲﴾",
              urdu_="یہ تمہارے پروردگار کی مہربانی کا بیان ہے جو اس نے اپنے بندے زکریا پر کی تھی۔",
          ),

      Verse(
              id_=3,
              arabic_="اِذۡ  نَادٰی  رَبَّہٗ  نِدَآءً  خَفِیًّا ﴿۳﴾ ",
              urdu_="جب انہوں نے اپنے پروردگار کو دبی آواز سے پکارا۔",
          ),

      Verse(
              id_=4,
              arabic_="قَالَ رَبِّ اِنِّیۡ وَہَنَ الۡعَظۡمُ مِنِّیۡ وَ اشۡتَعَلَ الرَّاۡسُ شَیۡبًا وَّ لَمۡ  اَکُنۡۢ بِدُعَآئِکَ  رَبِّ  شَقِیًّا ﴿۴﴾ ",
              urdu_="اور کہا کہ اے میرے پروردگار میری ہڈیاں بڑھاپے کے سبب کمزور ہو گئ ہیں اور سر سفید بالوں کیوجہ سے چمکنے لگا ہے اور اے میرے پروردگار میں تجھ سے مانگ کر کبھی محروم نہیں رہا۔",
          ),

      Verse(
              id_=5,
              arabic_="وَ اِنِّیۡ خِفۡتُ الۡمَوَالِیَ مِنۡ وَّرَآءِیۡ وَ کَانَتِ  امۡرَاَتِیۡ عَاقِرًا فَہَبۡ لِیۡ مِنۡ لَّدُنۡکَ  وَلِیًّا  ۙ﴿۵﴾",
              urdu_="اور میں اپنے بعد اپنے بھائی بندوں سے ڈرتا ہوں اور میری بیوی بانجھ ہے پھر بھی مجھے اپنے پاس سے ایک وارث عطا فرما۔",
          ),

      Verse(
              id_=6,
              arabic_="یَّرِثُنِیۡ وَ یَرِثُ مِنۡ اٰلِ یَعۡقُوۡبَ ٭ۖ وَ اجۡعَلۡہُ   رَبِّ  رَضِیًّا ﴿۶﴾ ",
              urdu_="جو میری اور اولاد یعقوب کی میراث کا مالک ہو۔ اور اے میرے پروردگار اسکو خوش اخلاق بنائیو۔",
          ),

      Verse(
              id_=7,
              arabic_="یٰزَکَرِیَّاۤ  اِنَّا نُبَشِّرُکَ بِغُلٰمِۣ اسۡمُہٗ یَحۡیٰی ۙ لَمۡ  نَجۡعَلۡ  لَّہٗ  مِنۡ  قَبۡلُ  سَمِیًّا ﴿۷﴾ ",
              urdu_="فرمایا اے زکریا ہم تمکو ایک لڑکے کی بشارت دیتے ہیں جس کا نام یحیٰی ہو گا اس سے پہلے ہم نے اس نام کا کوئی شخص پیدا نہیں کیا۔",
          ),

      Verse(
              id_=8,
              arabic_="قَالَ رَبِّ اَنّٰی یَکُوۡنُ لِیۡ غُلٰمٌ وَّ کَانَتِ امۡرَاَتِیۡ  عَاقِرًا  وَّ  قَدۡ  بَلَغۡتُ مِنَ  الۡکِبَرِ  عِتِیًّا ﴿۸﴾ ",
              urdu_="انہوں نے کہا کہ پروردگار میرے ہاں کس طرح لڑکا ہو گا۔ جبکہ میری بیوی بانجھ ہے اور میں بڑھاپے کی اس حالت کو پہنچ گیا کہ سوکھ گیا ہوں۔",
          ),

      Verse(
              id_=9,
              arabic_="قَالَ  کَذٰلِکَ ۚ قَالَ رَبُّکَ ہُوَ  عَلَیَّ ہَیِّنٌ  وَّ قَدۡ خَلَقۡتُکَ مِنۡ قَبۡلُ وَ لَمۡ  تَکُ شَیۡئًا ﴿۹﴾ ",
              urdu_="حکم ہوا کہ اسی طرح ہو گا تمہارے پروردگار نے فرمایا ہے کہ مجھے یہ آسان ہے اور میں پہلے تمکو بھی تو پیدا کر چکا ہوں اور تم کچھ چیز نہ تھے۔",
          ),

      Verse(
              id_=10,
              arabic_="قَالَ رَبِّ اجۡعَلۡ  لِّیۡۤ   اٰیَۃً ؕ قَالَ اٰیَتُکَ اَلَّا تُکَلِّمَ النَّاسَ ثَلٰثَ لَیَالٍ سَوِیًّا ﴿۱۰﴾ ",
              urdu_="کہا کہ پروردگار میرے لئے کوئی نشانی مقرر فرما۔ فرمایا نشانی یہ ہے کہ تم صحیح و سالم ہوتے ہوئے تین رات دن لوگوں سے بات نہ کر سکو گے۔",
          ),

      Verse(
              id_=11,
              arabic_="فَخَرَجَ عَلٰی قَوۡمِہٖ مِنَ الۡمِحۡرَابِ فَاَوۡحٰۤی  اِلَیۡہِمۡ اَنۡ سَبِّحُوۡا بُکۡرَۃً وَّ عَشِیًّا ﴿۱۱﴾ ",
              urdu_="پھر وہ عبادت کے حجرے سے نکل کر اپنی قوم کے پاس آئے تو ان سے اشارے سے کہا کہ صبح و شام تسبیح کرتے رہو۔",
          ),

      Verse(
              id_=12,
              arabic_="یٰیَحۡیٰی خُذِ الۡکِتٰبَ بِقُوَّۃٍ ؕ وَ اٰتَیۡنٰہُ الۡحُکۡمَ  صَبِیًّا ﴿ۙ۱۲﴾ ",
              urdu_="فرمایا اے یحیٰی ہماری کتاب کو مضبوطی سے تھامے رہو۔ اور ہم نے انکو لڑکپن ہی میں دانائی عطا فرمائی تھی۔",
          ),

      Verse(
              id_=13,
              arabic_="وَّ حَنَانًا مِّنۡ لَّدُنَّا وَ زَکٰوۃً ؕ وَ کَانَ تَقِیًّا ﴿ۙ۱۳﴾ ",
              urdu_="اور اپنے پاس سے شفقت اور پاکیزگی دی تھی اور وہ پرہیزگار تھے۔",
          ),

      Verse(
              id_=14,
              arabic_="وَّ بَرًّۢا بِوَالِدَیۡہِ وَ لَمۡ یَکُنۡ جَبَّارًا عَصِیًّا ﴿۱۴﴾ ",
              urdu_="اور وہ اپنے ماں باپ کے ساتھ نیکی کرنے والے تھے اور سرکش اور نافرمان نہیں تھے۔",
          ),

      Verse(
              id_=15,
              arabic_="وَ سَلٰمٌ  عَلَیۡہِ یَوۡمَ وُلِدَ وَ  یَوۡمَ  یَمُوۡتُ وَ  یَوۡمَ  یُبۡعَثُ حَیًّا ﴿٪۱۵﴾ ",
              urdu_="اور جس دن وہ پیدا ہوئے اور جس دن وفات پائیں گے اور جس دن زندہ کر کے اٹھائے جائیں گے۔ ان پر سلام ہے۔",
          ),

      Verse(
              id_=16,
              arabic_="وَ اذۡکُرۡ فِی الۡکِتٰبِ مَرۡیَمَ ۘ اِذِ انۡتَبَذَتۡ مِنۡ اَہۡلِہَا مَکَانًا شَرۡقِیًّا ﴿ۙ۱۶﴾",
              urdu_="اور کتاب یعنی قرآن میں مریم کا بھی تذکرہ کرو جب وہ اپنے لوگوں سے الگ ہو کر مکان کے مشرقی حصے میں چلی گئیں۔",
          ),

      Verse(
              id_=17,
              arabic_="فَاتَّخَذَتۡ مِنۡ دُوۡنِہِمۡ  حِجَابًا ۪۟ فَاَرۡسَلۡنَاۤ  اِلَیۡہَا رُوۡحَنَا فَتَمَثَّلَ لَہَا بَشَرًا سَوِیًّا ﴿۱۷﴾ ",
              urdu_="پھر انہوں نے انکی طرف سے پردہ کر لیا  تو ہم نے ان کی طرف اپنا فرشتہ بھیجا سو وہ انکے سامنے ایک خوبصورت مرد کی صورت میں آیا۔",
          ),

      Verse(
              id_=18,
              arabic_="قَالَتۡ اِنِّیۡۤ  اَعُوۡذُ بِالرَّحۡمٰنِ مِنۡکَ اِنۡ کُنۡتَ تَقِیًّا ﴿۱۸﴾ ",
              urdu_="مریم بولیں کہ اگر تم پرہیزگار ہو تو میں تم سے بچنے کیلئے رحمٰن کی پناہ لیتی ہوں۔",
          ),

      Verse(
              id_=19,
              arabic_="قَالَ  اِنَّمَاۤ  اَنَا رَسُوۡلُ رَبِّکِ ٭ۖ لِاَہَبَ لَکِ غُلٰمًا  زَکِیًّا ﴿۱۹﴾ ",
              urdu_="فرشتے نے کہا کہ میں تو تمہارے پروردگار کا بھیجا ہوا یعنی فرشتہ ہوں اور اس لئے آیا ہوں کہ تمہیں پاکیزہ لڑکا بخشوں۔",
          ),

      Verse(
              id_=20,
              arabic_="قَالَتۡ اَنّٰی یَکُوۡنُ لِیۡ غُلٰمٌ وَّ لَمۡ یَمۡسَسۡنِیۡ  بَشَرٌ  وَّ  لَمۡ   اَکُ  بَغِیًّا ﴿۲۰﴾ ",
              urdu_="مریم نے کہا کہ میرے ہاں لڑکا کیونکر ہو گا جبکہ مجھے کسی مرد نے بطور شوہر چھوا تک نہیں اور میں بدکار بھی نہیں ہوں۔",
          ),

      Verse(
              id_=21,
              arabic_="قَالَ  کَذٰلِکِ ۚ قَالَ رَبُّکِ ہُوَ  عَلَیَّ ہَیِّنٌ ۚ وَ  لِنَجۡعَلَہٗۤ  اٰیَۃً  لِّلنَّاسِ وَ رَحۡمَۃً  مِّنَّا ۚ وَ کَانَ  اَمۡرًا مَّقۡضِیًّا ﴿۲۱﴾ ",
              urdu_="فرشتے نے کہا کہ یونہی ہو گا تمہارے پروردگار نے فرمایا کہ یہ مجھے آسان ہے اور میں اسے اسی طریق پر پیدا کروں گا تاکہ ہم اس کو لوگوں کے لئے اپنی طرف سے نشانی اور ذریعہ رحمت بنائیں اور یہ کام مقرر ہو چکا ہے۔",
          ),

      Verse(
              id_=22,
              arabic_="فَحَمَلَتۡہُ  فَانۡتَبَذَتۡ بِہٖ مَکَانًا قَصِیًّا ﴿۲۲﴾ ",
              urdu_="چنانچہ وہ اس بچے کے ساتھ حاملہ ہو گئیں اور اسے لیکر ایک دور جگہ چلی گئیں۔",
          ),

      Verse(
              id_=23,
              arabic_="فَاَجَآءَہَا الۡمَخَاضُ  اِلٰی جِذۡعِ  النَّخۡلَۃِ ۚ قَالَتۡ یٰلَیۡتَنِیۡ مِتُّ قَبۡلَ ہٰذَا  وَ کُنۡتُ نَسۡیًا مَّنۡسِیًّا ﴿۲۳﴾ ",
              urdu_="پھر دردزہ انکو کھجور کے تنے کی طرف لے آیا۔ کہنے لگیں کہ کاش میں اس سے پہلے مر چکتی اور بھولی بسری ہو گئ ہوتی۔",
          ),

      Verse(
              id_=24,
              arabic_="فَنَادٰٮہَا مِنۡ تَحۡتِہَاۤ  اَلَّا تَحۡزَنِیۡ قَدۡ جَعَلَ  رَبُّکِ  تَحۡتَکِ  سَرِیًّا ﴿۲۴﴾ ",
              urdu_="تب انکے ٹیلے کے نیچے کی جانب سے فرشتے نے انکو آواز دی کہ غمناک نہ ہو۔ تمہارے پروردگار نے تمہارے نیچے ایک چشمہ پیدا کر دیا ہے۔",
          ),

      Verse(
              id_=25,
              arabic_="وَ ہُزِّیۡۤ  اِلَیۡکِ بِجِذۡعِ النَّخۡلَۃِ  تُسٰقِطۡ عَلَیۡکِ  رُطَبًا جَنِیًّا ﴿۫۲۵﴾ ",
              urdu_="اور کھجور کے تنے کو پکڑ کر اپنی طرف ہلاؤ وہ تم پر تازہ عمدہ کھجوریں گرائے گا۔",
          ),

      Verse(
              id_=26,
              arabic_="فَکُلِیۡ وَ اشۡرَبِیۡ وَ قَرِّیۡ عَیۡنًا ۚ فَاِمَّا تَرَیِنَّ مِنَ الۡبَشَرِ اَحَدًا ۙ فَقُوۡلِیۡۤ  اِنِّیۡ نَذَرۡتُ لِلرَّحۡمٰنِ صَوۡمًا فَلَنۡ اُکَلِّمَ الۡیَوۡمَ  اِنۡسِیًّا ﴿ۚ۲۶﴾ ",
              urdu_="تو کھاؤ اور پیو اور آنکھیں ٹھنڈی کرو۔ اگر تم کسی آدمی کو دیکھو تو کہنا کہ میں نے اللہ کے لئے روزے کی منت مانی تو آج میں کسی آدمی سے ہرگز گلام نہیں کروں گی۔",
          ),

      Verse(
              id_=27,
              arabic_="فَاَتَتۡ بِہٖ  قَوۡمَہَا تَحۡمِلُہٗ ؕ قَالُوۡا  یٰمَرۡیَمُ لَقَدۡ جِئۡتِ  شَیۡئًا فَرِیًّا ﴿۲۷﴾ ",
              urdu_="پھر وہ اس بچے کو اٹھا کر اپنی برادری والوں کے پاس لے آئیں۔ وہ کہنے لگے کہ مریم یہ تو تو نے برا کام کیا۔",
          ),

      Verse(
              id_=28,
              arabic_="یٰۤاُخۡتَ ہٰرُوۡنَ مَا کَانَ  اَبُوۡکِ امۡرَ اَ سَوۡءٍ وَّ مَا  کَانَتۡ  اُمُّکِ  بَغِیًّا ﴿ۖۚ۲۸﴾ ",
              urdu_="اے ہارون کی بہن نہ تو تیرا باپ ہی برا آدمی تھا اور نہ تیری ماں ہی بدکار تھی۔",
          ),

      Verse(
              id_=29,
              arabic_="فَاَشَارَتۡ اِلَیۡہِ ؕ قَالُوۡا کَیۡفَ نُکَلِّمُ مَنۡ  کَانَ فِی  الۡمَہۡدِ  صَبِیًّا ﴿۲۹﴾ ",
              urdu_="تو مریم نے اس بچے کی طرف اشارہ کیا۔ وہ بولے کہ ہم اس سے کہ گود کا بچہ ہے کیسے بات کریں۔",
          ),

      Verse(
              id_=30,
              arabic_="قَالَ اِنِّیۡ عَبۡدُ اللّٰہِ ۟ؕ اٰتٰنِیَ الۡکِتٰبَ وَ جَعَلَنِیۡ  نَبِیًّا ﴿ۙ۳۰﴾ ",
              urdu_="بچے نے کہا کہ میں اللہ کا بندہ ہوں اس نے مجھے کتاب دی ہے اور نبی بنایا ہے۔",
          ),

      Verse(
              id_=31,
              arabic_="وَّ جَعَلَنِیۡ مُبٰرَکًا اَیۡنَ مَا کُنۡتُ ۪ وَ اَوۡصٰنِیۡ بِالصَّلٰوۃِ  وَ الزَّکٰوۃِ مَا دُمۡتُ  حَیًّا ﴿۪ۖ۳۱﴾",
              urdu_="اور میں جہاں ہوں اور جس حال میں ہوں اس نے مجھے صاحب برکت بنایا ہے اور جب تک زندہ ہوں مجھ کو نماز اور زکوٰۃ کا ارشاد فرمایا ہے۔",
          ),

      Verse(
              id_=32,
              arabic_="وَّ بَرًّۢا بِوَالِدَتِیۡ ۫ وَ لَمۡ  یَجۡعَلۡنِیۡ جَبَّارًا شَقِیًّا ﴿۳۲﴾ ",
              urdu_="اور مجھے اپنی ماں کے ساتھ نیک سلوک کرنے والا بنایا ہے اور سرکش و بدبخت نہیں بنایا۔",
          ),

      Verse(
              id_=33,
              arabic_="وَ السَّلٰمُ عَلَیَّ یَوۡمَ وُلِدۡتُّ وَ یَوۡمَ اَمُوۡتُ  وَ  یَوۡمَ  اُبۡعَثُ  حَیًّا ﴿۳۳﴾ ",
              urdu_="اور جس دن میں پیدا ہوا اور جس دن مروں گا اور جس دن زندہ کر کے اٹھایا جاؤں گا مجھ پر سلام ہے۔",
          ),

      Verse(
              id_=34,
              arabic_="ذٰلِکَ عِیۡسَی ابۡنُ مَرۡیَمَ ۚ قَوۡلَ الۡحَقِّ الَّذِیۡ  فِیۡہِ  یَمۡتَرُوۡنَ ﴿۳۴﴾ ",
              urdu_="یہ مریم کے بیٹے عیسٰی ہیں اور یہ سچی بات ہے جس میں لوگ شک کرتے ہیں۔",
          ),

      Verse(
              id_=35,
              arabic_="مَا کَانَ لِلّٰہِ اَنۡ یَّتَّخِذَ مِنۡ  وَّلَدٍ ۙ سُبۡحٰنَہٗ ؕ اِذَا  قَضٰۤی اَمۡرًا فَاِنَّمَا یَقُوۡلُ لَہٗ  کُنۡ فَیَکُوۡنُ ﴿ؕ۳۵﴾ ",
              urdu_="اللہ کو شایاں نہیں کہ کسی کو بیٹا بنائے وہ پاک ہے جب کسی چیز کا ارادہ کرتا ہے تو اس کو یہی کہتا ہے کہ ہو جا تو وہ ہو جاتی ہے۔",
          ),

      Verse(
              id_=36,
              arabic_="وَ اِنَّ اللّٰہَ رَبِّیۡ وَ رَبُّکُمۡ فَاعۡبُدُوۡہُ ؕ ہٰذَا صِرَاطٌ  مُّسۡتَقِیۡمٌ ﴿۳۶﴾ ",
              urdu_="اور بیشک اللہ ہی میرا اور تمہارا پروردگار ہے تو اسی کی عبادت کرو یہی سیدھا رستہ ہے۔",
          ),

      Verse(
              id_=37,
              arabic_="فَاخۡتَلَفَ الۡاَحۡزَابُ مِنۡۢ بَیۡنِہِمۡ ۚ فَوَیۡلٌ لِّلَّذِیۡنَ  کَفَرُوۡا مِنۡ مَّشۡہَدِ یَوۡمٍ عَظِیۡمٍ ﴿۳۷﴾ ",
              urdu_="پھر اہل کتاب کے فرقوں نے باہم اختلاف کیا۔ سو جو لوگ کافر ہوئے ہیں ان کو بڑے دن یعنی قیامت کے روز حاضر ہونے سے خرابی ہے۔",
          ),

      Verse(
              id_=38,
              arabic_="اَسۡمِعۡ بِہِمۡ وَ اَبۡصِرۡ ۙ یَوۡمَ یَاۡتُوۡنَنَا لٰکِنِ الظّٰلِمُوۡنَ الۡیَوۡمَ فِیۡ ضَلٰلٍ مُّبِیۡنٍ ﴿۳۸﴾ ",
              urdu_="وہ جس دن ہمارے سامنے آئیں گے۔ کیسے سننے والے اور کیسے دیکھنے والے ہوں گے مگر ظالم آج کھلی گمراہی میں ہیں۔",
          ),

      Verse(
              id_=39,
              arabic_="وَ اَنۡذِرۡہُمۡ  یَوۡمَ  الۡحَسۡرَۃِ  اِذۡ  قُضِیَ الۡاَمۡرُ ۘ وَ  ہُمۡ   فِیۡ غَفۡلَۃٍ   وَّ  ہُمۡ  لَا  یُؤۡمِنُوۡنَ ﴿۳۹﴾ ",
              urdu_="اور انکو حسرت اور افسوس کے دن سے ڈرا دو جب بات فیصل کر دی جائے گی اور وہ غفلت میں پڑے ہوئے ہیں اور ایمان نہیں لاتے۔",
          ),

      Verse(
              id_=40,
              arabic_="اِنَّا نَحۡنُ نَرِثُ الۡاَرۡضَ وَ مَنۡ عَلَیۡہَا وَ اِلَیۡنَا یُرۡجَعُوۡنَ ﴿٪۴۰﴾ ",
              urdu_="ہم ہی زمین کے اور جو لوگ اس پر بستے ہیں ان کے وارث ہونگے۔ اور ہماری ہی طرف انکو لوٹنا ہو گا۔",
          ),

      Verse(
              id_=41,
              arabic_="وَ اذۡکُرۡ  فِی الۡکِتٰبِ اِبۡرٰہِیۡمَ ۬ؕ اِنَّہٗ  کَانَ صِدِّیۡقًا  نَّبِیًّا ﴿۴۱﴾ ",
              urdu_="اور کتاب میں ابراہیم کا ذکر کرو بیشک وہ نہایت سچے تھے پیغمبر تھے۔",
          ),

      Verse(
              id_=42,
              arabic_="اِذۡ  قَالَ لِاَبِیۡہِ یٰۤاَبَتِ لِمَ تَعۡبُدُ مَا لَا یَسۡمَعُ وَ لَا یُبۡصِرُ وَ لَا یُغۡنِیۡ عَنۡکَ شَیۡئًا ﴿۴۲﴾ ",
              urdu_="جب انہوں نے اپنے باپ سے کہا کہ ابا آپ ایسی چیزوں کو کیوں پوجتے ہیں جو نہ سنیں اور نہ دیکھیں اور نہ آپ کے کچھ کام آسکیں۔",
          ),

      Verse(
              id_=43,
              arabic_="یٰۤاَبَتِ اِنِّیۡ قَدۡ جَآءَنِیۡ مِنَ الۡعِلۡمِ  مَا لَمۡ یَاۡتِکَ فَاتَّبِعۡنِیۡۤ  اَہۡدِکَ صِرَاطًا سَوِیًّا ﴿۴۳﴾ ",
              urdu_="ابا مجھے ایسا علم ملا ہے جو آپ کو نہیں ملا تو میرے ساتھ ہو جائیے میں آپ کو سیدھی راہ پر لے چلوں گا۔",
          ),

      Verse(
              id_=44,
              arabic_="یٰۤاَبَتِ لَا تَعۡبُدِ الشَّیۡطٰنَ ؕ اِنَّ الشَّیۡطٰنَ  کَانَ  لِلرَّحۡمٰنِ عَصِیًّا ﴿۴۴﴾ ",
              urdu_="ابا شیطان کی پوجا نہ کیجئے بیشک شیطان اللہ کا نافرمان ہے۔",
          ),

      Verse(
              id_=45,
              arabic_="یٰۤاَبَتِ اِنِّیۡۤ  اَخَافُ اَنۡ یَّمَسَّکَ عَذَابٌ مِّنَ الرَّحۡمٰنِ فَتَکُوۡنَ لِلشَّیۡطٰنِ وَلِیًّا ﴿۴۵﴾ ",
              urdu_="ابا مجھے ڈر لگتا ہے کہ آپ کو اللہ کا عذاب آ پکڑے تو آپ شیطان کے ساتھی ہو جائیں۔",
          ),

      Verse(
              id_=46,
              arabic_="قَالَ  اَرَاغِبٌ اَنۡتَ عَنۡ  اٰلِہَتِیۡ یٰۤـاِبۡرٰہِیۡمُ ۚ لَئِنۡ لَّمۡ تَنۡتَہِ لَاَرۡجُمَنَّکَ وَ اہۡجُرۡنِیۡ  مَلِیًّا ﴿۴۶﴾ ",
              urdu_="اس نے کہا کہ ابراہیم! کیا تو میرے معبودوں کے خلاف ہے؟ اگر تو باز نہ آئے گا تو میں تجھے سنگسار کر دوں گا۔ اور تو ہمیشہ کے لئے مجھ سے دور ہو جا۔",
          ),

      Verse(
              id_=47,
              arabic_="قَالَ سَلٰمٌ  عَلَیۡکَ ۚ سَاَسۡتَغۡفِرُ  لَکَ  رَبِّیۡ ؕ اِنَّہٗ  کَانَ  بِیۡ  حَفِیًّا ﴿۴۷﴾ ",
              urdu_="ابراہیم نے سلام علیکم کہا اور کہا کہ میں آپ کے لئے اپنے پروردگار سے بخشش مانگوں گا۔ بیشک وہ مجھ پر نہایت مہربان ہے۔",
          ),

      Verse(
              id_=48,
              arabic_="وَ اَعۡتَزِلُکُمۡ وَ مَا تَدۡعُوۡنَ مِنۡ دُوۡنِ اللّٰہِ  وَ اَدۡعُوۡا  رَبِّیۡ ۫ۖ عَسٰۤی اَلَّاۤ  اَکُوۡنَ  بِدُعَآءِ   رَبِّیۡ  شَقِیًّا ﴿۴۸﴾ ",
              urdu_="اور میں آپ لوگوں سے اور جنکو آپ اللہ کے سوا پکارا کرتے ہیں ان سے کنارہ کرتا ہوں اور اپنے پروردگار ہی کو پکاروں گا امید ہے کہ میں اپنے پروردگار کو پکار کر محروم نہیں رہوں گا۔",
          ),

      Verse(
              id_=49,
              arabic_="فَلَمَّا اعۡتَزَلَہُمۡ وَ مَا یَعۡبُدُوۡنَ مِنۡ دُوۡنِ اللّٰہِ ۙ وَہَبۡنَا لَہٗۤ  اِسۡحٰقَ  وَ  یَعۡقُوۡبَ ؕ وَ کُلًّا  جَعَلۡنَا  نَبِیًّا ﴿۴۹﴾",
              urdu_="اور جب ابراہیم ان لوگوں سے اور جنکی وہ اللہ کے سوا پرستش کرتے تھے الگ ہو گئے تو ہم نے انکو اسحٰق اور اسحٰق کو یعقوب بخشے اور ہر ایک کو پیغمبر بنایا۔",
          ),

      Verse(
              id_=50,
              arabic_="وَ وَہَبۡنَا لَہُمۡ مِّنۡ رَّحۡمَتِنَا وَ جَعَلۡنَا لَہُمۡ  لِسَانَ  صِدۡقٍ  عَلِیًّا ﴿٪۵۰﴾ ",
              urdu_="اور ہم نے انکو اپنی رحمت سے بہت سی چیزیں عنایت کیں۔ اور انکا ذکر جمیل بلند کیا۔",
          ),

      Verse(
              id_=51,
              arabic_="وَ اذۡکُرۡ فِی الۡکِتٰبِ مُوۡسٰۤی ۫ اِنَّہٗ  کَانَ مُخۡلَصًا وَّ کَانَ رَسُوۡلًا  نَّبِیًّا ﴿۵۱﴾ ",
              urdu_="اور کتاب میں موسٰی کا بھی ذکر کرو بیشک وہ ہمارے برگزیدہ تھے اور رسول تھے نبی تھے۔",
          ),

      Verse(
              id_=52,
              arabic_="وَ نَادَیۡنٰہُ مِنۡ جَانِبِ الطُّوۡرِ الۡاَیۡمَنِ  وَ قَرَّبۡنٰہُ  نَجِیًّا ﴿۵۲﴾ ",
              urdu_="اور ہم نے انکو طور کی داہنی طرف سے پکارا اور باتیں کرنے کے لئے نزدیک بلایا۔",
          ),

      Verse(
              id_=53,
              arabic_="وَ وَہَبۡنَا لَہٗ  مِنۡ رَّحۡمَتِنَاۤ  اَخَاہُ ہٰرُوۡنَ نَبِیًّا ﴿۵۳﴾ ",
              urdu_="اور اپنی مہربانی سے انکو ان کا بھائی ہارون پیغمبر بنا کر عطا کیا۔",
          ),

      Verse(
              id_=54,
              arabic_="وَ اذۡکُرۡ فِی الۡکِتٰبِ اِسۡمٰعِیۡلَ ۫ اِنَّہٗ کَانَ صَادِقَ الۡوَعۡدِ وَ کَانَ رَسُوۡلًا نَّبِیًّا  ﴿ۚ۵۴﴾",
              urdu_="اور کتاب میں اسمٰعیل کا بھی ذکر کرو وہ وعدے کے سچے اور رسول تھے نبی تھے۔",
          ),

      Verse(
              id_=55,
              arabic_="وَ کَانَ یَاۡمُرُ اَہۡلَہٗ  بِالصَّلٰوۃِ  وَ الزَّکٰوۃِ ۪ وَ کَانَ عِنۡدَ رَبِّہٖ  مَرۡضِیًّا ﴿۵۵﴾ ",
              urdu_="اور وہ اپنے گھر والوں کو نماز اور زکوٰۃ کا حکم کرتے تھے اور اپنے پروردگار کے ہاں پسندیدہ تھے۔",
          ),

      Verse(
              id_=56,
              arabic_="وَ اذۡکُرۡ فِی  الۡکِتٰبِ  اِدۡرِیۡسَ ۫ اِنَّہٗ  کَانَ  صِدِّیۡقًا  نَّبِیًّا ﴿٭ۙ۵۶﴾ ",
              urdu_="اور کتاب میں ادریس کا بھی ذکر کرو۔ وہ بھی نہایت سچے تھے نبی تھے۔",
          ),

      Verse(
              id_=57,
              arabic_="وَّ رَفَعۡنٰہُ  مَکَانًا عَلِیًّا ﴿۵۷﴾ ",
              urdu_="اور ہم نے انکو اونچی جگہ اٹھا لیا تھا۔",
          ),

      Verse(
              id_=58,
              arabic_="اُولٰٓئِکَ الَّذِیۡنَ اَنۡعَمَ اللّٰہُ عَلَیۡہِمۡ مِّنَ النَّبِیّٖنَ مِنۡ ذُرِّیَّۃِ  اٰدَمَ ٭ وَ مِمَّنۡ حَمَلۡنَا مَعَ نُوۡحٍ ۫ وَّ مِنۡ ذُرِّیَّۃِ  اِبۡرٰہِیۡمَ وَ اِسۡرَآءِیۡلَ ۫ وَ مِمَّنۡ ہَدَیۡنَا وَ اجۡتَبَیۡنَا ؕ اِذَا تُتۡلٰی عَلَیۡہِمۡ اٰیٰتُ الرَّحۡمٰنِ  خَرُّوۡا  سُجَّدًا  وَّ  بُکِیًّا ﴿ٛ۵۸﴾ ",
              urdu_="یہ وہ لوگ ہیں جن پر اللہ نے اپنے پیغمبروں میں سے فضل کیا یعنی اولاد آدم میں سے اور ان لوگوں میں سے جنکو ہم نے نوح کے ساتھ کشتی میں سوار کیا اور ابراہیم اور یعقوب کی اولاد میں سے اور ان لوگوں میں سے جنکو ہم نے ہدایت دی اور جنہیں برگزیدہ کیا۔ جب ان کے سامنے ہماری آیتیں پڑھی جاتی تھیں تو سجدے میں گر پڑتے اور روتے رہتے تھے۔",
          ),

      Verse(
              id_=59,
              arabic_="فَخَلَفَ مِنۡۢ بَعۡدِہِمۡ خَلۡفٌ اَضَاعُوا الصَّلٰوۃَ وَ اتَّبَعُوا الشَّہَوٰتِ فَسَوۡفَ یَلۡقَوۡنَ  غَیًّا ﴿ۙ۵۹﴾ ",
              urdu_="پھر ان کے بعد چند ناخلف ان کے جانشین ہوئے جنہوں نے نماز کو ضائع کر دیا۔ اور خواہشات نفسانی کے پیچھے لگ گئے۔ سو عنقریب انکو گمراہی کی سزا ملے گی۔",
          ),

      Verse(
              id_=60,
              arabic_="اِلَّا مَنۡ تَابَ وَ اٰمَنَ وَ عَمِلَ صَالِحًا فَاُولٰٓئِکَ یَدۡخُلُوۡنَ  الۡجَنَّۃَ  وَ لَا یُظۡلَمُوۡنَ  شَیۡئًا ﴿ۙ۶۰﴾ ",
              urdu_="ہاں جس نے توبہ کی اور ایمان لایا اور عمل نیک کئے تو ایسے لوگ بہشت میں داخل ہوں گے اور انکا ذرا نقصان نہ کیا جائے گا۔",
          ),

      Verse(
              id_=61,
              arabic_="جَنّٰتِ عَدۡنِۣ الَّتِیۡ وَعَدَ الرَّحۡمٰنُ عِبَادَہٗ بِالۡغَیۡبِ ؕ اِنَّہٗ کَانَ وَعۡدُہٗ  مَاۡتِیًّا ﴿۶۱﴾ ",
              urdu_="یعنی ہمیشہ کی بہشتوں میں جس کا اللہ نے اپنے بندوں سے وعدہ کیا ہے کہ جو انکی آنکھوں سے پوشیدہ ہے بیشک اس کا وعدہ نیکوکاروں کے سامنے آنے والا ہے۔",
          ),

      Verse(
              id_=62,
              arabic_="لَا یَسۡمَعُوۡنَ فِیۡہَا لَغۡوًا اِلَّا سَلٰمًا ؕ وَ لَہُمۡ  رِزۡقُہُمۡ  فِیۡہَا بُکۡرَۃً  وَّ عَشِیًّا ﴿۶۲﴾ ",
              urdu_="وہ اس میں سلام کے سوا کوئی بیہودہ کلام نہ سنیں گے اور انکو صبح و شام کھانا ملے گا۔",
          ),

      Verse(
              id_=63,
              arabic_="تِلۡکَ الۡجَنَّۃُ الَّتِیۡ نُوۡرِثُ مِنۡ عِبَادِنَا مَنۡ  کَانَ  تَقِیًّا ﴿۶۳﴾ ",
              urdu_="یہی وہ جنت ہے جس کا ہم اپنے بندوں میں سے ایسے شخص کو مالک بنائیں گے جو پرہیزگار ہو گا۔",
          ),

      Verse(
              id_=64,
              arabic_="وَ مَا نَتَنَزَّلُ  اِلَّا بِاَمۡرِ رَبِّکَ ۚ لَہٗ مَا بَیۡنَ اَیۡدِیۡنَا وَ مَا خَلۡفَنَا وَ مَا بَیۡنَ ذٰلِکَ ۚ وَ مَا  کَانَ  رَبُّکَ  نَسِیًّا ﴿ۚ۶۴﴾ ",
              urdu_="اور فرشتوں نے پیغمبر کو جواب دیا کہ ہم تمہارے پروردگار کے حکم کے سوا اتر نہیں سکتے جو کچھ ہمارے آگے ہے اور جو پیچھے ہے اور جو اسکے درمیان ہے سب اسی کا ہے اور تمہارا پروردگار بھولنے والا نہیں۔",
          ),

      Verse(
              id_=65,
              arabic_="رَبُّ السَّمٰوٰتِ وَ الۡاَرۡضِ  وَ مَا بَیۡنَہُمَا فَاعۡبُدۡہُ وَ اصۡطَبِرۡ  لِعِبَادَتِہٖ ؕ ہَلۡ  تَعۡلَمُ  لَہٗ  سَمِیًّا ﴿٪۶۵﴾ ",
              urdu_="یعنی آسمان اور زمین کا اور جو ان دونوں کے درمیان ہے سب کا پروردگار تو اسی کی عبادت کرو اور اسکی عبادت پر ثابت قدم رہو۔ بھلا تم کوئی اس کا ہم نام جانتے ہو۔",
          ),

      Verse(
              id_=66,
              arabic_="وَ یَقُوۡلُ الۡاِنۡسَانُ ءَ اِذَا مَا مِتُّ  لَسَوۡفَ اُخۡرَجُ  حَیًّا ﴿۶۶﴾",
              urdu_="اور کافر انسان کہتا ہے کہ جب میں مر جاؤں گا تو کیا زندہ کر کے نکالا جاؤں گا؟",
          ),

      Verse(
              id_=67,
              arabic_="اَوَ لَا یَذۡکُرُ الۡاِنۡسَانُ اَنَّا خَلَقۡنٰہُ مِنۡ قَبۡلُ  وَ لَمۡ  یَکُ شَیۡئًا ﴿۶۷﴾ ",
              urdu_="کیا ایسا انسان یاد نہیں کرتا کہ ہم نے اس کو پہلے بھی تو پیدا کیا تھا جبکہ وہ کچھ بھی چیز نہ تھا۔",
          ),

      Verse(
              id_=68,
              arabic_="فَوَ رَبِّکَ لَنَحۡشُرَنَّہُمۡ وَ الشَّیٰطِیۡنَ ثُمَّ لَنُحۡضِرَنَّہُمۡ  حَوۡلَ  جَہَنَّمَ  جِثِیًّا ﴿ۚ۶۸﴾ ",
              urdu_="سو تمہارے پروردگار کی قسم! ہم انکو جمع کریں گے اور شیطانوں کو بھی۔ پھر ان سب کو جہنم کے گرد حاضر کریں گے اور وہ گھٹنوں پر گرے ہوئے ہوں گے۔",
          ),

      Verse(
              id_=69,
              arabic_="ثُمَّ  لَنَنۡزِعَنَّ مِنۡ کُلِّ  شِیۡعَۃٍ  اَیُّہُمۡ  اَشَدُّ عَلَی الرَّحۡمٰنِ عِتِیًّا ﴿ۚ۶۹﴾ ",
              urdu_="پھر ہر گروہ میں سے ہم ایسے لوگوں کو کھینچ نکالیں گے جو رحمٰن کے مقابلے میں سخت سرکشی کرتے تھے۔",
          ),

      Verse(
              id_=70,
              arabic_="ثُمَّ لَنَحۡنُ اَعۡلَمُ بِالَّذِیۡنَ ہُمۡ اَوۡلٰی بِہَا صِلِیًّا ﴿۷۰﴾ ",
              urdu_="پھر ہم ان لوگوں سے خوب واقف ہیں جو اس میں داخل ہونے کے زیادہ لائق ہیں۔",
          ),

      Verse(
              id_=71,
              arabic_="وَ اِنۡ مِّنۡکُمۡ  اِلَّا وَارِدُہَا ۚ کَانَ عَلٰی رَبِّکَ حَتۡمًا مَّقۡضِیًّا ﴿ۚ۷۱﴾ ",
              urdu_="اور تم میں سے کوئی شخص نہیں مگر اسے اس پر گذرنا ہو گا۔ یہ تمہارے پروردگار پر لازم ہے اور مقرر ہے۔",
          ),

      Verse(
              id_=72,
              arabic_="ثُمَّ نُنَجِّی الَّذِیۡنَ اتَّقَوۡا وَّ نَذَرُ الظّٰلِمِیۡنَ  فِیۡہَا جِثِیًّا ﴿۷۲﴾ ",
              urdu_="پھر ہم پرہیزگاروں کو نجات دیں گے۔ اور ظالموں کو اس میں گھٹنوں کے بل پڑا ہوا چھوڑ دیں گے۔",
          ),

      Verse(
              id_=73,
              arabic_="وَ اِذَا تُتۡلٰی عَلَیۡہِمۡ اٰیٰتُنَا بَیِّنٰتٍ قَالَ الَّذِیۡنَ کَفَرُوۡا لِلَّذِیۡنَ اٰمَنُوۡۤا ۙ اَیُّ الۡفَرِیۡقَیۡنِ خَیۡرٌ  مَّقَامًا وَّ اَحۡسَنُ نَدِیًّا ﴿۷۳﴾ ",
              urdu_="اور جب ان لوگوں کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو جو کافر ہیں وہ مومنوں سے کہتے ہیں کہ دونوں فریق میں سے مکان کس کے اچھے اور مجلسیں کس کی بہتر ہیں؟",
          ),

      Verse(
              id_=74,
              arabic_="وَ کَمۡ اَہۡلَکۡنَا قَبۡلَہُمۡ مِّنۡ قَرۡنٍ ہُمۡ اَحۡسَنُ  اَثَاثًا  وَّ رِءۡیًا ﴿۷۴﴾ ",
              urdu_="اور ہم نے ان سے پہلے بہت سی امتیں ہلاک کر دیں۔ وہ لوگ ان سے ٹھاٹھ باٹھ اور نمود و نمائش میں کہیں اچھے تھے۔",
          ),

      Verse(
              id_=75,
              arabic_="قُلۡ مَنۡ کَانَ فِی الضَّلٰلَۃِ  فَلۡیَمۡدُدۡ لَہُ الرَّحۡمٰنُ مَدًّا ۬ۚ حَتّٰۤی  اِذَا رَاَوۡا مَا یُوۡعَدُوۡنَ  اِمَّا  الۡعَذَابَ وَ اِمَّا السَّاعَۃَ ؕ فَسَیَعۡلَمُوۡنَ مَنۡ ہُوَ شَرٌّ مَّکَانًا  وَّ  اَضۡعَفُ  جُنۡدًا ﴿۷۵﴾ ",
              urdu_="کہدو کہ جو شخص گمراہی میں پڑا ہوا ہے۔ رحمٰن اسکو آہستہ آہستہ مہلت دیئے جاتا ہے یہاں تک کہ جب اس چیز کو دیکھ لیں گے جس کا ان سے وعدہ کیا جاتا ہے خواہ عذاب اور خواہ قیامت تو اس وقت جان لیں گے کہ مکان کس کا برا ہے اور لشکر کس کا کمزور ہے۔",
          ),

      Verse(
              id_=76,
              arabic_="وَ یَزِیۡدُ اللّٰہُ  الَّذِیۡنَ اہۡتَدَوۡا ہُدًی ؕ وَ الۡبٰقِیٰتُ الصّٰلِحٰتُ خَیۡرٌ عِنۡدَ رَبِّکَ ثَوَابًا وَّ  خَیۡرٌ  مَّرَدًّا ﴿۷۶﴾ ",
              urdu_="اور جو لوگ ہدایت یاب ہیں اللہ انکو زیادہ ہدایت دیتا ہے اور نیکیاں جو باقی رہنے والی ہیں وہ تمہارے پروردگار کے ہاں صلے کے لحاظ سے خوب اور انجام کے اعتبار سے بہتر ہیں۔",
          ),

      Verse(
              id_=77,
              arabic_="اَفَرَءَیۡتَ الَّذِیۡ کَفَرَ بِاٰیٰتِنَا وَ قَالَ لَاُوۡتَیَنَّ  مَالًا  وَّ  وَلَدًا ﴿ؕ۷۷﴾ ",
              urdu_="بھلا تم نے اس شخص کو دیکھا جس نے ہماری آیتوں سے کفر کیا اور کہنے لگا کہ اگر میں ازسرنو زندہ ہوا بھی تو یہی مال اور اولاد مجھے وہاں ملے گا۔",
          ),

      Verse(
              id_=78,
              arabic_="اَطَّلَعَ  الۡغَیۡبَ اَمِ  اتَّخَذَ عِنۡدَ الرَّحۡمٰنِ  عَہۡدًا ﴿ۙ۷۸﴾ ",
              urdu_="کیا اس نے غیب کی خبر پا لی ہے یا اللہ کے یہاں سے عہد لے لیا ہے؟",
          ),

      Verse(
              id_=79,
              arabic_="کَلَّا ؕ سَنَکۡتُبُ مَا یَقُوۡلُ وَ نَمُدُّ  لَہٗ  مِنَ الۡعَذَابِ  مَدًّا ﴿ۙ۷۹﴾ ",
              urdu_="ہر گز نہیں۔ یہ جو کچھ کہتا ہے ہم اس کو لکھتے جاتے اور اسکے لئے آہستہ آہستہ عذاب بڑھاتے جاتے ہیں۔",
          ),

      Verse(
              id_=80,
              arabic_="وَّ  نَرِثُہٗ  مَا یَقُوۡلُ وَ یَاۡتِیۡنَا  فَرۡدًا ﴿۸۰﴾ ",
              urdu_="اور جو چیزیں یہ بتاتا ہے انکے ہم وارث ہوں گے اور یہ اکیلا ہمارے سامنے آئے گا۔",
          ),

      Verse(
              id_=81,
              arabic_="وَ اتَّخَذُوۡا مِنۡ دُوۡنِ اللّٰہِ اٰلِہَۃً  لِّیَکُوۡنُوۡا  لَہُمۡ  عِزًّا ﴿ۙ۸۱﴾ ",
              urdu_="اور ان لوگوں نے اللہ کے سوا اور معبود بنا لئے ہیں تاکہ وہ ان کے لئے موجب عزت ہوں۔",
          ),

      Verse(
              id_=82,
              arabic_="کَلَّا ؕ سَیَکۡفُرُوۡنَ  بِعِبَادَتِہِمۡ وَ یَکُوۡنُوۡنَ  عَلَیۡہِمۡ  ضِدًّا ﴿٪۸۲﴾ ",
              urdu_="ہرگز نہیں وہ معبود ان باطل انکی پرستش سے انکار کریں گے اور ان کے دشمن و مخالف ہوں گے۔",
          ),

      Verse(
              id_=83,
              arabic_="اَلَمۡ  تَرَ  اَنَّـاۤ  اَرۡسَلۡنَا الشَّیٰطِیۡنَ عَلَی الۡکٰفِرِیۡنَ  تَؤُزُّہُمۡ   اَزًّا ﴿ۙ۸۳﴾ ",
              urdu_="کیا تم نے نہیں دیکھا کہ ہم نے شیطانوں کو کافروں پر چھوڑ رکھا ہے کہ وہ انکو خوب اکساتے رہتے ہیں۔",
          ),

      Verse(
              id_=84,
              arabic_="فَلَا  تَعۡجَلۡ عَلَیۡہِمۡ ؕ اِنَّمَا  نَعُدُّ  لَہُمۡ  عَدًّا ﴿ۚ۸۴﴾ ",
              urdu_="تو تم ان پر عذاب کے لئے جلدی نہ کرو۔ کہ ہم تو ان کے لئے دن شمار کر رہے ہیں۔",
          ),

      Verse(
              id_=85,
              arabic_="یَوۡمَ نَحۡشُرُ الۡمُتَّقِیۡنَ  اِلَی الرَّحۡمٰنِ وَفۡدًا ﴿ۙ۸۵﴾ ",
              urdu_="جس روز ہم پرہیزگاروں کو رحمٰن کے سامنے بطور مہمان جمع کریں گے۔",
          ),

      Verse(
              id_=86,
              arabic_="وَّ نَسُوۡقُ الۡمُجۡرِمِیۡنَ اِلٰی جَہَنَّمَ وِرۡدًا ﴿ۘ۸۶﴾ ",
              urdu_="اور گناہگاروں کو دوزخ کی طرف پیاسے ہانک لے جائیں گے۔",
          ),

      Verse(
              id_=87,
              arabic_="لَا یَمۡلِکُوۡنَ الشَّفَاعَۃَ  اِلَّا مَنِ اتَّخَذَ عِنۡدَ  الرَّحۡمٰنِ  عَہۡدًا ﴿ۘ۸۷﴾ ",
              urdu_="تو لوگ کسی کی سفارش کا اختیار نہ رکھیں گے مگر جس نے رحمٰن سے اقرار لے لیا ہو۔",
          ),

      Verse(
              id_=88,
              arabic_="وَ قَالُوا  اتَّخَذَ  الرَّحۡمٰنُ  وَلَدًا ﴿ؕ۸۸﴾ ",
              urdu_="اور کہتے ہیں کہ رحمٰن بیٹا رکھتا ہے۔",
          ),

      Verse(
              id_=89,
              arabic_="لَقَدۡ  جِئۡتُمۡ  شَیۡئًا  اِدًّا ﴿ۙ۸۹﴾ ",
              urdu_="ایسا کہنے والو یہ تو تم بری بات زبان پر لاتے ہو۔",
          ),

      Verse(
              id_=90,
              arabic_="تَکَادُ السَّمٰوٰتُ یَتَفَطَّرۡنَ مِنۡہُ وَ تَنۡشَقُّ الۡاَرۡضُ وَ تَخِرُّ الۡجِبَالُ ہَدًّا ﴿ۙ۹۰﴾ ",
              urdu_="قریب ہے کہ اس جھوٹ سے آسمان پھٹ پڑیں اور زمین شق ہو جائے اور پہاڑ پارہ پارہ ہو کر گر پڑیں۔",
          ),

      Verse(
              id_=91,
              arabic_="اَنۡ  دَعَوۡا  لِلرَّحۡمٰنِ  وَلَدًا ﴿ۚ۹۱﴾ ",
              urdu_="کہ انہوں نے رحمٰن کے لئے بیٹا تجویز کیا۔",
          ),

      Verse(
              id_=92,
              arabic_="وَ مَا یَنۡۢبَغِیۡ لِلرَّحۡمٰنِ اَنۡ  یَّتَّخِذَ  وَلَدًا ﴿ؕ۹۲﴾ ",
              urdu_="اور رحمٰن کو شایاں نہیں کہ کسی کو بیٹا بنائے۔",
          ),

      Verse(
              id_=93,
              arabic_="اِنۡ کُلُّ مَنۡ فِی السَّمٰوٰتِ وَ الۡاَرۡضِ  اِلَّاۤ اٰتِی  الرَّحۡمٰنِ  عَبۡدًا ﴿ؕ۹۳﴾ ",
              urdu_="ہر شخص جو آسمانوں اور زمین میں ہے رحمٰن کے روبرو بطور بندہ ہی آئے گا۔",
          ),

      Verse(
              id_=94,
              arabic_="لَقَدۡ  اَحۡصٰہُمۡ  وَ عَدَّہُمۡ  عَدًّا ﴿ؕ۹۴﴾ ",
              urdu_="اس نے ان سب کو اپنے علم سے گھیر رکھا اور ایک ایک کو شمار کر رکھا ہے۔",
          ),

      Verse(
              id_=95,
              arabic_="وَ  کُلُّہُمۡ  اٰتِیۡہِ  یَوۡمَ الۡقِیٰمَۃِ   فَرۡدًا ﴿۹۵﴾ ",
              urdu_="اور یہ سب قیامت کے دن اس کے سامنے اکیلے اکیلے حاضر ہوں گے۔",
          ),

      Verse(
              id_=96,
              arabic_="اِنَّ الَّذِیۡنَ اٰمَنُوۡا وَ عَمِلُوا الصّٰلِحٰتِ سَیَجۡعَلُ  لَہُمُ  الرَّحۡمٰنُ  وُدًّا ﴿۹۶﴾ ",
              urdu_="اور جو لوگ ایمان لائے اور عمل نیک کئے رحمٰن انکی محبت مخلوقات کے دل میں پیدا کر دے گا۔",
          ),

      Verse(
              id_=97,
              arabic_="فَاِنَّمَا یَسَّرۡنٰہُ بِلِسَانِکَ لِتُبَشِّرَ بِہِ الۡمُتَّقِیۡنَ  وَ  تُنۡذِرَ  بِہٖ  قَوۡمًا  لُّدًّا ﴿۹۷﴾ ",
              urdu_="اے پیغمبر ﷺ ہم نے یہ قرآن تمہاری زبان میں آسان بنا کر  نازل کیا ہے تاکہ تم اس سے پرہیزگاروں کو خوشخبری پہنچا دو اور جھگڑالوؤں کو ڈر سنا دو۔",
          ),

      Verse(
              id_=98,
              arabic_="وَ کَمۡ  اَہۡلَکۡنَا قَبۡلَہُمۡ مِّنۡ قَرۡنٍ ؕ ہَلۡ تُحِسُّ مِنۡہُمۡ مِّنۡ  اَحَدٍ اَوۡ تَسۡمَعُ لَہُمۡ رِکۡزًا ﴿٪۹۸﴾ ",
              urdu_="اور ہم نے ان سے پہلے بہت سے گروہوں کو ہلاک کر دیا ہے بھلا تم ان میں سے کسی کو دیکھتے ہو یا کہیں انکی بھنک سنتے ہو۔",
          ),
    ),
