              arabic_="طٰہٰ ۚ﴿۱﴾ ",
              urdu_="طٰہٰ۔",
          ),

      Verse(
              id_=2,
              arabic_="مَاۤ   اَنۡزَلۡنَا عَلَیۡکَ  الۡقُرۡاٰنَ  لِتَشۡقٰۤی ۙ﴿۲﴾ ",
              urdu_="اے نبی ﷺ ہم نے تم پر قرآن اس لئے نازل نہیں کیا کہ تم مشقت میں پڑ جاؤ۔",
          ),

      Verse(
              id_=3,
              arabic_="اِلَّا  تَذۡکِرَۃً   لِّمَنۡ  یَّخۡشٰی  ۙ﴿۳﴾",
              urdu_="مگر ہر اس شخص کی نصیحت کے لئے نازل کیا ہے جو خوف رکھتا ہے۔",
          ),

      Verse(
              id_=4,
              arabic_="تَنۡزِیۡلًا مِّمَّنۡ خَلَقَ الۡاَرۡضَ وَ السَّمٰوٰتِ الۡعُلٰی  ؕ﴿۴﴾",
              urdu_="یہ اس ذات کا اتارا ہوا ہے جس نے زمین اور اونچے اونچے آسمان بنائے۔",
          ),

      Verse(
              id_=5,
              arabic_="اَلرَّحۡمٰنُ  عَلَی الۡعَرۡشِ  اسۡتَوٰی  ﴿۵﴾",
              urdu_="یعنی رحمٰن جو عرش پر جلوہ افروز ہوا۔",
          ),

      Verse(
              id_=6,
              arabic_="لَہٗ مَا فِی السَّمٰوٰتِ  وَ مَا فِی الۡاَرۡضِ وَ مَا بَیۡنَہُمَا وَ مَا  تَحۡتَ الثَّرٰی  ﴿۶﴾",
              urdu_="جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور جو کچھ ان دونوں کے بیچ میں ہے اور جو کچھ مٹی کے نیچے ہے سب اسی کا ہے۔",
          ),

      Verse(
              id_=7,
              arabic_="وَ اِنۡ تَجۡہَرۡ بِالۡقَوۡلِ فَاِنَّہٗ یَعۡلَمُ السِّرَّ وَ اَخۡفٰی  ﴿۷﴾",
              urdu_="اور اگر تم پکار کر بات کہو تو کیا ہے وہ تو آہستہ کہی ہوئی اور نہایت پوشیدہ بات تک کو جانتا ہے۔",
          ),

      Verse(
              id_=8,
              arabic_="اَللّٰہُ  لَاۤ  اِلٰہَ  اِلَّا ہُوَ ؕ لَہُ  الۡاَسۡمَآءُ الۡحُسۡنٰی ﴿۸﴾ ",
              urdu_="اللہ وہ معبود برحق ہے کہ اس کے سوا کوئی معبود نہیں ہے اس کے سب نام اچھے ہیں۔",
          ),

      Verse(
              id_=9,
              arabic_="وَ ہَلۡ  اَتٰٮکَ  حَدِیۡثُ  مُوۡسٰی  ۘ﴿۹﴾",
              urdu_="اور کیا تمہیں موسٰی کے حال کی خبر ملی ہے۔",
          ),

      Verse(
              id_=10,
              arabic_="اِذۡ رَاٰ نَارًا فَقَالَ  لِاَہۡلِہِ  امۡکُثُوۡۤا  اِنِّیۡۤ اٰنَسۡتُ نَارًا لَّعَلِّیۡۤ  اٰتِیۡکُمۡ مِّنۡہَا بِقَبَسٍ  اَوۡ  اَجِدُ  عَلَی النَّارِ  ہُدًی ﴿۱۰﴾ ",
              urdu_="جب انہوں نے آگ دیکھی تو اپنے گھر والوں سے کہا کہ تم یہاں ٹھہرو۔ میں نے آگ دیکھی ہے میں وہاں جاتا ہوں شاید اس میں سے میں تمہارے پاس انگارہ لاؤں یا آگ کے مقام سے اپنا رستہ معلوم کر سکوں۔",
          ),

      Verse(
              id_=11,
              arabic_="فَلَمَّاۤ   اَتٰٮہَا  نُوۡدِیَ  یٰمُوۡسٰی ﴿ؕ۱۱﴾ ",
              urdu_="پھر جب وہ وہاں پہنچے تو آواز آئی کہ اے موسٰی۔",
          ),

      Verse(
              id_=12,
              arabic_="اِنِّیۡۤ  اَنَا رَبُّکَ فَاخۡلَعۡ نَعۡلَیۡکَ ۚ اِنَّکَ بِالۡوَادِ  الۡمُقَدَّسِ طُوًی  ﴿ؕ۱۲﴾",
              urdu_="میں تو تمہارا پروردگار ہوں تو اپنے جوتے اتار دو تم یہاں پاک میدان یعنی طوٰی میں ہو۔",
          ),

      Verse(
              id_=13,
              arabic_="وَ  اَنَا  اخۡتَرۡتُکَ  فَاسۡتَمِعۡ  لِمَا یُوۡحٰی ﴿۱۳﴾ ",
              urdu_="اور میں نے تم کو منتخب کر لیا تو جو حکم دیا جائے اسے سنو۔",
          ),

      Verse(
              id_=14,
              arabic_="اِنَّنِیۡۤ  اَنَا اللّٰہُ  لَاۤ  اِلٰہَ   اِلَّاۤ   اَنَا  فَاعۡبُدۡنِیۡ ۙ وَ  اَقِمِ  الصَّلٰوۃَ   لِذِکۡرِیۡ ﴿۱۴﴾",
              urdu_="بیشک میں ہی اللہ ہوں۔ میرے سوا کوئی معبود نہیں تو میری عبادت کیا کرو۔ اور میری یاد کے لئے نماز پڑھا کرو۔",
          ),

      Verse(
              id_=15,
              arabic_="اِنَّ السَّاعَۃَ  اٰتِیَۃٌ  اَکَادُ اُخۡفِیۡہَا لِتُجۡزٰی کُلُّ  نَفۡسٍۭ  بِمَا  تَسۡعٰی ﴿۱۵﴾ ",
              urdu_="قیامت یقیناً آنے والی ہے۔ میں چاہتا ہوں کے اس کے وقت کو پوشیدہ رکھوں تاکہ ہر شخص جو کوشش کرے اس کا بدلہ پائے۔",
          ),

      Verse(
              id_=16,
              arabic_="فَلَا یَصُدَّنَّکَ عَنۡہَا مَنۡ لَّا یُؤۡمِنُ بِہَا وَ اتَّبَعَ  ہَوٰٮہُ  فَتَرۡدٰی ﴿۱۶﴾ ",
              urdu_="تو جو شخص اس پر ایمان نہیں رکھتا اور اپنی خواہش کے پیچھے چلتا ہے کہیں تمکو اس کے یقین سے روک نہ دے تو اس صورت میں تم ہلاک ہو جاؤ۔",
          ),

      Verse(
              id_=17,
              arabic_="وَ  مَا تِلۡکَ بِیَمِیۡنِکَ  یٰمُوۡسٰی ﴿۱۷﴾ ",
              urdu_="اور اے موسٰی یہ تمہارے داہنے ہاتھ میں کیا ہے؟",
          ),

      Verse(
              id_=18,
              arabic_="قَالَ ہِیَ عَصَایَ ۚ اَتَوَکَّوٴُا عَلَیۡہَا وَ اَہُشُّ بِہَا عَلٰی غَنَمِیۡ وَ لِیَ فِیۡہَا مَاٰرِبُ  اُخۡرٰی  ﴿۱۸﴾",
              urdu_="انہوں نے کہا یہ میری لاٹھی ہے اس پر میں ٹیک لگاتا ہوں اور اس سے اپنی بکریوں کے لئے پتے جھاڑتا ہوں اور اس میں میرے اور بھی کئ فائدے ہیں۔",
          ),

      Verse(
              id_=19,
              arabic_="قَالَ  اَلۡقِہَا یٰمُوۡسٰی ﴿۱۹﴾ ",
              urdu_="فرمایا کہ موسٰی اسے زمین پر ڈال دو۔",
          ),

      Verse(
              id_=20,
              arabic_="فَاَلۡقٰہَا فَاِذَا ہِیَ حَیَّۃٌ  تَسۡعٰی  ﴿۲۰﴾",
              urdu_="تو انہوں نے اسکو ڈال دیا اور وہ یکایک سانپ بن کر دوڑنے لگا۔",
          ),

      Verse(
              id_=21,
              arabic_="قَالَ خُذۡہَا وَ لَا تَخَفۡ ٝ سَنُعِیۡدُہَا سِیۡرَتَہَا الۡاُوۡلٰی ﴿۲۱﴾ ",
              urdu_="فرمایا کہ اسے پکڑ لو اور ڈرنا مت۔ ہم اسکو ابھی اسکی پہلی حالت پر لوٹا دیں گے۔",
          ),

      Verse(
              id_=22,
              arabic_="وَ اضۡمُمۡ یَدَکَ  اِلٰی جَنَاحِکَ تَخۡرُجۡ بَیۡضَآءَ  مِنۡ غَیۡرِ  سُوۡٓءٍ  اٰیَۃً  اُخۡرٰی ﴿ۙ۲۲﴾  ",
              urdu_="اور اپنا ہاتھ اپنی بغل میں دبا لو کہ وہ کسی عیب و بیماری کے بغیر سفید چمکتا دمکتا نکلے گا۔ یہ دوسری نشانی ہے۔",
          ),

      Verse(
              id_=23,
              arabic_="لِنُرِیَکَ مِنۡ اٰیٰتِنَا الۡکُبۡرٰی  ﴿ۚ۲۳﴾",
              urdu_="تاکہ ہم تمہیں اپنے بڑے بڑے نشانات دکھائیں۔",
          ),

      Verse(
              id_=24,
              arabic_="اِذۡہَبۡ  اِلٰی  فِرۡعَوۡنَ  اِنَّہٗ  طَغٰی  ﴿٪۲۴﴾",
              urdu_="تم فرعون کے پاس جاؤ کہ وہ سرکش ہو رہا ہے۔",
          ),

      Verse(
              id_=25,
              arabic_="قَالَ  رَبِّ  اشۡرَحۡ  لِیۡ  صَدۡرِیۡ ﴿ۙ۲۵﴾ ",
              urdu_="کہا اے میرے پروردگار اس کام کے لئے میرا سینہ کھولدے۔",
          ),

      Verse(
              id_=26,
              arabic_="وَ  یَسِّرۡ لِیۡۤ   اَمۡرِیۡ ﴿ۙ۲۶﴾",
              urdu_="اور میرا کام آسان کر دے۔",
          ),

      Verse(
              id_=27,
              arabic_="وَ  احۡلُلۡ  عُقۡدَۃً   مِّنۡ  لِّسَانِیۡ ﴿ۙ۲۷﴾ ",
              urdu_="اور میری زبان کی گرہ کھول دے۔",
          ),

      Verse(
              id_=28,
              arabic_="یَفۡقَہُوۡا  قَوۡلِیۡ ﴿۪۲۸﴾ ",
              urdu_="تاکہ وہ میری بات سمجھ لیں۔",
          ),

      Verse(
              id_=29,
              arabic_="وَ اجۡعَلۡ  لِّیۡ  وَزِیۡرًا مِّنۡ  اَہۡلِیۡ ﴿ۙ۲۹﴾ ",
              urdu_="اور میرے گھر والوں میں سے ایک کو میرا وزیر یعنی مددگار مقرر فرما۔",
          ),

      Verse(
              id_=30,
              arabic_="ہٰرُوۡنَ  اَخِی ﴿ۙ۳۰﴾ ",
              urdu_="یعنی میرے بھائی ہارون کو۔",
          ),

      Verse(
              id_=31,
              arabic_="اشۡدُدۡ  بِہٖۤ   اَزۡرِیۡ ﴿ۙ۳۱﴾ ",
              urdu_="اس سے میری کمر کو مضبوط کر۔",
          ),

      Verse(
              id_=32,
              arabic_="وَ  اَشۡرِکۡہُ   فِیۡۤ   اَمۡرِیۡ ﴿ۙ۳۲﴾ ",
              urdu_="اور اسے میرے کام میں شریک کر۔",
          ),

      Verse(
              id_=33,
              arabic_="کَیۡ  نُسَبِّحَکَ  کَثِیۡرًا ﴿ۙ۳۳﴾ ",
              urdu_="تاکہ ہم تیری بہت سی تسبیح کریں۔",
          ),

      Verse(
              id_=34,
              arabic_="وَّ  نَذۡکُرَکَ  کَثِیۡرًا ﴿ؕ۳۴﴾ ",
              urdu_="اور تجھے کثرت سے یاد کریں۔",
          ),

      Verse(
              id_=35,
              arabic_="اِنَّکَ  کُنۡتَ  بِنَا بَصِیۡرًا ﴿۳۵﴾ ",
              urdu_="تو ہم کو ہر حال میں دیکھ رہا ہے۔",
          ),

      Verse(
              id_=36,
              arabic_="قَالَ  قَدۡ  اُوۡتِیۡتَ  سُؤۡلَکَ  یٰمُوۡسٰی ﴿۳۶﴾ ",
              urdu_="فرمایا۔ موسٰی تمہاری دعا قبول کی گئ۔",
          ),

      Verse(
              id_=37,
              arabic_="وَ لَقَدۡ مَنَنَّا عَلَیۡکَ  مَرَّۃً   اُخۡرٰۤی  ﴿ۙ۳۷﴾",
              urdu_="اور ہم نے تم پر ایک بار اور بھی احسان کیا تھا۔",
          ),

      Verse(
              id_=38,
              arabic_="اِذۡ   اَوۡحَیۡنَاۤ  اِلٰۤی  اُمِّکَ  مَا یُوۡحٰۤی ﴿ۙ۳۸﴾ ",
              urdu_="جب ہم نے تمہاری والدہ کو الہام کیا تھا جو تمہیں بتایا جاتا ہے۔",
          ),

      Verse(
              id_=39,
              arabic_="اَنِ اقۡذِفِیۡہِ فِی التَّابُوۡتِ فَاقۡذِفِیۡہِ فِی الۡیَمِّ فَلۡیُلۡقِہِ الۡیَمُّ بِالسَّاحِلِ یَاۡخُذۡہُ عَدُوٌّ لِّیۡ وَ عَدُوٌّ لَّہٗ ؕ وَ اَلۡقَیۡتُ عَلَیۡکَ مَحَبَّۃً مِّنِّیۡ ۬ۚ وَ لِتُصۡنَعَ  عَلٰی عَیۡنِیۡ  ﴿ۘ۳۹﴾",
              urdu_="وہ یہ تھا کہ اسے یعنی موسٰی کو صندوق میں رکھو پھر اس صندوق کو دریا میں ڈالدو تو دریا اسکو کنارے پر ڈال دے گا اور میرا اور اس کا دشمن اسے اٹھا لے گا۔ اور موسٰی میں نے تم پر اپنی طرف سے محبت ڈال دی اور اس لئے کہ  تم میرے سامنے پرورش پاؤ۔",
          ),

      Verse(
              id_=40,
              arabic_="اِذۡ تَمۡشِیۡۤ اُخۡتُکَ فَتَقُوۡلُ ہَلۡ اَدُلُّکُمۡ  عَلٰی مَنۡ  یَّکۡفُلُہٗ ؕ فَرَجَعۡنٰکَ اِلٰۤی اُمِّکَ کَیۡ تَقَرَّ عَیۡنُہَا وَ لَا تَحۡزَنَ ۬ؕ وَ قَتَلۡتَ نَفۡسًا فَنَجَّیۡنٰکَ مِنَ الۡغَمِّ وَ فَتَنّٰکَ فُتُوۡنًا ۬۟ فَلَبِثۡتَ سِنِیۡنَ فِیۡۤ  اَہۡلِ مَدۡیَنَ ۬ۙ ثُمَّ جِئۡتَ عَلٰی قَدَرٍ یّٰمُوۡسٰی ﴿۴۰﴾ ",
              urdu_="جب تمہاری بہن وہاں فرعون کے ہاں گئ اور کہنے لگی کہ میں تمہیں اس کا پتہ دوں جو اسکو پالے پوسے۔ تو اس طریق سے ہم نے تمکو تمہاری ماں کے پاس پہنچا دیا تاکہ انکی آنکھیں ٹھنڈی ہوں اور وہ رنج نہ کریں اور تم نے ایک شخص کو مار ڈالا تھا تو ہم نے تم کو غم سے نجات دی اور ہم نے تمہاری کئ بار آزمائش کی۔ پھر تم کئ سال اہل مدین میں ٹھہرے رہے۔ پھر اے موسٰی تم ایک اندازے پر آ پہنچے۔",
          ),

      Verse(
              id_=41,
              arabic_="وَ اصۡطَنَعۡتُکَ  لِنَفۡسِیۡ ﴿ۚ۴۱﴾ ",
              urdu_="اور میں نے تمکو اپنے کام کے لئے بنایا ہے۔",
          ),

      Verse(
              id_=42,
              arabic_="اِذۡہَبۡ اَنۡتَ وَ اَخُوۡکَ بِاٰیٰتِیۡ وَ لَا تَنِیَا فِیۡ  ذِکۡرِیۡ ﴿ۚ۴۲﴾ ",
              urdu_="تو تم اور تمہارا بھائی دونوں ہماری نشانیاں لے کر جاؤ اور میری یاد میں سستی نہ کرنا۔",
          ),

      Verse(
              id_=43,
              arabic_="اِذۡہَبَاۤ  اِلٰی  فِرۡعَوۡنَ  اِنَّہٗ  طَغٰی  ﴿ۚۖ۴۳﴾",
              urdu_="دونوں فرعون کے پاس جاؤ کہ وہ سرکش ہو رہا ہے۔",
          ),

      Verse(
              id_=44,
              arabic_="فَقُوۡلَا لَہٗ  قَوۡلًا لَّیِّنًا لَّعَلَّہٗ  یَتَذَکَّرُ اَوۡ یَخۡشٰی ﴿۴۴﴾ ",
              urdu_="اور اس سے نرمی سے بات کرنا شاید وہ غور کرے یا ڈر جائے۔",
          ),

      Verse(
              id_=45,
              arabic_="قَالَا رَبَّنَاۤ  اِنَّنَا نَخَافُ اَنۡ یَّفۡرُطَ عَلَیۡنَاۤ اَوۡ  اَنۡ  یَّطۡغٰی  ﴿۴۵﴾",
              urdu_="دونوں کہنے لگے کہ اے ہمارے پروردگار ہمیں خوف ہے کہ وہ ہم پر زیادتی کرنے لگے یا زیادہ سرکش ہو جائے۔",
          ),

      Verse(
              id_=46,
              arabic_="قَالَ لَا تَخَافَاۤ اِنَّنِیۡ مَعَکُمَاۤ  اَسۡمَعُ وَ اَرٰی  ﴿۴۶﴾",
              urdu_="اللہ نے فرمایا کہ ڈرو مت میں تمہارے ساتھ ہوں کہ سنتا اور دیکھتا ہوں۔",
          ),

      Verse(
              id_=47,
              arabic_="فَاۡتِیٰہُ  فَقُوۡلَاۤ  اِنَّا  رَسُوۡلَا  رَبِّکَ فَاَرۡسِلۡ مَعَنَا بَنِیۡۤ  اِسۡرَآءِیۡلَ ۬ۙ  وَ لَا تُعَذِّبۡہُمۡ ؕ قَدۡ جِئۡنٰکَ بِاٰیَۃٍ  مِّنۡ رَّبِّکَ ؕ وَ  السَّلٰمُ  عَلٰی  مَنِ  اتَّبَعَ  الۡہُدٰی ﴿۴۷﴾ ",
              urdu_="اچھا تو اسکے پاس جاؤ اور کہو کہ ہم آپکے پروردگار کے بھیجے ہوئے ہیں۔ تو بنی اسرائیل کو ہمارے ساتھ جانے کی اجازت دیجئیے اور انہیں عذاب نہ کیجئے۔ ہم آپکے پاس آپ کے پروردگار کی طرف سے نشانی لے کر آئے ہیں۔ اور جو ہدایت کی بات مانے اسکو سلامتی ہو۔",
          ),

      Verse(
              id_=48,
              arabic_="اِنَّا  قَدۡ اُوۡحِیَ  اِلَیۡنَاۤ  اَنَّ الۡعَذَابَ عَلٰی مَنۡ  کَذَّبَ  وَ تَوَلّٰی ﴿۴۸﴾ ",
              urdu_="ہماری طرف یہ وحی آئی ہے کہ جو جھٹلائے اور منہ پھیرے اس کے لئے عذاب تیار ہے۔",
          ),

      Verse(
              id_=49,
              arabic_="قَالَ  فَمَنۡ  رَّبُّکُمَا  یٰمُوۡسٰی ﴿۴۹﴾ ",
              urdu_="غرض موسٰی اور ہارون فرعون کے پاس گئے اس نے کہا کہ موسٰی تمہارا پروردگار کون ہے۔",
          ),

      Verse(
              id_=50,
              arabic_="قَالَ رَبُّنَا الَّذِیۡۤ اَعۡطٰی کُلَّ شَیۡءٍ خَلۡقَہٗ  ثُمَّ  ہَدٰی ﴿۵۰﴾ ",
              urdu_="کہا کہ ہمارا پروردگار وہ ہے جس نے ہر چیز کو اسکی شکل و صورت بخشی پھر راہ دکھائی۔",
          ),

      Verse(
              id_=51,
              arabic_="قَالَ  فَمَا  بَالُ  الۡقُرُوۡنِ  الۡاُوۡلٰی ﴿۵۱﴾ ",
              urdu_="کہا تو پہلی قوموں کا کیا حال ہوا؟",
          ),

      Verse(
              id_=52,
              arabic_="قَالَ عِلۡمُہَا عِنۡدَ رَبِّیۡ فِیۡ  کِتٰبٍ ۚ  لَا یَضِلُّ  رَبِّیۡ  وَ لَا  یَنۡسَی ﴿۫۵۲﴾ ",
              urdu_="فرمایا کہ ان کا علم میرے پروردگار کو ہے جو کتاب میں لکھا ہوا ہے میرا پروردگار نہ چوکتا ہے نہ بھولتا ہے۔",
          ),

      Verse(
              id_=53,
              arabic_="الَّذِیۡ جَعَلَ لَکُمُ الۡاَرۡضَ مَہۡدًا وَّ سَلَکَ  لَکُمۡ  فِیۡہَا سُبُلًا وَّ  اَنۡزَلَ مِنَ السَّمَآءِ  مَآءً ؕ فَاَخۡرَجۡنَا بِہٖۤ  اَزۡوَاجًا مِّنۡ  نَّبَاتٍ  شَتّٰی ﴿۵۳﴾ ",
              urdu_="وہ وہی تو ہے جس نے تم لوگوں کے لئے زمین کر فرش بنایا۔ اور اس میں تمہارے لئے رستے بنا دیئے اور آسمان سے پانی برسایا پھر اس سے مختلف نباتات پیدا کیں۔",
          ),

      Verse(
              id_=54,
              arabic_="کُلُوۡا وَ ارۡعَوۡا اَنۡعَامَکُمۡ ؕ اِنَّ  فِیۡ ذٰلِکَ لَاٰیٰتٍ  لِّاُولِی  النُّہٰی ﴿٪۵۴﴾ ",
              urdu_="کہ خود بھی کھاؤ اور اپنے مویشیوں کو بھی چراؤ۔ بیشک ان باتوں میں عقل والوں کے لئے بہت سی نشانیاں ہیں۔",
          ),

      Verse(
              id_=55,
              arabic_="مِنۡہَا خَلَقۡنٰکُمۡ وَ فِیۡہَا نُعِیۡدُکُمۡ  وَ مِنۡہَا نُخۡرِجُکُمۡ  تَارَۃً   اُخۡرٰی ﴿۵۵﴾ ",
              urdu_="اسی زمین سے ہم نے تمکو پیدا کیا اور اسی میں ہم تمہیں لوٹائیں گے اور اسی سے ہم تمہیں دوسری دفعہ نکالیں گے۔",
          ),

      Verse(
              id_=56,
              arabic_="وَ لَقَدۡ اَرَیۡنٰہُ  اٰیٰتِنَا کُلَّہَا فَکَذَّبَ وَ اَبٰی ﴿۵۶﴾ ",
              urdu_="اور ہم نے فرعون کو اپنی سب نشانیاں دکھائیں مگر وہ تکذیب اور انکار ہی کرتا رہا۔",
          ),

      Verse(
              id_=57,
              arabic_="قَالَ  اَجِئۡتَنَا لِتُخۡرِجَنَا مِنۡ  اَرۡضِنَا بِسِحۡرِکَ یٰمُوۡسٰی ﴿۵۷﴾ ",
              urdu_="کہنے لگا کہ موسٰی کیا تم ہمارے پاس اس لئے آئے ہو کہ اپنے جادو کے زور سے ہمیں ہمارے ملک سے نکال دو۔",
          ),

      Verse(
              id_=58,
              arabic_="فَلَنَاۡتِیَنَّکَ بِسِحۡرٍ مِّثۡلِہٖ فَاجۡعَلۡ بَیۡنَنَا وَ بَیۡنَکَ مَوۡعِدًا  لَّا نُخۡلِفُہٗ  نَحۡنُ  وَ لَاۤ  اَنۡتَ  مَکَانًا  سُوًی ﴿۵۸﴾ ",
              urdu_="تو ہم بھی تمہارے مقابل ایسا ہی جادو لائیں گے تو ہمارے اور اپنے درمیان ایک وقت مقرر کر لو کہ نہ تو ہم اسکے خلاف کریں اور نہ تم اور یہ مقابلہ ایک ہموار میدان میں ہو گا۔",
          ),

      Verse(
              id_=59,
              arabic_="قَالَ مَوۡعِدُکُمۡ یَوۡمُ الزِّیۡنَۃِ وَ اَنۡ یُّحۡشَرَ  النَّاسُ ضُحًی ﴿۵۹﴾ ",
              urdu_="موسٰی نے کہا کہ آپ کے لئے یوم زنیت کا وعدہ ہے اور یہ کہ لوگ اس روز دن چڑھے اکٹھے ہو جائیں۔",
          ),

      Verse(
              id_=60,
              arabic_="فَتَوَلّٰی فِرۡعَوۡنُ فَجَمَعَ  کَیۡدَہٗ  ثُمَّ  اَتٰی ﴿۶۰﴾ ",
              urdu_="تو فرعون لوٹ گیا اور اپنے سامان جمع کر کے پھر آیا۔",
          ),

      Verse(
              id_=61,
              arabic_="قَالَ لَہُمۡ مُّوۡسٰی وَیۡلَکُمۡ لَا تَفۡتَرُوۡا عَلَی اللّٰہِ  کَذِبًا فَیُسۡحِتَکُمۡ  بِعَذَابٍ ۚ وَ قَدۡ  خَابَ  مَنِ افۡتَرٰی ﴿۶۱﴾ ",
              urdu_="موسٰی نے ان جادوگروں سے کہا کہ یہ تمہاری بربادی ہو گی۔ اللہ پر جھوٹ نہ باندھو وہ تمہیں عذاب سے فنا کر دے گا۔ اور جس نے جھوٹ باندھا وہ نامراد رہا۔",
          ),

      Verse(
              id_=62,
              arabic_="فَتَنَازَعُوۡۤا اَمۡرَہُمۡ بَیۡنَہُمۡ وَ اَسَرُّوا النَّجۡوٰی  ﴿۶۲﴾",
              urdu_="تو وہ باہم اپنے معاملے میں جھگڑنے اور چپکے چپکے سرگوشی کرنے لگے۔",
          ),

      Verse(
              id_=63,
              arabic_="قَالُوۡۤا  اِنۡ ہٰذٰٮنِ لَسٰحِرٰنِ یُرِیۡدٰنِ اَنۡ یُّخۡرِجٰکُمۡ  مِّنۡ اَرۡضِکُمۡ  بِسِحۡرِہِمَا وَ یَذۡہَبَا بِطَرِیۡقَتِکُمُ  الۡمُثۡلٰی ﴿۶۳﴾ ",
              urdu_="کہنے لگے کہ یہ دونوں جادوگر ہیں چاہتے ہیں کہ اپنے جادو کے زور سے تمکو تمہارے ملک سے نکال دیں۔ اور تمہارے بہترین مذہب کو نابود کر دیں۔",
          ),

      Verse(
              id_=64,
              arabic_="فَاَجۡمِعُوۡا کَیۡدَکُمۡ  ثُمَّ  ائۡتُوۡا  صَفًّا ۚ وَ  قَدۡ  اَفۡلَحَ  الۡیَوۡمَ  مَنِ  اسۡتَعۡلٰی ﴿۶۴﴾ ",
              urdu_="تو تم جادو کا سامان اکٹھا کر لو اور پھر قطار باندھ کر آؤ۔ آج جو غالب رہا وہی کامیاب ہوا۔",
          ),

      Verse(
              id_=65,
              arabic_="قَالُوۡا یٰمُوۡسٰۤی  اِمَّاۤ  اَنۡ تُلۡقِیَ وَ  اِمَّاۤ  اَنۡ نَّکُوۡنَ  اَوَّلَ  مَنۡ  اَلۡقٰی ﴿۶۵﴾ ",
              urdu_="جادوگر بولے کہ موسٰی یا تو تم اپنی چیز ڈالو یا ہم اپنی چیزیں پہلے ڈالتے ہیں۔",
          ),

      Verse(
              id_=66,
              arabic_="قَالَ بَلۡ اَلۡقُوۡا ۚ فَاِذَا حِبَالُہُمۡ وَ عِصِیُّہُمۡ  یُخَیَّلُ   اِلَیۡہِ مِنۡ سِحۡرِہِمۡ  اَنَّہَا  تَسۡعٰی  ﴿۶۶﴾",
              urdu_="موسٰی نے کہا بلکہ تم ہی پہلے ڈالو جب انہوں نے چیزیں ڈالیں تو یکایک انکی رسیاں اور لاٹھیاں موسٰی کے خیال میں ایسی آنے لگیں کہ وہ میدان میں ادھر ادھر دوڑ رہی ہیں۔",
          ),

      Verse(
              id_=67,
              arabic_="فَاَوۡجَسَ  فِیۡ  نَفۡسِہٖ  خِیۡفَۃً  مُّوۡسٰی ﴿۶۷﴾ ",
              urdu_="اس وقت موسٰی نے اپنے دل میں خوف محسوس کیا۔",
          ),

      Verse(
              id_=68,
              arabic_="قُلۡنَا لَا  تَخَفۡ اِنَّکَ اَنۡتَ الۡاَعۡلٰی ﴿۶۸﴾ ",
              urdu_="ہم نے کہا خوف نہ کرو بلاشبہ تم ہی غالب رہو گے۔",
          ),

      Verse(
              id_=69,
              arabic_="وَ اَلۡقِ مَا فِیۡ یَمِیۡنِکَ تَلۡقَفۡ مَا صَنَعُوۡا ؕ اِنَّمَا صَنَعُوۡا کَیۡدُ سٰحِرٍ ؕ وَ لَا  یُفۡلِحُ  السَّاحِرُ  حَیۡثُ  اَتٰی ﴿۶۹﴾ ",
              urdu_="اور جو چیز یعنی لاٹھی تمہارے داہنے ہاتھ میں ہے اسے ڈالدو کہ جو کچھ انہوں نے بنایا ہے اسکو وہ نگل جائے گی جو کچھ انہوں نے بنایا ہے یہ تو جادوگروں کے ہتھکنڈے ہیں اور جادوگر جہاں بھی جائے کامیاب نہیں ہو گا۔",
          ),

      Verse(
              id_=70,
              arabic_="فَاُلۡقِیَ السَّحَرَۃُ  سُجَّدًا قَالُوۡۤا  اٰمَنَّا بِرَبِّ ہٰرُوۡنَ  وَ  مُوۡسٰی ﴿۷۰﴾ ",
              urdu_="اس پر جادوگر سجدے میں گر پڑے کہنے لگے کہ ہم موسٰی اور ہارون کے پروردگار پر ایمان لائے۔",
          ),

      Verse(
              id_=71,
              arabic_="قَالَ اٰمَنۡتُمۡ لَہٗ  قَبۡلَ اَنۡ اٰذَنَ  لَکُمۡ ؕ اِنَّہٗ لَکَبِیۡرُکُمُ الَّذِیۡ عَلَّمَکُمُ السِّحۡرَ ۚ فَلَاُقَطِّعَنَّ  اَیۡدِیَکُمۡ وَ اَرۡجُلَکُمۡ مِّنۡ خِلَافٍ وَّ لَاُصَلِّبَنَّکُمۡ فِیۡ جُذُوۡعِ النَّخۡلِ ۫ وَ لَتَعۡلَمُنَّ  اَیُّنَاۤ   اَشَدُّ  عَذَابًا  وَّ  اَبۡقٰی ﴿۷۱﴾",
              urdu_="فرعون بولا کہ پیشتر اسکے کہ میں تمہیں اجازت دوں تم اس پر ایمان لے آئے۔ بیشک یہ تمہارا بڑا یعنی استاد ہے جس نے تمکو جادو سکھایا ہے۔ سو میں تمہارے ہاتھ اور پاؤں مخالفت سمت سے کٹوا دوں گا اور تمکو کھجوروں کے تنوں پر سولی چڑھوا دوں گا اور تمکو معلوم ہو گا کہ ہم میں سے کس کا عذاب زیادہ سخت اور دیر تک رہنے والا ہے۔",
          ),

      Verse(
              id_=72,
              arabic_="قَالُوۡا  لَنۡ نُّؤۡثِرَکَ عَلٰی مَا جَآءَنَا مِنَ الۡبَیِّنٰتِ وَ الَّذِیۡ فَطَرَنَا فَاقۡضِ مَاۤ  اَنۡتَ قَاضٍ ؕ اِنَّمَا تَقۡضِیۡ ہٰذِہِ  الۡحَیٰوۃَ الدُّنۡیَا ﴿ؕ۷۲﴾ ",
              urdu_="انہوں نے کہا کہ جو دلائل ہمارے پاس آ گئے ہیں ان پر اور جس نے ہم کو پیدا کیا ہے اس پر ہم آپ کو ہرگز ترجیح نہیں دیں گے۔ تو آپ کو جو حکم دینا ہو دے دیجئے اور آپ جو حکم دے سکتے ہیں وہ صرف اسی دنیا کی زندگی میں دے سکتے ہیں۔",
          ),

      Verse(
              id_=73,
              arabic_="اِنَّـاۤ  اٰمَنَّا بِرَبِّنَا لِیَغۡفِرَ لَنَا خَطٰیٰنَا وَ مَاۤ  اَکۡرَہۡتَنَا عَلَیۡہِ  مِنَ  السِّحۡرِ ؕ وَ اللّٰہُ  خَیۡرٌ  وَّ  اَبۡقٰی ﴿۷۳﴾ ",
              urdu_="ہم تو اپنے پروردگار پر ایمان لے آئے تاکہ وہ ہمارے گناہوں کو معاف کرے اور اسے بھی جو آپ نے ہم سے زبردستی جادو کرایا اور اللہ سب سے اچھا ہے اور ہمیشہ باقی رہنے والا ہے۔",
          ),

      Verse(
              id_=74,
              arabic_="اِنَّہٗ  مَنۡ یَّاۡتِ رَبَّہٗ  مُجۡرِمًا فَاِنَّ  لَہٗ جَہَنَّمَ ؕ لَا  یَمُوۡتُ  فِیۡہَا وَ  لَا  یَحۡیٰی ﴿۷۴﴾ ",
              urdu_="جو شخص اپنے پروردگار کے پاس گناہگار ہو کر آئے گا تو اسکے لئے جہنم ہے جس میں نہ وہ مرے گا نہ جئے گا۔",
          ),

      Verse(
              id_=75,
              arabic_="وَ مَنۡ یَّاۡتِہٖ مُؤۡمِنًا قَدۡ عَمِلَ الصّٰلِحٰتِ فَاُولٰٓئِکَ  لَہُمُ  الدَّرَجٰتُ  الۡعُلٰی ﴿ۙ۷۵﴾ ",
              urdu_="اور جو اسکے روبرو ایماندار ہو کر آئے گا اور عمل بھی نیک کئے ہوں گے تو ایسے لوگوں کے لئے اونچے اونچے درجے ہیں۔",
          ),

      Verse(
              id_=76,
              arabic_="جَنّٰتُ عَدۡنٍ  تَجۡرِیۡ مِنۡ  تَحۡتِہَا  الۡاَنۡہٰرُ خٰلِدِیۡنَ فِیۡہَا ؕ وَ ذٰلِکَ جَزٰٓؤُا  مَنۡ  تَزَکّٰی ﴿٪۷۶﴾ ",
              urdu_="یعنی ہمیشہ رہنے کے باغ جنکے نیچے نہریں بہ رہی ہیں۔ ہمیشہ ان میں رہیں گے اور یہ اس شخص کا بدلہ ہے جو پاک ہوا۔",
          ),

      Verse(
              id_=77,
              arabic_="وَ لَقَدۡ اَوۡحَیۡنَاۤ  اِلٰی مُوۡسٰۤی ۬ۙ اَنۡ  اَسۡرِ بِعِبَادِیۡ فَاضۡرِبۡ لَہُمۡ طَرِیۡقًا فِی الۡبَحۡرِ یَبَسًا ۙ لَّا  تَخٰفُ دَرَکًا وَّ لَا  تَخۡشٰی ﴿۷۷﴾ ",
              urdu_="اور ہم نے موسٰی کی طرف وحی بھیجی کہ ہمارے بندوں کو راتوں رات نکال لے جاؤ پھر انکے لئے سمندر میں لاٹھی مار کر خشک رستہ بنا دو پھر تم کو نہ فرعون کے آ پکڑنے کا خوف ہو گا اور نہ غرق ہونے کا ڈر۔",
          ),

      Verse(
              id_=78,
              arabic_="فَاَتۡبَعَہُمۡ فِرۡعَوۡنُ بِجُنُوۡدِہٖ فَغَشِیَہُمۡ مِّنَ  الۡیَمِّ  مَا غَشِیَہُمۡ ﴿ؕ۷۸﴾ ",
              urdu_="پھر فرعون نے اپنے لشکروں کے ساتھ ان کا تعاقب کیا۔ تو سمندر کی موجوں نے ان پر چڑھ کر انہیں ڈھانک لیا یعنی ڈبو دیا۔",
          ),

      Verse(
              id_=79,
              arabic_="وَ اَضَلَّ فِرۡعَوۡنُ  قَوۡمَہٗ  وَ مَا ہَدٰی ﴿۷۹﴾ ",
              urdu_="اور فرعون نے اپنی قوم کو گمراہ کر دیا اور سیدھے رستے پر نہ ڈالا۔",
          ),

      Verse(
              id_=80,
              arabic_="یٰبَنِیۡۤ  اِسۡرَآءِیۡلَ قَدۡ  اَنۡجَیۡنٰکُمۡ مِّنۡ عَدُوِّکُمۡ وَ وٰعَدۡنٰکُمۡ جَانِبَ الطُّوۡرِ الۡاَیۡمَنَ وَ نَزَّلۡنَا عَلَیۡکُمُ الۡمَنَّ وَ السَّلۡوٰی ﴿۸۰﴾ ",
              urdu_="اے آل یعقوب ہم نے تمکو تمہارے دشمن سے نجات دی اور تورات دینے کے لئے تم سے کوہ طور کی داہنی جانب مقرر کی اور تم پر من اور سلوٰی نازل کیا۔",
          ),

      Verse(
              id_=81,
              arabic_="کُلُوۡا مِنۡ طَیِّبٰتِ مَا رَزَقۡنٰکُمۡ وَ لَا تَطۡغَوۡا فِیۡہِ فَیَحِلَّ عَلَیۡکُمۡ غَضَبِیۡ ۚ وَ مَنۡ یَّحۡلِلۡ عَلَیۡہِ غَضَبِیۡ فَقَدۡ ہَوٰی ﴿۸۱﴾ ",
              urdu_="اور حکم دیا کہ جو پاکیزہ چیزیں ہم نے تمکو دی ہیں انکو کھاؤ۔ اور اس میں حد سے نہ نکلنا۔ ورنہ تم پر میرا غضب نازل ہو گا اور جس پر میرا غضب عذاب نازل ہوا وہ ہلاک ہو گیا۔",
          ),

      Verse(
              id_=82,
              arabic_="وَ اِنِّیۡ لَغَفَّارٌ لِّمَنۡ تَابَ وَ اٰمَنَ وَ عَمِلَ صَالِحًا  ثُمَّ  اہۡتَدٰی ﴿۸۲﴾ ",
              urdu_="اور جو توبہ کرے اور ایمان لائے اور عمل نیک کرے پھر سیدھے رستے چلے اسکو میں بخش دینے والا ہوں۔",
          ),

      Verse(
              id_=83,
              arabic_="وَ  مَاۤ   اَعۡجَلَکَ  عَنۡ  قَوۡمِکَ  یٰمُوۡسٰی ﴿۸۳﴾ ",
              urdu_="اور اے موسٰی تم نے اپنی قوم سے آگے چلے آنے میں کیوں جلدی کی؟",
          ),

      Verse(
              id_=84,
              arabic_="قَالَ ہُمۡ اُولَآءِ عَلٰۤی  اَثَرِیۡ وَ عَجِلۡتُ اِلَیۡکَ  رَبِّ  لِتَرۡضٰی ﴿۸۴﴾ ",
              urdu_="کہا وہ میرے پیچھے آ رہے ہیں اور اے میرے پروردگار میں نے تیری طرف آنے کی جلدی اس لئے کی کہ تو خوش ہو جائے۔",
          ),

      Verse(
              id_=85,
              arabic_="قَالَ فَاِنَّا قَدۡ فَتَنَّا قَوۡمَکَ مِنۡۢ بَعۡدِکَ وَ اَضَلَّہُمُ  السَّامِرِیُّ ﴿۸۵﴾ ",
              urdu_="فرمایا کہ ہم نے تمہاری قوم کو تمہارے بعد آزمائش میں ڈالدیا ہے اور سامری نے انکو بہکا دیا ہے۔",
          ),

      Verse(
              id_=86,
              arabic_="فَرَجَعَ مُوۡسٰۤی اِلٰی قَوۡمِہٖ غَضۡبَانَ  اَسِفًا ۬ۚ  قَالَ یٰقَوۡمِ  اَلَمۡ  یَعِدۡکُمۡ  رَبُّکُمۡ وَعۡدًا حَسَنًا ۬ؕ اَفَطَالَ عَلَیۡکُمُ الۡعَہۡدُ اَمۡ  اَرَدۡتُّمۡ اَنۡ یَّحِلَّ عَلَیۡکُمۡ غَضَبٌ  مِّنۡ  رَّبِّکُمۡ فَاَخۡلَفۡتُمۡ مَّوۡعِدِیۡ ﴿۸۶﴾ ",
              urdu_="اور موسٰی غصے اور غم کی حالت میں اپنی قوم کے پاس واپس آئے اور کہنے لگے کہ اے قوم کیا تمہارے پروردگار نے تم سے اچھا وعدہ نہیں کیا تھا؟ کیا میری جدائی کی مدت تمہیں لمبی معلوم ہوئی یا تم نے یہ چاہا کہ تم پر تمہارے پروردگار کی طرف سے غضب نازل ہو۔ اور اس لئے تم نے مجھ سے جو وعدہ کیا تھا اس کے خلاف کیا۔",
          ),

      Verse(
              id_=87,
              arabic_="قَالُوۡا مَاۤ  اَخۡلَفۡنَا مَوۡعِدَکَ بِمَلۡکِنَا وَ لٰکِنَّا حُمِّلۡنَاۤ  اَوۡزَارًا مِّنۡ زِیۡنَۃِ الۡقَوۡمِ فَقَذَفۡنٰہَا فَکَذٰلِکَ اَلۡقَی  السَّامِرِیُّ ﴿ۙ۸۷﴾",
              urdu_="وہ کہنے لگے کہ ہم نے اپنے اختیار سے آپ سے وعدہ خلافی نہیں کی۔ لیکن ہم ان لوگوں کے زیوروں کا بوجھ اٹھائے ہوئے تھے۔ پھر ہم نے اسکو آگ میں ڈال دیا اور اسی طرح سامری نے ڈال دیا۔",
          ),

      Verse(
              id_=88,
              arabic_="فَاَخۡرَجَ لَہُمۡ عِجۡلًا جَسَدًا لَّہٗ خُوَارٌ فَقَالُوۡا ہٰذَاۤ  اِلٰـہُکُمۡ وَ اِلٰہُ  مُوۡسٰی ۬ فَنَسِیَ ﴿ؕ۸۸﴾ ",
              urdu_="تو اس نے انکے لئے ایک بچھڑا بنا دیا یعنی ایک بیجان جسم جس میں گائے کی سی آواز تھی۔ تو لوگ کہنے لگے کہ یہی تمہارا معبود ہے اور یہی موسٰی کا معبود ہے۔ مگر وہ بھول گئے ہیں۔",
          ),

      Verse(
              id_=89,
              arabic_="اَفَلَا  یَرَوۡنَ  اَلَّا  یَرۡجِعُ  اِلَیۡہِمۡ  قَوۡلًا ۬ۙ وَّ لَا یَمۡلِکُ  لَہُمۡ  ضَرًّا  وَّ لَا  نَفۡعًا ﴿٪۸۹﴾ ",
              urdu_="تو کیا یہ لوگ نہیں دیکھتے کہ وہ انکی کسی بات کا جواب نہیں دیتا۔ اور نہ انکے نقصان اور نفع کا کچھ اختیار رکھتا ہے۔",
          ),

      Verse(
              id_=90,
              arabic_="وَ لَقَدۡ قَالَ لَہُمۡ ہٰرُوۡنُ مِنۡ قَبۡلُ یٰقَوۡمِ اِنَّمَا فُتِنۡتُمۡ بِہٖ ۚ وَ  اِنَّ رَبَّکُمُ  الرَّحۡمٰنُ فَاتَّبِعُوۡنِیۡ  وَ اَطِیۡعُوۡۤا  اَمۡرِیۡ ﴿۹۰﴾ ",
              urdu_="اور ہارون نے ان سے پہلے ہی کہہ دیا تھا کہ لوگو اس کے ذریعے تمہیں فتنے میں ڈال دیا گیا ہے اور تمہارا پروردگار تو رحمٰن ہی ہے تو میری پیروی کرو اور میرا کہا مانو۔",
          ),

      Verse(
              id_=91,
              arabic_="قَالُوۡا لَنۡ نَّبۡرَحَ عَلَیۡہِ عٰکِفِیۡنَ حَتّٰی یَرۡجِعَ   اِلَیۡنَا مُوۡسٰی  ﴿۹۱﴾",
              urdu_="وہ کہنے لگے کہ جب تک موسٰی ہمارے پاس واپس نہ آئیں ہم تو اس کی پوجا پر قائم رہیں گے۔",
          ),

      Verse(
              id_=92,
              arabic_="قَالَ یٰہٰرُوۡنُ مَا مَنَعَکَ اِذۡ  رَاَیۡتَہُمۡ ضَلُّوۡۤا ﴿ۙ۹۲﴾ ",
              urdu_="پھر موسٰی نے ہارون سے کہا کہ اے ہارون جب تم نے انکو دیکھا تھا کہ گمراہ ہو گئے ہیں تو تم کو کس چیز نے روکا تھا۔",
          ),

      Verse(
              id_=93,
              arabic_="اَلَّا  تَتَّبِعَنِ ؕ اَفَعَصَیۡتَ   اَمۡرِیۡ ﴿۹۳﴾ ",
              urdu_="یعنی اس بات سے کہ تم میرے پیچھے چلے آؤ بھلا تم نے میرے حکم کے خلاف کیوں کیا؟",
          ),

      Verse(
              id_=94,
              arabic_="قَالَ یَبۡنَؤُمَّ  لَا تَاۡخُذۡ بِلِحۡیَتِیۡ  وَ لَا بِرَاۡسِیۡ ۚ اِنِّیۡ خَشِیۡتُ اَنۡ تَقُوۡلَ فَرَّقۡتَ بَیۡنَ بَنِیۡۤ  اِسۡرَآءِیۡلَ وَ لَمۡ تَرۡقُبۡ قَوۡلِیۡ ﴿۹۴﴾ ",
              urdu_="کہنے لگے کہ اے ماں جائے میری داڑھی اور سر کے بالوں کو نہ پکڑیئے میں تو اس سے ڈرا کہ آپ یہ نہ کہیں کہ تم نے بنی اسرائیل میں تفرقہ ڈال دیا اور میری بات کو ملحوظ نہ رکھا۔",
          ),

      Verse(
              id_=95,
              arabic_="قَالَ فَمَا خَطۡبُکَ  یٰسَامِرِیُّ ﴿۹۵﴾ ",
              urdu_="موسٰی نے فرمایا کہ اے سامری تیرا کیا حال ہے؟",
          ),

      Verse(
              id_=96,
              arabic_="قَالَ بَصُرۡتُ بِمَا لَمۡ یَبۡصُرُوۡا بِہٖ فَقَبَضۡتُ قَبۡضَۃً  مِّنۡ  اَثَرِ الرَّسُوۡلِ فَنَبَذۡتُہَا وَ کَذٰلِکَ سَوَّلَتۡ لِیۡ نَفۡسِیۡ ﴿۹۶﴾ ",
              urdu_="اس نے کہا کہ میں نے ایسی چیز دیکھی جو اوروں نے نہیں دیکھی تو میں نے فرشتے کے نقش پا سے مٹی کی ایک مٹھی بھر لی۔ پھر اسکو بچھڑے کے جسم میں ڈال دیا اور مجھے میرے جی نے اس کام کو اچھا بتایا۔",
          ),

      Verse(
              id_=97,
              arabic_="قَالَ فَاذۡہَبۡ فَاِنَّ لَکَ فِی الۡحَیٰوۃِ  اَنۡ تَقُوۡلَ لَا مِسَاسَ ۪ وَ اِنَّ لَکَ مَوۡعِدًا لَّنۡ تُخۡلَفَہٗ ۚ وَ انۡظُرۡ  اِلٰۤی  اِلٰـہِکَ الَّذِیۡ ظَلۡتَ عَلَیۡہِ عَاکِفًا ؕ لَنُحَرِّقَنَّہٗ  ثُمَّ لَنَنۡسِفَنَّہٗ  فِی الۡیَمِّ  نَسۡفًا ﴿۹۷﴾ ",
              urdu_="موسٰی نے کہا جا تجھ کو دنیا کی زندگی میں یہ سزا ہے کہ کہتا رہے کہ مجھ کو ہاتھ نہ لگانا اور تیرے لئے ایک اور وعدہ ہے یعنی عذاب کا جو تجھ سے ٹل نہ سکے گا۔ اور جس معبود کی پوجا پر تو جما ہوا تھا اسکو دیکھ ہم اسے جلا دیں گے پھر اس کی راکھ کر اڑا کر دریا میں بکھیر دیں گے۔",
          ),

      Verse(
              id_=98,
              arabic_="اِنَّمَاۤ  اِلٰـہُکُمُ  اللّٰہُ  الَّذِیۡ لَاۤ اِلٰہَ  اِلَّا ہُوَ ؕ وَسِعَ  کُلَّ  شَیۡءٍ  عِلۡمًا ﴿۹۸﴾ ",
              urdu_="تمہارا معبود تو اللہ ہی ہے جسکے سوا کوئی معبود نہیں اس کا علم ہر چیز پر محیط ہے۔",
          ),

      Verse(
              id_=99,
              arabic_="کَذٰلِکَ نَقُصُّ عَلَیۡکَ مِنۡ اَنۡۢبَآءِ مَا قَدۡ سَبَقَ ۚ وَ قَدۡ اٰتَیۡنٰکَ مِنۡ لَّدُنَّا ذِکۡرًا ﴿ۖۚ۹۹﴾ ",
              urdu_="اس طرح ہم تم سے وہ حالات بیان کرتے ہیں جو گذر چکے ہیں۔ اور ہم نے تمہیں اپنے پاس سے نصیحت کی کتاب عطا فرمائی ہے۔",
          ),

      Verse(
              id_=100,
              arabic_="مَنۡ اَعۡرَضَ عَنۡہُ فَاِنَّہٗ یَحۡمِلُ یَوۡمَ الۡقِیٰمَۃِ  وِزۡرًا ﴿۱۰۰﴾ۙ ",
              urdu_="جو شخص اس سے منہ پھیرے گا وہ قیامت کے دن گناہ کا بوجھ اٹھائے گا۔",
          ),

      Verse(
              id_=101,
              arabic_="خٰلِدِیۡنَ فِیۡہِ ؕ وَ سَآءَ لَہُمۡ یَوۡمَ الۡقِیٰمَۃِ  حِمۡلًا ﴿۱۰۱﴾ۙ ",
              urdu_="ایسے لوگ ہمیشہ اس عذاب میں مبتلا رہیں گے اور یہ بوجھ قیامت کے روز انکے لئے برا ہوگا۔",
          ),

      Verse(
              id_=102,
              arabic_="یَّوۡمَ یُنۡفَخُ فِی الصُّوۡرِ وَ نَحۡشُرُ الۡمُجۡرِمِیۡنَ  یَوۡمَئِذٍ  زُرۡقًا ﴿۱۰۲﴾ۚۖ ",
              urdu_="جس روز صور پھونکا جائے گا اور ہم گناہگاروں کو یوں اکٹھا کریں گے کہ انکی آنکھیں نیلی نیلی ہوں گی۔",
          ),

      Verse(
              id_=103,
              arabic_="یَّتَخَافَتُوۡنَ بَیۡنَہُمۡ  اِنۡ لَّبِثۡتُمۡ اِلَّا عَشۡرًا ﴿۱۰۳﴾ ",
              urdu_="تو وہ آپس میں آہستہ آہستہ کہیں گے کہ تم دنیا میں صرف دس ہی دن رہے ہو۔",
          ),

      Verse(
              id_=104,
              arabic_="نَحۡنُ اَعۡلَمُ بِمَا یَقُوۡلُوۡنَ اِذۡ یَقُوۡلُ اَمۡثَلُہُمۡ طَرِیۡقَۃً اِنۡ لَّبِثۡتُمۡ اِلَّا یَوۡمًا ﴿۱۰۴﴾٪ ",
              urdu_="جو باتیں یہ کریں گے ہم خوب جانتے ہیں۔ اس وقت ان میں سب سے اچھی راہ والا یعنی عاقل و ہوشمند کہے گا کہ نہیں بلکہ صرف ایک ہی روز ٹھہرے ہو۔",
          ),

      Verse(
              id_=105,
              arabic_="وَ یَسۡـَٔلُوۡنَکَ عَنِ الۡجِبَالِ فَقُلۡ یَنۡسِفُہَا رَبِّیۡ  نَسۡفًا ﴿۱۰۵﴾ۙ ",
              urdu_="اے نبی ﷺ لوگ تم سے پہاڑوں کے بارے میں دریافت کرتے ہیں کہدو کہ میرا رب انکو راکھ بنا کر بکھیر دے گا۔",
          ),

      Verse(
              id_=106,
              arabic_="فَیَذَرُہَا  قَاعًا صَفۡصَفًا ﴿۱۰۶﴾ۙ ",
              urdu_="پھر زمین کو ہموار میدان کر چھوڑے گا۔",
          ),

      Verse(
              id_=107,
              arabic_="لَّا  تَرٰی  فِیۡہَا عِوَجًا  وَّ  لَاۤ   اَمۡتًا ﴿۱۰۷﴾ؕ ",
              urdu_="جس میں تم کوئی نشیب و فراز نہ دیکھو گے۔",
          ),

      Verse(
              id_=108,
              arabic_="یَوۡمَئِذٍ یَّتَّبِعُوۡنَ الدَّاعِیَ لَا عِوَجَ لَہٗ ۚ وَ خَشَعَتِ الۡاَصۡوَاتُ لِلرَّحۡمٰنِ فَلَا تَسۡمَعُ   اِلَّا ہَمۡسًا ﴿۱۰۸﴾ ",
              urdu_="اس روز لوگ پکارنے والے کے پیچھے چلیں گے اور اسکی پیروی سے انحراف نہ کر سکیں گے اور رحمٰن کے سامنے آوازیں پست ہو جائیں گی تو تم ایک ہلکی آواز کے سوا کوئی آواز نہ سنو گے۔",
          ),

      Verse(
              id_=109,
              arabic_="یَوۡمَئِذٍ لَّا تَنۡفَعُ الشَّفَاعَۃُ  اِلَّا مَنۡ اَذِنَ  لَہُ  الرَّحۡمٰنُ  وَ رَضِیَ  لَہٗ  قَوۡلًا ﴿۱۰۹﴾ ",
              urdu_="اس روز کسی کی سفارش کچھ فائدہ نہ دے گی مگر اس شخص کی جسے رحمٰن اجازت دے اور اسکی بات کو پسند فرمائے۔",
          ),

      Verse(
              id_=110,
              arabic_="یَعۡلَمُ مَا بَیۡنَ اَیۡدِیۡہِمۡ  وَ مَا خَلۡفَہُمۡ  وَ لَا یُحِیۡطُوۡنَ  بِہٖ  عِلۡمًا ﴿۱۱۰﴾ ",
              urdu_="جو کچھ انکے آگے ہے اور جو کچھ انکے پیچھے ہے وہ اسکو جانتا ہے اور وہ اپنے علم سے اللہ کے علم کا احاطہ نہیں کر سکتے۔",
          ),

      Verse(
              id_=111,
              arabic_="وَ عَنَتِ الۡوُجُوۡہُ  لِلۡحَیِّ الۡقَیُّوۡمِ ؕ وَ قَدۡ خَابَ  مَنۡ  حَمَلَ  ظُلۡمًا ﴿۱۱۱﴾ ",
              urdu_="اور اس زندہ و قائم کے روبرو منہ لٹک جائیں گے۔ اور جس نے ظلم کا بوجھ اٹھایا وہ نامراد رہا۔",
          ),

      Verse(
              id_=112,
              arabic_="وَ مَنۡ یَّعۡمَلۡ مِنَ الصّٰلِحٰتِ وَ ہُوَ مُؤۡمِنٌ فَلَا یَخٰفُ ظُلۡمًا وَّ لَا ہَضۡمًا  ﴿۱۱۲﴾",
              urdu_="اور جو نیک کام کرے گا اور مومن بھی ہوگا تو اسکو نہ ظلم کا خوف ہو گا اور نہ نقصان کا۔",
          ),

      Verse(
              id_=113,
              arabic_="وَ کَذٰلِکَ اَنۡزَلۡنٰہُ  قُرۡاٰنًا عَرَبِیًّا وَّ صَرَّفۡنَا فِیۡہِ مِنَ الۡوَعِیۡدِ لَعَلَّہُمۡ یَتَّقُوۡنَ اَوۡ  یُحۡدِثُ  لَہُمۡ  ذِکۡرًا ﴿۱۱۳﴾ ",
              urdu_="اور ہم نے اسکو اسی طرح کا قرآن عربی نازل کیا ہے اور اس میں طرح طرح کے ڈراوے بیان کر دیئے ہیں تاکہ لوگ پرہیزگار بنیں۔ یا اللہ انکے لئے نصیحت پیدا کر دے۔",
          ),

      Verse(
              id_=114,
              arabic_="فَتَعٰلَی اللّٰہُ  الۡمَلِکُ الۡحَقُّ ۚ وَ لَا تَعۡجَلۡ بِالۡقُرۡاٰنِ مِنۡ قَبۡلِ اَنۡ یُّقۡضٰۤی اِلَیۡکَ وَحۡیُہٗ ۫ وَ  قُلۡ  رَّبِّ  زِدۡنِیۡ  عِلۡمًا ﴿۱۱۴﴾ ",
              urdu_="تو اللہ جو سچا بادشاہ ہے عالی قدر ہے۔ اور قرآن کی وحی جو تمہاری طرف بھیجی جاتی ہے اس کے پورا ہونے سے پہلے قرآن کے پڑھنے میں جلدی نہ کیا کرو اور دعا کرو کہ میرے پروردگار مجھے اور زیادہ علم دے۔",
          ),

      Verse(
              id_=115,
              arabic_="وَ لَقَدۡ عَہِدۡنَاۤ  اِلٰۤی اٰدَمَ مِنۡ قَبۡلُ فَنَسِیَ  وَ  لَمۡ  نَجِدۡ  لَہٗ  عَزۡمًا ﴿۱۱۵﴾٪ ",
              urdu_="اور ہم نے پہلے آدم سے عہد لیا تھا مگر وہ اسے بھول گئے اور ہم نے ان میں ارادے کی پختگی نہ پائی۔",
          ),

      Verse(
              id_=116,
              arabic_="وَ اِذۡ  قُلۡنَا لِلۡمَلٰٓئِکَۃِ اسۡجُدُوۡا لِاٰدَمَ فَسَجَدُوۡۤا   اِلَّاۤ   اِبۡلِیۡسَ ؕ اَبٰی ﴿۱۱۶﴾ ",
              urdu_="اور جب ہم نے فرشتوں سے کہا کہ آدم کے آگے سجدہ کرو تو سب سجدے میں گر پڑے مگر ابلیس نے انکار کیا۔",
          ),

      Verse(
              id_=117,
              arabic_="فَقُلۡنَا یٰۤـاٰدَمُ  اِنَّ  ہٰذَا عَدُوٌّ لَّکَ وَ لِزَوۡجِکَ فَلَا یُخۡرِجَنَّکُمَا مِنَ الۡجَنَّۃِ فَتَشۡقٰی  ﴿۱۱۷﴾",
              urdu_="اس پر ہم نے فرمایا کہ آدم یہ تمہارا اور تمہاری بیوی کا دشمن ہے تو یہ کہیں تم دونوں کو بہشت سے نہ نکلوا دے۔ پھر تم تکلیف میں پڑ جاؤ۔",
          ),

      Verse(
              id_=118,
              arabic_="اِنَّ  لَکَ  اَلَّا  تَجُوۡعَ  فِیۡہَا وَ لَا  تَعۡرٰی ﴿۱۱۸﴾ۙ ",
              urdu_="یہاں تمکو یہ آسائش ہے کہ نہ بھوکے رہو نہ ننگے۔",
          ),

      Verse(
              id_=119,
              arabic_="وَ اَنَّکَ لَا  تَظۡمَؤُا فِیۡہَا وَ لَا تَضۡحٰی ﴿۱۱۹﴾ ",
              urdu_="اور یہ کہ نہ پیاسے رہو اور نہ دھوپ کھاؤ۔",
          ),

      Verse(
              id_=120,
              arabic_="فَوَسۡوَسَ اِلَیۡہِ الشَّیۡطٰنُ قَالَ یٰۤـاٰدَمُ ہَلۡ اَدُلُّکَ عَلٰی شَجَرَۃِ الۡخُلۡدِ وَ مُلۡکٍ لَّا یَبۡلٰی ﴿۱۲۰﴾ ",
              urdu_="پھر شیطان نے انکے دل میں وسوسہ ڈالا۔ اور کہا کہ آدم بھلا میں تمکو ایسا درخت بتاؤں جو ہمیشہ کی زندگی کا باعث ہو اور ایسی بادشاہت کہ کبھی زائل نہ ہو۔",
          ),

      Verse(
              id_=121,
              arabic_="فَاَکَلَا مِنۡہَا فَبَدَتۡ لَہُمَا سَوۡاٰتُہُمَا وَ طَفِقَا یَخۡصِفٰنِ عَلَیۡہِمَا مِنۡ وَّرَقِ الۡجَنَّۃِ ۫ وَ عَصٰۤی  اٰدَمُ   رَبَّہٗ  فَغَوٰی  ﴿۱۲۱﴾۪ۖ",
              urdu_="سو دونوں نے اس درخت کا پھل کھا لیا تو ان پر انکی شرمگاہیں ظاہر ہو گئیں اور وہ اپنے اوپر بہشت کے پتے چپکانے لگے اور آدم نے اپنے پروردگار کے حکم کے خلاف کیا تو وہ اپنے مقصد سے بے راہ ہو گئے۔",
          ),

      Verse(
              id_=122,
              arabic_="ثُمَّ  اجۡتَبٰہُ رَبُّہٗ  فَتَابَ عَلَیۡہِ  وَ  ہَدٰی ﴿۱۲۲﴾ ",
              urdu_="پھر انکے پروردگار نے انکو نوازا تو ان پر مہربانی سے توجہ فرمائی اور سیدھی راہ بتائی۔",
          ),

      Verse(
              id_=123,
              arabic_="قَالَ اہۡبِطَا مِنۡہَا جَمِیۡعًۢا بَعۡضُکُمۡ لِبَعۡضٍ عَدُوٌّ ۚ فَاِمَّا یَاۡتِیَنَّکُمۡ مِّنِّیۡ ہُدًی ۬ۙ فَمَنِ اتَّبَعَ ہُدَایَ  فَلَا  یَضِلُّ  وَ لَا  یَشۡقٰی  ﴿۱۲۳﴾",
              urdu_="فرمایا کہ تم دونوں یہاں سے نیچے اتر جاؤ۔ تم میں بعض بعض کے دشمن ہوں گے پھر اگر میری طرف سے تمہارے پاس ہدایت آئے تو جو شخص میری ہدایت کی پیروی کرے گا وہ نہ گمراہ ہو گا اور نہ تکلیف میں پڑے گا۔",
          ),

      Verse(
              id_=124,
              arabic_="وَ مَنۡ اَعۡرَضَ عَنۡ ذِکۡرِیۡ فَاِنَّ لَہٗ مَعِیۡشَۃً ضَنۡکًا وَّ نَحۡشُرُہٗ یَوۡمَ الۡقِیٰمَۃِ  اَعۡمٰی ﴿۱۲۴﴾ ",
              urdu_="اور جو میری نصیحت سے منہ پھیرے گا اسکی زندگی تنگ ہو جائے گی اور قیامت کو ہم اسے اندھا کر کے اٹھائیں گے۔",
          ),

      Verse(
              id_=125,
              arabic_="قَالَ رَبِّ  لِمَ حَشَرۡتَنِیۡۤ  اَعۡمٰی وَ قَدۡ کُنۡتُ  بَصِیۡرًا ﴿۱۲۵﴾ ",
              urdu_="وہ کہے گا کہ میرے پروردگار تو نے مجھے اندھا کر کے کیوں اٹھایا میں تو دیکھتا بھالتا تھا۔",
          ),

      Verse(
              id_=126,
              arabic_="قَالَ  کَذٰلِکَ اَتَتۡکَ اٰیٰتُنَا فَنَسِیۡتَہَا ۚ  وَکَذٰلِکَ  الۡیَوۡمَ  تُنۡسٰی ﴿۱۲۶﴾ ",
              urdu_="اللہ فرمائے گا کہ ایسا ہی چاہیے تھا تیرے پاس ہماری آیتیں آئیں تو تو نے انکو بھلا دیا۔ اسی طرح آج ہم تجھ کو بھلا دیں گے۔",
          ),

      Verse(
              id_=127,
              arabic_="وَ کَذٰلِکَ نَجۡزِیۡ مَنۡ اَسۡرَفَ وَ لَمۡ  یُؤۡمِنۡۢ بِاٰیٰتِ رَبِّہٖ ؕ وَ لَعَذَابُ الۡاٰخِرَۃِ اَشَدُّ وَ اَبۡقٰی  ﴿۱۲۷﴾",
              urdu_="اور جو شخص حد سے نکل جائے اور اپنے پروردگار کی آیتوں پر ایمان نہ لائے ہم اسکو ایسا ہی بدلہ دیتے ہیں۔ اور آخرت کا عذاب بہت سخت اور ہمیشہ باقی رہنے والا ہے۔",
          ),

      Verse(
              id_=128,
              arabic_="اَفَلَمۡ یَہۡدِ لَہُمۡ کَمۡ اَہۡلَکۡنَا قَبۡلَہُمۡ مِّنَ الۡقُرُوۡنِ یَمۡشُوۡنَ فِیۡ مَسٰکِنِہِمۡ ؕ اِنَّ فِیۡ ذٰلِکَ  لَاٰیٰتٍ  لِّاُولِی  النُّہٰی ﴿۱۲۸﴾٪ ",
              urdu_="تو کیا یہ بات ان لوگوں کے لئے موجب ہدایت نہ ہوئی کہ ہم ان سے پہلے بہت سے فرقوں کو ہلاک کر چکے ہیں جنکے رہنے کے مقامات میں یہ چلتے پھرتے ہیں عقل والوں کے لئے اس میں بہت سی نشانیاں ہیں۔",
          ),

      Verse(
              id_=129,
              arabic_="وَ لَوۡ لَا کَلِمَۃٌ سَبَقَتۡ مِنۡ رَّبِّکَ لَکَانَ لِزَامًا  وَّ  اَجَلٌ  مُّسَمًّی ﴿۱۲۹﴾ؕ ",
              urdu_="اور اگر ایک بات تمہارے پروردگار کی طرف سے پہلے صادر اور جزائے اعمال کے لئے ایک میعاد مقرر نہ ہو چکی ہوتی تو نزول عذاب لازم ہو جاتا۔",
          ),

      Verse(
              id_=130,
              arabic_="فَاصۡبِرۡ عَلٰی مَا یَقُوۡلُوۡنَ وَ سَبِّحۡ بِحَمۡدِ رَبِّکَ قَبۡلَ طُلُوۡعِ الشَّمۡسِ وَ قَبۡلَ غُرُوۡبِہَا ۚ وَ مِنۡ اٰنَآیِٔ الَّیۡلِ فَسَبِّحۡ وَ اَطۡرَافَ النَّہَارِ لَعَلَّکَ تَرۡضٰی ﴿۱۳۰﴾ ",
              urdu_="پس جو کچھ یہ بکواس کرتے ہیں اس پر صبر کرو۔ اور سورج کے نکلنے سے پہلے اور اسکے غروب ہونے سے پہلے اپنے پروردگار کی تسبیح و تحمید کیا کرو اور رات کی کچھ گھڑیوں میں بھی اسکی تسبیح کیا کرو اور دن کے مختلف اوقات یعنی دوپہر کے وقت بھی تاکہ تم خوش ہو جاؤ۔",
          ),

      Verse(
              id_=131,
              arabic_="وَ لَا تَمُدَّنَّ عَیۡنَیۡکَ  اِلٰی مَا مَتَّعۡنَا بِہٖۤ اَزۡوَاجًا مِّنۡہُمۡ زَہۡرَۃَ الۡحَیٰوۃِ  الدُّنۡیَا ۬ۙ لِنَفۡتِنَہُمۡ فِیۡہِ ؕ وَ  رِزۡقُ  رَبِّکَ خَیۡرٌ  وَّ  اَبۡقٰی  ﴿۱۳۱﴾",
              urdu_="اور کئ طرح کے لوگوں کو جو ہم نے دنیا کی زندگی میں آرائش کی چیزوں سے نوازا ہے تاکہ انکی آزمائش کریں ان پر نگاہ نہ کرنا اور تمہارے پروردگار کی عطا فرمائی ہوئی روزی بہت بہتر اور باقی رہنے والی ہے۔",
          ),

      Verse(
              id_=132,
              arabic_="وَ اۡمُرۡ اَہۡلَکَ بِالصَّلٰوۃِ وَ اصۡطَبِرۡ عَلَیۡہَا ؕ لَا نَسۡـَٔلُکَ رِزۡقًا ؕ نَحۡنُ نَرۡزُقُکَ ؕ وَ الۡعَاقِبَۃُ  لِلتَّقۡوٰی ﴿۱۳۲﴾ ",
              urdu_="اور اپنے گھر والوں کو نماز کا حکم کرو اور اس پر قائم رہو ہم تم سے روزی کے خواستگار نہیں بلکہ تمہیں ہم روزی دیتے ہیں اور نیک انجام اہل تقوٰی کا ہے۔",
          ),

      Verse(
              id_=133,
              arabic_="وَ قَالُوۡا لَوۡ لَا یَاۡتِیۡنَا بِاٰیَۃٍ مِّنۡ رَّبِّہٖ ؕ اَوَ لَمۡ  تَاۡتِہِمۡ بَیِّنَۃُ  مَا فِی الصُّحُفِ الۡاُوۡلٰی ﴿۱۳۳﴾ ",
              urdu_="اور کہتے ہیں کہ یہ پیغمبر اپنے پروردگار کی طرف سے ہمارے پاس کوئی نشانی کیوں نہیں لائے۔ کیا انکے پاس پہلی کتابوں کی نشانی نہیں آئی۔",
          ),

      Verse(
              id_=134,
              arabic_="وَ لَوۡ اَنَّـاۤ  اَہۡلَکۡنٰہُمۡ بِعَذَابٍ مِّنۡ قَبۡلِہٖ لَقَالُوۡا رَبَّنَا لَوۡ لَاۤ  اَرۡسَلۡتَ  اِلَیۡنَا رَسُوۡلًا فَنَتَّبِعَ اٰیٰتِکَ مِنۡ قَبۡلِ اَنۡ  نَّذِلَّ  وَ  نَخۡزٰی  ﴿۱۳۴﴾",
              urdu_="اور اگر ہم انکو پیغمبر کے بھیجنے سے پیشتر کسی عذاب سے ہلاک کر دیتے تو یہ کہتے کہ اے ہمارے پروردگار تو نے ہماری طرف کوئی پیغمبر کیوں نہ بھیجا کہ ہم ذلیل اور رسوا ہونے سے پہلے تیرے احکام کی پیروی کرتے۔",
          ),

      Verse(
              id_=135,
              arabic_="قُلۡ کُلٌّ مُّتَرَبِّصٌ فَتَرَبَّصُوۡا ۚ فَسَتَعۡلَمُوۡنَ مَنۡ  اَصۡحٰبُ الصِّرَاطِ السَّوِیِّ  وَ مَنِ  اہۡتَدٰی ﴿۱۳۵﴾٪ ",
              urdu_="کہدو کہ سب نتائج اعمال کے منتظر ہیں سو تم بھی منتظر رہو۔ عنقریب تمکو معلوم ہو جائے گا کہ دین کے سیدھے رستے پر چلنے والے کون ہیں اور جنت کی طرف راہ پانے والے کون ہیں ہم یا تم۔",
          ),
